vec2 GetIntersectBox(vec3 rayPos, vec3 rayDir, vec3 boxPos, vec3 boxSize)
{
    vec3 diff = rayPos - boxPos;
	vec3 m = 1.0 / rayDir;
    vec3 n = m * diff;
    vec3 k = abs(m) * boxSize;
    vec3 t1 = -n - k;
