CtrlPts ctrlPts;

surface map(vec3 p)
{
	surface result = SURF_NOHIT(1e5);

    // cource
    vec3 cp;
    float d = sdCatmullRomSpline(p, ctrlPts, cp);

    //d = sdRoundBox(p-cp, vec3(0.05), 0.01);
    d = sdBox(p-cp, vec3(0.5, 0.05, 0.5));

    result = opU(result, SURF_LOAD(d, p, cp));

    /*
    // boze
    vec3 q = rotate(p, iTime * 0.2, vec3(0,1,0));
    surface boze = sdBoze(q-vec3(0,0.5,0), vec3(5), abs(sin(iTime *0.5)));

    result = opU(result, boze);
    */

    // boze gate
    vec3 q = rotate(p+vec3(0,-0.15,1.35), -RAD90, vec3(0,1,0));
    surface gate = sdBozeGate(q, vec3(8), 3.);
    result = opU(result, gate);

    // sushi
    vec3 offset = vec3(0, 0.125, 0);
    for(int i = 0; i < 3; i++)
    {
        float t = iTime * CAMERA_SPEED + 0.075 + sin(iTime * (1.+hash11(float(i+6396))))*0.05;
        vec3 sp = catmullRom(fract(t), ctrlPts);
        vec3 fwd = normalize(catmullRom(fract(t+0.02), ctrlPts)- sp);
        vec3 right = cross(fwd, vec3(0,1,0));

        //sp += offset + right * (-0.2 * (float(i-1)+sin(iTime * hash11(float(i+158)))*0.05));
        sp += offset + right * -0.2 * (float(i-1));

        vec3 sq = rotate(p-sp, atan(fwd.z, fwd.x)+RAD90, vec3(0,1,0));
        //vec3 sq = rotate_vertex_position(p-sp, vec3(0,1,0), atan(fwd.z, fwd.x)+RAD90);
        sq = rotate(sq, acos(fwd.y)-RAD90, vec3(1,0,0));

        // maguro
        vec3 col1 = mix(vec3(1,0.8,0.9), vec3(0.95,0,0.01), smoothstep(0.0, 0.2, fract(abs(sq.x + sq.y + sq.z)*10.)));
        // tamago
		vec3 col2 = vec3(0.9,0.87,0.05);
        // sake
        vec3 col3 = mix(vec3(1,0.8,0.7), vec3(0.95,0.3,0.01), smoothstep(0.0, 0.2, fract(abs(sq.x + sq.y + sq.z+2.3)*22.)));
        vec3 col = (i == 0) ? col1 : (i == 1) ? col2 : col3;
        surface sushi = sdSushi(sq, vec3(0.1), col);

        result = opU(result, sushi);
    }

    return result;
}
