float shadow(in vec3 p, in vec3 l)
{
    float t = 0.15;
    float t_max = 20.0;

    float res = 1.0;
    for (int i = 0; i < 16; ++i)
    {
        if (t > t_max) break;

        float d = map(p + t*l).dist;
        if (d < 0.01)
