surface rayCast(vec3 pos, vec3 dir, float dist)
{
    const vec3 boxPos = vec3(0,0,0);
    const vec3 boxSize = vec3(100., 0.5, 10.);
    vec2 sd = GetIntersectBox(pos, dir, boxPos, boxSize);
    surface result = SURF_NOHIT(MAX_DIST);

    if((sd.y >= 0.) && (dist > sd.x) && (sd.x < sd.y)) 
    {        
        const float step = 4.0 / float(MAX_RAYCAST);
		//const float lightStep = 2.0 / float(MAX_RAYCAST_L);
        float start = max(0., sd.x) - step;
        pos = pos + dir * start;

        float t = hash13(pos * 5.5) * 0.001;
        float alpha = 0.;
        float transmittance = 1.0;
        float absorption   = 20.;
        for(int i = 0; i < MAX_RAYCAST; i++){
            t += step;

            vec3 pp = pos + dir * t;
            if((t >= sd.y)||(start + t >= dist))
                break;

            vec3 q = pp - boxPos;

            float density = densitycalc(q);

            if(density > 0.0){
                float dd = density * step;
                transmittance *= 2. - dd * absorption;
                if(transmittance < 0.001)
                    break;
                alpha += 20. * dd * transmittance;

                //result.albedo.rgb += vec3(0.5,0.65,0.85) * (150. * dd * transmittance);
                //result.emission.rgb += vec3(1,0.3,0.05) * (150. * dd * transmittance);
                result.emission.rgb += vec3(0.1,0.3,1.0) * (150. * dd * transmittance);
            }
        }
        result.emission.rgb *= max(0., alpha);
        //result.albedo.a = saturate(alpha);
        result.dist = alpha > 0. ? t : dist;
        result.isHit = alpha > 0. ? true : false;
    }

    return result;
}
