// putting these in a "box" struct leads to precision problems
// on Android... :(
mat4  box_world_to_obj;
mat4  box_obj_to_world;
vec3  box_size;
float box_radius;
bool  side;

vec2 intersect( in vec3 ro, in vec3 rd )
{
    vec2 res = vec2(1e20,-1.0);

    // plane
    {
        float t = (-1.0-ro.y)/rd.y;
        if( t>0.0 ) res = vec2(t,1.0);
    }

    // rounded box
    {
        // convert ray from world to box space
        vec3 rdd = ntransform(box_world_to_obj, rd );
        vec3 roo = ptransform(box_world_to_obj, ro );
        // intersect in box space
        float t;
        if(side)
        {
        	t = roundedboxIntersect(roo,rdd,box_size,box_radius);
        }
        else
        {
        	t = roundedboxIntersect2(roo,rdd,box_size,box_radius);
        }
        if( t>0.0 && t<res.x ) res = vec2(t,2.0);
    }

    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // camera movement	
    float an = 0.15*(iTime-10.0);
    vec3 ta = vec3( 0.0, 0.0, 0.0 );
    vec3 ro = ta + vec3( 2.0*cos(an), 0.2, 2.0*sin(an) );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.6,8.0,0.0) ) );
    vec3 vv =          ( cross(uu,ww));

    // rounded box animation
    box_obj_to_world = 
                       rotate( normalize(vec3(1.0,1.0,0.1)), iTime ); 
    box_world_to_obj = inverse( box_obj_to_world );
    box_size = vec3((-cos(iTime) + 1.) * 0.5) * 0.75;
    box_radius = (cos(iTime) + 1.) * 0.5 *.75;
    side = fragCoord.x < iMouse.x;

    // render
    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (2.0*(fragCoord+o)-iResolution.xy)/iResolution.y;
        #else    
        vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.6*ww );

        // sky
        vec3 col = vec3(0.6,0.75,0.85) - 0.97*rd.y;

        // raymarch geometry
        vec2 tm = intersect( ro, rd );
        if( tm.y>0.0 )
        {
            // shading
            vec3 pos = ro + tm.x*rd;
            vec3 nor;
            vec3 mate;

            if( tm.y<1.5 ) // floor
            {
                nor = vec3(0.0,1.0,0.0);
                mate = 0.35*texture( iChannel0, 0.2*pos.xz ).xyz;
            }
            else // rounded box
            {
                // convert position from world to box space
                vec3 bpos = ptransform(box_world_to_obj,pos);
                // compute normal in box space
                vec3 bnor = roundedboxNormal(bpos,box_size,box_radius);
                // convert normal from box to world space
                nor = ntransform(box_obj_to_world,bnor);
                // sample texture
                mate = 0.35*boxmap( iChannel0, bpos, bnor ).xyz;
            }

            // lighting
            vec3  lig = normalize(vec3(0.8,0.4,-0.6));
            float dif = clamp( dot(nor,lig), 0.0, 1.0 );
            vec3  hal = normalize(lig-rd);
            float sha = step( intersect( pos+0.01*nor, lig ).y, 0.0 );
            float amb = 0.6 + 0.4*nor.y;
            float bou = clamp(0.3-0.7*nor.y,0.0,1.0);
            float spe = clamp(dot(nor,hal),0.0,1.0);
            col  = 4.0*vec3(1.00,0.80,0.60)*dif*sha;
            col += 2.0*vec3(0.20,0.30,0.40)*amb;
            col += 2.0*vec3(0.30,0.20,0.10)*bou;
			col *= mate;            
            col += 0.3*pow(spe,8.0)*dif*sha*(0.04+0.96*pow(clamp(dot(lig,hal),0.0,1.0),5.0));
            col = mix( col, vec3(0.6,0.7,0.8), 1.0-exp(-0.001*tm.x*tm.x) );           
        }

        // vignetting        
        col *= 1.0 - 0.1*dot(p,p);

	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

    // gamma
    tot = pow( tot, vec3(0.45) );

	// grading
    tot = clamp(tot,0.0,1.0);
    tot = tot*tot*(3.0-2.0*tot);

    tot = mix(tot, vec3(1,0,0),max(0.,1. - abs(iMouse.x - fragCoord.x) / 2.));

    fragColor = vec4( tot, 1.0 );
}

// Basic procedural sky shader that simulates atmospheric scattering (Mie and Rayleigh) with
// raymarching. The implementation largely follows the approach of the Scratchapixel tutorial
// https://www.scratchapixel.com/lessons/procedural-generation-virtual-worlds/simulating-sky/
// simulating-colors-of-the-sky and was written with accuracy in mind, not speed. Multiple
// scattering is not taken into account.
//
// SPP can be increased to, e.g, 4 or 8 to produce anti-aliasing. Increasing the
// NUM_PRIMARY_STEPS and/or NUM_SECONDARY_STEPS of the raymarcher will make the rendering more
// accurate but also slower. There are some other options and parameters that can be tweaked as
// well :)
//
// Author: Johan NysjÃ¶

#define SPP 1
#define NUM_PRIMARY_STEPS 30
#define NUM_SECONDARY_STEPS 10
#define RAYLEIGH_SCATTERING_ENABLED 1
#define MIE_SCATTERING_ENABLED 1
#define EQUIRECTANGULAR_VIEW_ENABLED 0
#define ANIMATION_ENABLED 1
#define EXPOSURE 1.0
#define SUN_DIRECTION vec3(0.0, -1.0, 10.0)
#define SUN_INTENSITY 2.0
#define DISPLAY_SUN 0

#define PI 3.141592

struct Ray {
	vec3 origin;
    vec3 direction;
    float t_min;
    float t_max;
};

struct HitInfo {
	float t_min;
    float t_max;
};

struct Camera {
	vec3 eye;
    vec3 center;
    vec3 up;
    float fovy;
    float aspect;
};

void generate_ray(Camera camera, vec2 uv, out Ray ray)
{
	vec3 f = normalize(camera.center - camera.eye);
    vec3 s = cross(f, normalize(camera.up));
    vec3 u = cross(s, f);

    float half_width = tan(0.5 * camera.fovy);
    float half_height = camera.aspect * half_width;
    float x = 2.0 * uv.x - 1.0;
    float y = 2.0 * uv.y - 1.0;

    ray.origin = camera.eye;
    ray.direction = normalize(f + x * half_width * s + y * half_height * u);
}

// Converts a 2D equirectangular texture coordinate to a 3D ray direction, which is stored in
// the output ray along with the origin.
void generate_ray_equirect(vec3 origin, vec2 uv, out Ray ray)
{
    uv = clamp(uv, 0.0, 1.0);

    float theta = PI * (2.0 * uv.s - 1.0);
    float phi = PI * (uv.t - 0.5);
    float x = cos(phi) * sin(theta);
    float y = sin(phi);
    float z = cos(phi) * cos(theta);

    ray.origin = origin;
    ray.direction = normalize(vec3(x, y, z));
}

// R2 low-discrepancy sequence
// Reference: http://extremelearning.com.au/unreasonable-effectiveness-of-quasirandom-sequences/
vec2 ldseq_r2(float index)
{
    float phi = 1.324717957;
    return fract(vec2(index / phi, index / (phi * phi)));
}

float hash2d(vec2 x)
{
    return fract(sin(dot(x, vec2(12.9898, 78.2331))) * 43758.5453);
}

struct SkyParams {
    vec3 earth_center;
	float earth_radius_km;
    float atmosphere_radius_km;
    vec3 mie_scattering;
    vec3 rayleigh_scattering;
    float mie_scale_height_km;
    float rayleigh_scale_height_km;
    float mie_phase_g;
    vec3 sun_direction;
    vec3 sun_intensity;
    float sun_size_rad;
};

// Ray-sphere intersection test that finds the distance to the closest (t_min) and farthest
// (t_max) intersection points between a ray and a solid sphere. Returns true if the ray hits
// the sphere and false otherwise.
bool sphere_hit(vec3 sphere_center, float sphere_radius, Ray ray, out HitInfo hit_info)
{
    vec3 oc = ray.origin - sphere_center;
    float a = dot(ray.direction, ray.direction);
    float b = dot(oc, ray.direction);
    float c = dot(oc, oc) - sphere_radius * sphere_radius;

    float discriminant = b * b - a * c;
    if (discriminant > 0.0) {
		hit_info.t_min = max(ray.t_min, (-b - sqrt(discriminant)) / a);
        hit_info.t_max = (-b + sqrt(discriminant)) / a;
        return hit_info.t_max > ray.t_min && hit_info.t_max < ray.t_max;
    }

    return false;
}

float rayleigh_phase_function(float cos_theta)
{
	return 0.75 * (1.0 + cos_theta * cos_theta);
}

float mie_phase_function(float cos_theta, float g)
{
    float g2 = g * g;
	return 1.5 * ((1.0 - g2) * (1.0 + cos_theta * cos_theta)) /
        ((2.0 + g2) * pow(1.0 + g2 - 2.0 * g * cos_theta, 1.5));
}

vec3 beer_lambert(vec3 sigma_t, float path_length_km)
{
    return exp(-sigma_t * path_length_km);
}

// NOTE: visibility is the same as transmittance in this case
vec3 raymarch_visibility(vec3 world_pos, int num_steps, SkyParams sky_params)
{
	Ray world_ray;
    world_ray.origin = world_pos;
    world_ray.direction = -sky_params.sun_direction;
    world_ray.t_min = 1e-3;
    world_ray.t_max = 1e6;

    vec3 visibility = vec3(1.0, 1.0, 1.0);
    HitInfo hit_info;
    if (sphere_hit(sky_params.earth_center, sky_params.earth_radius_km, world_ray, hit_info)) {
        visibility = vec3(0.0, 0.0, 0.0);
    }
    else if (sphere_hit(sky_params.earth_center, sky_params.atmosphere_radius_km, world_ray,
                        hit_info)) {
        world_ray.t_min = hit_info.t_min;
        world_ray.t_max = hit_info.t_max;

        float path_length_km = world_ray.t_max - world_ray.t_min;
        float step_length_km = path_length_km / float(num_steps);

        float t = world_ray.t_min;
        while (t < world_ray.t_max) {
            vec3 sample_pos = world_ray.origin + (t + 0.5 * step_length_km) * world_ray.direction;
            float height_km = max(0.0, length(sample_pos - sky_params.earth_center) -
                                  sky_params.earth_radius_km);

#if RAYLEIGH_SCATTERING_ENABLED
            float rayleigh_scaling = exp(-height_km / sky_params.rayleigh_scale_height_km);
            visibility *= beer_lambert(sky_params.rayleigh_scattering * rayleigh_scaling,
                                       step_length_km);
#endif

#if MIE_SCATTERING_ENABLED            
            float mie_scaling = exp(-height_km / sky_params.mie_scale_height_km);
            visibility *= beer_lambert(sky_params.mie_scattering * mie_scaling,
                                       step_length_km);
#endif

            t += step_length_km;
        }
    }

    return visibility;
}

void raymarch(Ray world_ray, int num_primary_steps, int num_secondary_steps, SkyParams sky_params,
              out vec3 transmittance, out vec3 luminance)
{
    float path_length_km = world_ray.t_max - world_ray.t_min;
    float step_length_km = path_length_km / float(num_primary_steps);

    transmittance = vec3(1.0, 1.0, 1.0);
    luminance = vec3(0.0, 0.0, 0.0);
    float t = world_ray.t_min;
    while (t < world_ray.t_max) {
        vec3 sample_pos = world_ray.origin + (t + 0.5 * step_length_km) * world_ray.direction;
        float height_km = max(0.0, length(sample_pos - sky_params.earth_center) -
                              sky_params.earth_radius_km);

        vec3 visibility = raymarch_visibility(sample_pos, num_secondary_steps, sky_params);
        float cos_theta = dot(sky_params.sun_direction, -world_ray.direction);

#if RAYLEIGH_SCATTERING_ENABLED
        float rayleigh_scaling = exp(-height_km / sky_params.rayleigh_scale_height_km);        
        transmittance *= beer_lambert(sky_params.rayleigh_scattering * rayleigh_scaling,
                                      step_length_km);
#endif

#if MIE_SCATTERING_ENABLED
        float mie_scaling = exp(-height_km / sky_params.mie_scale_height_km);        
        transmittance *= beer_lambert(sky_params.mie_scattering * mie_scaling,
                                      step_length_km);
#endif

#if RAYLEIGH_SCATTERING_ENABLED
        luminance += transmittance * sky_params.rayleigh_scattering * rayleigh_scaling *
            visibility * rayleigh_phase_function(cos_theta) * sky_params.sun_intensity *
            step_length_km;
#endif        

#if MIE_SCATTERING_ENABLED        
        luminance += transmittance * sky_params.mie_scattering * mie_scaling *
            visibility * mie_phase_function(cos_theta, sky_params.mie_phase_g) *
            sky_params.sun_intensity * step_length_km;
#endif

        t += step_length_km;
    }
}

vec3 tonemap_aces(vec3 x)
{
    float a = 2.51;
    float b = 0.03;
    float c = 2.43;
    float d = 0.59;
    float e = 0.14;

    return clamp((x * (a * x + b)) / (x * (c * x + d) + e), 0.0, 1.0);   
}

vec3 lin2srgb(vec3 color)
{
 	return pow(color, vec3(0.454));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    // NOTE: the Mie and Rayleigh scattering parameter values used here are based on the values
    // used by Bruneton and Neyret in the paper Precomputed Atmospheric Scattering, EGSR, 2008
    SkyParams sky_params;
    sky_params.earth_center = vec3(0.0, 0.0, 0.0);
    sky_params.earth_radius_km = 6360.0;
    sky_params.atmosphere_radius_km = 6420.0;
    sky_params.mie_scattering = vec3(3.0e-3);
    sky_params.rayleigh_scattering = vec3(4.8e-3, 1.35e-2, 3.31e-2);
    sky_params.mie_scale_height_km = 1.2;
    sky_params.rayleigh_scale_height_km = 8.0;
    sky_params.mie_phase_g = 0.76;
    sky_params.sun_direction = normalize(SUN_DIRECTION);
    sky_params.sun_intensity = vec3(SUN_INTENSITY);
    sky_params.sun_size_rad = radians(0.5334);

    Camera camera;
#if 1 // view the atmosphere from earth
    float height = ANIMATION_ENABLED != 0 ? mod(2.0 * iTime, 20.0) + 0.1 : 0.1;
    camera.eye = vec3(0.0, sky_params.earth_radius_km + height, 0.0);
    camera.center = vec3(0.0, sky_params.earth_radius_km + height, -1.0);
    camera.up = vec3(0.0, 1.0, 0.0);
    camera.fovy = radians(125.0);
#else // view the atmosphere from outer space!
    camera.eye = vec3(0.0, 0.0, 4.0 * sky_params.earth_radius_km);
    camera.center = vec3(0.0, 0.0, 0.0);
    camera.up = vec3(0.0, 1.0, 0.0);
    camera.fovy = radians(60.0);
#endif
    camera.aspect = iResolution.y / iResolution.x;

    vec2 rnd = vec2(hash2d(uv), hash2d(uv + 0.1));

   	vec3 output_color = vec3(0.0, 0.0, 0.0);
    for (int i = 0; i < SPP; ++i) {
        int sample_idx = (SPP * iFrame + i) % 1000;
        vec2 aa_offset = fract(ldseq_r2(float(sample_idx)) + rnd) / iResolution.xy;

    	Ray world_ray;
#if EQUIRECTANGULAR_VIEW_ENABLED
        generate_ray_equirect(camera.eye, uv + aa_offset, world_ray);
#else
        generate_ray(camera, uv + aa_offset, world_ray);
#endif
    	world_ray.t_min = 1e-3;
    	world_ray.t_max = 1e6;

    	HitInfo hit_info;
    	if (sphere_hit(sky_params.earth_center, sky_params.atmosphere_radius_km, world_ray,
                       hit_info)) {
        	world_ray.t_min = hit_info.t_min;
        	world_ray.t_max = hit_info.t_max;

            // Check if the ray intersects the earth surface and clamp t_max to the intersection
            // point if that is the case (no need to raymarch beneath the surface, won't reach
            // any sunlight there...)
            bool earth_hit = sphere_hit(sky_params.earth_center, sky_params.earth_radius_km,
                                        world_ray, hit_info);
            world_ray.t_max = earth_hit ? max(hit_info.t_min, world_ray.t_min) : world_ray.t_max;

            // Calculate athmospheric scattering with raymarching
            vec3 transmittance = vec3(1.0, 1.0, 1.0);
            vec3 luminance = vec3(0.0, 0.0, 0.0);
        	raymarch(world_ray, NUM_PRIMARY_STEPS, NUM_SECONDARY_STEPS, sky_params,
                     transmittance, luminance);
            output_color += luminance;

#if DISPLAY_SUN
            // Display the sun as a disk with the specified angular diameter (radians), taking
            // occlusion from the earth into account. Here we don't need to perform an actual
            // disk intersection test but can just check the angle between the primary ray
            // direction and the outgoing sun direction, which is cheaper and simpler :)
            float sun_cos_theta = dot(sky_params.sun_direction, -world_ray.direction);
    		if (!earth_hit && sun_cos_theta >= cos(0.5 * sky_params.sun_size_rad)) {
    			output_color += transmittance * sky_params.sun_intensity;   
    		}
#endif
    	}
    }
    output_color /= float(SPP);

    output_color *= EXPOSURE;
    output_color = tonemap_aces(output_color);
    output_color = lin2srgb(output_color);

    fragColor = vec4(output_color, 1.0);
}
