// based on...
// https://www.shadertoy.com/view/lssfW2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p=(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 curve_p = vec2(0,0);
    float d = 10000.0;
    float di = 0.;
    for(int i = 0;i<1200;i++)
    {
        di = float(i)*.04;
        curve_p.x = -.1*sin(.4*iTime)+.05*float(di)*sin(float(di)+.2*iTime);
        curve_p.y = -.1*cos(.4*iTime)+.05*float(di)*cos(float(di)+.2*iTime);
        d = min(d, length(p-curve_p));

    }

    vec3 col = vec3(2.*d*d,.002 + .25*sin(.001*d*d+.1*length(p)), abs(.5+.13*sin(iTime*.8) - pow(d,.25)));
    float dn = col.x + col.y + col.z;
    col /= dn;
    // Output to screen
    fragColor = vec4(col,1.0);
}

struct Ray
{
    vec3 position;
    vec3 direction;
};

struct Intersection
{
    bool hit;
    vec3 position;
    vec3 color;
    float k;
};

struct Sphere
{
    vec3 position;
    vec3 color;
    float radius;
};

struct Plane
{
    vec3 position;
    vec3 normal;
};

struct Light
{
    vec3 direction;
};

Intersection I_Sphere(Ray ray, Sphere sphere, Light light)
{
    vec3 rs = ray.position - sphere.position;
    float drs = dot(ray.direction, rs);
    float dd = dot(ray.direction, ray.direction);
    float D4 = drs*drs - dd*(dot(rs, rs) - sphere.radius*sphere.radius);
    Intersection i;
    i.color = vec3(0.0);
    float k = (-drs-sqrt(D4))/dd;
    i.hit = k >= 0.0;
    i.position = ray.position + k*ray.direction;
    i.k = k;
    vec3 normal = normalize(i.position - sphere.position);
    i.color = sphere.color * clamp(-dot(normal, light.direction), 0.0, 1.0);
    return i;
}

Intersection I_Plane(Ray ray, Sphere sphere, Plane plane, Light light)
{
    float dn = dot(ray.direction, plane.normal);
    Intersection i;
    i.hit = false;
    i.color = vec3(0.0);
    if (dn != 0.0)
    {
        float k = dot(plane.position - ray.position, plane.normal)/dn;
        i.hit = k >= 0.0;
        i.position = ray.position + k*ray.direction;
        i.k = k;
        int mx = int(mod(i.position.x, 2.0) < 1.0);
        int mz = int(mod(i.position.z, 2.0) < 1.0);
        if ((mx + mz) % 2 == 1) i.color = vec3(0.5);
        else                    i.color = vec3(0.25);
        Ray test;
        test.position = i.position;
        test.direction = -light.direction;
        Intersection shadow = I_Sphere(test, sphere, light);
        if (shadow.hit) i.color -= vec3(0.20);
    }
    return i;
}

Intersection I_Scene(Ray ray, Sphere sphere, Plane plane, Light light)
{
    Intersection i[2];
    i[0] = I_Plane(ray, sphere, plane, light);
    i[1] = I_Sphere(ray, sphere, light);

    Intersection result;
    result.hit = false;
    result.color = vec3(0.0);
    result.k = 1000000000.0;
    for (int n = 0; n < 2; ++n)
    {
        if (i[n].hit)
        {
            if (i[n].k < result.k)
            {
                result = i[n];
            }
        }
    }
    return result;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    Ray eye;
    eye.position = vec3(0.0, 0.0, -1.0);
    eye.direction = normalize(vec3(uv.x, uv.y, 0.0) - eye.position);
    Light light;
    light.direction = normalize(vec3(1.0, -1.0, 1.0));
    Plane plane;
    plane.position = vec3(0.0, -1.0, 0.0);
    plane.normal = normalize(vec3(0.0, 1.0, 0.0));
    Sphere sphere;
    sphere.position = vec3(0.0, sin(iTime), 2.0);
    sphere.color = vec3(1.0);
    sphere.radius = 1.0;
    Intersection i;
    i = I_Scene(eye, sphere, plane, light);
    fragColor = vec4(i.color, 1.0);
}

#define pointcount 5

vec3 getPoint(int i)
{
	switch(i)
    {
    case 0:
        return vec3(0,0,0);
    case 1:
        return vec3(cos(iTime*3.+1.),sin(iTime+1.),cos(iTime));
    case 2:
        return vec3(cos(iTime+2.),sin(iTime+2.),sin(iTime+2.));
    case 3:
        return vec3(cos(iTime+3.),sin(iTime+3.),cos(iTime+3.));
    case 4:
        return vec3(cos(iTime*2.+4.),sin(iTime+5.),cos(iTime-2.));
    } 
}

vec3 getPointColor(int i)
{
	switch(i)
    {
    case 0:
        return vec3(1.0, 1.0, 0.0);
    case 1:
        return vec3(0.0, 1.0, 1.0);
    case 2:
        return vec3(1.0, 0.0, 0.0);
    case 3:
        return vec3(0.0, 1.0, 0.0);
    case 4:
        return vec3(0.0, 0.0, 1.0);
    } 
}


float dist(vec3 pos1, vec3 pos2)
{
    vec3 diff = abs(pos2-pos1);
	//return diff.x*diff.x+diff.y*diff.y+diff.z*diff.z;
    //return min(min(diff.x, diff.y), diff.z);
    return max(max(diff.x, diff.y), diff.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 uv = vec3((fragCoord/iResolution.xy-vec2(0.5,0.5))*4., sin(iTime));

    int bestIndex = 0;
    float shortestDist = dist(uv, getPoint(0));
    for(int i = 1;i<pointcount;i++)
    {
    	float dist = dist(uv, getPoint(i));
        if(dist<shortestDist)
        {
        	shortestDist = dist;
            bestIndex = i;
        }
    }
    vec3 pointColor = getPointColor(bestIndex);
    vec3 color = min(pointColor/(shortestDist), pointColor);

    fragColor = vec4(color,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float DitherFactor = abs(sin(iTime*1.0));

    if (abs(DitherFactor) > .001)
	{
		float RandCos = cos(dot(floor(fragCoord), vec2(347.83451793,3343.28371963)));
		float RandomVal = fract(RandCos * 1000.0);
		float RetVal = 0.0; //(DitherFactor < 0.0) ? 
        if(DitherFactor < RandomVal)
        {
            RetVal = 1.0;
        }

        fragColor = vec4(RetVal);
	}

}

// Hexagon Tiles Saw Blades Pattern
// https://www.shadertoy.com/view/4dX3zl

/*
 * Originally made in Blender nodes for a #nodegolf challenge:
 * https://twitter.com/GelamiSalami/status/1335114160535871490
 *
 * Not really hexagonal tiling, but triangle tiling made to look like a hexagonal one
 * Try fiddling with the constants, really makes some cool patterns!
 *
 * Codegolfed version: https://www.shadertoy.com/view/3sGBWm
 */

// Thanks FabriceNeyret2!
#define S(v) smoothstep(1.5*scale/iResolution.y, 0., v)    

#define pi acos(-1.)
#define tau (2.*pi)

const float scale = 4.;
const float angleOffset = .25;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;

    vec2 pos = uv * scale;

    vec2 md = vec2(sqrt(3.), 1.);
    vec2 mn = md*.5;

    vec2 ta = mod(pos, md)-mn;
    vec2 tb = mod(pos-mn, md)-mn;

    vec2 tri = dot(abs(ta), mn.yx) < sqrt(3.)/4. ? ta : tb;

    tri.x = abs(tri.x)-sqrt(3.)/6.;

    float angle = 1.-(atan(tri.y, tri.x)+pi)*(1./tau);
    float len = length(tri);

    //float sides = 3.; // Regular hexagonal tiling
	float sides = 3.+smoothstep(0.,1.,sin(tau*(iTime+3.)/4.)*.5+.5)*9.;

    float a = angle-floor(sides*angle+angleOffset)/sides;
    float c = cos(a*tau)*len;

    float lin = S(c - .26);
    float lout = 1.-S(c - .08);

    vec3 col = vec3(lin*lout);

    fragColor = vec4(vec3(col),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

     uv -= .5; // -0.5 <> 0.5
     uv.x *= iResolution.x/iResolution.y; 
     float l = abs(uv.x);
     float b = abs(uv.y);

     vec3 c;

     if(l <.4 && b <.2) 
         c = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
     else 
         c = vec3(1.);

    // Output to screen
    fragColor = vec4(c,1.0);
}

#define PI 3.14159265358979323846

// 2D rotation with pivot point (.5,.5)
vec2 rotate2D(vec2 st, float angle){
    st -= 0.5;
    st =  mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle)) * st;
    st += 0.5;
    return st;
}
// a square with given size as side 
float square(vec2 st, vec2 side){
    vec2 border = vec2(0.5)-side*0.5;
    vec2 pq = smoothstep(border,border+.01,st);
    pq *= smoothstep(border,border+.01,vec2(1.0)-st);
    return pq.x*pq.y;
}
// pseudo-random function, returns value between [0.,1.]
float rand (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(31.7667,14.9876)))
                 * 833443.123456);
}
//bilinear value noise function
float bilinearNoise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners of a 2D square
    float f00 = rand(i);
    float f10 = rand(i + vec2(1.0, 0.0));
    float f01 = rand(i + vec2(0.0, 1.0));
    float f11 = rand(i + vec2(1.0, 1.0));

    vec2 u = smoothstep(0.,1.,(1.-f));
	return u.x*u.y*f00+(1.-u.x)*u.y*f10+
    u.x*(1.-u.y)*f01+(1.-u.x)*(1.-u.y)*f11;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    vec2 uv = fragCoord.xy/iResolution.xy;
    uv-=.5;
    vec3 color = vec3(0.0);
    float scale=6.-4.*iMouse.x/iResolution.x;
    // Divide the space into cells
	vec2 ixy=.5+floor(scale*uv);
    uv = fract(scale*uv);
    // Using a 2x2 matrix to rotate with given angle
    uv = rotate2D(uv,.5*PI*sin((ixy.x+ixy.y)*iTime));
 	float nr=bilinearNoise(uv);
    // Draw a noisy square
    color =vec3(1.*nr)+vec3(square(uv,vec2(0.7)));
    color*=vec3(1.,.5+.5*sin(40.*nr+ixy.x*iTime), .5+.5*sin(40.*nr+ixy.y*iTime));

    fragColor = vec4(color,1.0);
}

// ------------------------------------------------------------------------------------
// Original Character By MikkaBouzu : https://twitter.com/mikkabouzu777
// ------------------------------------------------------------------------------------

#define saturate(x) clamp(x, 0.0, 1.0)
#define MAX_MARCH 200
#define MAX_DIST 100.

const float EPS = 1e-3;
const float EPS_N = 1e-4;
const float OFFSET = EPS * 10.0;

#define M_PI 3.1415926
#define RAD90 (M_PI * 0.5)

struct surface {
	float dist;
    vec4 albedo;
    int count;
    bool isHit;
};

// Surface Data Define
#define SURF_NOHIT(d)   (surface(d, vec4(0), 		 	0, false))
#define SURF_BLACK(d) 	(surface(d, vec4(0,0,0,1),   	0, true))
#define SURF_FACE(d) 	(surface(d, vec4(1,0.7,0.6,1), 	0, true))
#define SURF_MOUSE(d) 	(surface(d, vec4(1,0,0.1,1),   	0, true))
#define SURF_CHEEP(d) 	(surface(d, vec4(1,0.3,0.4,1), 	0, true))


/////////////////////////////////////////////////////////////////////////////////////////////////
// Basic Distance function
/////////////////////////////////////////////////////////////////////////////////////////////////
float sdRoundBox(vec3 p, vec3 size, float r)
{
    return length(max(abs(p) - size * 0.5, 0.0)) - r;
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r)
{
	vec3 pa = p - a, ba = b - a;
	float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
	return length(pa - ba*h) - r;
}

float sdEllipsoid( vec3 p, vec3 r )
{
    float k0 = length(p/r);
    float k1 = length(p/(r*r));
    return k0*(k0-1.0)/k1;
}

float sdCappedTorus(in vec3 p, in vec2 sc, in float ra, in float rb)
{
  p.x = abs(p.x);
  float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
  return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
  vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
  return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}

vec3 rotate(vec3 p, float angle, vec3 axis)
{
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    mat3 m = mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
    return m * p;
}

// Union, Subtraction, SmoothUnion (distance, Material) 
surface opU(surface d1, surface d2)
{
    if(d1.dist < d2.dist){
        return d1;
    } else {
    	return d2;
    }
}

float opU( float d1, float d2 ) {  return min(d1,d2); }

surface opS( surface d1, surface d2 )
{
    if(-d1.dist > d2.dist){
        d1.dist = -d1.dist;
    	return d1;
    } else {
    	return d2;
    }
}

surface opSU( surface d1, surface d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2.dist - d1.dist)/k, 0.0, 1.0 );
    float d = mix( d2.dist, d1.dist, h ) - k*h*(1.0-h);
    vec4 albedo = mix( d2.albedo, d1.albedo, h );
    return surface(d, albedo, d1.count, true);
}

float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

/////////////////////////////////////////////////////////////////////////////////////////////////
// Mikka Boze Distance Function
/////////////////////////////////////////////////////////////////////////////////////////////////
float sdEar(vec3 p)
{
    p = rotate(p, RAD90+0.25, vec3(0,0,1));    
    return sdCappedTorus(p + vec3(0.05, 0.175, 0), vec2(sin(0.7),cos(0.7)), 0.03, 0.01);
}

#define EYE_SPACE 0.04

vec3 opBendXY(vec3 p, float k)
{
    float c = cos(k*p.x);
    float s = sin(k*p.x);
    mat2  m = mat2(c,-s,s,c);
    return vec3(m*p.xy,p.z);
}

vec3 opBendXZ(vec3 p, float k)
{
    float c = cos(k*p.x);
    float s = sin(k*p.x);
    mat2  m = mat2(c,-s,s,c);
    vec2 xz = m*p.xz;
    return vec3(xz.x, p.y, xz.y);
}

float sdMouse(vec3 p, float ms)
{
    vec3 q = opBendXY(p, 2.0);
    ms += 0.00001;
    return sdEllipsoid(q - vec3(0,0,0.2), vec3(0.035, 0.01 * ms,0.05 * ms));
}

float sdCheep(vec3 p)
{    
    const float x = 0.05;
    const float z = -0.175;
    const float r = 0.0045;
    const float rb1 = 100.;

    p = rotate(p, M_PI * -0.6 * (p.x - x), vec3(-0.2,0.8,0));

    float d = sdCapsule(opBendXY(p + vec3(x, -0.01, z), rb1), vec3(-0.005,0.0,0.0), vec3(0.005, 0., 0.001), r);
    float d1 = sdCapsule(opBendXY(p + vec3(x+0.01, -0.01, z), 200.0), vec3(-0.0026,0.0,0), vec3(0.0026, 0., 0), r);
    float d2 = sdCapsule(opBendXY(p + vec3(x+0.019, -0.015, z), -rb1), vec3(-0.01,0.0,-0.01), vec3(0.0045, 0., 0.0), r);

    return opU(opU(d, d1), d2);
}

float sdEyeBrow(vec3 p)
{
    const float x = 0.05;
    p = opBendXZ(p + vec3(0.02,0,-0.02), -6.5);
    return sdRoundBox(p + vec3(0.005, -0.14,-0.11), vec3(0.003,0.0025,0.05), 0.001);
}

surface sdBoze(vec3 p, vec3 sc, float ms)
{    
    surface result = SURF_NOHIT(1e5);

    float minsc = min(sc.x, min(sc.y, sc.z));
    p /= sc;

    // head
	float d = sdCapsule(p, vec3(0,0.05,0), vec3(0, 0.11, 0), 0.125);

    float d1 = sdRoundedCylinder(p + vec3(0,0.025,0), 0.095, 0.05, 0.0);

    d = smin(d, d1, 0.1);

    vec3 mxp = vec3(-abs(p.x), p.yz);

    // ear
    float d2 = sdEar(mxp);
    d = opU(d, d2);

	surface head = SURF_FACE(d);

	// eye
    float d4 = sdCapsule(mxp, vec3(-EYE_SPACE, 0.06, 0.13), vec3(-EYE_SPACE, 0.08, 0.125), 0.0175);
    surface eye = SURF_BLACK(d4);

    // mouse
    float d6 = sdMouse(p, ms);
    surface mouse = SURF_MOUSE(d6);

    // cheep
    float d7 = sdCheep(mxp);
    surface cheep = SURF_CHEEP(d7);

    // eyebrows
    float d9 = sdEyeBrow(mxp);
    eye.dist = opU(eye.dist, d9);

    // integration
    mouse = opU(eye, mouse);
    result = opS(mouse, head);
    result = opU(cheep, result);

    result.dist *= minsc;

    return result;
}
/////////////////////////////////////////////////////////////////////////////////////////////////
// End of Mikka Boze
/////////////////////////////////////////////////////////////////////////////////////////////////

surface map(vec3 p)
{
	surface result = SURF_NOHIT(1e5);

    float ms = sin(iTime) * 0.5 + 0.5;

    // boze
    surface boze = sdBoze(p, vec3(1), ms);

    result = opU(result, boze);

    return result;
}

vec3 norm(in vec3 position) {
    // https://www.shadertoy.com/view/XltyRf
    vec4 n = vec4(0);
    for (int i = 0 ; i < 4 ; i++) {
        vec4 s = vec4(position, 0);
        s[i] += 0.001;
        n[i] = map(s.xyz).dist;
    }
    return normalize(n.xyz-n.w);

}

surface traceRay(in vec3 origin, in vec3 direction, float dist, out vec3 pos) {
    float t = 0.0;

    pos = origin;

    int count = 0;
    surface hit;
    float d;

    for (int i = 0; i < MAX_MARCH; i++) {
        hit = map(pos);
        d = hit.dist;

        if (d <= EPS || d >= MAX_DIST) {
        	break;
        }

        t += d;
        pos = origin + direction * t;
        count++;        
    }

    hit.dist = t;
	hit.count = count;

    pos = origin + direction * t;

    if(hit.isHit)
    {
        // Lighting
        vec3 normal = norm(pos);

        vec3 lightDir = normalize(vec3(cos(iTime), 1, sin(iTime)));
		vec3 lightColor = vec3(1.5);

        float NoL = saturate(dot(normal, lightDir));

        vec3 ambientColor = vec3(0.1);

        hit.albedo.rgb *= NoL * lightColor +  ambientColor;
    }

    if(d <= EPS){
        hit.isHit = true;
        return hit;
    }else{

        hit.isHit = false;
        return hit;
    }
}

vec3 render(vec3 p, vec3 ray, vec2 uv)
{
    vec3 pos;
    surface mat = traceRay(p, ray, 0., pos);

    vec3 col = vec3(0,0,0);
    vec3 sky = vec3(0.3);

    col = mat.isHit ? mat.albedo.rgb : sky;

    return col;

}

mat3 camera(vec3 ro, vec3 ta, float cr )
{
	vec3 cw = normalize(ta - ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

    vec3 ro = vec3(0, 0.05, 0.5);
    vec3 ta = vec3(0, 0.05, 0);

    mat3 c = camera(ro, ta, 0.0);
    vec3 ray = c * normalize(vec3(p, 1.5));
    vec3 col = render(ro, ray, fragCoord.xy);

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec3 Strand(in vec2 fragCoord, in vec3 color, in float hoffset, in float hscale, in float vscale, in float timescale)
{
    float glow = 0.07 * iResolution.y;
    float twopi = 7.28318530718;
    float curve = 2.0 - abs(fragCoord.y - (sin(mod(fragCoord.x * hscale / 200.0 / iResolution.x * 2000.0 + iTime * timescale + hoffset, twopi)) * iResolution.y * 0.35 * vscale + iResolution.y / 3.0));
    float i = clamp(curve, 0.1, 2.0);
    i += clamp((glow + curve) / glow, 0.1, 2.0) * 0.5 ;
    return i * color;
}

vec3 Muzzle(in vec2 fragCoord, in float timescale)
{
    float theta = atan(iResolution.y / 3.0 - fragCoord.y, iResolution.x - fragCoord.x + 0.23 * iResolution.x);
	float len = iResolution.y * (20.0 + sin(theta * 30.0 + float(int(iTime * 30.0)) * -45.0)) / 21.0;
    float d = max(-0.7, 2.0 - (sqrt(pow(abs(iResolution.x - fragCoord.x), 3.0) + pow(abs(iResolution.y / 3.0 - ((fragCoord.y - iResolution.y / 3.0) * 5.0 + iResolution.y / 3.0)), 3.0)) / len));
    return vec3(d * (2.0 + sin(theta * 20.0 + floor(iTime * 30.0) * 20.77) * 0.6), d * (2.0 + -cos(theta * 9.0 - floor(iTime * 30.0) * 9.77) * 0.6), d * (2.0 + -sin(theta * 7.0 - floor(iTime * 30.0) * 234.77) * 0.6));
}

