vec2 randomGradient(int ix, int iy, float seed) {
    float x = float(ix);
    float y = float(iy);
    float random = seed + 2920.f * sin(x * 21942.f + y * 171324.f + 8912.f) * cos(x * 23157.f * y * 217832.f + 9758.f);
    return vec2(cos(random), sin(random));
}

float interpolate(float a0, float a1, float w) {
    if (0.0 > w) return a0;
    if (1.0 < w) return a1;
    float diff = a1 - a0;
    return a0 + diff * smoothstep(a0, a1, a0 + diff * w);
}

float get_dot(ivec2 cell_loc, vec2 frag_coord, vec2 grid_size, float seed) {
	vec2 cell_size = iResolution.xy / grid_size;

    vec2 grad = randomGradient(cell_loc.x, cell_loc.y, seed);
    vec2 offset = (frag_coord - vec2(cell_loc) * cell_size) / cell_size;
    float dot_product = (dot(grad, offset) + 1.0) / 2.0;
	return dot_product;
}

vec4 get_perlin(float grid_width, vec2 frag_coord) {
    float grid_height = iResolution.y * grid_width / iResolution.x;
    vec2 grid_size = vec2(grid_width, grid_height);
	vec2 cell_size = iResolution.xy / grid_size;
    ivec2 cell_loc = ivec2(floor(frag_coord / cell_size));

    float seed = float(iFrame) / 20.0;

	float dots[] = float[](
    	get_dot(cell_loc, frag_coord, grid_size, seed),
    	get_dot(cell_loc + ivec2(1, 0), frag_coord, grid_size, seed),
    	get_dot(cell_loc + ivec2(0, 1), frag_coord, grid_size, seed),
    	get_dot(cell_loc + ivec2(1, 1), frag_coord, grid_size, seed)
    );

    vec2 weight = frag_coord / cell_size - vec2(cell_loc);

    float diff1 = (dots[1] - dots[0]);
    float diff2 = (dots[3] - dots[2]);
    float i1 = interpolate(dots[0], dots[1], weight.x);
    float i2 = interpolate(dots[2], dots[3], weight.x);
    float i = interpolate(i1, i2, weight.y);
	return vec4(i, 0, 0, 0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 c1 = get_perlin(4.0, fragCoord);
    vec4 c2 = get_perlin(8.0, fragCoord);
    vec4 c3 = get_perlin(16.0, fragCoord);
    vec4 c4 = get_perlin(32.0, fragCoord);
    fragColor = (c1 + c2 / 2.0 + c3 / 4.0 + c4 / 16.0) / (1.0 + 0.5 + 0.25 + 0.125);
}

