/*
    Decorated Christmas Spiral
    --------------------------

    Happy Christmas to you all the great Shadertoy community !

    It seems that the archimedian spiral can be used as a distance field with coordinates
    a quite neat way to create a repetition domain where you can put blinking balls and ribbons.

    Spirals are commonly use in art, and we may find this figure frequently in the nature.
    Of course there is several kinds of spirals. For example, the Logarithmic Spiral that IQ  
    used in his awesome Snail shader and the ones built using arc of circles (multiple center spirals).

    Regarding the Archimedean spiral (also known as the arithmetic spiral), the Shadertoy's user kibble
    demonstrated a very neat way to get UV coordinates for a point on the screen, using it's polar 
    coordinates (Î¸,r). The u coordinates is calculated using the length to the center among the spiral, 
    and the v coordinate from the position of the point on the segment joining two consecutive turns
    of the spiral.

    Related references:    

    Nyarchimedes Spiral - kibble
    https://www.shadertoy.com/view/lsS3WV

    Quick Lighting Tech - blackle 
    https://www.shadertoy.com/view/ttGfz1

    soft shadows in raymarched SDFs - IQ
    http://iquilezles.org/www/articles/rmshadows/rmshadows.htm

    outdoors lighting- IQ
    https://iquilezles.org/www/articles/outdoorslighting/outdoorslighting.htm

    Cubic Truchet Pattern - Shane
    https://www.shadertoy.com/view/4lfcRl

*/

// #define AA

#define MAX_STEPS 256
#define MAX_DIST 10.
#define SURF_DIST .001
#define TAU 6.283185

#define S smoothstep
#define T (iTime+10.0)
#define PI 3.14159265

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float Hash21(vec2 p) {
    p = fract(p*vec2(123.34,233.53));
    p += dot(p, p+23.234);
    return fract(p.x*p.y);
}

float sdVerticalCapsule( vec3 p, float r, float h )
{
  p.x -= clamp( p.x, 0.0, h );
  return length( p ) - r;
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdHexagram( in vec2 p, in float r )
{
    const vec4 k = vec4(-0.5,0.86602540378,0.57735026919,1.73205080757);
    p = abs(p);
    p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;
    p -= 2.0*min(dot(k.yx,p),0.0)*k.yx;
    p -= vec2(clamp(p.x,r*k.z,r*k.w),r);
    return length(p)*sign(p.y);
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float braids(vec3 p,float l,out float id) {
    p.yz*=Rot(p.x*3.14159*.5);
    p.yz = abs(p.yz)-0.25; // 4 for the price of one
    p.yz = (p.yz + vec2(p.z, -p.y))*sqrt(0.5); // Shortcut for 45-degrees rotation https://www.shadertoy.com/view/WsGyWR
    vec2 sector=step(0.0,p.yz);
    id = sector.x + 2.0 * sector.y; 
    p.yz = abs(p.yz)-0.05;
    p.yz*=Rot(p.x*3.14159*4.0);
    p.yz = abs(p.yz)-0.02;
    float d = sdVerticalCapsule(p,0.02,l);
    return(d);
}

float carvings(vec3 p,float l, out float id) {
    p.x-=.03;
    float n = round(l);
    id = clamp(round(p.x*2.0),1.0,n*2.0-1.0);
    p.x-=id*.5;
    p.zy*=Rot(id*3.1415*.25); 
    return min(sdHexagram(p.xy,0.045),(sdBox(abs(p.xz)-0.033,vec2(0.022)+.006)));
}

float balls(vec3 p,float l, out float id) {
    float rank = round(p.x);
    p.yz*=Rot((rank+0.25)*3.14159*.5);
    p.x -= clamp(rank,0.0,l);
    vec2 sector=step(0.0,p.yz);
    id = sector.x + 2.0 * sector.y; 
    float r = .05*(1.0+id*.2);
    p.yz = abs(p.yz);
    p.yz -= vec2(.25,.25);
    id += 4.0*rank;
    return length(p)-r;
}

float smax( float a, float b, float k )
{
    float h = max(k-abs(a-b),0.0);
    return max(a, b) + h*h*0.25/k;
}

float decoratedStick(vec3 p,out float m,out float glowDist) {
    float l = 40.0;   // lenght of the stick
    float d = 1e10; // distance
    // materials : 1.0=spiral 2.x=tubes 3.x=stars and squares 4.0=inside spiral 5.0 Balls
    m = 1.0;    // materials
    glowDist = 1e10;
    float core = length(vec3(p.x-clamp(p.x,0.0,l),p.y,p.z));
    float outer = core-.25;
    float inner = core-.22;
    // Stars and Littles windows carvings, change sign to have holes or bumps
    float id;
    float carvings = -carvings(p,l,id);
    d = smax(outer,carvings,.025);
    if ( inner    < d ) { d = inner    ; m = 4.0+id/1024.0 ; }
    // Braids 
    float braids = braids(p,l,id);
    if ( braids < d ) { d=braids ; m= 2.0 + id/1024.0 ; }; // packing the ID in the material
    // Balls :-)
    float balls = balls(p,l,id);
    if ( balls < d ) { d=balls ; m= 5.0 + id/1024.0 ; }; // packing the ID in the material
    // Some blinking
    float blink=1.0-cos(5.0*id+2.0*T);
    glowDist = balls+blink*.1; 
    return d; 
}

// approximated !
float arclength(float turn) {
	float d = turn * turn;
	return d * PI;
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

// Derived it myself, any comment welcome
// SDF for Archimedean Spiral
// https://www.shadertoy.com/view/stB3WK
float spiralUV(vec2 p,float size,float spacing, float turnStart,float turnEnd, out vec2 uv) {        
    // below we are computing the polar coordinates of the point p
    float r = length(p)/(size+spacing); // radius
    float a = atan(p.y, p.x); // angle
	float turn = (r - a/TAU) ;
    // manage round and atan discontinuities, very neat way
    float count = round(turn);
    float d = 1e10;
    float position = count + a/TAU - 1.0;
    //  manages the case when the spiral is opened
    if ( turnStart <= turnEnd || fract(position-turnStart) <= fract(turnEnd-turnStart) ) {
        // distance in each turn
        float e = clamp(position,turnStart,turnEnd);          
        float stp = floor(position - e );
        d = abs(turn - count + stp)*(size+spacing) -.5*size;
    }
     // start border 
    {
        vec2 rad = vec2(cos(turnStart*TAU), sin(turnStart*TAU));
        vec2 a = rad * ( (size+spacing) * (turnStart+.5) +spacing*.5 ) ;
        vec2 b = rad * ( (size+spacing) * (turnStart+.5) +size + spacing*.5 );
        float dStart = sdSegment(p,a,b) ;
        d = d > 0.0 ? min(d,dStart) : max(d,-dStart);        
    }
     // end border
    {
        turnEnd++;
        vec2 rad = vec2(cos(turnEnd*TAU), sin(turnEnd*TAU));
        vec2 a = rad * ( (size+spacing) * (turnEnd+.5) +spacing*.5 ) ;
        vec2 b = rad * ( (size+spacing) * (turnEnd+.5) +size + spacing*.5 );
        float dEnd = sdSegment(p,a,b) ;
        d = d > 0.0 ? min(d,dEnd) : max(d,-dEnd);        
    }
    // approximated UV calculations
    uv.x = arclength(position+1.0)*(size+spacing)/size-arclength(turnStart+1.0)*(size+spacing)/size;
    uv.y = (turn - count)*(size+spacing)/size;
	return d;
}

float GetDist(vec3 p,out float objID, out float glowDist, out float dC ) {
    p.xz *= Rot(T*.1);
    p.xy *= Rot(-sin(T*.1)*.5);
    glowDist = 1e10;
    objID = 1.0;
    float extands = S(0.8,-0.8,cos(T*.1));
    extands*= extands;
    float spacing = 0.7*extands,
        tmin = 0.0,
        tmax = 2.0;
    vec2 uv;
    float d = 1e10;
    dC = abs(p.y)-0.5;
    if ( dC < SURF_DIST ) {
        float dSpiral = spiralUV(p.xz,1.0,spacing,tmin,tmax,uv);
        dC = abs(dSpiral);
        if ( dSpiral < SURF_DIST-.02 ) {
            vec3 q = vec3(uv.x, p.y, uv.y ); // spiral UV space
            d = decoratedStick(q-vec3(.5,0.,0.),objID,glowDist);
        } 
    }
    return d;
}

float GetDist(vec3 p,out float glowDist,out float dC) {
    float objID;
    return GetDist(p,objID,glowDist,dC);
}

float GetDist(vec3 p) {
    float glowDist,objID,dC;
    return GetDist(p,objID,glowDist,dC);
}

float GetMat(vec3 p) {
    float glowDist,objID,dC;
    float d = GetDist(p,objID,glowDist,dC);
    return objID;
}

float RayMarch(vec3 ro, vec3 rd,out float glowCumul) {
	float dO=0.0;  
    float dS;
    float dC; // distance to cell boundaries
    float glowDist;
    glowCumul=0.0;
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        dS = GetDist(p,glowDist,dC);
        dO += min(dS*.9,dC+0.05); 
        float at = 1.0 / (1. + pow(glowDist*20.,3.0) );
        glowCumul+=at;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }    
    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
    float res = 1.0;
    float t = mint;
    for( int i=0; i<24; i++ )
    {
		float h = GetDist( ro + rd*t );
        float s = clamp(8.0*h/t,0.0,1.0);
        res = min( res, s*s*(3.0-2.0*s) );
        t += clamp( h, 0.02, 0.2 );
        if( res<0.004 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

float calcOcclusion( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.11*float(i)/4.0;
        vec3 opos = pos + h*nor;
        float d = GetDist( opos );
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

