        float sun_dif = clamp(dot( nor, sun_lig ), 0.0, 1.0 );
        vec3  sun_hal = normalize( sun_lig-rd );
        float sun_sha = calcSoftshadow( pos+0.01*nor, sun_lig, 0.01, 0.5 );
        float sun_spe = ks*pow(clamp(dot(nor,sun_hal),0.0,1.0),8.0)*sun_dif*(0.04+0.96*pow(clamp(1.0+dot(sun_hal,rd),0.0,1.0),5.0));
		float sky_dif = sqrt(clamp( 0.5+0.5*nor.y, 0.0, 1.0 ));
        float bou_dif = sqrt(clamp( 0.1-0.9*nor.y, 0.0, 1.0 ))*clamp(1.0-0.1*pos.y,0.0,1.0);
		vec3 lin = vec3(0.0);
        // materials  
        if ( m >=5.0 ) {            // Balls
            float ballID = fract(m)*1024.0;
            c = fract(ballID*.5) > 0.0 ? vec3(1.0,0.1,0.01) : vec3(0.6,0.6,0.2)*.5;
            float directionality=0.75;
            float sharpness=0.5;
            float spec = length(sin(ref * 3.) * directionality + (1. - directionality)) / sqrt(3.);
            spec = spec + pow(spec, 10. * sharpness);
            float blink=1.0+cos(5.0*ballID+2.0*T);
            lin = vec3(blink)*3.3;
            c = spec * c;  
        } else if ( m >=4.0 ) {     // bright inside spiral
            float starID=fract(m)*1024.0;
            float blink=1.0+cos(5.0*starID+3.15*T);
            c = vec3(0.7,0.7,0.1)*.3;
            lin = vec3(blink)*3.0;
            float directionality=0.75;
            float sharpness=0.5;
            float spec = length(sin(ref * 3.) * directionality + (1. - directionality)) / sqrt(3.);
            spec = spec + pow(spec, 10. * sharpness);
            c = spec * c;  
        } else if ( m >=3.0 ) {    
            // Material Not used c = vec3(0.3,0.1,0.01);
        } else if ( m >=2.0 ) {     // tubes 
            float ropeID=fract(m)*1024.0;
            c = ropeID>2.0 ? vec3(0.5,0.5,0.01)*.25 : vec3(0.01,0.6,0.01)*.1;
        } else if ( m >=1.0 ) {     
            // spiral core
            // I think I'am not making good use of here of backle metal effect
            // https://www.shadertoy.com/view/tlscDB
            c = vec3(0.28,0.2,0.02);
            float directionality=0.75;
            float sharpness=0.7;
            float spec = length(sin(ref * 4.) * directionality + (1. - directionality)) / sqrt(3.);
            spec = spec + pow(spec, 10. * sharpness);
            c =  spec * c * (.3+.7*sun_sha);  
        }
        lin += sun_dif*vec3(8.10,6.00,4.20)*sun_sha*.5;
        lin += sky_dif*vec3(0.50,0.70,1.00)*occ*2.0;
        lin += bou_dif*vec3(0.40,1.00,0.40)*occ*2.0;
		col = c*lin;
		col += sun_spe*vec3(8.10,6.00,4.20)*sun_sha;
        // fog
        float fog=S(12.0,5.0,d);
        col = mix(bgcol, col, fog);

    } else {
        col = bgcol;
    }
    col += vec3(0.1,0.1,0.01)*glowCumul;
    col = sqrt(col);	// gamma correction    
    tcol+=col;
#ifdef AA
		}
	tcol /= 4.;
#endif

    fragColor = vec4(tcol,1.0);
}

//////////////////////////////////////////////////////////////////////
//
// spherical arc spline by mattz
//
// License CC BY 4.0 https://creativecommons.org/licenses/by/4.0/
//
// Given two points on a sphere and two tangent vectors, construct
// a smooth spline (orange) out of a pair of equal-radius arcs
// connecting the points.
//
// The orange spline touches the black points, and is tangent
// to the green arrows.
//
// The maroon dots are the arc centers -- each one is an equal
// distance to the black points.
//
//////////////////////////////////////////////////////////////////////

// light pos
vec3 L = vec3(-0.15, 0.3, 1);

const float PI = 3.141592653589793;
const float EPS = 1e-3;

// animation parameters
const float PERIOD = 30.0; // seconds per repeat
const float PFREQ = 5.0;
const float QFREQ = 1.0;

// antialiasing distance
float aa = 0.01;

// should we draw decorations
bool draw_extras = true;

//////////////////////////////////////////////////////////////////////
// rotate about x-axis 

mat3 rotX(in float t) {
    float cx = cos(t), sx = sin(t);
    return mat3(1., 0, 0, 
                0, cx, sx,
                0, -sx, cx);
}

//////////////////////////////////////////////////////////////////////
// rotate about y-axis 

mat3 rotY(in float t) {
    float cy = cos(t), sy = sin(t);
    return mat3(cy, 0, -sy,
                0, 1., 0,
                sy, 0, cy);

}

//////////////////////////////////////////////////////////////////////
// ray-sphere intersection

const vec4 miss = vec4(-1);

vec4 sphere(in vec3 o, in vec3 d, in vec3 ctr, in float r) {

    vec3 oc = o - ctr;

    float a = dot(d, d);
    float b = 2.0*dot(oc, d);
    float c = dot(oc, oc) - r*r;

    float D = b*b - 4.0*a*c;

    if (D > 0.0) {

        float sqrtD = sqrt(D);

        float t = 0.5 * ( -b - sqrtD ) / a;

        if (t >= 0.0) {
            vec3 n = normalize( oc + t*d );
            return vec4(n, t);	
        }

    }

    return miss;

}

//////////////////////////////////////////////////////////////////////
// arclengh distance between unit vectors on sphere

float sdist(vec3 a, vec3 b) {
    return acos(clamp(dot(a, b), -1.0, 1.0));
}

//////////////////////////////////////////////////////////////////////
// distance to arrow at p0 with tangent l0 

float darrow(vec3 p, vec3 p0, vec3 l0) {

    float k = 0.125;

    vec3 q = cos(k)*p0 + sin(k)*l0;

    vec3 a0 = normalize(cross(p0, l0));
    vec3 l1 = normalize(cross(q, a0));

    float d = abs(dot(p, a0));
    d = max(d, -dot(p, l0));
    d = max(d, -dot(p, l1));

    float dh = 1e5;

    for (float s=-1.0; s<=1.0; s+=2.0) {

        vec3 r = normalize(q + 0.035*s*a0 + 0.4*p0);

        vec3 l2 = normalize(cross(q, r));
        vec3 l4 = normalize(cross(q, l2));
        vec3 l5 = normalize(cross(r, l2));

        float d2 = abs(dot(p, l2));
        d2 = max(d2, dot(p, l4));
        d2 = max(d2, -dot(p, l5));

        dh = min(dh, d2);
        d = max(d, -s*dot(p, l2));

    }

    d = min(d, dh);

    return d;

}

//////////////////////////////////////////////////////////////////////
// data structure to store spline

struct arc_spline {

    vec3 p0, l0, p1, l1, c0, c1, m, w;
    float r;

};

//////////////////////////////////////////////////////////////////////
// compute a spline on the sphere made of two arcs that are tangent 
// to each other have desired tangents at given points
//
// inputs:
//
//   p0: first point on sphere (unit vector)
//   l0: tangent vector at p0 (unit vector with dot(p0, l0) = 0)
//   p1: second point on sphere (unit vector) 
//   l1: tangent vector at p1 (unit vector with dot(p1, l1) = 0)
//
// output: an arc_spline

void compute_spline(in vec3 p0, in vec3 l0,
                    in vec3 p1, in vec3 l1,
                    out arc_spline S) {

    // fill in data members of spline that copy inputs
    S.p0 = p0;
    S.l0 = l0;

    S.p1 = p1;
    S.l1 = l1;

    // compute the line connecting p0 & p1
    vec3 tmp = normalize(cross(p0, p1));

    // special case: p0 and p1 are connected by a segment of a great circle
    // the line from p0 to p1 hits them at the correct tangents 
    if (max(abs(dot(tmp, l0)), abs(dot(tmp, l1))) < EPS) {
        // in this case we set S.r = 0 and set S.w to be the line
        S.w = tmp;
        S.r = 0.0;
        return;
    }

    // compute the points orthogonal to (l0, p0) and (l1, p1), respectively
    vec3 a0 = cross(l0, p0);
    vec3 a1 = cross(l1, p1);

    // we will construct arc centers 
    //
    //   c0 = cos(r) * p0 + sin(r) * a0
    //   c1 = cos(r) * p1 + sin(r) * a1
    //
    // which are a distance r away from p0 & p1 respectively
    // by construction, dot(c0, l0) = dot(c1, l1) = 0
    //
    // now we want to solve for r such that dot(c0, c1) = cos(2*r)
    //
    // start by observing that 
    //
    //   (cos(r)Â²       * a +
    //    cos(r)*sin(r) * b
    //    sin(r)Â²       * c) = cos(2r)
    //
    // where a = dot(p0, p1), b = dot(p0, a1) + dot(p1, a0), and 
    // c = dot(a0, a1).
    //
    // applying the half angle identities and setting Î¸ = 2r, we find
    //
    //   a*(1 + cos(Î¸))/2 + b*sin(Î¸)/2 + c*(1 - cos(Î¸))/2 = cos(Î¸)
    //   a*(1 + cos(Î¸)) + b*sin(Î¸)+ c*(1 - cos(Î¸)) = 2*cos(Î¸)
    //   (a - c - 2)*cos(Î¸) + b*sin(Î¸) = -(a + c)
    //
    // we can rewrite that as
    //
    //   d*cos(Î¸) + b*sin(Î¸) = e
    //
    // where d = a - c - 2 and e = -(a + c).
    //
    // finally, we can rewrite that as
    //
    //   Î±*cos(Î¸ - Ï) = e
    //
    // where Î± = sqrt(dÂ² + bÂ²) and Ï = atan(b, d).
    // the solution is given by
    //
    //   Î¸ = Ï Â± acos(e / Î±)
    //
    // and r = 0.5 * Î¸.

    float a = dot(p0, p1);
    float b = dot(p0, a1) + dot(p1, a0);
    float c = dot(a0, a1);

    float d = (a - c - 2.);
    float e = -(a + c);

    float alpha = length(vec2(d, b));
    float phi = atan(b, d); // in [-pi, pi]
    float tau = acos(e/alpha); // in [0, pi]

    // we want the r with the least magnitude so choose tau with the 
    // opposite sign as phi
    float r = 0.5 * (phi > 0. ? phi - tau : phi + tau);

    // now get c0 & c1
    float cr = cos(r);
    float sr = sin(r);

    S.c0 = normalize(cr*p0 + sr*a0);
    S.c1 = normalize(cr*p1 + sr*a1);

    // m is the midpoint of c0 & c1, the point
    // of mutual tangency of the two arcs
    S.m = normalize(S.c0 + S.c1);

    // get the line connecting c0 & c1
    S.w = normalize(cross(S.c0, S.c1));

    // no longer need the sign of r, want it positive to compute distances later
    S.r = abs(r);

    // choose w to point towards p0
    if (dot(S.w, p0) < 0.) { S.w = -S.w; }

}

//////////////////////////////////////////////////////////////////////
// distance to arc on sphere
//
// inputs: 
//
//   p: query point (unit vector)
//   c: arc center point (unit vector)
//   r: arc radius in radians
//   l: lower tangent (unit vector)
//   w: upper tangent (unit vector)

float darc(vec3 p, vec3 c, float r, vec3 l, vec3 w) {

    float d = abs(sdist(p, c) - r);

    d = max(d, -dot(p, l));
    d = max(d, -dot(p, w));

    return d;

}

//////////////////////////////////////////////////////////////////////
// distance from point p to spline S

float dspline(vec3 p, arc_spline S) {

    if (S.r == 0.0) {

        // spline is the great circle segment connecting p0 & p1
        return max(abs(dot(p, S.w)), -min(dot(p, S.l0), dot(p, S.l1)));

    } else {

        // spline is one or two arcs 

        // ignore either arc segment if the midpoint lies at the corresponding endpoint 
        float d = 1e5;

        if (dot(S.m, S.p0) < 1.0 - EPS) { d = min(d, darc(p, S.c0, S.r, S.l0,  S.w)); }
        if (dot(S.m, S.p1) < 1.0 - EPS) { d = min(d, darc(p, S.c1, S.r, S.l1, -S.w)); }

        return d;

    }

}


//////////////////////////////////////////////////////////////////////
// draw diagram on sphere

vec3 draw(vec3 p) {

    //////////////////////////////////////////////////
    // figure out some sample points and tangents

    vec3 p0 = vec3(0, 0, 1);
    vec3 l0 = vec3(0, 1, 0);

    float u = 2.*PI*fract(iTime/PERIOD);

    vec3 p1 = normalize(vec3(0.25, 0.4, 1.0) + vec3(0.3*sin(PFREQ*u), 0.3*cos(PFREQ*u), 0));    
    vec3 q = normalize(vec3(1, 0, 0.5) + vec3(0.2*sin(QFREQ*u + 0.3), 0.2*cos(QFREQ*u + 0.1), 0));
    vec3 l1 = normalize(cross(q, p1));

    //////////////////////////////////////////////////
    // compute and draw the spline

    arc_spline S;    
    compute_spline(p0, l0, p1, l1, S);

    float bblue = 1e5;
    float blue = min(abs(dot(p, l0)), abs(dot(p, l1)));
    float black = min(sdist(p0, p), sdist(p1, p));
    float green = min(darrow(p, p0, l0), darrow(p, p1, l1));  
    float maroon = 1e5;
    float orange = dspline(p, S);

    if (draw_extras && S.r != 0.0) {

        blue = min(blue, max(abs(dot(p, S.w)), -min(dot(p, l0), dot(p, l1))));

        maroon = min(maroon, sdist(p, S.c0));
        maroon = min(maroon, sdist(p, S.c1));
        black = min(black, sdist(p, S.m));

    }

    vec3 color = vec3(1);

    if (!draw_extras) {
        green = 1e5;
    }

    color = min(color, mix(vec3(1), vec3(0, 0.5, 1), 0.4*smoothstep(aa, 0.0, blue - 0.002)));
    color = min(color, mix(vec3(1), vec3(0.1, 0.6, 0), smoothstep(aa, 0.0, green - 0.002)));
    color = min(color, mix(vec3(1), vec3(1.0, 0.4, 0), smoothstep(aa, 0.0, orange - 0.008)));
    color = min(color, mix(vec3(1), vec3(0.7, 0, 0.4), smoothstep(aa, 0.0, maroon - 0.015)));
    color *= smoothstep(0., aa, black - 0.015);

    return color;

}

//////////////////////////////////////////////////////////////////////
// raytrace to sphere and compute color & lighting

vec4 shade(vec3 ro, vec3 rd) {

    // raytrace
    vec4 res = sphere(ro, rd, vec3(0), 1.0);

    // if miss sphere, gray background
    if (res.w < 0.) { return vec4(vec3(0.85), 1); }

    // get intersection point on sphere
    vec3 p = res.xyz;

    // render on sphere
    vec3 color = draw(p);

    // goofy non realistic lighting
    color *= 0.3*dot(p, L) + 0.7;

    return vec4(color, 1);

}


//////////////////////////////////////////////////////////////////////
// stereographic projection

vec3 sphere_from_plane(vec2 q) {

    float qq = dot(q, q);

    return vec3(2.*q, -1.+qq) / (1. + qq);

}

//////////////////////////////////////////////////////////////////////
// pretty vanilla main handles scene transform & mouse interaction

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // distance from camera to sphere centers
    const float cdist = 6.0;

    // center-relative fragment coordinates
    vec2 uv = fragCoord.xy - 0.5*iResolution.xy;

    // focal length in pixels
    float f = 0.45/iResolution.y;

    vec2 theta = vec2(-0.2, 0.15);

    if (max(iMouse.x, iMouse.y) > 20.0) { 
        theta.x = (iMouse.y - .5*iResolution.y) * 3.5/iResolution.y; 
        theta.y = (iMouse.x - .5*iResolution.x) * -5.0/iResolution.x; 
    }

    draw_extras = (max(iMouse.z, iMouse.w) <= 0.0);

    mat3 Rview = rotY(theta.y)*rotX(theta.x); 

    if (texture(iChannel0, vec2(83.5/256., 0.75)).x > 0.) {

        vec2 uv = (fragCoord.xy - 0.5*iResolution.xy) * 1.5/ iResolution.y;

        vec3 p = sphere_from_plane(uv);

        p.z = -p.z;

        aa = sqrt(length(cross(dFdx(p), dFdy(p))));

        p = Rview*p;

        vec3 color = draw(p);

        fragColor = vec4(color, 1);

    } else {

         aa = 4.0 / iResolution.y;

        // ray origin and direction
        vec3 rd = normalize(vec3(f*uv, -1));
        vec3 ro = vec3(0, 0, cdist);

        L = Rview*L;
        fragColor = shade(Rview*ro, Rview*rd);

    }        

    fragColor.xyz = pow(fragColor.xyz, vec3(0.7));

}

vec2 Hash2(uint n) 
{
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    uvec2 k = n * uvec2(n,n*16807U);
    return vec2( k & uvec2(0x7fffffffU))/float(0x7fffffff);
}



float rand (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

vec3 rand3(vec3 st) {

 	return vec3(Hash2(uint(st.x)), rand(st.yz));    

}
float sph(in vec3 pos, in vec3 cen, in float r){
	return length(pos - cen) - r; 
}

float plane(in vec3 pos, in float r){

	return pos.y - (-r);
}
vec2 mmax(in vec2 a, in vec2 b){

	return a.x > b.x ? a: b;
}

vec2 mmin(in vec2 a, in vec2 b){

	return a.x < b.x ? a: b;
}
float BeerLambert(float absorptionCoefficient, float distanceTraveled) {
    return exp(-absorptionCoefficient * distanceTraveled);
}
vec2 map(in vec3 pos){

    //float disp = 
	vec2 d = vec2(sph(pos, vec3(0.0), 1.5), 1.0);
    vec2 d1 = vec2(sph(pos, vec3(-3.0*sin(iTime), 0.3-0.31-sin(iTime*5.0), 3.0*cos(iTime)), 0.31*abs(cos(2.5*iTime) + sin(2.5*iTime))), 2.0);
    vec2 d2 = vec2 (plane(pos, 1.5), 3.0);
    vec2 d3 = vec2(sph(pos, vec3(3.0*cos(iTime), -1.0, 3.0*sin(iTime)), 0.50),4.0);
    vec2 d4 = vec2(sph(pos, vec3(10.0, 10.0,0.0),1.0) , 5.0);


    vec2 d5 = vec2(sph(pos, vec3(-0.5, 0.0, 5.0), 1.5), 6.0);
    vec2 res = mmin(mmin(d, mmin(d1,d2)), d3);



    res = mmin(res, d4);
    res = mmin(res, d5); 
    return res;
}


vec3 ray_cast(in vec3 ro, in vec3 rd){

	float t = 0.0; 
    float d = 0.0; 
    float absSum = 1.0;
    for(int i = 0; i < 1000; i++){
    	vec2 pos = map(ro + t* rd); 

        if(pos.x < 0.001){

            if (pos.y == 6.0) {
             	float absorp = 1.3;
                float opacity = 1.0;
                float d = 0.0;   
				absSum = 0.0;



                //t += 0.01;

                while(pos.y == 6.0){

                    float prev_opacity = opacity;


                 	pos = map(ro + t*rd);

                    t += 0.01;

                    if(pos.x < 0.0)
                    	d += 0.01;

                    opacity *= BeerLambert(absorp, 0.01);

                    float absorb = prev_opacity -opacity;

                    absSum += absorb;

                }              

            }
        	pos.x = t; 

            return vec3(pos, absSum);
        } 

        if(pos.x > 20.0){
        	break;
        }

        t += pos.x;
    }

    return vec3(vec2(-1.0), absSum);
}
vec3 normal(in vec3 pos){

	const vec3 eps = vec3(0.001, 0.0, 0.0);



    float grad_x = map(pos + eps.xyy).x - map(pos - eps.xyy).x;
    float grad_y = map(pos + eps.yxy).x - map(pos - eps.yxy).x;
    float grad_z = map(pos + eps.yyx).x - map(pos - eps.yyx).x;



    return normalize(vec3(grad_x, grad_y, grad_z));



}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) * (0.5+0.5*nor.y);
}


float checkers( in vec3 p ) {
    vec3 s = sign(fract(p*.5)-.5);
    return .5 - .5*s.x*s.y*s.z;
}

float diffuse_light(in vec3 pos, in vec3 cen){

	vec3 nor = normal(pos);
    vec3 dir_to_light = normalize(cen-pos);
    float diff_intens = dot(nor, dir_to_light);

    return diff_intens;

}
float phong_light(in vec3 pos, in vec3 cen, in vec3 ro, in float k){

    float specPower = k;
    vec3 n = normal(pos);
    vec3 l = normalize(cen-pos);
    vec3 v = normalize(ro-pos);
    vec3 r = reflect(v, n);
    float phong_light = pow ( max ( dot ( l, r ), 0.0 ), specPower );
    return phong_light;

}
float fog(in vec3 pos) {

    float ans = 1.0;
    if(abs(pos.x) > 10.0) ans *= 1.0 / (pos.x*pos.x*0.01);
    //if(abs(pos.y) > 10.0) ans *= 1.0 / (pos.y*pos.y*0.01);
    if(abs(pos.z) > 10.0 ) ans *= 1.0 / (pos.z*pos.z*0.01);

    return ans;

}


float shadow(vec3 pos, vec3 lightpos){
	vec3 rd = normalize(lightpos-pos);
    float res = 1.0;
    float t = 0.0;

    for (float i = 0.0; i < 1000.0; i++)
    {
		vec2 h = map(pos + rd * t);
        res = min(res, 15.0 * h.x / t);
        t += h.x;

        if ((res < 0.000001 || t > 3200.0)) break;

    }

    return clamp(res, 0.0, 1.0);

}

vec3 fogMarch(vec3 pos){
    float absorp = 1.0;
    float opacity = 1.0;
    float absSum = 0.0; 
    float h = 0.0;

    float prev_opacity = opacity;


    opacity *= BeerLambert(absorp, h);

    float absorb = prev_opacity -opacity; 

    absSum += absorb; 
    h+= 0.01;


    return vec3(absSum);

}

vec3 mirror(in vec3 opos, in vec3 ord) {


    vec3 n = normal(opos);
    vec3 ro = opos;
    vec3 rd = n;
    int max_step =1;
    vec3 lp = vec3(5.0, 5.0,0.0);
    vec3 col =vec3(1.0,1.0,1.0);
    for(int i=0; i < max_step; i++){
        vec3 h = ray_cast(ro+0.1*n, reflect(-ro, n));


        if(h.x > -1.0) {
            vec3 pos = ro + h.x *rd;

            if(h.y == 2.0){
                col = vec3(0.0, 0.0, 1.0) * calcAO(pos, normal(pos));
                col *= shadow(pos, lp);
                col *= 2.0;

            }


            if(h.y == 3.0) {

                col = vec3(0.7, 0.7, 0.7) * calcAO(pos, normal(pos));


                col += checkers(pos);
