void rot(inout vec3 p,vec3 a,float t){
	a=normalize(a);
	vec3 u=cross(a,p),v=cross(a,u);
	p=u*sin(t)+v*cos(t)+a*dot(a,p);   
}

#define sabs(a)sqrt((a)*(a)+1e-4)
float scale;
float map(vec3 p){
 	rot(p,vec3(cos(iTime*.07),sin(iTime*.06),sin(iTime*.05)*.5),iTime*.5+1.2*sin(iTime*.8));
    p.x-=10.;
    float s=2.;
    p=sabs(p);
    for(int j=0;j<8;j++){
        p=-sign(p)*(sabs(sabs(sabs(p)-2.)-1.)-1.);
        float l=(-2.13+.3*sin(iTime*.7+.5*sin(iTime*.5)))
            /max(.43+.38*sin(iTime*.9+.3*sin(iTime*.8)),dot(p,p));
        s*=l;
        p*=l;
        p-=.55+.01*sin(iTime*1.2+.5*sin(iTime*.5));
    }
    scale=s;
    return dot(p,normalize(vec3(1,2,3)))/s;
}

vec3 calcNormal(vec3 p)
{
  vec3 n=vec3(0);
  for(int i=0; i<4; i++){
    vec3 e=.003*(vec3(9>>i&1,i>>1&1,i&1)*2.-1.);
    n+=e*map(p+e);
  }
  return normalize(n);
}

float march(vec3 ro, vec3 rd, float near, float far)
{
    float t=near,d;
    for(int i=0;i<90;i++)
    {
        t+=d=map(ro+rd*t);
        if (d<.001) return t;
        if (t>=far) return far;
    }
    return far;
}

vec3 doColor(vec3 p)
{
    return cos(vec3(3,2,8)+log(scale*.003)+iTime*1.5)*.5+.5;
}

