// Signed distance and analytic gradient to an isosceles
// triangle. More accurate than central differences and
// faster to compute than automatic differentiation/duals.

// List of other 2D distances+gradients:
//
// https://iquilezles.org/www/articles/distgradfunctions2d/distgradfunctions2d.htm
//
// and
//
// https://www.shadertoy.com/playlist/M3dSRf


// .x = f(p)
// .y = âf(p)/âx
// .z = âf(p)/ây
// .yz = âf(p) with ââf(p)â = 1
vec3 sdgTriangleIsosceles( in vec2 p, in vec2 q )
{
    float w = sign(p.x);
    p.x = abs(p.x);
	vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
    vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
    float k = sign( q.y );
    float l1 = dot(a,a);
    float l2 = dot(b,b);
    float d = sqrt((l1<l2)?l1:l2);
    vec2  g =      (l1<l2)? a: b;
    float s = max( k*(p.x*q.y-p.y*q.x),k*(p.y-q.y)  );
    return vec3(d,vec2(w*g.x,g.y)/d)*sign(s);
}

#define AA 2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

        p.y -= 0.3;
        // size
        vec2 si = vec2(0.5,-0.5) + vec2(0.3,-0.3)*cos( iTime + vec2(0.0,1.57) + 0.0 );

        // sdf(p) and gradient(sdf(p))
        vec3 dg = sdgTriangleIsosceles(p,si);
        float d = dg.x;
        vec2 g = dg.yz;

        // central differenes based gradient, for comparison
        // g = vec2(dFdx(d),dFdy(d))/(2.0/iResolution.y);

        // coloring
         vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(0.4,0.7,0.85);
        col *= 1.0 + vec3(0.5*g,0.0);
      //col = vec3(0.5+0.5*g,1.0);
        col *= 1.0 - 0.5*exp(-16.0*abs(d));
        col *= 0.9 + 0.1*cos(150.0*d);
        col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );

	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

vec2 distort(vec2 uv, float k4, float mag) {
    vec2 v = uv - vec2(0.5);
    float r2 = dot(v, v);
    vec2 dr = 1.0 / (1.0 + k4 * r2 * r2) * v;
    return dr * mag + vec2(0.5);
}

float noise(vec2 co){
  return fract(sin(dot(co.xy,vec2(1.0,0.42829))) * 51126.88915);
}

float vignette(vec2 uv, float scale) {
    uv = (uv - .5) * scale;
    return smoothstep(0.0, 1.0, sqrt(pow(abs(uv.x), 8.0) + pow(abs(uv.y), 8.0)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float noisy = abs(sin(iTime) * 0.02) +
                  pow(sin(iTime * 0.25) * 0.5 + 0.5, 8.0) + 0.01;

    float noisy2 = noisy * noisy;
    float randomizer = noise(vec2(iTime, iTime * 3.0));
    float randomizer2 = noise(vec2(iTime* 1.234, iTime * 2.345));
    float scanline = sin(uv.y * 500.0 - iTime * 10.0);
    float vflip = sin(uv.y * 4.0 - iTime * 120.0);

    // SCAN NOISE OFFSET
    float scannoise = randomizer * 0.5 * noisy2;
    vec2 scannoise_offset1 = vec2(scanline * scannoise, 0.0) + vec2(vflip * scannoise, 0.0);
    vec2 scannoise_offset2 = vec2((randomizer - 0.5) * noisy2, (randomizer2 - 0.5) * noisy2);

    // CRT DISTORTION
    vec2 uv_bend = distort(fract(uv + scannoise_offset1 + scannoise_offset2), -0.5, 0.8);

    // BLOCK NOISE
    float block_size = 0.0001 + 0.02 * (randomizer - 0.5);
    vec2 uv_blocked = uv_bend - mod(uv_bend, block_size);
    float block = 1.0 - step(abs(noise(uv_blocked + vec2(iTime, iTime)) * randomizer2 * noisy), 0.01);
    uv_bend = mix(uv_bend, uv_blocked, block);

    // VIDEO SOURCE SAMPLING
    vec3 col;
    col.r = texture(iChannel0, uv_bend + vec2(0.01, 0.0)).r;
    col.g = texture(iChannel0, uv_bend).g;
    col.b = texture(iChannel0, uv_bend + vec2(-0.01, 0.0)).b;

    // GRAIN NOISE
    col = mix(col, vec3(noise(uv * 100.0 + randomizer)), 0.1 + noisy2 * randomizer * 0.9);

    // FADING OUT
    vec3 bg = vec3(0.25, 0.5, 0.5);
    col = mix(col, bg, ((scanline * 0.5 + 0.5) + (vflip * 0.5 + 0.5)) * 0.125);
    col = mix(col, bg, vignette(uv, 1.5));
    fragColor = vec4(col,1.0);
}

