mat2 rotMat(float k) {
    float c = cos(k);
    float s = sin(k);
    return mat2(c, -s, s,  c);
}

mat3 rotateY(float k) {
    float c = cos(k);
    float s = sin(k);
    return mat3(c,  0, -s,
                0,  1,  0,
                s,  0,  c);
}

SdfCtx calcDist(vec3 p) {
    float t = iTime;

    float dStripedSphere = stripe(stripe(stripe(stripe(sdSphere(p * rotateY(t * 0.05) + vec3(2.05, 0., 2.05), 0.264 + 0.08), 0.08), 0.06), 0.02), 0.01);
    float dStripes = max(max(dStripedSphere, p.y), -(p.y + 0.001));

    vec3 moonP = 0.1 + (p * rotateY(t * 0.3) - vec3(0.1) * rotateY(t * 3.5));

    //PLANETS
    SdfCtx d = 			SdfCtx(0.,  vec3(0.,   0.,   0.),   sdSphere(p * rotateY(t * 0.)    + vec3(0., 0., 0.),     0.42));   // SUN
    d = addPrimitive(d, SdfCtx(0.,  vec3(0.1,  0.1,  0.1), 	sdSphere(moonP 				    + vec3(0.75, 0., 0.75), 0.02)));  // MOO
    d = addPrimitive(d, SdfCtx(0., 	vec3(0.3,  0.3,  0.3),  sdSphere(p * rotateY(t * 2.)    + vec3(0.45, 0., 0.45), 0.03)));  // MER
    d = addPrimitive(d, SdfCtx(0., 	vec3(0.57, 0.57, 0.57), sdSphere(p * rotateY(t * 1.)    + vec3(0.65, 0., 0.65), 0.072))); // VEN
    d = addPrimitive(d, SdfCtx(0., 	vec3(0.1,  0.3,  0.4),  sdSphere(p * rotateY(t * 0.3)   + vec3(0.85, 0., 0.85), 0.078))); // EAR
    d = addPrimitive(d, SdfCtx(1., 	vec3(0.53, 0.24, 0.15), sdSphere(p * rotateY(t * 0.2)   + vec3(1.1,  0., 1.1),  0.06)));  // MAR
    d = addPrimitive(d, SdfCtx(35., vec3(0.4,  0.2,  0.07), sdSphere(p * rotateY(t * 0.1)   + vec3(1.45, 0., 1.45), 0.33)));  // JUP
    d = addPrimitive(d, SdfCtx(30., vec3(0.3,  0.2,  0.1),  sdSphere(p * rotateY(t * 0.05)  + vec3(2.05, 0., 2.05), 0.264))); // SAT
    d = addPrimitive(d, SdfCtx(2., 	vec3(0.45, 0.59, 0.71), sdSphere(p * rotateY(t * 0.01)  + vec3(2.55, 0., 2.55), 0.15)));  // URA
    d = addPrimitive(d, SdfCtx(40., vec3(0.2,  0.29, 0.47), sdSphere(p * rotateY(t * 0.005) + vec3(2.8,  0., 2.8),  0.1)));   // NEP

    vec3 orbitCol = vec3(0.15);

    //ORBITS
    d = addPrimitive(d, SdfCtx(0., orbitCol, sdTorus(p, 0.63))); // MER
    d = addPrimitive(d, SdfCtx(0., orbitCol, sdTorus(p, 0.92))); // VEN
    d = addPrimitive(d, SdfCtx(0., orbitCol, sdTorus(p, 1.2)));  // EAR
    d = addPrimitive(d, SdfCtx(0., orbitCol, sdTorus(p, 1.55))); // MAR 
    d = addPrimitive(d, SdfCtx(0., orbitCol, sdTorus(p, 2.05))); // JUP
    d = addPrimitive(d, SdfCtx(0., orbitCol, sdTorus(p, 2.9)));  // SAT
    d = addPrimitive(d, SdfCtx(0., orbitCol, sdTorus(p, 3.6)));  // URA
    d = addPrimitive(d, SdfCtx(0., orbitCol, sdTorus(p, 3.95))); // NEP

    //RINGS
    d = addPrimitive(d, SdfCtx(0.,  vec3(0.3,  0.2,  0.1),  dStripes)); // SAT

    return d;
}  

vec3 calcNormal(vec3 pos) {
    float d = calcDist(pos).d;
    return normalize(vec3(d - calcDist(pos - vec3(EPS, 0,  0 )).d,
                          d - calcDist(pos - vec3( 0, EPS, 0 )).d,
                          d - calcDist(pos - vec3( 0,  0, EPS)).d));
}

vec3 calcLight(vec3 fragPos, vec3 lightPos, vec3 lightCol, vec3 camDir) {    
    vec3 normal = calcNormal(fragPos);
    vec3 lightDir = normalize(lightPos - fragPos);

    vec3 ambient = vec3(0.1);
    vec3 diffuse = vec3(max(dot(normal, lightDir), 0.));

    return lightCol * (ambient + diffuse);
}

mat3 calcLookAtMatrix(vec3 camPos, vec3 at) {
    vec3 zAxis = normalize(at - camPos);
    vec3 xAxis = normalize(cross(zAxis, vec3(0., 1., 0.)));
    vec3 yAxis = normalize(cross(xAxis, zAxis));
    return mat3(xAxis, yAxis, zAxis);
}

SdfCtx rayMarch(vec3 rayO, vec3 rayD) {
    SdfCtx res = SdfCtx(0., vec3(0), 0.);
    for( int i = 0 ; i < 100; i++ ) {
        SdfCtx dS = calcDist(rayO + rayD * res.d);
        if (dS.d < EPS || res.d > MAX_DIST) break;
        res.col = dS.col;
        res.k = dS.k;
        res.d += dS.d;
    }
    return res;
}

vec3 calcPlanetTexture(vec2 p, vec3 col, float k) {
    return vec3(sin(p.y * k) * 0.1 + 0.4) * col;
}

float fallingStar(vec2 p, vec2 a, vec2 b) {
    p -= a;
    b -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.);
    p -= b * h;
    return h * smoothstep(2. * h / iResolution.y, 0., length(p));
}

float hash(vec3 p) {
	p = fract(p * vec3(.1031, .11369, .13787));
    p += dot(p, p.yzx + 19.19);
    return fract((p.x + p.y) * p.z);
}

float calcStarLayer(vec3 d, float intensity) {
    return smoothstep(intensity, 0., length(fract(d) - 0.5)) * smoothstep(0.98, 1., hash(floor(d)));
}

vec3 calcBgColor(vec2 xy, vec3 rayDir) {
    return fallingStar(xy * rotMat(PI), vec2(-0.04) + tan(iTime / 4.), vec2(0.04) + tan(iTime / 4.)) * vec3(0.3, 0.4, 0.7) +
           vec3(calcStarLayer(rayDir * 550., abs(sin(iTime / 2.)) / 2.)) * vec3(0.5, 0.28, 0.73) + 
           vec3(calcStarLayer(rayDir * 550., abs(cos(iTime / 2.)) / 2.)) * vec3(0.3, 0.6,  0.73) + 
           vec3(calcStarLayer(rayDir * 500., abs(cos(iTime)) / 2.)) * vec3(0.5, 0.58, 0.43) + 
           vec3(calcStarLayer(rayDir * 500., abs(sin(iTime)) / 2.)) * vec3(0.5);
}

vec2 calcUV(vec3 p) {
    vec3 n = calcNormal(p);
    return vec2(atan(n.x, n.z) / (2. * PI) + 0.5, n.y * 0.5 + 0.5);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 xy = (fragCoord.xy - iResolution.xy / 2.) / min(iResolution.x, iResolution.y);

    vec3 camPos = vec3(-7., 6. * max(abs(cos(iTime / 30.)), 0.4), -8. * sin(iTime / 30.));
    mat3 cam = calcLookAtMatrix(camPos, vec3(0, -0.5, 0));

    vec3 rayDir = cam * normalize(vec3(xy, 2.));
    SdfCtx rayRes = rayMarch(camPos, rayDir);     

    vec3 col = vec3(0);

    if(rayRes.d < MAX_DIST){
        vec3 p = camPos + rayRes.d * rayDir;
        if (rayRes.col.x == 0.) {
            col = (texture(iChannel0, calcUV(p * rotateY(iTime / 5.))).xyz / 1.1 + 0.5) * 1.5 * vec3(0.96, 0.55, 0);
        } else {
            col = vec3(calcPlanetTexture(vec2(p), rayRes.col, rayRes.k)) + calcLight(p, vec3(0), vec3(0.4), rayDir);
        }
    } else {
        col = calcBgColor(xy, rayDir) + vec3(0.09 / length(xy - vec2(0, 0.07))) * vec3(0.7, 0.5, 0.);
    }

    fragColor = vec4(col, 1);
}

#define PI 3.1415926535
#define CS(a) vec2(cos(a), sin(a))
#define rot(a) mat2(cos(a), sin(a), -sin(a), cos(a))
#define hue(v) ( .6 + .6 * cos( 2.*PI*(v) + vec4(0,-2.*PI/3.,2.*PI/3.,0)))

float smoothhill(float a, float b, float c, float x) {
    return smoothstep(a,b,x)-smoothstep(b,c,x);
}
float smoothParity(float x, float b) {
    float m = mod(x-.5, 2.);
    return smoothstep(.5-b, .5+b, m) - smoothstep(1.5-b, 1.5+b, m);
}

vec2 target (vec2 z, float power) {
    if (length(z) > 100.) return z;  // Prevent blowup.
    vec2 weightedSum=vec2(0); float weight=0.;
#define N 5.
    for (float i = 0.; i < N; i++) {
        float theta = 6.28*(i/N);
        vec2 point = CS(theta), d = z-point;
        float L = length(d), w = pow(L, -10.);
        float scale = 2.*pow(L, power-1.);
        weight += w;
        weightedSum += w*scale*d;
    }
#undef N
    return weightedSum/weight;
}

vec4 render (in vec2 coord) {
    vec2 R = iResolution.xy, pw = 1./R, uv = coord*pw;
    float cycleT=20., t = fract(iTime/cycleT), maxIt = 5., 
          iterations = maxIt*smoothhill(0.,.5,1.,t), zoom = 4.;
    float parity = smoothParity(iTime/cycleT, .05);
    vec4 bandColor = mix(hue(.0), hue(.6)*.5, parity);
    float b = mix(.5, .5, parity);
    float bw = mix(.2, .3, parity);
    float power = mix(3., 1.8, parity);

    vec2 p = rot(radians(36.+90.))*((coord-.5*R)/R.y)*zoom;
    for (float i = 0.; i < 10.; i++){
        if (i < floor(iterations)) p = target(p, power);
    }
    p = mix(p, target(p, power), fract(iterations));

    float m = length(p);
    vec4 col = vec4(mix(.85, 1., smoothhill(0.,.5,1.5,m)));
    col = mix(col, bandColor, .3*smoothhill(b-bw,b,b+bw,m));
    return col;
}

void mainImage( out vec4 O, in vec2 u ) {
    // 2D Antialiasing:
    vec2 d = vec2(.5,0);
    O = (render(u+d.xy)+render(u-d.xy)+render(u+d.yx)+render(u-d.yx))*.25;
}

mat2 Rot(float a) {
	float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float Triangle( in vec2 p )
{
    const float k = sqrt(3.0);
    p.x = abs(p.x) - 1.0;
    p.y = p.y + 1.0/k;
    if( p.x+k*p.y>0.0 ) p = vec2(p.x-k*p.y,-k*p.x-p.y)/2.0;
    p.x -= clamp( p.x, -2.0, 0.0 );
    return -length(p)*sign(p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xy;
    uv*=17.;
    vec3 col = vec3(0);
    uv.y*=.58;
    uv.y=abs(uv.y);
    uv.x=abs(uv.x);

    uv += mod(uv.x, uv.y);
    uv.x += 1.-uv.y;
    uv.y -= .3;

    float t = iTime * .3;

    float s1 = 1.47;
    float s2 = 0.16;

    uv*=Rot(-t*.4);

    uv*=Rot(t);
    col.r += smoothstep(s1, s2, Triangle(uv))*1.;
    uv*=Rot(-t);
    uv*=Rot(t*.4);
    col.b += smoothstep(s1, s2, Triangle(vec2(uv.x+1., uv.y)))*1.;
    uv*=Rot(-t*.4);
    uv*=Rot(-t*1.24);
    col.g += smoothstep(s1, s2, Triangle(vec2(uv.x-1., uv.y)))*1.;
    uv*=Rot(t*1.24);

    uv*=Rot(-t);
    col.rg += smoothstep(s1, s2, Triangle(uv))*1.;
    uv*=Rot(t);
    uv*=Rot(-t*.71);
    col -= smoothstep(s1, s2, Triangle(vec2(uv.x+1., uv.y+(1.*sin(iTime)*.5+.5))))*1.;
    uv*=Rot(t*.3);
    uv*=Rot(t*1.24);
    col.b += smoothstep(s1, s2, Triangle(vec2(uv.x-1., uv.y)))*1.;
    uv*=Rot(-t*1.1);

    uv*=Rot(t*1.78);
    col -= smoothstep(s1, s2, Triangle(vec2(uv.x-1., uv.y)))*1.;
    uv*=Rot(-t*1.78);

    float t1 = sin(t);
    col -= smoothstep(0.81+t1*.1, 0.8+t1*.1, length(uv));

    col.rg += smoothstep(0.41, 0.4, length(uv));

    uv = (fragCoord-.5*iResolution.xy)/iResolution.xy;
    col *= smoothstep(.47, -.2, length(uv.y));
    uv*= 12.;
    uv.y*=.58;
    uv.y=abs(uv.y);
    uv.x=abs(uv.x);

    uv += dot(uv.x*uv.x*uv.x, uv.y);

    uv *= Rot(2.);

    t = iTime * .7;
    uv*=Rot(t*.17);

    s1 = 0.17;
    s2 = 0.16;

    uv*=Rot(t);
    col.b += smoothstep(s1, s2, Triangle(uv))*1.;
    uv*=Rot(-t);
    uv*=Rot(t*.4);
    col.g += smoothstep(s1, s2, Triangle(vec2(uv.x+1., uv.y)))*1.;
    uv*=Rot(-t*.4);
    uv*=Rot(-t*1.24);
    col.g += smoothstep(s1, s2, Triangle(vec2(uv.x-1., uv.y)))*1.;
    uv*=Rot(t*1.24);

    uv*=Rot(-t);
    col.r += smoothstep(s1, s2, Triangle(uv))*1.;
    uv*=Rot(t);
    uv*=Rot(-t*.71);
    col -= smoothstep(s1, s2, Triangle(vec2(uv.x+1., uv.y+(1.*sin(iTime)*.5+.5))))*1.;
    uv*=Rot(t*.3);
    uv*=Rot(t*1.24);
    col.b += smoothstep(s1, s2, Triangle(vec2(uv.x-1., uv.y)))*1.;
    uv*=Rot(-t*1.1);

    uv*=Rot(t*1.78);
    col -= smoothstep(s1, s2, Triangle(vec2(uv.x-1., uv.y)))*1.;
    uv*=Rot(-t*1.78);

    t1 = sin(t);
    col -= smoothstep(0.81+t1*.1, 0.8+t1*.1, length(uv));

    col -= smoothstep(0.41, 0.4, length(uv));

    uv = (fragCoord-.5*iResolution.xy)/iResolution.xy;
    col *= smoothstep(.47, -.2, length(uv.y));

    col += smoothstep(.1, .0, length(uv))*2.;
    col *= smoothstep(.3, .0, length(uv));

    col *= vec3(1., 0.5, 1.);

    fragColor = vec4(col,1.0);
}

// https://darkeclipz.blogspot.com/2020/12/iterated-function-systems-ifs.html 

#define PI 3.14159265358979323846264
#define MOUSE_DOWN (iMouse.z > 0.)
#define SS 4.
#define R iResolution.xy

float random (in vec2 st) {
    return fract(sin(dot(st.xy, vec2(12454.1,78345.2))) * 43758.5);
}

vec2 random2(in vec2 st) {
	return vec2(random(st), random(st));    
}

vec2 ifs(vec2 p, float s, float r, int n) {
    float co = cos(r), si = sin(r);
    mat2 rot = mat2(co, si, -si, co);
    for(int i=0; i < n; i++) {
        p.x = abs(p.x);
        p -= vec2(1.0, 0);
        p *= rot;
        p *= s;
    }
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);
    float px = 1. / iResolution.y;
    for(float i=0.; i < SS; i++) {
        vec2 uv = 5.5 * (2.*(fragCoord + random2(R+i)) - R) / R.y;
        float s = MOUSE_DOWN ? 1.11 + (iMouse.y / iResolution.y - 0.5) * 0.1
                             : 1.11;
        float r = MOUSE_DOWN ? PI / 2. + iMouse.x / iResolution.x * 2. * PI
                             : 2.*PI*fract((iTime + 20.2*2.)/100.);
        uv = ifs(uv, s, r, 24);
        uv = ifs(uv, 1.17, PI/2., 8);
        float ds = 0.5;
        col += vec3(1) * smoothstep(ds, ds-16.*px, length(uv));
    }
    fragColor = vec4(col / SS, 1.0);
}

