    vec3 col = (
        TwinDragon(fragCoord+d.xy)+
        TwinDragon(fragCoord-d.xy)+
        TwinDragon(fragCoord+d.yx)+
        TwinDragon(fragCoord-d.yx)
    )*.35;

	fragColor = vec4(col,2.);

}

