const float cam_dist = 5.5; // camera distance
const float bb_size = 2.5; // bounding sphere size
const float rm_rlmin = 0.0; // ray march ray_length minimum
const float rm_rlmax = 50.0; // ray march ray-length maximum
const int   rm_imax = 150; // ray march maximal number of iterations

const float rm_p3slmul = 0.32772; // ray march pow(step length multiplier,3)
// anti-aliasing
const float AA = 1.0;

////////////////////////////////////////////////////////////////////////////////

// const
const float PI = 3.14159265359;

// 2D transformations: vec2 => vec2
// complex
vec2 cmul(vec2 za,vec2 zb) // za*zb
{
    return za*mat2(zb.x,-zb.y,zb.yx);
}

vec2 cinv(vec2 z) // 1/z
{
    return z*vec2(1.,-1.)/dot(z,z);
}

vec2 cdiv(vec2 z, vec2 w) // z/w
{
    return cmul(z,cinv(w));
}

vec2 cpow(vec2 z, float q) // z^q
{
    float r = pow(length(z), q);
	float a=q* atan(z.y,z.x);
    return vec2(r*cos(a),r*sin(a));
}

vec2 cpow(vec2 z, int n) // z^n
{
    vec2 w = z;
    for (int i = 1; i < n; i++){
    w = cmul(w,z);
    }
    return w;
}

vec2 crot(vec2 z,float a) // z*e^(i*a)
{
    return cmul(z, vec2(cos(-a),sin(-a)));
}

vec2 crpt(vec2 z,float a, int n, float x0) // z_out = (z*e^ia)^n-x0
{
	return cpow(crot(z, a), n) - vec2(x0, 0.);
}

// 3D-transformations: vec3 => vec3
//color
vec3 hsv2rgb(float h, float s, float v)  // hue, saturation, value
{
    vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing       
    return v * mix( vec3(1.0), rgb, s);
}

vec2 isphere(in vec3 ro, in vec3 rd, in float r )
{
    // sphere centered at the origin, with size rd   
	float b = dot(ro,rd);
	float c = dot(ro,ro) - r*r;
    float h = b*b - c;

    if( h<0.0 ) return vec2(-1.0);

    h = sqrt( h );

    return -b + vec2(-h,h);
}

////////////////////////////////////////////////////////////////////////////////

float map( in vec3 p, out vec4 mat )
{
    //// sdf's
    float time = 0.1 * iTime;
	vec3 pn = normalize(p);
    vec2 z, zk; 
    // F3,3(z) tetrahedral symmetry
    int k = 3, n = 3;
    const float a1 = 2.*sqrt(2.);
    if (pn.z < 0.) // stereographic (north pol)
    {
        z = pn.xy/(1.0-pn.z);
        zk = cpow(z,k);
        z = 1./(8.*a1) * zk;
        z = cmul(z, cpow(zk-vec2(a1,0.),k));
        z = cdiv(z, cpow(zk+ vec2(1./a1,0.0),n));
    }else        // stereographic (south pol)
    {
        z = vec2(pn.x, -pn.y)/(1.+pn.z);
        zk = cpow(z,k);
        z = 1./(8.*a1)*cinv(zk);       
        z = cmul(z, cpow(vec2(1.,0.) - zk * a1 ,k));
        z = cdiv(z, cpow(vec2(1.,0.) + zk / a1 ,n));    
    }

    // polygon properties
    float sr = float(length(z)>1.0?-n:k);
	float au = atan(z.y,z.x)-0.5*PI*sign(sr);
	float av = atan(log(length(z))*0.1);

	// spherical => cartesian coords
	p = length(p)*vec3(	sin(av)*cos(au),
                        sin(av)*sin(au),
                        cos(av));

    // torus 
    au = atan(p.y, p.x);
    z = vec2( length(p.xy), p.z);
    z = cmul(z,z); z.x += 4.0;

    float ssr = sign(sr);
	// fractal level 1
    z = crpt(z, -ssr*au/3.0, 3,  2.0+3.0*cos(au));

	// fractal level 2
    z = crpt(z, PI*ssr*time, 2, 1. + 0.5 * cos(time) );

    // actual ray data
	mat = vec4(z,au,sr);

    // step length estimation
	return log(length(z)); // :( Not perfect!
}

float intersect( in vec3 ro, in vec3 rd, out vec4 rescol, in float px )
{
    float res = -1.0; // init no rayintersection

    // boundingshape
    vec2 dis = isphere( ro, rd ,abs(bb_size));
    if( dis.y<0.0 ) // does ray hit boundingshape?
        return -1.0;
    dis.x = max( dis.x, max(rm_rlmin,0.0) );// start_raylength from bb_near_hit or raylength_minimum
    dis.y = min( dis.y, rm_rlmax );	// end_raylength from bb_far_hit or raylength_maximum

    // raymarch signed distance field
	vec4 data; // data from surface hit point and accumulated data while raymarching
	float fh = (0.5-0.0001)*rm_p3slmul*rm_p3slmul*rm_p3slmul + 0.0001; // fh slider response curve
	float t = dis.x; // ray iteration starts at boundingshape
	for( int i=0; i<rm_imax; i++  )
    { 
        vec3 pos = ro + rd*t;
        float th = 0.005*px*t;// sdf surface hit tolerance
		float h = map(pos, data);
		if( t>dis.y || abs(h)<th ) break; // ray is outside boundingshape or sdf_surface_hit.
        t += h*fh; // update ray length  with step_length * step_length_multiplier
    }

    if( t<dis.y ) // Is ray inside boundingshape?
    {
        rescol = data; // return data
        res = t; // return ray_length
    }
    return res;
}

vec3 calcNormal( in vec3 pos, in float px )
{
    vec4 tmp; // dummy variable
    vec2 e = vec2(1.0,-1.0)*0.5773*0.25*px;
    return normalize( e.xyy*map( pos + e.xyy,tmp ) + 
					  e.yyx*map( pos + e.yyx,tmp ) + 
					  e.yxy*map( pos + e.yxy,tmp ) + 
					  e.xxx*map( pos + e.xxx,tmp ) );
}

// from iq
float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<1; i++ )
    {
    	vec4 temp;
    	float h = map( ro + rd*t, temp );
        res = min( res, 8.0*h/t );
        t += h*.25;
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0., 1. );
}

// from iq
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<1; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
    	vec4 temp;
    	float dd = map( aopos, temp );
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );
}

// transform from mla
vec3 transform(in vec4 p)
{
	if (iMouse.x > 0.0)
	{
		float phi = (2.0*iMouse.x-iResolution.x)/iResolution.x*PI;
		float theta = (2.0*iMouse.y-iResolution.y)/iResolution.y*PI;
		p.yz = crot(p.yz,theta);
		p.zx = crot(p.zx,-phi);
	}
	p.xz = crot(p.xz,p.w*0.01);
	return p.xyz;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // time
    float time = iTime;

    // camera
    float fle = 2.0;
	vec3 ro = transform(vec4(0,0,-cam_dist,time)).xyz;

	// anti-aliasing
	vec3 aacol = vec3(0);
	for (float i = 0.0; i < max(-time,AA); i++) {
		for (float j = 0.0; j < max(-time,AA); j++) {

			// ray direction
			vec2 uv = (2.0*(fragCoord+vec2(i,j)/AA)-iResolution.xy)/iResolution.y;
			vec3 rd = normalize(transform(vec4(uv,fle,time)));

			// ray distance
			vec4 mat = vec4(0.0);
			float px = 2.0/( iResolution.y*fle );
			float t = intersect( ro, rd, mat, px );

			// light
			const vec3 ld = 0.5*vec3(0.,1.,.5); // ligth direction
			const vec3 lc = vec3(0.4);// ligth color
			vec3 bg = vec3(0.8,0.9,2.0)*0.3*(0.9+0.1*rd.y);	// background-color

			// color
			vec3 col = vec3(0.6,0.4,0.7);

			if (t < 0.0){ // sky
				col = bg;
				col += 6.0*vec3(0.8,0.7,0.5)*pow( clamp(dot(rd,lc),0.0,1.0), 32.0 ); // sun
			}
			else{ // object surface
				vec3 p = ro + rd * t;
				vec3 n = calcNormal( p, px );

				// texture_coords
				float u = mat.z*0.5/PI;
				float v = sign(mat.w)*atan(mat.y,mat.x)*0.5/PI;

				// texture_color
				float l = 0.5+0.5*cos(0.3*iTime);
				float col_h = 1.0;
				col_h *= v;
				//col_h *= u;
				float col_s = 1.0;
				float col_v = 1.0;
				col = hsv2rgb(col_h,col_s,col_v); //(hue, saturation, value)

				// lighting
				float occ = calcAO( p, n );
				float amb = clamp( 0.5+0.5*n.y, 0.0, 1.0 );
				vec4 temp; // dummy variable
				float dif = clamp( dot( n, ld ), 0.0, 1.0 ) * map( p+n*1.16, temp);
				float spe = pow(clamp( dot( rd, ld ), 0.0, 1.0 ),16.0);
				float sss = map( p - n*0.001, temp)/0.01;

				// shading
				dif *= softshadow( p, ld, 0.1, 1. );
				vec3 brdf = vec3(0.0);
				brdf += 0.2*dif*vec3(1.00,0.90,0.60);
				brdf += 0.2*spe*vec3(0.8,0.60,0.20)*dif;
				brdf += 0.2*amb*vec3(0.40,0.60,0.40)*occ;
				brdf += 0.4;
				col.rgb *= brdf;

				// fog
				col = mix( bg,col, exp( -0.025*t*t));
			}
			aacol += col;
		}
	}
	aacol /= float(AA*AA);
    // gamma
    aacol = sqrt(aacol);
    fragColor = vec4(aacol,1.);
}

const float ni = 3.07179708567758979858283697670; 
vec2 rot(vec2 a, float c){ 
float l = length(a);
a = normalize(a);    
float g = c*ni/180.0;
float ang = atan(a.y,a.x)+g;
return vec2(l*cos(ang),l*sin(ang));
}

vec3 r(vec3 p, vec3 c){
   p/=c;
   return ((p-floor(p))*c)-0.5*c; 
}

float box(vec3 a, vec3 c){
vec3 p = abs(a)-c;
return max(max(p.x,p.y),p.z);        
}

float dis(vec3 p){
    vec3 np = p;
    p = r(p, vec3(14.0,14.,6.0));
      p = vec3(rot(p.xy, iTime*20.0),p.z);

float a = box(-abs(p), vec3(4.0,4.0,2.0));
    float c = length(abs(p)-sin(iTime)*3.0)-2.0;
    float b = length(-abs(p)-1.2)-6.3;
    return min(max(max(a,-c),-b),
              box(r(np, vec3(1.0, 10.0, 40.0)), vec3(100.0,2.0,2.0))

              );

}

bool trac(out vec3 p, vec3 d, out float dd){
    for(int i = 0; i < 80; i++){
        dd = dis(p);
        if(dd<0.01)return true;
        p+=d*dd;
    }
    return false;
}

vec3 norm(vec3 p, float dd){
    return normalize(
    vec3(dd- dis(vec3(p.x-0.1, p.yz)),
         dd- dis(vec3(p.x,p.y-0.1,p.z)),
         dd- dis(vec3(p.xy,p.z-0.1))
        )
    );

}

float rough(float a, float c){
return exp(-pow(12.0*(1.0-a)*(c-1.0)-a ,2.0))/(ni*a);
}

float shadow(vec3 p, vec3 lig){
vec3 d = normalize(lig-p);
float dd, lgg;
    for(int i = 0; i < 40; i++){
        lgg = length(lig-p)-1.3;
        dd = min(dis(p),lgg);
        if(dd<0.01)break;
        p+=d*dd;

    }
    if(dd<0.01 && dd==lgg)return 1.0;
    return 0.3;
}

vec3 volume(vec3 prevp, vec3 p, vec3 lig){
    const int iter = 17;
    vec3 dp = (p-prevp)/float(iter);

    float l;
    for(int i = 0; i < iter; i++){
    l+=shadow(prevp+dp*float(i+1),lig);
    }
    l/=float(iter);

    return vec3(0.9,0.6,0.3)*l;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    uv *= 2.0;

    vec3 d = normalize(vec3(uv.x, 1.0, uv.y));
    vec2 mouse = iMouse.xy / iResolution.xy;
    mouse = mouse * 2.0 - 1.0;
    //d = -abs(d);
    d.yz = rot(d.yz, mouse.y*90.0);
    d.xy = rot(d.xy, -mouse.x*180.0);

    vec3 p = vec3(0.0,-10.0*3.0,7.0);

    vec3 col = vec3(0.2);

    vec3 lig = vec3(0.0, 5.0, 16.0);
    vec3 prevp = p;
    vec3 currp;
    float dd;
    if(trac(p,d,dd)){
        vec3 n = norm(p,dd);
        vec3 light = normalize(lig-p);
        col = vec3(1.0)*dot(n,light);
        col += rough(0.4, dot(reflect(d,n), light));
        col *= shadow(p-d*0.1, lig);
        currp = p;

        /*p-=d*0.2;
        d = reflect(d,n);
        if(trac(p,d,dd)){
            n = norm(p,dd);
            light = normalize(lig-p);
            vec3 col2 = vec3(1.0)*dot(n,light);
            col2 += rough(0.4, dot(reflect(d,n), light));
            col2 *= shadow(p-d*0.1, lig);
            col+=col2;
        }else{
        col+=0.2;
        }
        col/=2.0;*/
    }

    col+=volume(prevp, currp, lig);

    float ds = length(p-prevp);
    col-=(sqrt(ds)/8.0)*ds*0.005;

    float dss = abs(length(vec2(0.5)-(fragCoord/iResolution.xy)));
    col -= dss*dss*0.6;
    fragColor = vec4(col,1.0);
}

#define PI atan(1.)*4.
#define TAU atan(1.)*8.

vec3 lookAt(vec3 rd,vec3 ro,vec3 ta,vec3 up){
    vec3 w=normalize(ta-ro),u=normalize(cross(w,up));
    return rd.x*u+rd.y*cross(u,w)+rd.z*w;
}


#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

vec3 ta;
float g1=0.;

#define sabs(x,k)sqrt((x)*(x)+k)
vec2 sFold90(vec2 p, float k)
{
    return (p.x+p.y+vec2(1,-1)*sabs(p.x-p.y,k))*.5;
}

float doragonSpine(vec3 p)
{
    p.z-=iTime*4.;
    float s=1.;
    float c=1.2;
    p.z=mod(p.z,c)-.5*c;
    for(float i=0.;i<3.;i++){
        p=abs(p)-.25;
        p.yz=sFold90(p.yz,1e-3);
        p.xz=sFold90(p.xz,1e-3);
        p.xy=sFold90(p.xy,1e-3);
        p-=vec3(3.3,-1.,.3);
        p.xy*=rot(.23);
        p.yz*=rot(-.05);
        p.z+=.6;
        float b=.06;
        p=b-abs(abs(p-2.*b)-b);
        p*=2.;
        s*=2.;
    }
    p/=s;
    float h=.6;
    p.x-=clamp(p.x,-h,h);
    return max(abs(length(p.xy)-.7)-.03,abs(p.z)-.007);
}

float stella(vec3 p)
{
    p-=ta+vec3(
        cos(iTime*.5+cos(iTime*.3)*.3),
        sin(iTime*.5+sin(iTime*.5)*.2),
        cos(iTime*.3+cos(iTime*.3)*.5)*.3
        );
    p.xy*=rot(iTime*1.);
    p.xz*=rot(iTime*1.);
    float k=1e-3;
    p=sabs(p,k);
    p.xz=sFold90(p.xz,k);
	p.yz=sFold90(p.yz,k);
    p.xy=sFold90(p.xy,k);
    vec3 v = normalize(vec3(2,3,1));
    return dot(p,v)-1.;
}


float map(vec3 p)
{
    float de=stella(p);
    g1+=.5/(.1+de*de); // Distance glow by balkhan    
    return min(de,doragonSpine(p));
}


vec3 calcNormal(vec3 p)
{
  vec3 n=vec3(0);
  for(int i=0; i<4; i++){
    vec3 e=.01*(vec3(9>>i&1, i>>1&1, i&1)*2.-1.);
    n+=e*map(p+e);
  }
  return normalize(n);
}

float march(vec3 ro, vec3 rd, float near, float far)
{
    float t=near,d;
    for(int i=0;i<70;i++)
    {
        t+=d=map(ro+rd*t);
        if (d<.001) return t;
        if (t>=far) return far;
    }
    return far;
}

vec3 doColor(vec3 p)
{
    if(stella(p)<.001)return vec3(0);
    return vec3(.0,.2,.3);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy*2.-iResolution.xy)/iResolution.y;
    ta=vec3(cos(iTime*.5+1.3*cos(iTime*.3))*4.,sin(iTime*.3)*4.,0);
    vec3 ro=vec3(sin(iTime*.3)*4.,cos(iTime*.4+.5*cos(iTime*.3))*4.,2.5);
    vec3 rd=lookAt(normalize(vec3(uv,1)),ro,ta,vec3(0,1,0));
    vec3 col= vec3(.02,.02,.06);
	const float maxd=50.;
    float t=march(ro,rd,0.3,maxd);
    if(t<maxd)
    {
        vec3 p=ro+rd*t;
        col=doColor(p); 
        vec3 n=calcNormal(p);      
		vec3 lightPos=ro+vec3(2,15,2);
    	vec3 li=lightPos-p;
		float len=length(li);
		li/=len;
		float dif=clamp(dot(n,li),0.,1.);
        col*=max(dif,.2);
        float rimd=pow(clamp(1.-dot(reflect(-li,n),-rd),0.,1.),2.5);
		float frn=rimd+2.2*(1.-rimd);
    	col*=frn*.6;
        col*=max(.5+.5*n.y,.3);
        col*=exp2(-2.*pow(max(0.,1.-map(p+n*.8)/.8),2.));
        col+=vec3(.7,.2,.1)*pow(clamp(dot(reflect(rd,n),li),0.,1.),10.);
        col=mix(vec3(0),col,exp(-t*t*.01));
        if(stella(p)<.001)g1*=.1;
    }
    g1*=.1;
    col+=vec3(.9,.3,0.)*smoothstep(0.,1.,g1);
    col+=vec3(.5,.4,0.)*smoothstep(.2,.9,g1*.1);
    fragColor.xyz=col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.xy;
    vec3 look_at = vec3(0);
    float t_r = .6;
    float k = t_r, mx = iMouse.x * .01, my = (iMouse.y - 400.)*.001;
    vec3 ro = vec3(k*sin(mx), my, k*cos(mx));;
    vec3 f = normalize(vec3(ro - look_at));
    vec3 r = normalize(cross(vec3(0, 1, 0), f));
    vec3 u = cross(f, r);
	float zoom = 1.2;
   	vec3 i = ro - zoom * f + uv.x * r + uv.y * u;
    vec3 rd = normalize(i-ro);
    vec3 col = vec3(0.);

    float d = 0., dr;
   	vec3 p;
    for(int i = 0; i < 32; i++){
        p = ro + rd * d;
        dr = -(length(vec2(length(p.xz)-t_r, p.y)) - .5);
        if(dr < .0001 || dr > 1e5) break;
        d += dr;
    }
    if(dr < 0.01){
        float fi = atan(p.z, p.x);
        float r = atan(p.y, length(p.xz)-t_r);
        //col += ;
        float bands = cos(fi*20. + r*10. + iTime*5.);
        float ripples = cos(5.*(r*30. - fi*30.));
        float waves = smoothstep(-.3, .3, cos((r*5. - fi*5. + iTime)));
        float b1 = smoothstep(-.1, .1, bands);
        float b2 = smoothstep(-.1, .1, bands - .5);
        // col += ripples;
        // col += waves;
        col += b1 * (1.-b2);
        col += ripples * b2 * waves;

    }
    //col += vec3(d*.1);
    fragColor = vec4(col,1.0);
}

