/////////////////////////////////////////////////////////////////////////////
// toggle camera controlled by the mouse or by time

#define CAMERA_MOUSE_

/////////////////////////////////////////////////////////////////////////////
// ray marching values

#define RAY_MARCH_STEPS    66
#define RAY_MARCH_TOO_FAR  float( RAY_MARCH_STEPS )
#define RAY_MARCH_CLOSE    0.02
#define RAY_EPSILON        vec2( RAY_MARCH_CLOSE, .0 )

#define THANKS_SHANE_FOR_THE_RAY_SHORTENING_SUGGESTION

/////////////////////////////////////////////////////////////////////////////
// scene parameters

#define NOISIER_GROUND_

#define TREE_DISTANCE_MAX       33.
#define TREE_DISTANCE_SIMPLIFY  10.


// object ids

#define TRUNK  1.
#define BODY   2.
#define STAR   3.
#define GROUND 4.
#define SNOW   5.

/////////////////////////////////////////////////////////////////////////////
// useful constants and macros

#define TRIG(d, a)      ( d * vec2( cos( a ), sin( a ) ) )
#define RGB(r,g,b)      vec3( float(r)/255., float(g)/255., float(b)/255. )

#define VEC123          vec3( .1, .2, .3 )
#define VEC3X(x)        vec3( x, .0, .0 )
#define VEC3Y(y)        vec3( .0, y, .0 )
#define VEC3Z(z)        vec3( .0, .0, z )

/////////////////////////////////////////////////////////////////////////////
// from https://www.shadertoy.com/view/4dsGRl

float noise( in vec3 x )
{
    vec3 p = floor(x.xzy);
    vec3 f = fract(x.xzy);
	f = f*f*(3.0-2.0*f);

	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = textureLod( iChannel0, (uv+0.5)/256.0, 0.0 ).ba;
	return mix( rg.y, rg.x, f.z )-.5;
}


/////////////////////////////////////////////////////////////////////////////
// signed distance functions for types

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm 
float capo( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}

float capt( vec3 p, float h, float r )
{
  float q = clamp( p.y, 0.0, h );
  float i = clamp( h - p.y, .0, h * 1.0 );
  i *= 1.0-step(.999,i/h);

  p.y -= q;

  float n = noise( p * 13. );
  p *= ( .85 + .15 * n );

  r += 0.006 * sin( i * 33. * (.8 + .2* n ) );

  return length( p ) - r * i;
}

float caps( vec3 p, float h, float r )
{
  float q = clamp( p.y, 0.0, h );
  float i = clamp( h - p.y, .0, h * 1.0 );
  i *= 1.0-step(.999,i/h);

  p.y -= q;

  return length( p ) - r * i;
}

////

float starSegmentSDF( vec3 point, vec3 a, float thickness ) {
    float h = clamp( dot( point - a, - a ) / dot( a, a ), 0., 1. );
    vec3 q = a - a * h;
    //h = pow( h, 0.7 ) * .8;      
    return length( point - q ) - thickness * h;
}

float starSDF( vec3 point, float len, float thickness ) {
    vec3 a0 = vec3( +0.000 * len, +1.000 * len, .0 ); // @ r:+1.570 , d:90
    vec3 a1 = vec3( -0.952 * len, +0.309 * len, .0 ); // @ r:+2.827 , d:162
    vec3 a2 = vec3( -0.588 * len, -0.810 * len, .0 ); // @ r:+4.084 , d:234
    vec3 a3 = vec3( +0.587 * len, -0.810 * len, .0 ); // @ r:+5.340 , d:306
    vec3 a4 = vec3( +0.951 * len, +0.309 * len, .0 ); // @ r:+6.597 , d:378

    float d0 = starSegmentSDF( point, a0, thickness );
    float d1 = starSegmentSDF( point, a1, thickness );
    float d2 = starSegmentSDF( point, a2, thickness );
    float d3 = starSegmentSDF( point, a3, thickness );
    float d4 = starSegmentSDF( point, a4, thickness );

    return min( d0, min( d1, min( d2, min( d3, d4 ) ) ) );
}

/////////////////////////////////////////////////////////////////////////////
// create the scene

vec2 which( in vec2 current, in vec2 test ) {
    current.y = mix( current.y, test.y, step( test.x, current.x ) );
    current.x = min( current.x, test.x );
    return current;
}

float ground( in vec3 point ) {
    point *= 8.;
    point.y = .0;
    return 2. * (
        +4.50 * noise( point * .01 )
#ifdef NOISIER_GROUND
        +0.50 * noise( point * .1 )
        +0.15 * noise( point * .2 )
        +0.03 * noise( point * .4 )
#endif
    ) + 2.;
}

vec2 xmasTree( vec3 f, float total, float n ) {
    vec2 d = vec2( capo( f - VEC3Y( 0.0 ), .50, .04 ), TRUNK );    
    float r_1 = .41 + .4 * n;
    float r_2 = .37 + .3 * n;
    float r_3 = .33 + .3 * n;

#if 1
    d = which( d, vec2( capt( f - VEC3Y( 0.4 ), .80, r_1 ), BODY  ) ); // bottom
    d = which( d, vec2( capt( f - VEC3Y( 0.8 ), .61, r_2 ), BODY  ) ); // middle
    d = which( d, vec2( capt( f - VEC3Y( 1.1 ), .43, r_3 ), BODY  ) ); // top
#else
    // meh..
    float h_1 = total * .45;
    float h_2 = total * .35;
    float h_3 = total * .20;
    float y = .4;
    float q = 1.6;

    d = which( d, vec2( capt( f - VEC3Y( y ), h_1 * q, r_1 ), BODY  ) ); // bottom
    y += h_1;
    d = which( d, vec2( capt( f - VEC3Y( y ), h_2 * q, r_2 ), BODY  ) ); // middle
    y += h_2;
    d = which( d, vec2( capt( f - VEC3Y( y ), h_3 * q, r_3 ), BODY  ) ); // top
#endif

    d = which( d, vec2( starSDF( f - VEC3Y( total + .66 ), .13, .04 ), STAR ) );
    return d;
}

vec2 simpleTree( vec3 f, float total ) {      
    return vec2( caps( f - VEC3Y( .3 ), total * 1.4, .35 ), BODY );
}

vec2 tree( in vec3 point, in float dizzy ) {
    vec2 d = vec2( RAY_MARCH_TOO_FAR, 0. );

    //if ( dizzy > TREE_DISTANCE_MAX ) return d;

    // make repetion rate drop off...
    // not much luck...

    float dzo = pow( dizzy / TREE_DISTANCE_MAX, 2. ); 
    //float repeat = 3. + floor( dzo * 6. ) ;
    float repeat = 3. + floor( dzo * 6. * .4 );
    //float repeat = .25 * floor( mix( 10., 30., dzo ) );
    //float repeat = 1. + floor( mix( 2., 7., dzo - .2 ) );


    float halb = .5 * repeat;

    ///////////

    vec3 f = point;
    f.xz = mod( f.xz + halb, repeat ) - halb;

    vec3 i = point - f;

    float n = noise( i );

    f.y += ground( i );
    f.xz -=  n;

    /////////////

    float total = 1. + .3 * n;

    if ( dizzy < TREE_DISTANCE_SIMPLIFY ) {
        d = which( d, xmasTree( f, total, n ) );
    } else {
        d = which( d, simpleTree( f, total ) );
    }

    return d;
}

vec2 sceneDistance( in vec3 point, in float dizzy ) {
    vec2 d = vec2( RAY_MARCH_TOO_FAR, 0. );

    float g = ground( point );

    d = which( d, tree( point, dizzy ) );
    d = which( d, vec2( point.y + g , GROUND ) );

    float snow = noise( point * 1.2357 );
    d = which( d, vec2( point.y + g + snow , SNOW ) );

    return d;
}

vec2 sceneDistance( in vec3 point ) {
    return sceneDistance( point, .0 );
}


float eq( in float i, in float value ) {  
    return clamp(1.-step(0.2020,abs(i-floor(value))),0.,1.);
}

vec3 sceneNormal( in vec3 point ) {
    return normalize( sceneDistance( point ).x - vec3(
        sceneDistance( point - RAY_EPSILON.xyy ).x,
        sceneDistance( point - RAY_EPSILON.yxy ).x,
        sceneDistance( point - RAY_EPSILON.yyx ).x
    ));
}

vec3 colorAt( in vec3 point, vec2 d ) {
    vec3 color = vec3( .0 );

    color += RGB( 100,  80,  33 ) * eq( d.y, TRUNK );
    color += RGB( 180, 255, 180 ) * eq( d.y, BODY );
    color += RGB( 255, 255, 122 ) * eq( d.y, STAR );
    color += RGB(  71,  94,  33 ) * eq( d.y, GROUND );
    color += RGB( 255, 255, 255 ) * eq( d.y, SNOW );

    return color;
}

/////////////////////////////////////////////////////////////////////////////
// the ray marching bits


vec2 rayMarch( in vec3 origin, in vec3 direction ) {
    vec2 total = vec2( .0 );
    for ( int i = 0 ; i < RAY_MARCH_STEPS ; i++ ) {
        vec3 point = origin + direction * total.x;
        vec2 current = sceneDistance( point, total.x );

#ifdef THANKS_SHANE_FOR_THE_RAY_SHORTENING_SUGGESTION
        if ( total.x > RAY_MARCH_TOO_FAR || current.x < RAY_MARCH_CLOSE ) {
            break;
        }
        // Note: Ray advancement occurs after checking for a surface hit.
        //
        // Ray shortening: Shorter for the first few iterations.
        total.x += i<32? current.x*.35 : current.x*.85; 
        total.y = current.y;
#else
        total.x += current.x;
        total.y = current.y;

        if ( total.x > RAY_MARCH_TOO_FAR || current.x < RAY_MARCH_CLOSE ) {
            break;
        }     
#endif

    }
    return total;
}

/////////////////////////////////////////////////////////////////////////////
// lighting

float pointLight( vec3 point, vec4 light ) {
    vec3 normal = sceneNormal( point );

    vec3 towardLight = light.xyz - point;
    float toLight = length( towardLight );
    towardLight = normalize( light.xyz - point );

    float diffuse = clamp( dot( normal, towardLight ), 0., 1. );

    vec3 lightStart = point + normal * RAY_MARCH_CLOSE * 2.;
    float d = rayMarch( lightStart, towardLight ).x;
    diffuse *= 1. - 0.5 * smoothstep( d * 0.9, d, toLight );

    float lightStrength = .2 + .8 * light.w / dot( toLight, toLight );  
    return diffuse * lightStrength;
}

vec3 colorPoint( vec3 point, vec3 eye, vec2 d ) {
    vec4 light    = vec4( eye + vec3(.0,2.,+4.), 8. );
    float ambient = 0.007;
    float gamma   = 1.3;

    float lighting = pointLight( point, light );
    lighting = ( 1. -  ambient ) * gamma * lighting;

    vec3 color = colorAt( point, d );
	return vec3( color * ambient + color * lighting );
}

