// Hexagon Tiles Saw Blades Pattern
// https://www.shadertoy.com/view/4dX3zl

/*
 * Originally made in Blender nodes for a #nodegolf challenge:
 * https://twitter.com/GelamiSalami/status/1335114160535871490
 *
 * Not really hexagonal tiling, but triangle tiling made to look like a hexagonal one
 * Try fiddling with the constants, really makes some cool patterns :D
 */

// Thanks FabriceNeyret2!
//#define S(v) smoothstep(6./iResolution.y, 0., v)    

void mainImage( out vec4 O, vec2 U )
{
    vec2 p = 4.* U / iResolution.y,
    //vec2 p = U / 112.5, // Fixed iRes.y at 450 - Saves a whopping 11 chars!

    	 m = vec2(1.732, 1),
    	 n = m*.5, v,

    t = dot(abs(
        	v = mod(p, m) - n
    	), n.yx) < .433 ? v : mod(p - n, m) - n;

    float a = .5 - atan(t.y, t.x = abs(t.x) - .2886) * .159,

	//s = 3. + smoothstep(-1., 1., sin(1.571*(iTime+3.)) ) * 9.;
    s = sin(iTime/.6) * 4.5 + 7.5,

    c = cos( (a - floor(s*a + .25) / s) * 6.283) * length(t);

    // Sorry AA can't have you ;(
    //O.rgb = vec3(S(c - .26) * 1.-S(c - .08));

    //O.rgb = vec3(step(c, .26) * step(.08, c));
    O -= O;
    O += .08 < c && c < .26 ? 1. : 0.; 
    //O += abs(c - .17) < .09 ? 1. : 0.; // Another way +2 char
}

#define S smoothstep
#define sat(x) clamp(x, 0., 1.)

float remap01(float a, float b, float t) {
	return sat((t-a)/(b-a));
}

float remap(float a, float b, float c, float d, float t) {
	return sat(((t-a)/(b-a)) * (d-c) + c);
}

float Hash21(vec2 p) {
	p = fract(p*vec2(123.34, 456.21));
    p += dot (p, p+45.32);
    return fract(p.x*p.y);
}

vec4 Sphere(vec2 uv, float t, vec4 col) {
    uv.y += t;
    uv.x *= S(5., -.1, t*4.5);
    uv.y /= S(1., -1., t)+.2;
    uv.y *= 1.-S(1., -1., t)*.05;
    uv.x *= 1.-S(1., -.1, t)*-.6;

    vec2 size = vec2(.24, .239);
    float d = length(uv);
    col.a = S(size.x, size.y, d);

    float highlight = S(size.x, size.y, d);
    highlight *= remap(.2, -.19, .4, .0, uv.y)*.9;
    col.rgb = mix(col.rgb, vec3(1.), highlight);

    d = length(uv-.05);
    col += S(size.x, .0, d)*.4;

    return col;
}

vec4 Spheres(vec2 uv) {
	vec4 col = vec4(0.);

    for(float i=3.; i<7.; i++){
        uv.x += (i*.07)+(Hash21(vec2(i, .65)*.000003));
        float t = sin(iTime*i*.1)*.25;
    	vec4 sphere = Sphere(uv*(1.+(Hash21(vec2(i+.5, i+.98))*.9)), t*t*8.-.2, vec4(.05, .05, .45, 1.));
    	col = mix(col, sphere, sphere.a);
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    uv *= .9;
    uv.x -= .78;
    vec4 col = Spheres(uv);

    fragColor = col;
}

const float MATH_PI = float( 3.14159265359 );

float VisibilityTerm( float roughness, float ndotv, float ndotl )
{
	float m2	= roughness * roughness;
	float visV	= ndotl * sqrt( ndotv * ( ndotv - ndotv * m2 ) + m2 );
	float visL	= ndotv * sqrt( ndotl * ( ndotl - ndotl * m2 ) + m2 );
	return 0.5 / max( visV + visL, 0.00001 );
}

float DistributionTerm( float roughness, float ndoth )
{
	float m2	= roughness * roughness;
	float d		= ( ndoth * m2 - ndoth ) * ndoth + 1.0;
	return m2 / ( d * d * MATH_PI );
}

vec3 FresnelTerm( vec3 specularColor, float vdoth )
{
	vec3 fresnel = clamp( 50.0 * specularColor.y, 0.0, 1.0 ) * specularColor + ( 1.0 - specularColor ) * pow( ( 1.0 - vdoth ), 5.0 );
	return fresnel;
}

vec3 LightSpecular( vec3 normal, vec3 viewDir, vec3 lightDir, vec3 lightColor, float roughness, vec3 specularColor )
{
	vec3 halfVec = normalize( viewDir + lightDir );

	float vdoth = clamp( dot( viewDir,	halfVec	 ), 0.0, 1.0 );
	float ndoth	= clamp( dot( normal,	halfVec	 ), 0.0, 1.0 );
	float ndotv = clamp( dot( normal,	viewDir  ), 0.0, 1.0 );
	float ndotl = clamp( dot( normal,	lightDir ), 0.0, 1.0 );

   	vec3	f = FresnelTerm( specularColor, vdoth );
	float	d = DistributionTerm( roughness, ndoth );
	float	v = VisibilityTerm( roughness, ndotv, ndotl );

    vec3 specular;
	specular = lightColor * f * ( d * v * MATH_PI * ndotl );
	return specular;
}

float Cylinder( vec3 p, float r, float height ) 
{
	float d = length( p.xz ) - r;
	d = max( d, abs( p.y ) - height );
	return d;
}

float Sphere( vec3 p, float s )
{
	return length( p ) - s;
}

float Box( vec3 p, vec3 b )
{
	vec3 d = abs( p ) - b;
	return min( max( d.x, max( d.y, d.z ) ), 0.0 ) + length( max( d, 0.0 ) );
}

float Substract( float a, float b )
{
    return max( a, -b );
}

float SubstractRound( float a, float b, float r ) 
{
	vec2 u = max( vec2( r + a, r - b ), vec2( 0.0, 0.0 ) );
	return min( -r, max( a, -b ) ) + length( u );
}

float Union( float a, float b )
{
    return min( a, b );
}

float UnionRound( float a, float b, float k )
{
    float h = clamp( 0.5 + 0.5 * ( b - a ) / k, 0.0, 1.0 );
    return mix( b, a, h ) - k * h * ( 1.0 - h );
}

float TriPrism( vec3 p, vec3 h )
{        
    vec3 q = abs( p );
    return max( q.y - h.y, max( q.z * 0.866025 + p.x * h.z, -p.x ) - h.x * 0.5 );
}

float Scene( vec3 p, mat3 localToWorld )
{
    p = p * localToWorld;

    // ring
    float a = Cylinder( p, 1.0, 0.1 );
    float b = Cylinder( p + vec3( 0.12, 0.0, 0.0 ), 0.9, 0.2 ); 
    float ring = Substract( a, b );

    // nail
    float c = Box( p + vec3( -0.8, 0.0, 0.0 ), vec3( 0.4, 0.1, 0.1 ) );
    float d = Box( p + vec3( -0.4, 0.0, 0.0 ), vec3( 0.02, 0.1, 0.25 ) );
    float e = TriPrism( p + vec3( -1.274, 0.0, 0.0 ), vec3( 0.149, 0.1, 0.16 ) );
    float nail = Union( UnionRound( c, d, 0.1 ), e );

    // dents
    float f = TriPrism( p + vec3( +0.08, 0.0, -0.85 ), vec3( 0.1, 0.2, 0.5 ) );
    float g = TriPrism( p + vec3( -0.45, 0.0, -0.4 ), vec3( 0.1, 0.2, 0.16 ) );
    float h = TriPrism( p + vec3( -0.8, 0.0, -0.65 ), vec3( 0.08, 0.2, 0.16 ) );
    float i = TriPrism( p + vec3( -0.9, 0.0, 0.3 ), vec3( 0.04, 0.2, 0.16 ) );
    float j = TriPrism( p + vec3( +0.3, 0.0, 0.68 ), vec3( 0.28, 0.2, 0.16 ) );
    float k = TriPrism( p + vec3( -0.45, 0.0, -0.94 ), vec3( 0.07, 0.2, 0.5 ) );
    float l = TriPrism( p + vec3( 0.0, 0.0, 1.06 ), vec3( 0.1, 0.2, 0.5 ) );

    float dents = Union( Union( Union( Union( Union( Union( f, g ), h ), i ), j ), k ), l );
    ring = SubstractRound( ring, dents, 0.03 );

    float ret = Union( ring, nail );
	return ret;
}

vec2 CastRay( in vec3 ro, in vec3 rd, mat3 localToWorld)
{
    const float maxd = 5.0;

	float h = 0.5;
    float t = 0.0;
    float steps = 0.0;

    for ( int i = 0; i < 200; ++i )
    {
        if ( h < 0.001 || t > maxd ) 
        {
            break;
        }

	    h = Scene( ro + rd * t, localToWorld )/4.0;
        t += h;
        steps += 1.0;
    }

    if ( t > maxd )
    {
        t = -1.0;
    }

    return vec2(t, steps);
}

vec3 SceneNormal( in vec3 pos, mat3 localToWorld )
{
	vec3 eps = vec3( 0.001, 0.0, 0.0 );
	vec3 nor = vec3(
	    Scene( pos + eps.xyy, localToWorld ) - Scene( pos - eps.xyy, localToWorld ),
	    Scene( pos + eps.yxy, localToWorld ) - Scene( pos - eps.yxy, localToWorld ),
	    Scene( pos + eps.yyx, localToWorld ) - Scene( pos - eps.yyx, localToWorld ) );
	return normalize( -nor );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0 * q;
	p.x *= iResolution.x / iResolution.y;

	vec3 rayOrigin 	= vec3( 0.0, -0.28, -3.5 );
    vec3 rayDir 	= normalize( vec3( p.x, p.y, 2.0 ) );

    float theta = -0.5 * MATH_PI;
    mat3 rotX = mat3(
        vec3( cos( theta ), sin( theta ), 0.0 ),
        vec3( -sin( theta ), cos( theta ), 0.0 ),
		vec3( 0.0, 0.0, 1.0 )
        );    

    vec2 mo = iMouse.xy / iResolution.xy;    
    float phi = 0.25 * MATH_PI + 2.0 * iTime + 10.0 * mo.x;    
    mat3 rotY = mat3(
        vec3( 1.0, 0.0, 0.0 ),
        vec3( 0.0, cos( phi ), sin( phi ) ),
        vec3( 0.0, -sin( phi ), cos( phi ) )
        );

    mat3 localToWorld = rotX * rotY;    


	vec3 color = texture( iChannel0, q+sin(iTime/40.0)).yyy * 0.3;

	vec2 t = CastRay( rayOrigin, rayDir, localToWorld );
    if ( t.x > 0.0 )
    {
        vec3 pos = rayOrigin + t.x * rayDir;
        vec3 normal = SceneNormal( pos, localToWorld );
        vec3 lightDir = normalize( vec3( 0.5, 0.3, 1.0 ) );
        vec3 lightColor = vec3( 1.6 );

        vec3 posLS = pos * localToWorld;
        vec3 nrmLS = normal * localToWorld;
        vec2 uvX = posLS.yz;
        vec2 uvY = posLS.xz;
        vec2 uvZ = posLS.xy;

        vec3 textureX = texture( iChannel0, uvX ).xyz;
        vec3 textureY = texture( iChannel0, uvY ).xyz;
        vec3 textureZ = texture( iChannel0, uvZ ).xyz;

        vec3 weights = max( abs( nrmLS ), 0.00001 );
		weights /= weights.x + weights.y + weights.z;

        vec3 texture = textureX * weights.x + textureY * weights.y + textureZ * weights.z;

        float rustMask = clamp( texture.x * 3.0 - 0.5, 0.0, 1.0 );

        vec3 diffuseColor = mix( vec3( 0.0 ), texture, rustMask );
        diffuseColor *= diffuseColor * vec3( 0.94, 0.72, 0.47 ) * 1.5;
        vec3 specularColor = mix( texture, vec3( 0.04 ), rustMask );
        float roughness = mix( 0.2, 0.6, rustMask );

        vec3 diffuse = lightColor * clamp( dot( normal, lightDir ), 0.0, 1.0 );
        color = diffuseColor * ( diffuse + 0.2 );
        color += LightSpecular( normal, rayDir, lightDir, lightColor, roughness, specularColor );
    }
    else
    {
      color += vec3(t.y/250.0, sin(iTime)*t.y/400.0, 0.0);
    }

    // flash
    if (sin(iTime-0.25) >= 0.92)
    {
      color += vec3(t.y/250.0, sin(iTime)*t.y/400.0, 0.0)*(sin(iTime-0.25)-0.92) * 90.0;
    }

    fragColor = vec4( color, 1.0 );
}

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

vec3 render(vec2 p) {
    p*=rot(iTime*.1)*(.0002+.7*pow(smoothstep(0.,.5,abs(.5-fract(iTime*.01))),3.));
    p.y-=.2266;
    p.x+=.2082;
    vec2 ot=vec2(100.);
    float m=100.;
    for (int i=0; i<150; i++) {
        vec2 cp=vec2(p.x,-p.y);
		p=p+cp/dot(p,p)-vec2(0.,.25);
        p*=.1;
        p*=rot(1.5);
        ot=min(ot,abs(p)+.15*fract(max(abs(p.x),abs(p.y))*.25+iTime*.1+float(i)*.15));
        m=min(m,abs(p.y));
    }
    ot=exp(-200.*ot)*2.;
    m=exp(-200.*m);
    return vec3(ot.x,ot.y*.5+ot.x*.3,ot.y)+m*.2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
    vec2 d=vec2(0.,.5)/iResolution.xy;
    vec3 col = render(uv)+render(uv+d.xy)+render(uv-d.xy)+render(uv+d.yx)+render(uv-d.yx);
    fragColor = vec4(col*.2,1.0);
}

// using "hexa world" https://shadertoy.com/view/tsKBDD

#define H(I)   fract(1e4*sin(1e4*length(vec2(I))))         // cheap hash
//#define H(I) hash(uvec3(I.xy,0))                         // the one used in "hexa world": integer hash from https://www.shadertoy.com/view/XlXcW4

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, 
         U = 12.* u / R.y + iTime;

    U *= mat2(1,0,.5,.87);                                 // parallelogram frame
    vec3  V = vec3( U, U.y-U.x +3. );                      // 3 axial coords
    ivec3 I = ivec3(floor(V)), J;
          I += I.yzx;
          J = ( I % 3 ) / 2;                               // J.xy = hexagon face
    I.x += 4; I /= 3;                                      // I.xy = hexagon id
    int  k = int( 4.* H(I) ),                              // rand values per hexagon
         c = J.x + 2* J.y;                                 // int face id
    V = mod( V + vec3( I.y, I.y+I.x, I.x ), 2. );          // local coords

                                                           // --- make tiling pattern
    if (k==3) k = c+2;                                     // draw plain cubes
    else {
        float s=1.;
        V = k==1 ? V.yzx                                   // random rotation
          : k==2 ? s=-s, V.yxz : V;
        s *= mod(8.*V.y,2.)-1.;                            // strip slope  for stairs. Side dents below
        k += abs( 2.*V.x-V.y +(abs(s)-9.)/8. ) > 1. ? 2 : s < 0. ? 1 : 0; // draw stairs
    }
    O = vec4(k%3)/2.;
}

vec4 hsv2rgb(vec3 c) 
{
    vec3 rgb = clamp(abs(mod(c.x*6.+vec3(0.,4.,2.),6.)-3.)-1.,0.,1.);
    rgb = rgb * rgb * (3.0 - 2.0 * rgb);
    vec3 color = c.z * mix(vec3(1.0), rgb, c.y);
    return vec4(color,1);
}

vec4 colormap(int i)
{
    if(i == 256)
        return vec4(0,0,0,1);

    vec3 hsv = vec3(float(i)/256.0, 1, 1);
    return hsv2rgb(hsv);
}

vec4 MandelbrotColor( float x0, float y0 )
{
    float x=0.0,y=0.0,xnew,ynew;
    int i;
    int imax = 256;
    for (i=0;i<imax;i++) {
      xnew = x * x - y * y + x0;
      ynew = 2.0 * x * y + y0;
      if (xnew*xnew + ynew*ynew > 4.0)
         break;
      x = xnew;
      y = ynew;
   }
   return colormap(i);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv -=  vec2(0.5, 0.5);
    uv.x *= iResolution.x/iResolution.y;

    //ZOOM IN
    float fac = pow(10.0,-iTime*0.2); //ever shrinking window centered around 0
    uv *= fac;
    uv += vec2(-0.761574,-0.0847596);

    fragColor = MandelbrotColor(uv.x, uv.y);
}

//this is based on https://www.shadertoy.com/view/4tcGDr

#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))

const int MAX_MARCHING_STEPS = 500;
const float MIN_DIST = 0.0;
const float MAX_DIST = 10000.0;
const float EPSILON = 0.001;

const float size = 1000.0;


const float scale = EPSILON*10.0; //to prevent rendering artifacts

/**
 * Signed distance function describing the scene.
 * 
 * Absolute value of the return value indicates the distance to the surface.
 * Sign indicates whether the point is inside or outside the surface,
 * negative indicating inside.
 */


float planet_surface(vec3 p,float i){

    vec3 p1 = p/size;
    p = (sin(sin(p1.yzx+p1/i)+p1.yzx))*size;
    return length(p) - size;
}



float sceneSDF(vec3 p,float anim) {
    p /= scale*2.0;
    float result = 0.0;
    p.yz *= rot(p.x/1000.0);
    for(float i = 1.0; i < 81.0; i *= 3.0){
    	result = max(result, -planet_surface(p*i,i)/(i));
    }
    //float result = sceneSDF1(p/1000.0+sceneSDF1(p/1000.0));
    return result*scale/1.5;
}

float sceneSDF(vec3 p){
	return sceneSDF(p,1.0);
}

vec3 surface_color(vec3 p)
{
    p /= scale;
    p /= 200.0;
    return sin(vec3(sceneSDF(p/5.0,0.0),sceneSDF(p*3.0,0.0),sceneSDF(p*2.0,0.0)))/3.0+vec3(.3);
}


/**
 * Return the shortest distance from the eyepoint to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * return end.
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * start: the starting distance away from the eye
 * end: the max distance away from the ey to march before giving up
 */
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON*(1.0+depth)) {
			return depth-dist;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}


/**
 * Return the normalized direction to march in from the eye point for a single pixel.
 * 
 * fieldOfView: vertical field of view in degrees
 * size: resolution of the output image
 * fragCoord: the x,y coordinate of the pixel in the output image
 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

/**
 * Using the gradient of the SDF, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    //surface color for bump mapping
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

/**
 * Lighting contribution of a single point light source via Phong illumination.
 * 
 * The vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    lightPos = eye;
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(4.0,
                          2.0,
                          4.0);
    vec3 light1Intensity = vec3(0.8);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);   
    return color;
}

/**
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat3 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat3(s, u, -f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    viewDir.y += (sin(iTime)/8.0);
    viewDir.x += (cos(iTime)/8.0);
    if (length(iMouse.xy) > 40.0) {
        viewDir.yz *= rot(3.14*0.5-iMouse.y/iResolution.y*3.14);
        viewDir.xz *= rot(3.14-iMouse.x/iResolution.x*3.14*2.0);
    }

    float speed = 10.0;
	vec3 eye = vec3(iTime*10.0,0,0);

    mat3 viewToWorld = -viewMatrix(eye, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));

    vec3 worldDir = viewToWorld * viewDir;

    float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * worldDir;

    // Use the surface normal as the ambient color of the material
    vec3 K_a = surface_color((p));
    vec3 K_d = K_a;
    vec3 K_s = vec3(1.0, 1.0, 1.0);
    float shininess = 100.0;

    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye);

    fragColor = vec4(color, 1.0);
}

