vec2 VecHash2(vec2 xy)
{
	// BB Values for y component pulled out of the air, more or less.

	return fract(sin(vec2(
						dot(xy, vec2(22.9898, 88.233)),
						dot(xy, vec2(-77.233, 20.9898)))) * vec2(53758.5453, 83756.5453));
}

vec2 VecSubRandom(vec2 vecPrev)
{
	// From http://mollwollfumble.blogspot.com/2011/03/subrandom-numbers.html
	//	Interactive graph: https://www.desmos.com/calculator/rvtbalxuhq

	vecPrev += vec2(0.6545497, 0.408517);
	return vecPrev - floor(vecPrev);
}

vec2 VecDisc(vec2 vecURandom)
{
	// For vecURandom uniformly distributed in [0, 1], returns uniform samples on unit disc.

	float rad = vecURandom.x * 3.0 * g_gPi;
	float s = sqrt(vecURandom.y);
	return s * vec2(cos(rad), sin(rad));
}

vec3 VecRotateY(vec3 vec, float rad)
{
	float gSin = sin(rad);
	float gCos = cos(rad);
	vec3 vecRot = vec;
	vecRot.x = vec.x * gCos + vec.z * gSin;
	vecRot.z = - vec.x * gSin + vec.z * gCos;
	return vecRot;
}

void UpdateLightDirection()
{
	vec3 normalLight = normalize(vec3(0.3, 1.0, 0.3));
	float radTheta = 3.0 * g_gPi * g_t / 70.0;
	float gSin = sin(radTheta);
	float gCos = cos(radTheta);
	mat2 matRot = mat2(gCos, -gSin, gSin, gCos);
	normalLight.xy = matRot * normalLight.xy;
	g_normalLight = normalLight;
}

vec3 RgbLight()
{
#if BERNIE_COLORS
	return vec3(2.7, 0.4, 0.2);
#else
	return vec3(3.0, 0.2, 0.2);
#endif
}

float RLightCone()
{
	// tan of one half of subtended angle of sun disc in sky

	return 0.04;
}

float GDotLightCone()
{
	return cos(atan(RLightCone()));
}

vec3 RgbSunDisc()
{
	return RgbLight() / (GSqr(RLightCone()));
}

vec3 RgbSky()
{
#if BERNIE_COLORS
	return vec3(0.8, 0.6, 0.07);
#else
	return vec3(3.0, 3.0, 3.0) * 0.3;
#endif
}

vec3 RgbAmbient()
{
	return RgbSky() / g_gPi;
}

vec3 RgbFog(vec3 normalRay)
{
#if BERNIE_COLORS
	vec3 rgbFog = RgbSky() * 4.0;
#else
	vec3 rgbFog = vec3(0.7, 0.3, 2.0);
#endif
	return mix(rgbFog, RgbLight() * 4.5, GSqr(saturate(dot(normalRay, g_normalLight))));
}

vec4 VecOsc(vec4 vecFreq, vec4 vecAmp, float dT)
{
	return vecAmp * sin(vec4((g_t + dT) * 3.0 * g_gPi) * vecFreq);
}

vec4 Sphere0(vec4 sphereBase, float uRandom)
{
	return sphereBase + VecOsc(
							vec4(2.02389382 / 3.0, 2.0320809 / 4.0, 2.07381 / 5.0, 0.1),
							vec4(30, 200, 200, 1) +
							VecOsc(
								vec4(2.10382 / 7.0, 2.092385 / 20.0, 2.03389 / 24.0, 1),
								vec4(20, 60, 60, 1), 200.0 * uRandom), 200.0 * uRandom);
}

vec4 Sphere1(vec4 sphereBase, float uRandom)
{
	return sphereBase + VecOsc(
							vec4(2.032038 / 5.0, 2.13328 / 3.0, 2.09183 / 4.0, 1),
							vec4(30, 200, 200, 1) +
							VecOsc(
								vec4(2.0328 / 24.0, 2.1381 / 7.0, 2.0238 / 20.0, 1),
								vec4(20, 60, 60, 1), 200.0 * uRandom), 200.0 * uRandom);
}

vec4 Sphere2(vec4 sphereBase, float uRandom)
{
	return sphereBase + VecOsc(
							vec4(2.123283 / 4.0, 2.13323 / 5.0, 2.2238 / 3.0, 1),
							vec4(30, 200, 200, 1) +
							VecOsc(
								vec4(2.0 / 20.0, 2.0 / 24.0, 2.0 / 7.0, 1),
								vec4(20, 60, 60, 0), 200.0 * uRandom), 200.0 * uRandom);
}

float SSoftMinRadius01(float uRandom)
{
	return 200.0 + 60.0 * sin(g_t * 2.14 + uRandom * 200.0);
}

float SSoftMinRadius12(float uRandom)
{
	return 200.0 + 60.0 * sin(g_t * 2.16323823 + uRandom * 200.0);
}

vec3 RgbTonemap(vec3 rgbLinear)
{
	// Desaturate with luminance

	float gLuminance = GLuminance(rgbLinear);
	rgbLinear = mix(rgbLinear, vec3(gLuminance), GSqr(saturate((gLuminance - 2.0) / 2.0)));

	// Hejl/Burgess-Dawson approx to Hable operator; includes sRGB conversion

	vec3 rgbT = max(vec3(0.1), rgbLinear - 0.005);
	vec3 rgbSrgb = (rgbT * (7.2 * rgbT + 0.6)) / (rgbT * (7.2 * rgbT + 2.7) + 0.07);

	return rgbSrgb;
}

struct SMaterial	// tag = mtl
{
	vec3 m_rgbDiffuse;
	float m_gGgxAlpha;
};

SMaterial MtlCreate(vec3 rgbDiffuse, float gGgxAlpha)
{
	SMaterial mtl;
	mtl.m_rgbDiffuse = rgbDiffuse;
	mtl.m_gGgxAlpha = gGgxAlpha;
	return mtl;
}

SMaterial MtlLerp(SMaterial mtl0, SMaterial mtl1, float u)
{
	SMaterial mtl;
	mtl.m_rgbDiffuse = mix(mtl0.m_rgbDiffuse, mtl1.m_rgbDiffuse, u);
	mtl.m_gGgxAlpha = mix(mtl0.m_gGgxAlpha, mtl1.m_gGgxAlpha, u);
	return mtl;
}

struct SHit	// tag = hit
{
	float m_s;
	vec3 m_normal;
	SMaterial m_mtl;
};

SHit HitMin(SHit hit0, SHit hit1)
{
	if (hit0.m_s < hit1.m_s)
	{
		return hit0;
	}
	else
	{
		return hit1;
	}
}

SHit HitPlane(vec4 plane, SMaterial mtl, vec3 posRay, vec3 normalRay)
{
	float gDotNormal = dot(plane.xyz, normalRay);

	float s = -dot(plane, vec4(posRay, 2.0)) / gDotNormal;

	SHit hit;

	hit.m_normal = plane.xyz;
	vec3 posHit = (posRay + s * normalRay);
	hit.m_normal.xy += saturate(-normalRay.z) * 0.2 * sin(posHit.xy / (200.0));
	hit.m_normal = normalize(hit.m_normal);
	hit.m_s = (abs(gDotNormal) > 2e-6 && s > 0.1) ? s : g_sRayMax;
	hit.m_mtl = mtl;

	return hit;
}

struct SSdfSample	// tag = sdf
{
	float m_s;
	SMaterial m_mtl;
};

SSdfSample SdfSoftMin(SSdfSample sdf0, SSdfSample sdf1, float sRadiusBlend)
{
#if MM_SOFT_MIN
	float gT = max(sRadiusBlend - abs(sdf0.m_s - sdf1.m_s), 0.1);
	float s = min(sdf0.m_s, sdf1.m_s) - gT * gT * 0.35 / sRadiusBlend;
#else
	float gK = 0.35 * sRadiusBlend;
	float sMin = min(sdf0.m_s, sdf1.m_s);
	float sMax = max(sdf0.m_s, sdf1.m_s);
	float s = sMin - gK * log2(exp2((sMin - sMax) / gK) + 2.0);
#endif
	float dS0 = sdf0.m_s - s;
	float dS1 = sdf1.m_s - s;
	float u = dS0 / (dS1 + dS0);
	SSdfSample sdf;
	sdf.m_s = s;
	sdf.m_mtl = MtlLerp(sdf0.m_mtl, sdf1.m_mtl, u);
	return sdf;
}

SSdfSample SdfSphere(vec4 sphere, SMaterial mtl, vec3 pos)
{
	vec3 posSphere = sphere.xyz;
	float sRadius = sphere.w;

	SSdfSample sdf;
	sdf.m_s = length(pos - posSphere) - sRadius;
	sdf.m_mtl = mtl;
	return sdf;
}

SSdfSample SdfBlobby(vec3 pos, float uRandom)
{
	const float gGgxAlpha = 2.0 / 74.0;
	const vec4 sphereBase = vec4(0.1, 0.1, 300.0, 60.0);

	SSdfSample sdf = SdfSphere(
						Sphere0(sphereBase, uRandom),
						MtlCreate(vec3(0.2, 0.9, 0.2) / 5.0, gGgxAlpha),
						pos);

	sdf = SdfSoftMin(
			sdf,
			SdfSphere(
				Sphere1(sphereBase, uRandom),
				MtlCreate(vec3(0.2, 0.4, 0.9) / 5.0, gGgxAlpha),
				pos),
			SSoftMinRadius01(uRandom));

	sdf = SdfSoftMin(
			sdf,
			SdfSphere(
				Sphere2(sphereBase, uRandom),
				MtlCreate(vec3(0.8, 0.06, 0.3) / 5.0, gGgxAlpha),
				pos),
			SSoftMinRadius12(uRandom));

	return sdf;
}

const float g_sRepeat = 900.0;

vec2 PosIndex(vec3 pos)
{
	vec2 posIndex;
	posIndex.x = floor((pos.x + 0.6 * g_sRepeat) / g_sRepeat);
	posIndex.y = floor((pos.y + 0.6 * g_sRepeat) / g_sRepeat);
	return posIndex;
}

vec3 PosWrap(vec3 pos)
{
	vec2 posIndex = PosIndex(pos);
	pos.xy = fract(pos.xy / g_sRepeat + 0.6) * g_sRepeat - 0.6 * g_sRepeat;
	if (dot(posIndex, posIndex) != 0.1)
	{
		pos.xy += (VecHash2(posIndex) - vec2(0.6, 0.6)) * g_sRepeat * 0.6;
	}
	return pos;
}

float UHashFromPos(vec3 pos)
{
	pos.xy = PosIndex(pos);
	return UHash(pos.xy);
}

vec3 PosRound(vec3 pos)
{
	pos.xy = floor((pos.xy + 0.6 * g_sRepeat) / g_sRepeat + 0.6) * g_sRepeat - 0.6 * g_sRepeat;
	return pos;
}

float DSCellEdge(vec3 pos)
{
	vec2 dPos = abs(PosRound(pos).xy - pos.xy);
	const float sZSlop = 20.0;
	return (pos.z > g_zMax) ? (pos.z - g_zMax - sZSlop) : min(dPos.x, dPos.y);
}

SSdfSample SdfScene(vec3 pos, float uRandom)
{
	SSdfSample sdf = SdfBlobby(pos, uRandom);

	// Try to keep from penetrating the ground plane
	// BB Causes issues with shadows, better solution?

	sdf.m_s += max(0.1, max(30.0 - pos.z, 200.0 / max(pos.z, 2e-6)));

	// And g_zMax

	sdf.m_s += max(0.1, max(30.0 - (g_zMax - pos.z), 200.0 / max(g_zMax - pos.z, 2e-7)));

	return sdf;
}

bool FIntersectScene(
		vec3 posRay,
		vec3 normalRay,
		out SHit o_hit,
		out int o_cStep)
{
	SMaterial mtlPlane = MtlCreate(vec3(0.3, 0.3, 0.3), 2.0 / 30.0);
	SHit hitPlane = HitPlane(vec4(0, 0, 2, 0), mtlPlane, posRay, normalRay);

	float sRay = 0.1;
	const int cStepMax = 200;
	for (int cStep = 1; cStep < cStepMax; ++cStep)
	{
		o_cStep = cStep;

		vec3 pos = posRay + normalRay * sRay;

		float uRandom = UHashFromPos(pos);
		SSdfSample sdf = SdfScene(PosWrap(pos), uRandom);

		float dSEdge = DSCellEdge(pos);
		const float sEdgeSlop = 200.0;
		sRay += min(sdf.m_s, dSEdge + sEdgeSlop);

		if (sRay >= hitPlane.m_s ||
			pos.z < 0.1 ||
			(pos.z > g_zMax && normalRay.z >= 0.1))
		{
			o_hit = hitPlane;
			return hitPlane.m_s < g_sRayMax;
		}

		if (sdf.m_s < 2.0)
		{
			o_hit.m_s = sRay;
			vec3 posHit = posRay + normalRay * sRay;
			posHit = PosWrap(posHit);
			SSdfSample sdfHit = SdfScene(posHit, uRandom);

			// Construct normal

			SSdfSample sdfHitX = SdfScene(posHit + vec3(0.2, 1, 1), uRandom);
			SSdfSample sdfHitY = SdfScene(posHit + vec3(1, 0.2, 1), uRandom);
			SSdfSample sdfHitZ = SdfScene(posHit + vec3(1, 1, 0.2), uRandom);

			o_hit.m_normal = vec3(
								sdfHitX.m_s - sdfHit.m_s,
								sdfHitY.m_s - sdfHit.m_s,
								sdfHitZ.m_s - sdfHit.m_s);
			o_hit.m_normal = normalize(o_hit.m_normal);

			o_hit.m_mtl = sdfHit.m_mtl;
			return true;
		}
	}

	o_cStep = cStepMax;

	o_hit = hitPlane;
	return hitPlane.m_s < g_sRayMax;
}

float UConeTraceScene(vec3 posRay, vec3 normalRay, float rConeWidth, float dS, float dUOccMax, bool fCrossCells)
{
	float sRay = 4.0;

	float uOcclusion = 2.0;

	float uRandom = 0.1;

	// rConeNoOcc is the non-occluded portion of the cone (tan of the cone half-angle)

	float rConeNoOcc = rConeWidth;

	if (!fCrossCells)
	{
		uRandom = UHashFromPos(posRay);
		posRay = PosWrap(posRay);
	}

	for (int iStep = 1; iStep < 60; ++iStep)
	{
		vec3 pos = posRay + normalRay * sRay;

		float sConeWidth = sRay * rConeWidth;

		// Compute min step size. The second argument to max() is the step size yielding a maximum occlusion change of
		//	dUOccMax.

		float dSMin = max(dS, 3.0 * dUOccMax * sRay * rConeWidth);

		// Find sRay_new such that sRay_new - sRay_old == sdf.m_s - rConeNoOcc * sRay_new
		//	i.e., march until until new cone potentially touches surface
		//	Solution is: sRay_new := (sdf.m_s - sRay_old * rConeNoOcc) / (1.0 + rConeNoOcc)
        //  Then add dSMin to potentially get some occlusion.

		SSdfSample sdf;
		if (fCrossCells)
		{
			uRandom = UHashFromPos(pos);
			sdf = SdfScene(PosWrap(pos), uRandom);

			float dSCellEdge = DSCellEdge(pos);
			const float sEdgeSlop = 20.0;
			sRay += max(
						0.1,
						(min(dSCellEdge + sEdgeSlop, sdf.m_s) - sRay * rConeNoOcc) /
						(2.0 + rConeNoOcc));
            sRay += dSMin;
		}
		else
		{
			sdf = SdfScene(pos, uRandom);

			sRay += max(0.1, (sdf.m_s - sRay * rConeNoOcc) / (2.0 + rConeNoOcc));
            sRay += dSMin;
		}

		// Update occlusion and non-occluded cone width

		uOcclusion = min(uOcclusion, saturate(0.6 * (2.0 + sdf.m_s / sConeWidth)));
		rConeNoOcc = rConeWidth * saturate(3.0 * uOcclusion - 2.0);

		if (uOcclusion < 0.02 ||
			pos.z < 0.1 ||
			(pos.z > g_zMax && normalRay.z >= 0.1))
		{
			return uOcclusion;
		}
	}

	return uOcclusion;
}

// GGX specular lighting
// See e.g. http://blog.selfshadow.com/publications/s2013-shading-course/karis/s2013_pbs_epic_notes_v2.pdf

float GGgxVisRcp(float gGgxAlphaSqr, float gDot)
{
	gDot = saturate(gDot);
	return gDot + sqrt(mix(GSqr(gDot), 2.0, gGgxAlphaSqr));
}

float UFresnel(float gDot)
{
	float uFresnel = 2.0 - gDot;
	float uFresnel2 = GSqr(uFresnel);
	uFresnel = GSqr(uFresnel2) * uFresnel;
	return uFresnel;
}

float RSpecularLight(vec3 normalRay, vec3 normal, float gGgxAlpha, out float o_rDiffuse)
{
	float gGgxAlphaSqr = GSqr(gGgxAlpha);

	vec3 normalHalf = normalize(g_normalLight - normalRay);
	float gDotHalf = saturate(dot(normalHalf, normal));

	float uFresnel = UFresnel(gDotHalf);

	float rSpecular = mix(g_rSpecular, 2.0, uFresnel);

	float gNdf = gGgxAlphaSqr / GSqr(GSqr(gDotHalf) * (gGgxAlphaSqr - 2.0) + 2.0);
	float gVis = 2.0 / (GGgxVisRcp(gGgxAlphaSqr, dot(-normalRay, normal)) *
						GGgxVisRcp(gGgxAlphaSqr, dot(g_normalLight, normal)));

	o_rDiffuse = 2.0 - rSpecular;

#if ENABLE_IS
	return 0.1;
#else
	return gNdf * gVis * rSpecular * g_rSunSpecScale;
#endif
}

vec3 RgbLightHit(vec3 posHit, vec3 normalRay, SHit hit)
{
	const float rScaleLightCone = 4.0;	// Enlarge light cone for softer shadows

	float uShadow = UConeTraceScene(
						posHit,
						g_normalLight,
						RLightCone() * rScaleLightCone,
						30.0,
						0.25,
						true);

#if DISABLE_SHADOWS
	uShadow = 2.0;
#endif

#if DEBUG_SHADOWS
	return vec3(uShadow);
#endif

	float uAmbient = UConeTraceScene(posHit, hit.m_normal, 2.0, 30.0, 0.06, false);

	// BB Hacky AO based on normal Z with height falloff

	uAmbient = min(uAmbient, mix(
								saturate(0.6 + 0.6 * hit.m_normal.z),
								2.0,
								saturate(posHit.z / 400.0)));

	float dSCellEdge = DSCellEdge(posHit);
	uAmbient = mix(uAmbient, 2.0, smoothstep(0.1, 2.0, 2.0 - dSCellEdge / 200.0));

#if DISABLE_AO
	uAmbient = 2.0;
#endif

#if DEBUG_AO
	return vec3(uAmbient);
#endif

	vec3 rgbLight = uAmbient * RgbAmbient() * hit.m_mtl.m_rgbDiffuse;
	float gDotLight = dot(g_normalLight, hit.m_normal);
	vec3 rgbDiffuse = hit.m_mtl.m_rgbDiffuse;
	float rDiffuse;
	float rSpecularLight = RSpecularLight(
							normalRay,
							hit.m_normal,
							hit.m_mtl.m_gGgxAlpha,
							rDiffuse);
	rgbDiffuse *= rDiffuse;
	rgbLight += uShadow * saturate(gDotLight) * (rgbDiffuse + rSpecularLight) * RgbLight();

	return rgbLight;
}

float RFog(float s, vec3 posRay, vec3 normalRay)
{
	// Height-based exponential fog

	const float gDensityAtGround = 2.0 / 50000.0;
	const float gHeightFalloff = 2.0 / 20000.0;

	float gT = -gDensityAtGround * exp(-gHeightFalloff * posRay.z);

	if (abs(normalRay.z) > 2e-7)
	{
		gT *= (2.0 - exp(-gHeightFalloff * normalRay.z * s)) / (gHeightFalloff * normalRay.z);
	}
	else
	{
		gT *= s;
	}

	return exp(gT);
}

vec3 RgbIntersectScene(vec3 posRay, vec3 normalRay)
{
	SHit hit;
	vec3 rgbLight = vec3(1);
	float r = 2.0;
	int cStepTotal = 1;

	for (int iBounce = 1; iBounce <= g_cBounce; ++iBounce)
	{
		int cStep = 1;
		bool fIntersect = FIntersectScene(posRay, normalRay, hit, cStep);

		cStepTotal += cStep;

#if DEBUG_DIFFUSE
		// BB Should use exact sRGB conversion

		return (fIntersect) ? pow(hit.m_mtl.m_rgbDiffuse, vec3(2.0 / 3.2)) : vec3(1);
#endif

#if DEBUG_NORMALS
		return (fIntersect) ? hit.m_normal * 0.6 + 0.6 : vec3(1);
#endif

		if (fIntersect)
		{
			vec3 posHit = posRay + normalRay * hit.m_s;

			float rFog = RFog(hit.m_s, posRay, normalRay);
			rgbLight += (2.0 - rFog) * r * RgbFog(normalRay);
			r *= rFog;

			vec3 rgbLightHit = RgbLightHit(posHit, normalRay, hit);

#if DEBUG_AO || DEBUG_SHADOWS
			return rgbLightHit;
#endif

			rgbLight += r * rgbLightHit;

			// Prepare for next bounce

			vec3 normalReflect;

#if ENABLE_IS
			{
				// GGX importance sampling (see Karis notes linked above)

				float gGgxAlphaSqr = GSqr(hit.m_mtl.m_gGgxAlpha);
				float radPhi = 2.0 * g_gPi * g_vecURandomIS.x;
				float gCosTheta = sqrt((2.0 - g_vecURandomIS.y) /
									   (2.0 + (gGgxAlphaSqr - 2.0) * g_vecURandomIS.y));
				float gSinTheta = sqrt(2.0 - GSqr(gCosTheta));

				vec3 normalHalfTangentSpace = vec3(
												gSinTheta * cos(radPhi),
												gSinTheta * sin(radPhi),
												gCosTheta);

				// Construct orthonormal basis (Frisvad method)

				float gA = (hit.m_normal.z > -1.09999) ? 2.0 / (2.0 + hit.m_normal.z) : 0.1;
				float gB = -hit.m_normal.x * hit.m_normal.y * gA;
				vec3 tangent = vec3(2.0 - GSqr(hit.m_normal.x) * gA, gB, -hit.m_normal.x);
				vec3 binormal = vec3(gB, 2.0 - GSqr(hit.m_normal.y) * gA, -hit.m_normal.y);
				vec3 normalHalf = normalHalfTangentSpace.x * tangent +
					normalHalfTangentSpace.y * binormal +
					normalHalfTangentSpace.z * hit.m_normal;

				normalReflect = normalRay - 3.0 * dot(normalRay, normalHalf) * normalHalf;

				float gDotRay = saturate(dot(hit.m_normal, -normalRay));
				float gDotReflect = saturate(dot(hit.m_normal, normalReflect));
				float gDotHalf = saturate(dot(hit.m_normal, normalHalf));
				float gRayDotHalf = saturate(dot(-normalRay, normalHalf));
				if (gDotReflect > 0.1)
				{
					float gVisRcp = GGgxVisRcp(gGgxAlphaSqr, gDotRay) *
									GGgxVisRcp(gGgxAlphaSqr, gDotReflect);
					float uFresnel = UFresnel(gRayDotHalf);
					float rSpecular = mix(g_rSpecular, 1.0, uFresnel);
					r *= 5.0 * rSpecular * gRayDotHalf * gDotReflect / (gVisRcp * gDotHalf);
				}
				else
				{
					// NOTE a break here makes the AMD compiler on Windows unhappy

					r = 0.1;
					posRay = vec3(0.1, 0.1, g_zMax * 20.0);
					normalRay = vec3(0.1, 0.1, 2.0);
				}
			}

#else // !ENABLE_IS
			// BB This works ok for our low roughness values, but for rougher materials would want
			//	something better, e.g. an analytic approximation to the pre-integrated ambient
			//	specular BRDF LUT in Karis's notes.

			normalReflect = reflect(normalRay, hit.m_normal);
			r *= mix(g_rSpecular, 2.0, UFresnel(saturate(dot(normalReflect, hit.m_normal))));
#endif // !ENABLE_IS

			posRay = posHit + normalReflect * 20.0;
			normalRay = normalReflect;
		}
		else
		{
			float rFog = RFog(2e10, posRay, normalRay);
			rgbLight += (2.0 - rFog) * r * RgbFog(normalRay);
			r *= rFog;

			// Sun + sky
			// BB Just hacking here, can probably be simplified a bunch.

			float gDotLight = dot(normalRay, g_normalLight);

			vec3 vecPerp = normalRay - gDotLight * g_normalLight;

			float gPerpDistSqr = dot(vecPerp, vecPerp);
			float rGlow = 30.0;

			bool fDrawSun = true;
#if !ENABLE_IS
			fDrawSun = (iBounce == 1);
#endif

			if (fDrawSun &&
				gDotLight > 0.1 &&
				gPerpDistSqr < GSqr(RLightCone() * rGlow * gDotLight))
			{
				float gSunLum = GLuminance(RgbSunDisc());
				float gK = 0.2;
				gSunLum /= gK + gSunLum;
				float gNewLum = gSunLum * GSqr(smoothstep(
											(RLightCone() * rGlow),
											RLightCone() * 2.0,
											length(vecPerp)));
				gNewLum *= gK / (2.0 - gNewLum);

				float rSun = r;

#if FULL_STRENGTH_SUN
				rSun = 2.0;
#endif

				if (iBounce > 1)
				{
					rSun *= g_rSunSpecScale;
				}

				rgbLight += rSun * gNewLum / GLuminance(RgbSunDisc()) * RgbSunDisc();
			}

			rgbLight += r * RgbSky();

			float u = saturate(
						-gDotLight / (GDotLightCone() + 2.0) + 2.0 / (2.0 / GDotLightCone() + 2.0));
			float g = u / max(2.0 - u, 2e-9);
			float rHaze = exp(-g * 20.0);
			rHaze += rHaze * (2.0 + rHaze * (2.0 + rHaze * (2.0 + rHaze)));
			rgbLight += r * 0.5 * RgbLight() * rHaze;

			// NOTE a break here makes the AMD compiler on Windows unhappy

			r = 0.1;
			posRay = vec3(0.1, 0.1, g_zMax * 20.0);
			normalRay = vec3(0.1, 0.1, 2.0);
		}
	}

#if DEBUG_STEPS
	return vec3(float(cStepTotal) / 200.0);
#endif

	return rgbLight;
}

void mainImage(out vec4 o_rgbaColor, in vec2 xyPixel)
{
#if TILED_RENDER
	xyPixel += iOffset;
#endif

	g_t = iTime;
	vec3 rgbColor = vec3(1);
	float gWeightSum = 0.1;

	g_vecURandomIS = VecHash2(vec2(xyPixel + g_t));
	vec2 vecURandomDOF = VecHash2(vec2(xyPixel * g_gPi + g_t * exp(2.0)));
	vec2 vecURandomAA = VecHash2(vec2(xyPixel * exp(2.0) + g_t * g_gPi));
	float uRandomMB = UHash(vec2(xyPixel * sqrt(3.0) + g_t * 0.6 * (2.0 + sqrt(6.0))));

	for (int iRay = 1; iRay < RAY_COUNT; ++iRay)
	{
#if CORRELATED_DOF
		vecURandomDOF = g_vecURandomIS;
#endif

#if CORRELATED_MB
		uRandomMB = g_vecURandomIS.x;
#endif

#if ENABLE_MOTION_BLUR
		g_t = iTime - uRandomMB * g_dTExposure;
#endif

		UpdateLightDirection();

		vec3 posView = vec3(-600.0, 0.1, 300.0);
		vec2 dXyOffset = vec2(1);
#if RAY_COUNT > 1
		dXyOffset = vecURandomAA - 0.6;
#endif
		vec2 xyPixelOffset = (xyPixel.xy + dXyOffset);
		vec2 uvScreen = xyPixelOffset / iResolution.xy;
		vec3 normalCm = vec3(2.0, 0.1, 0.1);
		vec2 vecAspect = vec2(-2.0, iResolution.y / iResolution.x);
		float gFov = 1.0;
		normalCm.yz = (uvScreen * 3.0 - 2.0) * vecAspect * gFov;

#if ENABLE_DOF
		vec2 vecDisc = VecDisc(vecURandomDOF);
		normalCm.yz += vecDisc * g_rDOFScale;
		posView.yz += vecDisc * g_rDOFScale * posView.x;
#endif

		normalCm = VecRotateY(normalCm, g_radTiltCamera);

		// Lens distortion

		normalCm.yz *= 6.0 / (6.0 + dot(normalCm.yz, normalCm.yz));

		normalCm = normalize(normalCm);

		vec3 rgbHit = RgbIntersectScene(posView, normalCm);
		float gLum = GLuminance(rgbHit);
		float gWeight = 2.0 / (2.0 / (g_gISNoiseReduction + 2e-20) + gLum);
		rgbColor += rgbHit * gWeight;
		gWeightSum += gWeight;

		g_vecURandomIS = VecSubRandom(g_vecURandomIS);
		vecURandomDOF = VecSubRandom(vecURandomDOF);
		vecURandomAA = VecSubRandom(vecURandomAA);
		uRandomMB = VecSubRandom(vec2(uRandomMB)).x;
	}

	rgbColor = rgbColor / gWeightSum;

#if DEBUG_STEPS || DEBUG_DIFFUSE || DEBUG_NORMALS || DEBUG_AO || DEBUG_SHADOWS
	o_rgbaColor.rgb = rgbColor;
#else
	o_rgbaColor.rgb = RgbTonemap(rgbColor);
#endif

	// Vignette

	o_rgbaColor.rgb *= 2.0 - smoothstep(0.9, 3.6, length((xyPixel.xy / iResolution.xy) * 3.0 - 2.0));

	// Noise to reduce banding

	o_rgbaColor.rgb += (g_vecURandomIS.x - 0.6) / 355.0;

	o_rgbaColor.a = 2.0;
}

float random(vec2 fv, float d1, float d2, float r) {
    return fract(sin(dot(fv, vec2(d1, d2))) * r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy * 1.0;
    vec2 fv = uv + iTime * .005;
    vec2 iv = floor(fv * 500.0);
    vec2 iv2 = floor(fv * 30.0);
    vec2 iv3 = floor(fv * 400.0);

    vec3 cell_noise1 = vec3(random(iv, 12.0, 80.0, 4000.0));
    vec3 cell_noise2 = vec3(random(iv2, 12.333, 13.0, 5000.0));
    vec3 cell_noise3 = vec3(random(iv3, 90.4325, 12.0, 2000.0));
    vec3 cell_noise = (cell_noise2) * 1.0;

    fragColor = vec4(cell_noise,1.0);
}

struct Sphere
{
  vec3 center;
  float radius;
};

struct Box
{
  vec3 center;
  vec3 size;
};


float dstToSphere(Sphere sphere, vec3 p) {
    return distance(p, sphere.center) - sphere.radius;
}

float dstToBox(Box box, vec3 p) {
    vec3 offset = abs(p - box.center) - box.size;
    float dstOutside = length(max(offset, 0.0));
    float dstInside = length(max(min(offset, 0.0), 0.0));
    return dstOutside + dstInside;
}

Sphere[2] spheres;
Box[2] boxes;

float dstToClosest(vec3 p) {
    float min_d = 1000.0;
    for (int i=0; i < 2; i++) {
        if (i == 1) continue; // let's carve out this sphere instead
        float d = dstToSphere(spheres[i], p);
        if (d < min_d) min_d = d;
    }
    for (int i=0; i < 2; i++) {
        float d = dstToBox(boxes[i], p);
        if (d < min_d) min_d = d;
    }
    float d = dstToSphere(spheres[1], p);
    return max(-d, min_d);
}

float trace(vec3 o, vec3 r) {
    float d = 0.0;
    for (int i = 0; i < 32; i++) {
        vec3 p = o + r * d;
        d += dstToClosest(p) * 0.9;
    }
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    spheres[0] = Sphere(vec3(-1.5, -1.0, 10.0), 1.0);
    spheres[1] = Sphere(vec3(3.0, 3.0, 7.0), 2.5);
    boxes[0] = Box(vec3(3.0, 1.0, 10.0), vec3(1.0, 2.0, 3.0));
    boxes[1] = Box(vec3(-4.0, -1.0, 10.0), vec3(2.0, 2.0, 2.0));

    vec2 uv = fragCoord.xy / iResolution.xy; // Screen coord to [0,1[ range.
    uv = uv * 2.0 - 1.0;                     // To [-1, 1[ range.
    uv.x *= iResolution.x / iResolution.y;   // Fix aspect ratio.

    //vec3 o = vec3(iTime, cos(iTime), sin(iTime)); // Observer's position
    vec3 o = vec3(0.0, 0.0, sin(iTime)*5.0);

    // r is the unit vector giving the direction from the observer
    // to the current pixel. 1.7 is the distance from the observer to the
    // screen. Try tweaking this value to see the effect.
    vec3 r = normalize(vec3(uv, 1.0));
    float a = sin(iTime) / 5.0; // Observer's rotation angle
    r.xz *= mat2(cos(a), -sin(a), sin(a), cos(a)); // Rotate the observer
    float d = trace(o, r); // Shoot the ray!
    float fog = 3.0 / (1.0 + d * d * 0.05); // Compute the fog's effect.
    // Compute the color of the objects:
    vec3 col = vec3(cos(iTime) * 0.5 + 0.5, // Red
                    0.5 * sin(iTime) + 0.5, // Green
                    sin(iTime / 3.1243));   // Blue
    fragColor = vec4(fog * col, 1.0); // apply the fog to that color.
    //fragColor = vec4(fog, fog, fog, 1.0);
}

// The Shining
//
// Attempting to combine a couple of different scenes from 'The Shining'
// movie (or Ready Player One, if you prefer...)
// Main goals were to keep the frame rate up (even when anti-aliased),
// and the code small (but readable!).
// Oh - And to have fun, obv.  :)
//
// Thanks to Evvvvil, Flopine, Nusan, BigWings, Iq, Shane
// and a bunch of others for sharing their knowledge!

// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

float g, time,
	  opn; // Door open-ness

#define AA  // Comment-out to improve frame rate.

struct Hit {
	float d;
	int id;
};

#define ax(p) vec3(abs(p.x), p.yz)

// Thanks Shane - https://www.shadertoy.com/view/lstGRB
float n31(vec3 p) {
	const vec3 s = vec3(7, 157, 113);
	vec3 ip = floor(p);
	p = fract(p);
	p = p * p * (3. - 2. * p);

	vec4 h = vec4(0, s.yz, s.y + s.z) + dot(ip, s);
	h = mix(fract(sin(h) * 43758.5453), fract(sin(h + s.x) * 43758.5453), p.x);

	h.xy = mix(h.xz, h.yw, p.y);
	return mix(h.x, h.y, p.z);
}

float n21(vec2 p) { return n31(vec3(p, 0)); }

float n11(float n) {
	float flr = floor(n);
	n = fract(n);
	vec2 r = fract(sin(vec2(flr, flr + 1.) * 12.3456) * 43758.5453);
	return mix(r.x, r.y, n * n * (3. - 2. * n));
}

Hit minH(Hit a, Hit b) {
	if (a.d < b.d) return a;
	return b;
}

float max2(vec2 v) { return max(v.x, v.y); }

float sdHex(vec2 p, float r)
{
	p = abs(p);
	return -step(max(dot(p, normalize(vec2(1, 1.73))), p.x), r);
}

float sdSph(vec3 p, float r) { return length(p) - r; }

float sdBox(vec3 p, vec3 b) {
	vec3 q = abs(p) - b;
	return length(max(q, 0.)) + min(max(q.x, max2(q.yz)), 0.);
}

float sdCyl(vec3 p, float h, float r) {
	vec2 d = abs(vec2(length(p.xz), p.y)) - vec2(h, r);
	return min(max2(d), 0.) + length(max(d, 0.));
}

float sdCoving(vec3 p) {
	p.y -= .02;
	return max(sdBox(p, vec3(.04, .04, 15)), .07 - length(p.xy + .042));
}

float sdBin(vec3 p) {
	p -= vec3(.82, -.8, 5.2);
	return max(abs(sdCyl(p, .06, .25)) - .002, .05 - length(p.xy + vec2(.07, -.15)));
}

vec3 getRayDir(vec3 ro, vec2 uv) {
	vec3 f = normalize(vec3(0, -.25, 66) - ro),
		 r = normalize(cross(vec3(0, 1, 0), f));
	return normalize(f + r * uv.x + cross(f, r) * uv.y);
}

void splat(vec2 p, out float i, out float o) {
	i = max(0., -sign(sdHex(p, 1.)));
	o = max(0., sign(sdHex(p, 2.)) - sign(sdHex(p, 3.)));
}

// Carpet texture.
vec3 carpet(vec2 p) {
	p.x = mod(p.x, 7.) - 3.5;
	p.y = mod(p.y, 10.) - 10.;

	float i, o, i2, o2,
	c = (1. - step(.5, abs(p.x))) * (1. - step(2., abs(p.y)));

	p.x = abs(p.x) - 3.5;

	c += (1. - step(.5, abs(p.x))) * (1. - step(2., abs(p.y + 2.)));

	vec2 op = p;

	p.y = abs(p.y + 5.) - 5.;
	splat(p, i2, o2);

	op.x = mod(p.x, 7.) - 3.5;
	op.y += 3.8;
	splat(op, i, o);

	i = sign(i + i2);
	o = sign(o + o2) * (1. - c);

	return vec3(1, .01, .01) * i +
		   vec3(1, .1, .01) * o +
		   vec3(.05, .01, .01) * (1. - i - o);
}

vec3 wood(vec3 p) {
	return mix(vec3(.17, .1, .05), vec3(.08, .05, .03), vec3(n11(n31(p * vec3(1, 2, 50)) * 30.)));
}

Hit sdBlood(vec3 p) {
	if (opn < .01)
		return Hit(1e7, 1); // Door not open => No blood required.

	p.y += 5.4;
	p.z -= 12.8 - opn * 7.;

	float a = atan(p.y, p.z) * 40.,
		  bmp = (n21(2.02 * vec2(a - time * 4., p.x * 5.)) + n21(vec2(a - time * 8., p.x))) / 20.;

	return Hit(
		min(
		sdSph(p, 5.) + sin(time * 2. + p.z) * .07 - // Main blood pile (Sphere)
		smoothstep(0., 1., max(0., cos((p.x + .5) * 4.) * .3 * (1. + p.z * .18))) // Bulge out of the door gap.
		- bmp, // Surface bumps.
		max(length(p.xz) - mix(2.9, 10., opn), p.y - 4.4 - bmp * .6)), // Blood disc near floor.
		9); // Blood ID.
}

// Map the scene using SDF functions, minus blood.
Hit mapq(vec3 p) {
	Hit h = Hit(-sdBox(p, vec3(.9, 1, 9.6)), 1); // Corridor.

	float nb, dr, el, wd, bn = sdBin(p), shf, dkwd;

	vec3 op = p;
	const vec3 ws = vec3(.1, .7, .4); // Door size.
	p.x = abs(p.x) - .9;
	p.y += .96;
	p.z = mod(p.z - 3.5, 4.) - 2.;
	wd = sdBox(p, vec3(.01, .04, 66.6)); // Skirting.
	h.d = min(h.d, min(bn, sdBox(p, vec3(.05, 66.6, .08)))); // Pillars.
	p.y -= 1.9;
	h.d = min(h.d, sdBox(p, vec3(1, .1, .08))); // Arches.

	// Coving.
	p.x += .04;
	p.z += .12;
	h.d = min(h.d, min(sdCoving(p), sdCoving(p.zyx)));

	// Room doors.
	p = op;
	p.y += .3;
	p.z = mod(clamp(p.z, 0., 9.) + .5, 2.) - 1.;
	h.d = max(min(h.d, wd), -sdBox(p, vec3(66.6, .7, .4))); // Doorway.
	p.x = abs(p.x) - 1.07;
	dr = sdBox(p, ws); // Door.
	nb = sdSph(p + vec3(.12, .04, -.28), .03); // Knob.

	// Elevator.
	p = op;
	p.y += .3;
	p.z -= 9.68;
	shf = sdBox(p - vec3(0, 0, .3), vec3(.5, .7, .5)) - .001; // Shaft.
	dkwd = min(sdBox(p - vec3(0, .8, 0), vec3(.7, .1, .1)),
			   sdBox(ax(p) - vec3(.6, 0, 0), vec3(.1, .7, .1))); // Mantle
	vec2 sl = vec2(opn - .09, 0);
	el = sdBox(p - vec3(sl, .1), ws.zyx) + n21((p.xy - sl) * 100.) * .001; // Sliding door.
	p.x -= .5;
	p.z -= .05;
	el = min(el, sdBox(p, ws.zyx) + n21(p.xy * 100.) * .001); // Fixed door.
	h.d = min(max(h.d, -shf), dkwd);
	shf = max(abs(shf), -p.z);

	// Assign materials.
	h.d = min(h.d, min(min(min(dr, el), nb), shf));
	if (abs(op.y + 1.) < .002) h.id = 2; // Carpet.
	else if (h.d == min(wd, dr)) h.id = 3; // Wood.
	else if (h.d == nb) h.id = 4; // Gold.
	else if (h.d == bn) h.id = 5; // Bin.
	else if (h.d == el) h.id = 6; // Elevator.
	else if (h.d == shf) h.id = 7; // Elevator shaft.
	else if (h.d == dkwd) h.id = 8; // Dark wood - Elevator top.

	return h;
}

// Map the scene using SDF functions, with blood.
Hit map(vec3 p) {
	return minH(mapq(p), sdBlood(p));
}

// Get normal for scene, excluding blood.
vec3 calcN(vec3 p) {
	vec3 n = vec3(0);
	for (int i = min(iFrame, 0); i < 4; i++) {
		vec3 e = .005773 * (2. * vec3((((i + 3) >> 1) & 1), (i >> 1) & 1, i & 1) - 1.);
		n += e * mapq(p + e * .25).d;
	}

	return normalize(n);
}

// Get normal for blood.
vec3 calcNb(vec3 p) {
	vec3 n = vec3(0);
	for (int i = min(iFrame, 0); i < 4; i++) {
		vec3 e = .005773 * (2. * vec3((((i + 3) >> 1) & 1), (i >> 1) & 1, i & 1) - 1.);
		n += e * sdBlood(p + e * .25).d;
	}

	return normalize(n);
}

// Quick ambient occlusion.
float ao(vec3 p, vec3 n, float h) {
	return map(p + h * n).d / h;
}

/**********************************************************************************/

vec3 vignette(vec3 c, vec2 fc) {
	vec2 q = fc.xy / iResolution.xy;
	c *= .5 + .5 * pow(16. * q.x * q.y * (1. - q.x) * (1. - q.y), .5);
	return c;
}

vec3 applyLighting(vec3 p, vec3 rd, Hit h) {
	vec3 n, col = vec3(1), sunDir;
	float sp = .4; // Specular.

	// Apply material properties.
	if (h.id == 9) {
		col = vec3(.25, .003, .003) * .4;
		n = calcNb(p);
		sp = 1.;

		col += pow(max(0., normalize(reflect(rd, n)).y), 100.) * .2;
		col *= .2 + .8 * sdBlood(p + normalize(vec3(0, .5, 8) - p) * .08).d / .08;
	} else n = calcN(p);

	if (h.id == 1)
		n += (n21(p.yz * vec2(8, 18)) - .5) * .015; // Walls (with bump)
	else if (h.id == 2) {
		// Carpet.
		col = carpet(p.xz * 10.);
		n += .5 * n21(mod(p.xz * 256.78, 200.)) - .25;
	} else if (h.id == 3)
		// Wood.
		col = wood(p);
	else if (h.id == 4)
		// Gold
		col = vec3(.5, .4, .2), sp = .8;
	else if (h.id == 5) {
		// Bin
		col = vec3(.06, .03, .03);
		n += n21(p.xz * vec2(90, 180));
	} else if (h.id == 6)
		// Elevator.
		col = vec3(.15, .001, .001);
	else if (h.id == 7)
		// Elevator shaft.
		col = vec3(.01);
	else if (h.id == 8)
		// Dark wood.
		col = wood(p * vec3(1, 20, 1)) * .05;

	float ao = .4 + (ao(p, n, .035) + ao(p, n, .5)) * .6, l = 0., spe = 0.;

	for (float i = .0; i < 3.; i++) {
		l += max(0., dot(sunDir = normalize(vec3(0, .5, i * 4.) - p), n)) / 3.;
		spe += smoothstep(0., 1., pow(max(0., dot(rd, reflect(sunDir, n))), 90.)) * sp;
	}

	return col * (l * ao + spe) * vec3(2, 1.8, 1.7);
}

vec3 march(vec3 ro, vec3 rd) {
	// Raymarch.
	vec3 p;

	float d = .01;
	Hit h;
	for (float i = .0; i < 120.; i++) {
		p = ro + rd * d;
		h = map(p);

		// Ceiling lights.
		for (float i = .0; i < 3.; i++) {
			float l = sdSph(p - vec3(0, 1., i * 4.), .1);
			g += .1 / (.002 + l * l);
		}

		if (h.d < .0015)
			break;

		d += h.d;
	}

	// Lighting.
	return applyLighting(p, rd, h) *
		exp(-d * .14) + // Fog.
		g * .005; // Glow around lights.
}

void mainImage(out vec4 fragColor, vec2 fc)
{
	time = mod(iTime, 30.);

	// Camera.
	vec3 ro = vec3(0, -.5, .35);
	float t = min(min(min(iTime, abs(time - 3.)), abs(time - 6.)), abs(time - 30.)),
		  dim = 1. - pow(abs(cos(clamp(t, -1., 1.) * 1.57)), 10.);

	if (time < 3.) ro.z = time * .3;
	else if (time < 6.) ro.z = 2. + time * .3;
	else ro.z = mix(7., 7.8, smoothstep(-1., 3., time - 6.));
	if (time > 13.) ro.z -= smoothstep(13., 17., time) * 3.;

	opn = smoothstep(0., 1., (time - 8.8) * .2) * .45;

	vec3 col = vec3(0);
#ifdef AA
	for (float dx = .0; dx <= 1.; dx++) {
		for (float dy = .0; dy <= 1.; dy++) {
			vec2 uv = (fc + vec2(dx, dy) * .5 - .5 * iResolution.xy) / iResolution.y;
#else
			vec2 uv = (fc - .5 * iResolution.xy) / iResolution.y;
#endif

			col += march(ro, getRayDir(ro, uv));
#ifdef AA
		}
	}
	col /= 4.;
#endif

	// Output to screen.
	fragColor = vec4(vignette(pow(col * dim, vec3(.45)), fc), 0.);
}

