mat2 r2d(float a) { float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }
const float PI = 3.141592653;
#define sat(a) clamp(a, 0.,1.)

float cir(vec2 p, float r, float amp)
{
    p *= .5;
    float sspd = (amp < 1. ? 1. : -1.);
    float an = atan(p.y, p.x)+PI*.75+iTime*(r*.1+.5)+iTime*sspd;
    float v = sin(an*10.);

    float sCnt = 5.;

    float freq = texelFetch(iChannel0, ivec2(int(sCnt*abs(atan(p.y, p.x))/PI),0),0).x*.25;
    if (amp > 1.)
        v=asin(sin(an*10.))/(PI*.5);

    return length(p)-r-amp*v*0.01+freq;
}

float sub(float a, float b)
{
    return max(a, -b);
}

vec3 rdr(vec2 uv)
{
    uv += vec2(asin(sin(-iTime*5.)), cos(iTime*5.))*.15;
    vec3 col = vec3(18.,22.,60.)/255.;
    vec3 red = vec3(252.,139.,111.)/255.;
    vec3 green = vec3(113.,197.,157.)/255.;

    float th = 0.008;
    float sp = 0.05;

    float r = .02;
    //float a = abs(cir(uv, r, 0.01))-th;
    //col = mix(col, vec3(1.), 1.-sat(a*400.));

    float a;
    int i = 0;
    float bps = 2.;
    float beat = mod(iTime, 1./bps)*bps;
    int cnt = int(mix(2., 8., mod(iTime, 10.)/10.));
    while (i < cnt)
    {
        float fi = float(i);
        r += sp+texelFetch(iChannel0, ivec2(i*5, 0.), 0).x*.05;
        a = abs(cir(uv, r, fi/2.5+.5*sin(iTime+fi)))-th;
        vec3 rgb = (mod(fi, 2.) < 0.1 ? red : green);
    	col = mix(col, rgb, 1.-sat(a*iResolution.x*.025));
        col += .5*rgb*(1.-sat(a*5.))*sat(pow(1.-beat, .5)+.2);
    	++i;
    }

    col = mix(col, 2.*col.zyx*vec3(sin(iTime)*.2+.8, uv*.5+.5), sin(iTime*.5));

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-vec2(.5)*iResolution.xy)/iResolution.xx;
    vec2 ouv = uv;
	uv *= 4.;

//    uv *= (sin(iTime*.2)*.2+.5)+.1*sin(iTime*.34);
    uv *= sin(iTime)*.5+1.;

    uv *= r2d(sin(iTime*.5)*.5+length(ouv));
    uv += vec2(sin(iTime), cos(iTime));

    float rep = mix(3.,.75, float(mod(iTime, 6.) < 3.));
    uv = mod(uv+rep*.5, rep)-rep*.5;
    vec3 col = rdr(uv/rep);
	vec3 a = col + vec3(.23,.54,.36)*1.0-abs(ouv.x*2.);
    vec3 b = col + vec3(.23,.54,.36).zxy*abs(ouv.x*2.);
    col = mix(a, b, sin(iTime)*.5+.5);
    col = pow(col, vec3(.45));
    fragColor = vec4(col.zyx,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Curve 5 from https://mathworld.wolfram.com/HeartCurve.html

    vec2 uv =  ( fragCoord -vec2(0.5,0.83) * iResolution.xy )/ iResolution.y;

    float theta = atan(uv.y,uv.x);
    float sintheta= sin(theta);
    float it = (sin(iTime*4.30)+1.0);
    float mt = (sin((iTime+0.21)*4.3)+1.0);
    float r = sintheta*sqrt(abs(cos(theta))) / (sintheta + 7.0/4.0+min(it,mt)) - 2.0 * sintheta + 2.0;
    vec4 f = sin((vec4(theta*32.0)))+1.0;
    fragColor = step(length(uv),vec4(r)/5.0)*vec4(1.0,0.0,0.0,0.0);
}

// convert 2D seed to 1D
// 2 imad
uint seed(uint x) {
    return 19u * x;
}

uint seed(uvec2 p) {
    return 19u * p.x + 47u * p.y + 101u;
}

// convert 3D seed to 1D
uint seed(uvec3 p) {
    return 19u * p.x + 47u * p.y + 101u * p.z + 131u;
}

uint seed(uvec4 p) {
	return 19u * p.x + 47u * p.y + 101u * p.z + 131u * p.w + 173u;
}

uint pcg(uint v)
{
	uint state = v * 747796405u + 2891336453u;
	uint word = ((state >> ((state >> 28u) + 4u)) ^ state) * 277803737u;
	return (word >> 22u) ^ word;
}

float noise(float x)
{
	return float(pcg(uint(x*1000.0))) * (1.0/float(0xffffffffu));
}

vec2 noise(vec2 p)
{
	uint x = seed(uvec3(p.xy*1000.0, iTime*1000.1));
	float v = float(pcg(x)) * (1.0/float(0xffffffffu));
	return vec2(v);
}

float noise2( uint n ) 
{   // integer hash copied from Hugo Elias
	n = (n<<13U)^n;
    n = n*(n*n*15731U+789221U)+1376312589U;
    return float(n&uvec3(0x0fffffffU))/float(0x0fffffff);
}

// Basic noise
float bnoise( in float x )
{
    // setup    
    float i = floor(x);
    float f = fract(x);
    float s = sign(fract(x/2.0)-0.5);

    // use some hash to create a random value k in [0..1] from i
    float k = noise2(uint(i));

    // quartic polynomial
    return s*f*(f-1.0)*((16.0*k-4.0)*f*(f-1.0)-1.0);
}

float terrain(vec2 p)
{
	float v2 = 1.0 * bnoise(p.x*0.5);
    v2 += 0.05 * bnoise(p.x*2.2);
    v2 += 0.025 * bnoise(p.x*4.2);
    v2 += 0.0125 * bnoise(p.x*8.2);
    v2 += 0.0025 * bnoise(p.x*36.2);
    return v2;
}

vec3 noiseLine(vec2 p, vec3 col)
{
	float px = 1.0/iResolution.y;
    p.x = (p.x*0.3) + 0.5;
    return mix(col, vec3(0.0,1.0,1.0), 1.0 - smoothstep(0.0, px*2.0, abs(p.y-p.x)));
}

/**
 * Convert r, g, b to normalized vec3
 */
vec3 rgb(float r, float g, float b) {
	return vec3(r / 255.0, g / 255.0, b / 255.0);
}

/**
 * Draw a rectangle at vec2 `pos` with width `width`, height `height` and
 * color `color`.
 */
vec4 rectangle(vec2 uv, vec2 pos, float width, float height, vec3 color) {
	float t = 0.0;
	if ((uv.x > pos.x - width / 2.0) && (uv.x < pos.x + width / 2.0)
		&& (uv.y > pos.y - height / 2.0) && (uv.y < pos.y + height / 2.0)) {
		t = 1.0;
	}
	return vec4(color, t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float px = 1.0/iResolution.y;
    vec2 p = fragCoord*px;
    p.x += iTime*2.5;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col = vec3(0.5);

    vec2 n1 = vec2((terrain(p)+terrain(p*0.3)*2.0)*0.4, p.y);
    vec2 n2 = vec2(terrain(p*0.1), p.y);
    vec2 n3 = vec2(terrain(p*0.7), p.y);

    col = noiseLine(n1, col);
    col += noiseLine(n2, vec3(1.0, 0.0, 0.0));
    col += noiseLine(n3, vec3(1.0, 0.0, 0.0));

    vec4 r = rectangle(uv, vec2(0.4+(n1.x*-0.25), n1.x*0.3+0.514), 0.014, 0.032, vec3(1.0));
    r += rectangle(uv, vec2(0.5+(n2.x*-0.45), n2.x*0.3+0.514), 0.05, 0.032, vec3(1.0));
    r += rectangle(uv, vec2(0.6+(n3.x*-0.12), n3.x*0.3+0.508), 0.03, 0.018, vec3(1.0));

    fragColor = vec4(col, 1.0);
    fragColor = mix(fragColor, r, r.a);
}

// Engage Warp by Logan Apple (Twitter: @loganapple540)
// https://www.shadertoy.com/view/3syBDc
// MIT License

#define TIME_DELAY 0.1
#define DISPLACEMENT 0.5

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy - DISPLACEMENT;

    // Pixel color
    vec3 col;

    // Length of normalized and translated frag coord
    float len;
    for (int i = 0; i < 3; ++i) {
        len = length(uv);
        uv *= 1.0 + 2.0 / len * (cos(iTime) + 1.0) * abs(cos(0.5 * len - iTime));
        len *= mix(1.0, TIME_DELAY * sin(iTime * len), cos(iTime));

        col = vec3(
            i == 0 ? 0.1 / length(mod(uv, 1.0) - DISPLACEMENT) : col.r, 
            i == 1 ? 0.1 / length(mod(uv, 1.0) - DISPLACEMENT) : col.g, 
            i == 2 ? 0.1 / length(mod(uv, 1.0) - DISPLACEMENT) : col.b);
    }

    // Output to screen
    fragColor = vec4(col, 1.0);
}

// noise
// Volume raycasting by XT95
// https://www.shadertoy.com/view/lss3zr
mat3 m = mat3( 0.00,  0.80,  0.60,
              -0.80,  0.36, -0.48,
              -0.60, -0.48,  0.64 );
float hash( float n )
{
    return fract(sin(n)*43758.5453);
}

float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0 + 113.0*p.z;

    float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                        mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
                    mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                        mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
    return res;
}

float fbm( vec3 p )
{
    float f;
    f  = 0.5000*noise( p ); p = m*p*2.02;
    f += 0.2500*noise( p ); p = m*p*2.03;
    f += 0.12500*noise( p ); p = m*p*2.01;
    f += 0.06250*noise( p );
    return f;
}

float numericalMieFit(float costh)
{
    //return 3.0 / (16.0 * 3.14159265359) * (1.0 + costh * costh) + 0.255;
    // This function was optimized to minimize (delta*delta)/reference in order to capture
    // the low intensity behavior.
    float bestParams[10];
    bestParams[0]=9.805233e-06;
    bestParams[1]=-6.500000e+01;
    bestParams[2]=-5.500000e+01;
    bestParams[3]=8.194068e-01;
    bestParams[4]=1.388198e-01;
    bestParams[5]=-8.370334e+01;
    bestParams[6]=7.810083e+00;
    bestParams[7]=2.054747e-03;
    bestParams[8]=2.600563e-02;
    bestParams[9]=-4.552125e-12;

    float p1 = costh + bestParams[3];
    vec4 expValues = exp(vec4(bestParams[1] *costh+bestParams[2], bestParams[5] *p1*p1, bestParams[6] *costh, bestParams[9] *costh));
    vec4 expValWeight= vec4(bestParams[0], bestParams[4], bestParams[7], bestParams[8]);
    return dot(expValues, expValWeight) * 0.25;
}
float numericalMieFitMultiScatter() {
	// This is the acossiated multi scatter term used to simulate multi scatter effect.
	return 0.1026;
}

float Roberts1(int n) {
    const float g = 1.6180339887498948482;
    const float a = 1.0 / g;
    return  fract(0.5 + a * float(n));
}

vec2 Roberts2(int n) {
    const float g = 1.32471795724474602596;
    const vec2 a = vec2(1.0 / g, 1.0 / (g * g));
    return fract(0.5 + a * vec2(n));
}

vec3 UniformSampleSphere(const vec2 e) {
    float Phi = 2. * 3.14159265359 * e.x;
    float CosTheta = 1. - 2. * e.y;
    float SinTheta = sqrt(1. - CosTheta * CosTheta);

    vec3 H;
    H.x = SinTheta * cos(Phi);
    H.y = SinTheta * sin(Phi);
    H.z = CosTheta;

    return H;
}



