float stepUp(float t, float len, float smo)
{
  float tt = mod(t += smo, len);
  float stp = floor(t / len) - 1.0;
  return smoothstep(0.0, smo, tt) + stp;
}

// iq's smin
float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}


float map( in vec3 p )
{
    vec3 q = p - vec3(0.0,0.5,1.0)*iTime;
    float f = fbm(q);
    float s1 = 1.0 - length(p * vec3(0.5, 1.0, 0.5)) + f * 2.2;
    float s2 = 1.0 - length(p * vec3(0.1, 1.0, 0.2)) + f * 2.5;
    float torus = 1. - sdTorus(p * 2.0, vec2(6.0, 0.005)) + f * 3.5;
    float s3 = 1.0 - smin(smin(
                           length(p * 1.0 - vec3(cos(iTime * 3.0) * 6.0, sin(iTime * 2.0) * 5.0, 0.0)),
                           length(p * 2.0 - vec3(0.0, sin(iTime) * 4.0, cos(iTime * 2.0) * 3.0)), 4.0),
                           length(p * 3.0 - vec3(cos(iTime * 2.0) * 3.0, 0.0, sin(iTime * 3.3) * 7.0)), 4.0) + f * 2.5;

    float t = mod(stepUp(iTime, 4.0, 1.0), 4.0);

    float d = mix(s1, s2, clamp(t, 0.0, 1.0));
    d = mix(d, torus, clamp(t - 1.0, 0.0, 1.0));
    d = mix(d, s3, clamp(t - 2.0, 0.0, 1.0));
    d = mix(d, s1, clamp(t - 3.0, 0.0, 1.0));

    return max(0.0, clamp((d - 0.01) * 2., 0., 1.) * 16.);
}

float jitter;

#define MAX_STEPS 256
#define SHADOW_STEPS 64
#define VOLUME_LENGTH 30.
#define SHADOW_LENGTH 2.



vec4 cloudMarch(vec3 p, vec3 ray)
{
    float density = 0.;

    float stepLength = VOLUME_LENGTH / float(MAX_STEPS);
    vec3 light = normalize(vec3(1.0, 1.0, 1.0));
    vec3 bilight = normalize(cross(light, vec3(0.,1.,0.)));
    vec3 talight = cross(bilight, light);
    float phase = numericalMieFit(dot(ray, light));
    float multiScatterPhase = phase +  numericalMieFitMultiScatter();
    float dotSV = 1. - (dot(ray, light) + 1.) / 2.;

    vec4 sum = vec4(0., 0., 0., 1.);

    vec3 pos = p + ray * jitter * stepLength;

    for (int i = 0; i < MAX_STEPS; i++)
    {
        if (sum.a < 0.01) {
            break;
        }
        float d = map(pos);

        if( d > 0.001)
        {
            float shadow = 0.;        
            int shadowStep = int(mix(float(SHADOW_STEPS / 4), float(SHADOW_STEPS), sum.a));
            float shadowStepLength = SHADOW_LENGTH / float(shadowStep);
            vec3 lpos = pos + light * jitter * shadowStepLength;
            for (int s = 0; s < shadowStep; s++)
            {
                lpos += light * shadowStepLength;
                float len = float(s) * shadowStepLength * 1.;
                vec2 rnd = fract(Roberts2(s) + jitter);
                vec3 offset = (sin(rnd.x) * bilight + cos(rnd.x) * talight) * rnd.y;
                float lsample = map(lpos + len * offset);
                shadow += lsample;
            }

            density = d * stepLength;
            density = 1. - exp(-density);
            shadow = (1. - exp(-shadow * shadowStepLength * 2.) * dotSV) * exp(-shadow * shadowStepLength * 0.1);
            float msPhase = multiScatterPhase;
            vec3 s = shadow * msPhase * vec3(1.1, .9, .9) * 10.;
            sum.rgb += vec3(s * density) * sum.a;

            vec3 ambOffset = fract(Roberts1(i) + jitter) * UniformSampleSphere(fract(Roberts2(i) + jitter)) * 2.;

            sum.rgb += 0.4 * density * sum.a * mix(vec3(.4, .7, 1.3), vec3(.7, .7, 1.), clamp((pos.y + 2.) / 3., 0., 1.));
            sum.a *= 1. - density;
        }


        pos += ray * stepLength;
    }

    sum.rgb *= 2.;
    return sum;
}

mat3 camera(vec3 ro, vec3 ta, float cr )
{
    vec3 cw = normalize(ta - ro);
    vec3 cp = vec3(sin(cr), cos(cr),0.);
    vec3 cu = normalize( cross(cw,cp) );
    vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

vec3 aces_tonemap(vec3 color){  
    mat3 m1 = mat3(
        0.59719, 0.07600, 0.02840,
        0.35458, 0.90834, 0.13383,
        0.04823, 0.01566, 0.83777
    );
    mat3 m2 = mat3(
        1.60475, -0.10208, -0.00327,
        -0.53108,  1.10813, -0.07276,
        -0.07367, -0.00605,  1.07602
    );
    vec3 v = m1 * color;    
    vec3 a = v * (v + 0.0245786) - 0.000090537;
    vec3 b = v * (0.983729 * v + 0.4329510) + 0.238081;
    return pow(clamp(m2 * (a / b), 0.0, 1.0), vec3(1.0 / 2.2)); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    jitter = hash(p.x + p.y * 57.0 + iTime);
    float rot = iTime * .333 + ((iMouse.x / iResolution.x) - 0.5) * 16.;
    vec3 ro = vec3(cos(rot) * 10.0, ((iMouse.y / iResolution.y) - 0.5) * -25., sin(rot) * 10.0);
    vec3 ta = vec3(0.0, 1., 0.0);
    mat3 c = camera(ro, ta, 0.0);
    vec3 ray = c * normalize(vec3(p, 1.75));
    vec4 col = cloudMarch(ro, ray);
    float sundot = clamp(dot(ray,normalize(vec3(1.0, 1.0, 1.0))),0.0,1.0);
    vec3 result = col.rgb + (mix(vec3(0.3, 0.6, 1.0), vec3(0.05, 0.35, 1.0), p.y + 0.75) + 0.8*vec3(1.0,0.7,0.3)*pow(sundot, 4.0)) * col.a;

    result = aces_tonemap(result);

    fragColor = vec4(result, 1.0);
}

/*
Info on curves:
http://research.microsoft.com/en-us/um/people/hoppe/ravg.pdf
http://research.microsoft.com/en-us/um/people/cloop/LoopBlinn05.pdf
http://www.pouet.net/topic.php?which=9119&page=1
http://blog.gludion.com/2009/08/distance-to-quadratic-bezier-curve.html

The top link is where the get_distance_vector comes from.

Thanks also to other bezier curve shadertoys:
https://www.shadertoy.com/view/XsX3zf
https://www.shadertoy.com/view/lts3Df
*/

#define EDGE   0.001
#define SMOOTH 0.0025
#define inf 1e32
#define pi 3.14159265

// signed distance function for Circle, for control points
float SDFCircle( in vec2 coords, in vec2 offset )
{
    coords -= offset;
    float v = coords.x * coords.x + coords.y * coords.y - EDGE*EDGE;
    vec2  g = vec2(2.0 * coords.x, 2.0 * coords.y);
    return v/length(g); 
}

float det(vec2 a, vec2 b) { return a.x*b.y-b.x*a.y; }

vec2 get_distance_vector(vec2 b0, vec2 b1, vec2 b2) {
  float a=det(b0,b2), b=2.0*det(b1,b0), d=2.0*det(b2,b1); // Ã°ÂâºÂ¼,Ã°ÂâºÂ½,Ã°ÂâºÂ¿(Ã°ÂâÂ)
  float f=b*d-a*a; // Ã°Âââ(Ã°ÂâÂ)
  vec2 d21=b2-b1, d10=b1-b0, d20=b2-b0;
  vec2 gf=2.0*(b*d21+d*d10+a*d20);
  gf=vec2(gf.y,-gf.x); // Ã¢Ëâ¡Ã°Âââ(Ã°ÂâÂ)
  vec2 pp=-f*gf/dot(gf,gf); // Ã°ÂâÂÃ¢â¬Â²
  vec2 d0p=b0-pp; // Ã°ÂâÂÃ¢â¬Â² to origin
  float ap=det(d0p,d20), bp=2.0*det(d10,d0p); // Ã°ÂâºÂ¼,Ã°ÂâºÂ½(Ã°ÂâÂÃ¢â¬Â²)
  // (note that 2*ap+bp+dp=2*a+b+d=4*area(b0,b1,b2))
  float t=clamp((ap+bp)/(2.0*a+b+d), 0.0, 1.0); // Ã°ÂâÂ¡Ãâ¦
  return mix(mix(b0,b1,t),mix(b1,b2,t),t); // Ã°ÂâÂ£Ã°Âââ = Ã°ÂâÂ(Ã°ÂâÂ¡Ãâ¦)
}

float approx_distance(vec2 p, vec2 b0, vec2 b1, vec2 b2) {
  return length(get_distance_vector(b0-p, b1-p, b2-p));
}

