void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    float aspectRatio = iResolution.x / iResolution.y;
    vec2 uv = (((fragCoord) - 0.5 * iResolution.xy) / iResolution.y);
    vec2 mouse = (iMouse.xy / iResolution.xy) - vec2(0.25,0.5);
    mouse.x *= aspectRatio;
    vec2 A = vec2(-0.5,0.0);
    vec2 B =  vec2(0,0.3);
    vec2 C = vec2(0.5,0.0);

    vec3 color = vec3(1.0,1.0,1.0);



    float dist = approx_distance(uv, A, B, C);
    if (dist < EDGE + SMOOTH)
    {
        dist = smoothstep(EDGE - SMOOTH,EDGE + SMOOTH,dist);
        color *= vec3(dist);
    }
    float t=2.*iTime/pi;

    int i= int(t);
    float tt=(t-float(i));
    float T=(0.5+0.5*(sin(tt*pi-pi/2.)));
    vec2 tab = mix(A, B, T);
    vec2 tbc = mix(B, C, T);
    vec2 tr = mix(tab, tbc, T);
	vec4 co = vec4(1.-color,1.0);
    vec4 result = vec4((0.01 / length(uv - tr)),0,0,1)+co;

    fragColor = result;
}

//ref:https://www.twitch.tv/videos/825604155 00:55:50 nusan
precision highp float;

uniform vec2 resolution;
uniform float time;
uniform vec2 mouse;
uniform sampler2D backbuffer;

float rand(float n){return fract(sin(n) * 43758.5453123);}


mat2 rot(float a) {
	float c = cos(a), s = sin(a);
	return mat2(c, -s, s, c);
}
vec3 fra2(vec3 p, float a) {
	float s = 5.0;

    for(int i = 0 ; i < 9; i++) {
        p.xy *= rot(a * 0.01);
        p.yz *= rot(-a * 0.7);
        p.xz = abs(p.xz) - s;
        s *= 0.7;
    }
	return p;
}
vec3 fra(vec3 p, float a) {
	float s = 2.0;

    for(int i = 0 ; i < 24; i++) {
        p.xz *= rot(a);
        p.yz *= rot(-a * 0.1333);
        p.xz = abs(p.xz) - s;
        s *= 0.8;
    }
	return p;
}

float box(vec3 p, vec3 s) {
	p = abs(p) - s;
	return max(p.x, max(p.y, p.z));
}

float tunnel(vec3 p, vec3 s) {
	p = abs(p) - s;
	return length(p.xz) - 20.0;
}

float getlt(float offset) {
    float ft = fract(offset);
    return smoothstep(0.25, 0.75, ft) + floor(offset);
}

float map(vec3 p) {
    float time = iTime * 2.0;
    float t0 = 2.2 * getlt(-0.25 * time + 32.4);
    float t1 = 3.3 * getlt(0.125 * time + 5.0);
    float t2 = time * 0.125;
	float a = 5.0 - dot(p, vec3(0, 1, 0));

    a = min(a, box(fra(-p - 2.0, t1 * 0.3), vec3(10,1,3) * 0.1));
    a = min(a, box(fra(p + 2.0, t0 * 0.1), vec3(1,10,4) * 0.2));
    a = min(a, box(fra2(p, (t1 * t0) * 0.001), vec3(1,2,3) * 0.05));
	a = min(a, -tunnel(fra(p + 0.5, t2 * 0.3), vec3(1,1,1) * 0.1));
	return a;
}

vec3 getnor(vec3 p) {
    float t = map(p);
    vec2 d = vec2(0.01, 0.0);
    return normalize(vec3(
                     t - map(p + d.xyy),
                     t - map(p + d.yxy),
                     t - map(p + d.yyx)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float time = iTime;
	vec2 uv = ( 2.0 * fragCoord.xy - iResolution.xy ) / min(iResolution.x, iResolution.y);

	vec3 dir = vec3(-uv + vec2(cos(time), sin(time * 0.6)) * 0.1, 1.0 + 0.5*smoothstep(0.5, 0.9, abs(sin(1.2 * sin(time * 0.123))))) + vec3(0, 0.25, 0);
    dir = normalize(dir);
    vec2 pkd = (1.0 / vec2(iResolution.xy)) * 0.717;
    dir.x += (rand(time * dir.x) * 2.0 - 1.0) * pkd.x;
    dir.y += (rand(time * dir.y) * 2.0 - 1.0) * pkd.y;


     vec3 pos = vec3(0, -5, -20);
	vec3 p = pos;
	for(int i = 0 ; i < 100; i++ ){
		float k = map(p);
        if(k < (1.0 / 64.0)) break;
		p += dir * k;
	}
    float t = length(p - pos) * 0.08;

	vec3 N = normalize(getnor(p));
	vec3 V = normalize(-p);
	vec3 H = normalize(N + V);
	vec3 L = normalize(vec3(0.5, 2, 1));
    vec3 KGT = vec3(1, 2, 3) * 0.313;
	vec3 D = KGT * max(0.1, dot(N, L));
	vec3 S = KGT.zyx * max(0.1, pow(dot(H, N), 8.0));

	vec3 col = vec3(0);
    float ao = map(p - dir);

    float v = 1.0 - dot(uv, uv * vec2(0.5, 1.0) * 0.2);
    col += abs(ao);
    col = (col * S + col * D);
    col *= pow(mix(vec3(1,2,3), vec3(1,1,0.3), t).zyx, vec3(1.0 / 7.0));
    col.r += rand(col.r * time) * 0.1;
    col.g += rand(col.g * time) * 0.1;
    col.b += rand(col.b * time) * 0.1;

	fragColor = vec4(col * v, 1.0);
}

