float noise(vec2 p)
{
    return fract(sin(p.x * 100. + p.y * 5412.) * 4340.);
}

float noise(vec3 p)
{
    return fract(sin(p.x * 100. + p.y * 4241. + p.z) * 6340.);
}

// For 2D magic
float noiseSmooth(vec2 uv)
{
    vec2 lv = fract(uv);
    lv = lv * lv * (3.0 - 2.0 * lv); // Sigmoid interpolation

    vec2 index = floor(uv);    

    float topLeft = noise(index);
    float topRight = noise(index + vec2(1, 0));
    float top = mix(topLeft, topRight, lv.x);

    float botLeft = noise(index + vec2(0, 1));
    float botRight = noise(index + vec2(1, 1));
    float bot = mix(botLeft, botRight, lv.x);

    return mix(top, bot, lv.y);
}


// For 3D magic
float noiseSmooth(vec3 pos)
{
    vec3 lv = fract(pos);
    lv = lv * lv * (3.0 - 2.0 * lv); // Sigmoid interpolation
    lv.z = fract(pos.z);

    vec3 index = floor(pos);
    float delta = 1.;

    // Front plane
    float tl = noise(index + vec3(0, 0, 0));
    float tr = noise(index + vec3(1, 0, 0));
    float top = mix(tl, tr, lv.x);

    float bl = noise(index + vec3(0, 1, 0));
    float br = noise(index + vec3(1, 1, 0));
    float bot = mix(bl, br, lv.x);

    float frontPlane = mix(top, bot, lv.y);

    // Back plane
    tl = noise(index + vec3(0, 0, delta));
    tr = noise(index + vec3(1, 0, delta));
    top = mix(tl, tr, lv.x);

    bl = noise(index + vec3(0, 1, delta));
    br = noise(index + vec3(1, 1, delta));
    bot = mix(bl, br, lv.x);

    float backPlane = mix(top, bot, lv.y);

    return mix(frontPlane, backPlane, lv.z);   
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= (iResolution.x / iResolution.y); 

    vec3 pt = vec3(uv, 0.);
    pt.z += iTime / 10.;
    pt.x += iTime / 100.;


    float c = noiseSmooth(pt * 4.);  
    c += noiseSmooth(pt * 8.) * .5;  
    c += noiseSmooth(pt * 16.) * .25;  
    c += noiseSmooth(pt * 32.) * .125;  
    c += noiseSmooth(pt * 64.) * .0625;  

    c /= 2.;

    vec3 col = vec3(c);

    // Output to screen
    fragColor = vec4(col,1.0);
}

