vec2 intersectRaySphere(vec3 O, vec3 D, Sphere sphere) {
	float t1;
	float t2;
	vec3 C = sphere.center;
	float r = sphere.radius;
	vec3 OC = O - C;
	float k1 = dot(D,D);
	float k2 = 2. * dot(OC, D);
	float k3 = dot(OC,OC) - r * r;
	float discriminant = k2 * k2 - 4. * k1 * k3;
	if (discriminant < 0.) {
		return vec2(FLT_MAX, FLT_MAX);
	}
	t1 = (-k2 + sqrt(discriminant)) / (2. * k1);
	t2 = (-k2 - sqrt(discriminant)) / (2. * k1);

	return vec2(t1, t2);
}


float ComputeLighting(vec3 P, vec3 N, vec3 V, float s) { //zkid3a
      float i = 0.;
      for (int light_count = 0; light_count < main_scene.lights.length(); light_count++) {
          Light light_current = main_scene.lights[light_count];
          if (light_current.type == 0) {
            i += light_current.intensity;
          }
          vec3 L;
          if (light_current.type == 1) {
            L = light_current.position - P;
          }
          if (light_current.type == 2) {
            L = light_current.direction;
          }
          // zid4 diffuse 
           float n_dot_l = dot(N, L);
            if(n_dot_l > 0.) {
                i += light_current.intensity*n_dot_l/(length(N)*length(L));
          }
          // Specular
            if(s != -1.) {
                vec3 R = 2.* N *dot(N, L) - L; //zkid6 light disperced - change 2 to 1
                float r_dot_v = dot(R, V);
                if(r_dot_v > 0.) {
                    i += light_current.intensity*pow(r_dot_v/(length(R)*length(V)), s);
                }
                //r_dot_v = 0.;
            }

      }
      return i;
}

vec3 TraceRay(vec3 origin, vec3 direction, float t_min, float t_max) {

	float t_closest = FLT_MAX; //zkid1c changed this from 0 to FLT_MAX
	bool sphere_found = false; // zkid1d #A: forgot change switcher when found sphere 
    Sphere closest_sphere;
    vec3 color_val;

	for (int sphere = 0; sphere < main_scene.spheres.length(); sphere++) { // zkid1b
		closest_sphere = main_scene.spheres[sphere];
		vec2 intersections = intersectRaySphere(origin, direction, closest_sphere); //zkid5 disable
		if (t_min < intersections[0] && intersections[0] < t_max &&  intersections[0] < t_closest) {
			t_closest = intersections[0];			
			closest_sphere = main_scene.spheres[sphere];
            sphere_found = true; //zkid1d1
		} 
        if (t_min < intersections[1] && intersections[1] < t_max &&  intersections[1] < t_closest) {
			t_closest = intersections[1];			
			closest_sphere = main_scene.spheres[sphere];
            sphere_found = true; //zkid1d2
		}
    }
    if (!sphere_found) {
	   return main_scene.color_background; // zkid1 #Q: debug just black screen, no balls. Change it here chages color
	}
	vec3 P = origin + t_closest * direction;
    vec3 N = P - closest_sphere.center;
    N = N / length(N);
    float light_contrib = ComputeLighting(P, N, -direction,  closest_sphere.specular);
	color_val = closest_sphere.color + light_contrib; //zkid1a moving this into for-cycle (1b) not gives anything

    return color_val; // zkid1c
}


