#define TAU 6.283183
#define PI acos(-1.)
#define dt(sp,off) fract(iTime*sp+off)

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define mo(p,d)p=abs(p)-d;if(p.y>p.x)p=p.yx


void moda (inout vec2 p , float rep)
{
    float per = TAU/rep;
    float a = mod(atan(p.y,p.x),per)-per*0.5;
    p = vec2(cos(a),sin(a))*length(p);
}

float box (vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0.,max(q.x,max(q.y,q.z)))+length(max(q,0.));
}

float sc (vec3 p, float d)
{
    p = abs(p);
    p = max(p,p.yzx);
    return min(p.x,min(p.y,p.z))-d;
}

float g1=0.;
float SDF (vec3 p)
{
    p.yz *= rot(-atan(1./sqrt(2.)));
    p.xz *= rot(PI/4.);  

    vec3 pp = p;
    float per = mix(.5,5.,clamp(texture(iChannel0,vec2(0.00001,0.0002)).x*0.5,0.,1.)),
        l = 2.;
    vec3 id = floor((p-per*0.5)/per);
    p = p-per*clamp(round(p/per),-l,l);

    p.xz *= rot(dt(0.2,length(id*0.2))*TAU);
    p.yz *= rot(dt(0.5,length(id*0.5))*TAU);
    float d = max(-sc(p,0.9),box(p,vec3(1.)));
    p = pp;
    mo(p.yz,vec2(2.));
    p.y -= 1.4;
    p.xz *= rot(sin(p.y*0.4+dt(0.5,0.)*TAU));
    moda(p.xz,5.);
    p.x -= .5;
    d = min(d,length(p.xz)-0.2);

    g1 += 0.01/(0.01+d*d);
    return d;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;

    vec3 ro=vec3(uv*8.,-20.),rd =vec3(0.,0.,1.);

    vec2 uu = fract(uv)-.5;
    mo(uu,vec2(.05));  
    vec3 col = texture(iChannel0,vec2((uu.x+uu.y)*.7,0.25)).xxx;

    vec3 p = ro;
    bool hit = false; float shad = 0.;
    for (float i=0.; i<64.;i++)
    {
        float d = SDF(p);
        if (d<0.01)
        {
            hit = true;shad = i/64.; break;
        }
        p += d*rd;
    }

    if (hit)
    {
        col = vec3(shad);
    }

    col -= g1*0.05;
    fragColor = vec4(sqrt(clamp(col,0.,1.))*0.8,1.);
}

vec3 random3(vec3 st){
    mat3 A = mat3(
        vec3(127.1, 251.1, 311.2),
        vec3(134.3, 321.5, 231.5),
        vec3(227.2, 141.3, 151.6)
    );

    st = A * st;
    return 2.0 * fract(sin(st) * 43758.0) - 1.0;
}


#define L 3.0
float dotRandOffset(in vec3 i, vec3 f, vec3 pos) {
    i += pos;
    i = vec3(i.xy, mod(i.z, L));
    return dot( random3(i), f - pos );
}

float noise(vec3 st) {
    vec3 i = floor(st);
    vec3 f = fract(st);

    // Polynomial Interpolation
    vec3 u = f * f * (3.0 - 2.0 * f);
    u = f * f * f * (f * (6.0 * f - 15.0) + 10.0);

    return mix( mix( mix( dotRandOffset(i, f, vec3(0.0, 0.0, 0.0)),
                          dotRandOffset(i, f, vec3(1.0, 0.0, 0.0)), u.x),
                     mix( dotRandOffset(i, f, vec3(0.0, 1.0, 0.0)),
                          dotRandOffset(i, f, vec3(1.0, 1.0, 0.0)), u.x), u.y),
                mix( mix( dotRandOffset(i, f, vec3(0.0, 0.0, 1.0)),
                          dotRandOffset(i, f, vec3(1.0, 0.0, 1.0)), u.x),
                     mix( dotRandOffset(i, f, vec3(0.0, 1.0, 1.0)),
                          dotRandOffset(i, f, vec3(1.0, 1.0, 1.0)), u.x), u.y), u.z);
}

#define OCTAVES 8
float fBM(in vec3 st) {
    float value = 0.0;
    float amplitude = 0.5;

    for (int i = 0; i < OCTAVES; i++) {
        st += vec3(0.0, 0.0, 0.0);
        value += amplitude * noise(st);
        st *= vec3(2.0, 2.0, 1.0);
        amplitude *= 0.5;
    }
    return value;
}

vec3 distort3D(in vec3 p, mat3 A){
    return vec3(
            fBM(p + A[0]),
            fBM(p + A[1]),
            fBM(p + A[2])
            );
}

float pattern(in vec3 p, out vec3 r, out vec3 s, out vec3 t) {
    float v = .0;
    mat3 A = mat3(
        vec3(5.1, -2.4, 4.0),
        vec3(1.0, 1.4, -12.0),
        vec3(22.0, 111.0, 1.0)
    );
    mat3 B = mat3(
        vec3(-10.0, 0.0, 2.0),
        vec3(-8.1, 20.4, 1.0),
        vec3(2.6, 3.0, 0.2)
    );

    r = distort3D(p, B);
    s = distort3D(r * 2.5 + p, A);
    t = distort3D(s * 2.0 - r + p, B);

    v = fBM(t * 2.0 + r + s + p);
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
    vec3 uvw = vec3(uv * 4.0, iTime * .5);

    vec3 r = vec3(0.0);
    vec3 s = vec3(0.0);
    vec3 t = vec3(0.0);

    float v = pattern(uvw, r, s, t);

    vec3 col = vec3(mix(s, t, v))*1.3+0.4;

    fragColor = vec4(col,1.0);
}

uniform float time;
uniform vec2 mouse;
uniform vec2 resolution;

#define resolution iResolution 
#define  time iTime
#define mouse iMouse 



#define rot(a) mat2(cos(a), sin(a), -sin(a), cos(a))
#define EPS 0.0001

vec3 lightDir = normalize(vec3(1.,1., 1));

float dRing(vec3 p, float exR, float inR, float thick){
	float sdf2d = abs(length(p.xy)-exR)-inR;
	float d = abs(p.z)-thick;
	return max(sdf2d, d);
}
float map(vec3 p){
	vec3 q=p,r = p;
	p.xy *= rot(time*.5);
	p.xz *= rot(time*.3);
	float ring1 = dRing(p, .8, .09, .1);
	q.xy *= rot(time*-1.3);
	q.xz *= rot(time*-1.);
	float ring2 = dRing(q, .62, .07, .1);
	r.yz *= rot(time*2.);
	r.xz *= rot(time*-2.);
	float ring3 = dRing(r, .47, .06, .1);
	return min(ring1, min(ring2, ring3));
}

vec3 getNormal(vec3 p){
	return normalize(vec3(
		map(p)-map(vec3(p.x-EPS, p.yz)),
		map(p)-map(vec3(p.x, p.y-EPS, p.z)),
		map(p)-map(vec3(p.xy, p.z-EPS))
		));
}


vec4 render(vec2 coord ) {

	vec2 uv = (gl_FragCoord.xy*2.-resolution.xy)/min(resolution.x, resolution.y);
	vec3 col = vec3(.1);
	vec3 ro = vec3(0.,0,10.);
	vec3 rd = normalize(vec3(uv, 0)-ro);
	vec3 cur = ro;

	for(int i = 0; i <99;i++){
		float d = map(cur);
		if(d < EPS){
			vec3 normal = getNormal(cur);
			col = vec3(.6, .68, .8);
			col+= dot(normal, lightDir)+vec3(.1);
			break;
		}
		cur += rd*d;
	}

	return vec4( col, 1.0 );

}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    fragColor = render(fragCoord);
}

// Hue to RGB conversion from Fabrice's shadertoyunofficial blog:
#define hue2rgb(h) 0.6 + 0.6 * cos(6.3 * h + vec3(0.0, 23.0, 21.0))

float mapVolume(in vec3 p) {
    float volume = dot(sin(0.5 * p), vec3(1.0));
    float layers = sin(3.0 * volume - 2.0 * iTime);
    return max(layers, volume);
}

float mapScene(in vec3 p) {
    p /= 0.75;
    vec3 cell = floor(p) * 0.75;
    vec3 local = fract(p) * 0.75;
    return (length(local - 0.325) + 0.3 * min(0.0, mapVolume(cell + 0.325))) * 0.6;
}

vec3 getNormal(in vec3 p) {
    return normalize(vec3(mapScene(p + vec3(0.001, 0.0, 0.0)) - mapScene(p - vec3(0.001, 0.0, 0.0)),
                          mapScene(p + vec3(0.0, 0.001, 0.0)) - mapScene(p - vec3(0.0, 0.001, 0.0)),
                          mapScene(p + vec3(0.0, 0.0, 0.001)) - mapScene(p - vec3(0.0, 0.0, 0.001))));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
