float mapPlane(vec3 p)
{
    float r = 0.5;
    float l = p.y + (r);
    return l;
}
float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}
float quadEq(float t)
{
    return 1.0*t*t+t+0.01;
}
float mapCube(vec3 p, float r)
{
    //p=p-vec3(0.5,0.47,2.0);
    p = p - vec3(0.4,0.4,0.4);
    vec3 t = vec3(quadEq(p.x),quadEq(p.y),quadEq(p.z));
    return length(t)-r;
}
float map(vec3 p)
{

	float sp = sdBox(p,vec3(0.5,0.5,0.5));
    return sp;
}

Ob map1(vec3 p)
{
    Ob o;
    float tme = iTime*0.5;
    vec3 pt2 = vec3(0.1*cos(tme),0.0,0.3*sin(tme));
    vec3 pt1 = vec3(0.3*sin(tme),0.0,0.1*cos(tme));

    vec3 mov = p-pt1;
    vec3 mov1 =  p-pt2;
	float sp = mapCube(p,0.25);
    float p1 = length(mov)-0.02;
    float p2 =  length(mov1)-0.02;

    o.t = min(min(p1,p2),sp);

    o.ptL.pos = min(mov,mov1);

    vec3 A= ptLight2Col*(1.0-p2)+ptLight1Col*p2;
    vec3 B= ptLight1Col*(1.0-p1)+ptLight2Col*p1;

    o.ptL.col = mix(A,B,0.5);

    if (sp < p1 && sp < p2)
    {
        o.c = vec3(0.1,0.1,0.2);
        o.prop = CUBE;

    }
    if (p1 < sp  && p1 < p2)
    {
        o.c = ptLight1Col;
        o.prop = PTL;

    }
    else if (p2 < sp && p2 < p1)
    {
        o.c = ptLight2Col;
        o.prop = PTL;
    }
    return o;
}

vec3 calcNormal(vec3 pos)
{
	vec2 e = vec2(0.0001,0.0);
	return normalize(vec3(map(pos+e.xyy)-map(pos-e.xyy),
						  map(pos+e.yxy)-map(pos-e.yxy),
						  map(pos+e.yyx)-map(pos-e.yyx)));
}


float inCastRay(in vec3 ro, vec3 rd)
{
	float t =0.0;
	for(int i=0;i<100;i++)
	{
		vec3 pos = ro+t*rd;
		float h = map(pos);
		if (h<0.001)
			break;
		t+=h;
		if (t>20.0) break;
	}
	if (t>20.0) t=-0.1;
	return t;
}

void castRay(in vec3 ro, vec3 rd,inout Ob o)
{
    float t =0.0;
    for(int i=0;i<100;i++)
    {
        vec3 pos = ro+t*rd;
        o = map1(pos);
        float h = o.t;
        if (h<0.001)
            break;
        t+=h;
        if (t>20.0) break;
    }
    if (t>20.0) t=-0.1;
    o.t =t;
}


