float box(vec2 coord, vec2 uv, float size, float w){
   uv = 1.-uv;
   coord.x = 1.-coord.x;
   vec2 b = step(coord, uv) - step(coord,uv-size);
   vec2 b2 = step(coord+w, uv) - step(coord,uv-(size-w));
   return b.x * b.y - b2.x*b2.y;
}

