float de(vec3 pos)
{
	float time=iTime;
	z=pos;
	float O=7.;
	float sc=1.;
	float tsc=pow(Scale,float(Iterations));
	float t=time*Speed*10./tsc+100.;
	float amp1=Amplitude;
	float amp2=amp1*1.1256;
	float amp3=amp1*1.0586;
	float amp4=amp1*0.9565;
	float l1=length(z.xy-vec2(O*1.1586,0));
	float l2=length(z.xy+vec2(O*.98586,0));
	float l3=length(z.xy+vec2(0,O*1.13685));
	float l4=length(z.xy-vec2(0,O));
	for (int n=0; n<Iterations ; n++) {
		z+=sin(length(z.xy)*sc*Wavelength-t)*amp1/sc*2.;
		z+=sin(l1*sc*Wavelength-t)*amp1/sc;
		z+=sin(l2*sc*Wavelength-t)*amp2/sc;
		z+=sin(l3*sc*Wavelength-t)*amp3/sc;
		z+=sin(l4*sc*Wavelength-t)*amp4/sc;
		t=t*Scale*Scale;
		sc*=Scale;
	}
	//z.z*=3.;
	float wd=-z.z+2.;
	return length(z)-6.;
}

float T(float d) {
	return sin(13.4+.1*sin(iTime*2.)+d)*.2+.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= .5;
    uv.y -= .02;
    vec3 col = vec3(0.1,0.1*T(uv.x),0.1*T(uv.y));

    for(float i=1.; i<5.; i=i+.04){
        float d = length(uv+i/20.*T(i)-i*.017);
        float inner = 0.1 * i * T(i);
        float outer = 0.14 * i * T(i);
        float shado = 0.4 * i * T(i);
    	float cI = smoothstep(inner, inner+0.001, d);
    	float cO = smoothstep(outer, outer+0.001, d);
        col += mix(cI, cO, .7)*.01;
    	float s = smoothstep(shado, shado*1.724, d);
        col += mix(cO, s, .9)*.24;
    }

    col = clamp(col, 0., 1.);

    // vignette
    uv *= .95;
    uv.x *= 4.5;
    uv.y -= .48;
    col *= smoothstep(-.95, .0, uv.y);
    col *= smoothstep(0., -.95, uv.y);
    col *= smoothstep(-5., 1., uv.x);
    col *= smoothstep(5., -1., uv.x);
    col += col*4.;

    fragColor = vec4(col,1.0);
}

