bool solveQuadratic(float a, float b, float c, out float t0, out float t1)
{
    float disc = b * b - 4. * a * c;

    if (disc < 0.)
    {
        return false;
    } 

    if (disc == 0.)
    {
        t0 = t1 = -b / (2. * a);
        return true;
    }

    t0 = (-b + sqrt(disc)) / (2. * a);
    t1 = (-b - sqrt(disc)) / (2. * a);
    return true;    
}


