void lookAt(inout vec3 rd,vec3 ro,vec3 ta,vec3 up){
    vec3 w=normalize(ta-ro),u=normalize(cross(w,up));
    rd=rd.x*u+rd.y*cross(u,w)+rd.z*w;
}

void rot(inout vec3 p,vec3 a,float t){
	a=normalize(a);
	vec3 u=cross(a,p),v=cross(a,u);
	p=u*sin(t)+v*cos(t)+a*dot(a,p);   
}

float mabs(float p)
{
    return abs(p)+exp(-p*p*8.);
}

void sfold45(inout vec2 p)
{
    vec2 v=normalize(vec2(1,-1.03));
    float g=dot(p,v);
    p-=(g-mabs(g))*v;
}

float map(vec3 p){
	rot(p,vec3(cos(iTime*.03),sin(iTime*.02),sin(iTime*.05)*.5),iTime*.5);
	float s=4.;
	p=abs(p);
    vec3 q=p;
	for(int i=0;i<6;i++)
	{
	    p-=clamp(p,-1.,1.)*2.;
    	sfold45(p.xz);
		sfold45(p.yz);
		p*=-2.;
        p-=q*2.5;
        s*=2.;
  	}
  	return length(p.xy)/s-.1;
}

vec3 calcNormal(vec3 p)
{
  vec3 n=vec3(0);
  for(int i=0; i<4; i++){
    vec3 e=.001*(vec3(9>>i&1, i>>1&1, i&1)*2.-1.);
    n+=e*map(p+e);
  }
  return normalize(n);
}

float march(vec3 ro, vec3 rd, float near, float far)
{
    float t=near,d;
    for(int i=0;i<150;i++)
    {
        t+=d=map(ro+rd*t);
        if (d<.001) return t;
        if (t>=far) return far;
    }
    return far;
}

vec3 doColor(vec3 p)
{
    return (cos(vec3(7,6,4)+p*.2)*.5+.5)*2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy*2.-iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0,0,15);
    vec3 rd = normalize(vec3(uv,2));
    vec3 ta =vec3(0);
    lookAt(rd,ro,ta,vec3(0,1,0));    
    vec3 col= vec3(0,0,.03);
	const float maxd=30.;
    float t=march(ro,rd,0.,maxd);
    if(t<maxd)
    {
        vec3 p=ro+rd*t;
        col=doColor(p); 
        vec3 n=calcNormal(p);      
		vec3 lightPos=ro+vec3(2,5,2);
    	vec3 li=lightPos-p;
		float len=length(li);
		li/=len;
		float dif=clamp(dot(n,li),0.,1.);
        col*=max(dif,.2);
        float rimd=pow(clamp(1.-dot(reflect(-li,n),-rd),0.,1.),2.5);
		float frn=rimd+2.2*(1.-rimd);
    	col*=frn*.6;
        col*=max(.5+.5*n.y,.3);
        col*=exp2(-4.*pow(max(0.,1.-map(p+n*.8)/.8),2.));
        //col+=vec3(.8,.6,.2)*pow(clamp(dot(reflect(rd,n),li),0.,1.),100.);
        col+=vec3(11.2,.6,.2)*pow(clamp(dot(reflect(rd,n),li),0.,1.),8.); 
    }
    fragColor.xyz=col;
}

float noise(in vec2 p) {
    return fract(sin(p.x * 17.6452 + p.y * 565.337) * 5425.235);
}

float SmoothNoise(in vec2 p) {
    vec2 luv = smoothstep(0.0, 1.0, fract(p)); // :P
    vec2 cid = floor(p);

    float bl = noise(cid);
    float br = noise(cid + vec2(1.0, 0.0));
    float tl = noise(cid + vec2(0.0, 1.0));
    float tr = noise(cid + vec2(1.0, 1.0));

    return mix(mix(bl, br, luv.x), mix(tl, tr, luv.x), luv.y);
}

float CoherentNoise(in vec2 p, in float scale, in float octaves) {
    float value = 0.0;
    float nscale = 1.0;
    float tscale = 0.0;

    for (float octave=0.0; octave < octaves; octave++) {
        value += SmoothNoise(p * pow(2.0, octave) * scale) * nscale;
        tscale += nscale;
        nscale *= 0.5;
    }

    return value / tscale;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 ro = vec3(0.0, 0.0, 5.0);
    vec3 rd = normalize(vec3(uv, -1.0));
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    float dt = 0.0;
    for (int iter=0; iter < 50; iter++) {
        vec3 p = ro + rd * dt;

        float c = cos(iTime), s = sin(iTime);
        p.xz *= mat2(c, -s, s, c);

        float d = length(p) - 2.0 + 0.15 * length(vec3(CoherentNoise(p.xy, 10.0, 4.0), CoherentNoise(p.xz, 10.0, 4.0), CoherentNoise(p.yz, 10.0, 4.0)));
        if (d < 0.001) {
            p *= 0.25;
            vec3 n = normalize(p);
            vec3 l = normalize(vec3(-1.0, 1.0, 1.0));
            l.xz *= mat2(c, -s, s, c);
            fragColor.rgb += max(0.0, dot(n, l) * 0.75 * length(vec3(CoherentNoise(p.xy, 10.0, 4.0), CoherentNoise(p.xz, 10.0, 4.0), CoherentNoise(p.yz, 10.0, 4.0))));
            break;
        }

        if (dt > 10.0) {
            break;
        }

        dt += d;
    }
}

#define S(x,y,z) smoothstep(x,y,z)
#define debug(x) fragColor = vec4(vec3(x), 1.0); return
#define debug2(x) fragColor = vec4(vec2(x), 0.0, 1.0); return
#define debug3(x) fragColor = vec4(x, 1.0); return

const float PI = 3.14159;
const float TAU = PI * 2.0;
const float AA = 0.005;

float circle(vec2 uv, float r) {
    float d = length(uv);
    return S(r+AA, r-AA, d);
}

mat2 rot2D(float a) {
    float sa = sin(a), ca = cos(a);
    return mat2(ca, -sa, sa, ca);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 coords = uv * 2.0 - 1.0;
    coords.x *= iResolution.x / iResolution.y;
    coords = rot2D(-iTime* 2.) * coords;

    float numberOfDots = 8.0; 
    float angle = (atan(coords.y, coords.x) + PI) / TAU * numberOfDots;
    float rid = floor(angle);
    float baseAngle = rid / numberOfDots * TAU - PI;
    vec2 ruv = rot2D(baseAngle) * coords;

    //debug(rid / numberOfDots); // Debug radial ids
    //debug2(ruv);               // Debug radial uvs

    float sizeId = (numberOfDots - rid - 1.);
    float circleBaseRadius = 0.03;
    float circleDynRadius = sizeId * 0.01;
    float circleRadius = circleBaseRadius + circleDynRadius;
    float circleDistance = 0.4 - circleDynRadius;

    vec3 col = vec3(circle(ruv - vec2(circleDistance, circleRadius), circleRadius));

    fragColor = vec4(col, 1.0);
}

// Not really my code, original author and explanation:
//
// https://www.bidouille.org/prog/plasma

void mainImage( out vec4 fragColor, in vec2 fragCoord )
  {
    vec2 uv = fragCoord.xy / iResolution.xy;

    // pattern a
    float a = sin(uv.x * 12. + iTime);
    // pattern b
    float b = sin((uv.x * sin(iTime * 0.2) + uv.y * cos(iTime * .2)) * 20.);
    // pattern c
    float x = uv.x + sin(iTime) * 0.2 - .5;
    float y = uv.y + sin(iTime) * 0.2 - .5;
    float c = sin(sqrt((pow(x, 2.) + pow(y, 2.))) * 20. + iTime * 0.01);

    // final
    vec3 color1 = vec3(.5 + .5 * sin(iTime), 0.3, 0.);
    vec3 color2 = vec3(0., 0.3, .5 + .5 * cos(iTime));

    vec3 col = mix(color1, color2, a + b + c);

    fragColor = vec4(col, 1.);
  }

/*
Big thank you to Inigo Quilez's 2D distance function and smooth minimum articles!
2D distance functions article: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
Smooth minimum article: https://iquilezles.org/www/articles/smin/smin.htm
The smin(), ellipse(), and line() functions are taken from them.
*/

const float SIN_20  =  0.34202014332;
const float COS_20  =  0.93969262078;

const float SIN_40  =  0.64278760968;
const float COS_40  =  0.76604444311;

const float SIN_150 =  0.50000000000;
const float COS_150 = -0.86602540378;

const float SIN_315 = -0.70710678118;
const float COS_315 =  0.70710678118;

float noise(in float x) {
    return fract(sin(x * 12.5673) * 573.123);
}

float snoise(in float x) {
    const float r = 2.735;
    x += 4657.247;
    float c = floor(x / r) * r;
    float l = fract(x / r); l *= l * (3.0 - 2.0 * l);
    return mix(noise(c), noise(c + r), l);
}

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

float ellipse(in vec2 p, in vec2 r) {
    float k0 = length(p / r);
    float k1 = length(p / (r * r));
    return k0 * (k0 - 1.0) / k1;
}

float isosceles(in vec2 p, in float b, in float h) {
    p.x = abs(p.x), p.y += h / 3.0;
    vec2 pr = p - vec2(b, 0.0), tr = vec2(-b, h);
    vec2 be = p - vec2(b * clamp(p.x / b, 0.0, 1.0), 0.0);
    vec2 re = pr - tr * clamp(dot(pr, tr) / dot(tr, tr), 0.0, 1.0);
    return sqrt(min(dot(be, be), dot(re, re))) * sign(p.x - b / h * (h - p.y) * sign(p.y));
}

float line(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float unit = 2.0 / iResolution.y;
    vec3 color = vec3(0.25, 0.25, 1.0);

    // Snowy hill
    vec2 hillUV = uv;
    hillUV.x *= 0.3;
    hillUV.y += 0.05;
    float hill = hillUV.y + hillUV.x * hillUV.x;
    color = mix(color, vec3(1.0, 1.0, 1.0), smoothstep(unit, 0.0, hill));
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(hill)));

    // Tree trunk
    vec2 trunkUV = uv;
    trunkUV.x += 0.4;
    float trunk = max(abs(trunkUV.x) - 0.04, abs(trunkUV.y) - 0.2);
    color = mix(color, vec3(0.6, 0.3, 0.0), smoothstep(unit, 0.0, trunk));
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(trunk)));

    // Tree branches
    vec2 treeUV = uv;
    treeUV -= vec2(-0.4, 0.07);
    float treeUnit = unit;
    for (int i=0; i < 3; i++) {
        float branches = isosceles(treeUV, 0.25, 0.3);
        color = mix(color, vec3(0.0, 1.0, 0.0), smoothstep(treeUnit, 0.0, branches));
        color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(treeUnit, 0.0, abs(branches)));
        treeUV.y -= 0.15;
        treeUV *= 1.5;
        treeUnit *= 1.5;
    }

    // Snowman body
    vec2 snowmanUV = uv - vec2(0.1, -0.2);
    float snowmanDist = 1000000.0;
    float scale = 1.0;
    for (int i=0; i < 3; i++) {
        float snowball = (length(snowmanUV) - 0.125) / scale;
        snowmanDist = min(snowmanDist, snowball);
        snowmanUV.y -= 0.175;
        snowmanUV *= 1.25;
        scale *= 1.25;
    }

    color = mix(color, vec3(1.0, 1.0, 1.0), smoothstep(unit, 0.0, snowmanDist));
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(snowmanDist)));

    // Snowman nose
    snowmanUV = uv - vec2(0.1, 0.085);
    snowmanUV.x = abs(snowmanUV.x);
    snowmanUV.y *= -1.0;
    float nose = isosceles(snowmanUV, 0.015, 0.06);
    color = mix(color, vec3(1.0, 0.6, 0.0), smoothstep(unit, 0.0, nose));
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(nose)));

    // Snowman eyes
    float eyes = length(snowmanUV - vec2(0.03, -0.05)) - 0.01;
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, eyes));

    // Snowman buttons
    float bbox = abs(snowmanUV.y - 0.15) - 0.1;
    snowmanUV.y -= 0.275;
    snowmanUV.y = mod(snowmanUV.y, 0.08) - 0.04;
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, max(length(snowmanUV) - 0.01, bbox)));

    // Charlie Brown's legs
    vec2 legsUV = uv - vec2(0.57, -0.15);
    legsUV.x = abs(legsUV.x);
    legsUV.x -= 0.06;

    float legs = max(abs(legsUV.x) - 0.03, abs(legsUV.y) - 0.1);
    color = mix(color, vec3(0.9, 0.702, 0.5), smoothstep(unit, 0.0, legs));
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(legs)));

    // Charlie Brown's pants
    float pants = max(abs(legsUV.x) - 0.045, abs(legsUV.y) - 0.04);
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, pants));

    // Charlie Brown's shoes
    vec2 shoesUV = uv - vec2(0.55, -0.25);
    shoesUV.x = abs(shoesUV.x) - 0.06;
    bbox = -(shoesUV.y + 0.01);
    float shoes = max(ellipse(shoesUV, vec2(0.055, 0.04)), bbox);
    color = mix(color, vec3(0.8, 0.5, 0.0), smoothstep(unit, 0.0, shoes));
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(shoes)));

    // Charlie Brown's arms
    vec2 armsUV = uv;
    armsUV -= vec2(0.565, -0.08);

    armsUV.x = abs(armsUV.x);
    armsUV *= mat2(COS_20, SIN_20, -SIN_20, COS_20);
    armsUV.x -= 0.09 + 0.05 * sin(8.0 * armsUV.y + 1.57);

    float arms = line(armsUV, vec2(0.0, 0.1), vec2(0.0, -0.1)) - 0.0275;
    color = mix(color, vec3(0.9, 0.702, 0.5), smoothstep(unit, 0.0, arms));
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(arms)));

    // Charlie Brown's shirt
    vec2 shirtUV = uv - vec2(0.565, -0.09);
    bbox = -(shirtUV.y + 0.075);
    float shirt = max(ellipse(shirtUV, vec2(0.125, 0.2)), bbox);

    // Charlie Brown's neck
    vec2 neckUV = shirtUV;
    neckUV.y -= 0.2;
    float neck = max(abs(neckUV.x) - 0.035, abs(neckUV.y) - 0.05);
    color = mix(color, vec3(0.9, 0.702, 0.5), smoothstep(unit, 0.0, neck));
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(neck)));

    // Charlie Brown's head
    vec2 headUV = shirtUV;
    headUV.y -= 0.32;
    float head = length(headUV) - 0.1;

    // Charlie Brown's ears
    vec2 earsUV = headUV;
    earsUV.x = abs(earsUV.x) - 0.095;
    float ears = length(earsUV) - 0.025;
    head = min(head, ears);

    color = mix(color, vec3(0.9, 0.702, 0.5), smoothstep(unit, 0.0, head));
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(head)));

    // Charlie Brown's eyes
    vec2 eyesUV = headUV;
    eyesUV.y -= 0.02;
    eyesUV.x = abs(eyesUV.x) - 0.04;
    eyes = length(eyesUV) - 0.01;
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, eyes));

    // Charlie Brown's hair
    vec2 hairUV = eyesUV;
    hairUV -= vec2(-0.0175, 0.05);
    bbox = hairUV.y;
    float hair = max(abs(ellipse(hairUV, vec2(0.0225, 0.02))), bbox);
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, hair));

    // Charlie Brown's nose
    vec2 noseUV = headUV;
    noseUV.y += 0.015;
    nose = max(abs(length(noseUV) - 0.015), -dot(noseUV, vec2(COS_315, SIN_315)) - 0.004);
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, nose));

    // Charlie Brown's mouth
    vec2 mouthUV = headUV;
    mouthUV.y += 0.04;
    float mouth = max(abs(ellipse(mouthUV, vec2(0.045, 0.03))), noseUV.y + 0.025);
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, mouth));

    // Charlie Brown's sleeves
    vec2 sleevesUV = shirtUV;
    sleevesUV.y -= 0.135;
    sleevesUV.x = abs(sleevesUV.x) - 0.08;
    sleevesUV *= mat2(COS_40, SIN_40, -SIN_40, COS_40);

    bbox = -(sleevesUV.y + 0.04);
    float sleeves = max(ellipse(sleevesUV, vec2(0.04, 0.08)), bbox);
    shirt = smin(shirt, sleeves, 0.01);

    color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, shirt));
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(shirt)));

    // Black zigzag masked against the shirt
    shirtUV.y -= 0.01;
    shirtUV.y *= 8.0;
    float zigzag = abs(shirtUV.y - abs(round(fract(shirtUV.x * 10.0)) - fract(10.0 * shirtUV.x)));
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, max((zigzag - 0.2) / 8.0, shirt)));

    // Snoopy's body
    vec2 bodyUV = uv;
    bodyUV += vec2(0.2, 0.35);
    float body = ellipse(bodyUV, vec2(0.08, 0.06));

    // Snoopy's arm
    vec2 armUV = bodyUV;
    armUV *= mat2(COS_20, -SIN_20, SIN_20, COS_20);
    armUV.x -= 0.02;
    armUV.y += 0.02 * sin(armUV.x * 15.0 + 1.57);
    float arm = line(armUV, vec2(-0.04, 0.0), vec2(0.06, 0.0)) - 0.02;

    // Snoopy's left leg
    vec2 leftLegUV = bodyUV;
    leftLegUV -= vec2(0.075, 0.01);
    float leftLeg = max(max(abs(leftLegUV.x) - 0.05, abs(leftLegUV.y) - 0.0125), -body);

    // Snoopy's left foot
    leftLegUV -= vec2(0.05, 0.01);
    float leftFoot = ellipse(leftLegUV, vec2(0.02, 0.045));
    leftLeg = min(leftLeg, leftFoot);

    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(leftLeg)));

    // Snoopy's right leg
    vec2 rightLegUV = leftLegUV;
    rightLegUV += 0.03;
    float rightLeg = max(max(abs(rightLegUV.x) - 0.03, abs(rightLegUV.y) - 0.0125), -body);

    // Snoopy's right foot
    rightLegUV -= vec2(0.04, 0.01);
    float rightFoot = ellipse(rightLegUV, vec2(0.02, 0.045));
    rightLeg = min(rightLeg, rightFoot);

    color = mix(color, vec3(1.0, 1.0, 1.0), smoothstep(unit, 0.0, rightLeg));
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(rightLeg)));

    // Snoopy's neck
    bodyUV.x += 0.04;
    neck = max(abs(bodyUV.x) - 0.075, abs(bodyUV.y) - 0.015);
    body = smin(body, neck, 0.02);

    // Snoopy's collar
    vec2 collarUV = bodyUV;
    collarUV.x += 0.055;
    float collar = max(abs(collarUV.x) - 0.005, abs(collarUV.y) - 0.025);
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, collar));

    // Snoopy's head
    bodyUV += vec2(0.12, 0.01);
    head = length(bodyUV) - 0.05;
    body = smin(body, head, 0.02);

    // Snoopy's snout
    bodyUV -= vec2(0.01, 0.075);
    float snout = length(bodyUV) - 0.03;
    body = smin(body, snout, 0.05);

    // Snoopy's mouth
    mouthUV = bodyUV;
    mouthUV -= vec2(0.018, -0.03);
    bbox = dot(mouthUV, vec2(COS_150, SIN_150));
    mouth = max(abs(ellipse(mouthUV, vec2(0.015, 0.03))), bbox);
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, mouth));

    // Only an outline is drawn because the snow provides the inside :P
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(body)));

    // Snoopy's arm is drawn after the body to properly layer it onto the scene
    color = mix(color, vec3(1.0, 1.0, 1.0), smoothstep(unit, 0.0, arm));
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(arm)));

    // Snoopy's right ear
    earsUV = bodyUV;
    earsUV += vec2(0.05, 0.125);
    earsUV *= mat2(COS_40, -SIN_40, SIN_40, COS_40);
    float rightEar = ellipse(earsUV, vec2(0.02, 0.05));
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, rightEar));

    // Snoopy's left ear
    earsUV -= vec2(-0.04, 0.03);
    earsUV *= mat2(COS_40, SIN_40, -SIN_40, COS_40);
    earsUV.x += 0.025;
    earsUV = earsUV.yx;
    float leftEar = max(ellipse(earsUV, vec2(0.02, 0.05)), -body);
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, leftEar));

    // Snoopy's eye
    bodyUV += vec2(0.03, 0.06);
    float eye = length(bodyUV) - 0.005;
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, eye));

    // Snoopy's nose
    bodyUV -= vec2(0.03, 0.095);
    nose = max(length(bodyUV) - 0.01, -snout);
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, nose));

    // Snowflakes
    float screenRight = 0.5 * iResolution.x / iResolution.y;
    float snowflakesDist = 1000000.0;
    for (float column=-screenRight; column < screenRight; column += 0.2) {
        vec2 columnUV = uv - vec2(column, 0.0);
        columnUV.y += snoise(floor(column / 0.2)) * iTime;
        columnUV.x += 0.2 * sin(floor(columnUV.y / 0.2) + iTime);
        columnUV.y = mod(columnUV.y, 0.2) - 0.1;
        snowflakesDist = min(snowflakesDist, length(columnUV) - 0.015);
    }

    color = mix(color, vec3(1.0, 1.0, 1.0), smoothstep(unit, 0.0, snowflakesDist));
    color = mix(color, vec3(0.0, 0.0, 0.0), smoothstep(unit, 0.0, abs(snowflakesDist)));

    fragColor = vec4(color, 1.0);
}

#define time (iTime/60.0)*140.0
#define mod01 floor(mod(time * 4.0, 16.0))
#define mod02 floor(mod(time * 2.0, 8.0))

float gg = 0.0;

mat2 rot(float a)
{
    float ca = cos(a);
    float sa = sin(a);
    return mat2(ca, sa, -sa, ca);
}

struct matter
{
 	float m;
    int type;
    bool reflected;
};

float box(vec3 p, vec3 s)
{
	p = abs(p) - s;
    return max(p.x, max(p.y, p.z));
}

float sphere(vec3 p, float s)
{

    return length(p) - s;
}

vec3 kifs(vec3 p)
{
    float t1 = 0.5 + (time * 0.1 * 0.25);
    float s = 2.0;

    for(int i = 0; i < 4; ++i)
    {
     	p.yz *= rot(t1 + float(i) * 0.1);
        p.yz = abs(p.xz);
        p.yz -= s;
    }



    return p;
}

void map(inout matter mat, vec3 p)
{
	float mat01, mat02, mat03;





    p.xz *= rot(time * 0.2) * 1.05;
    p.yz *= rot(time * 0.15) * 1.15;
    p.xy *= rot(time * 0.05);

    vec3 p01 = p, p02 = p, p03 = p, p04 = kifs(p);

    mat02 = box(p, vec3(0.55 + 0.45 * abs(sin(time * 0.25))));
    //mat02 = min(mat02, -box(p, vec3(3.5)));

    if(mat02 < 0.01)
    {
     	mat.type = 1;   
    }

    if(mod(mod02, 4.0) <= 1.0)
    	mat01 = box(p, vec3(1.05 + 0.10 * mod02));
    if(mod(mod02, 4.0) > 1.0)
        mat01 = sphere(p, 1.05 + 0.10 * mod02);

    float rep01 = 0.45;

    p02.y = (fract(abs(p04.y + time * 0.05) / rep01 - 0.5) - 0.5) * rep01;
    float id = (floor(abs(p.y) / rep01 - 0.5) - 0.5) * rep01 * (16.0 * abs(sin(time * 0.5)));

    p03.y = (fract(abs(p04.y + time * 0.05) / rep01 - 0.5) - 0.5) * rep01;
    float id02 = (floor(abs(p.y) / rep01 - 0.5) - 0.5) * rep01 * (16.0 * abs(sin(time * 0.5)));

   	/*p02.xz *= rot(time * 0.2);
    p02.yz *= rot(time * 0.1);
    p02.xy *= rot(time * 0.1);*/

    mat01 = max(mat01, -box(p02 - vec3(0.5 * sin(p.x * 0.2), 0.0, 0.0), vec3(10.0, 0.15 + (sin(p.x * 0.1 * id * mod01) * 0.25) + (sin(p.z * 0.1) * 0.25), 10.0)));

    mat01 = max(mat01, -box(p02 - vec3(0.0, 0.0, 0.0), vec3(0.15 + (sin(p.z * 0.1 * id * mod01) * 0.25) + (sin(p.z * 0.1) * 0.25),10.0, 10.0)));


    gg += 0.15/(0.11+abs(mat01));

    mat.m = min(mat01, mat02);
}

vec3 normals(vec3 p)
{
 	vec2 uv = vec2(0.01, 0.0);

    matter m01,m02,m03,m04;

    map(m01, p);
    map(m02, p - uv.xyy);
    map(m03, p - uv.yxy);
    map(m04, p - uv.yyx);

    return normalize(m01.m - vec3(m02.m, m03.m,m04.m));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv /= vec2(iResolution.y/iResolution.x, 1.0);

    vec3 o = vec3(0.0, 0.0, 4.0), t= vec3(0.0);
    vec3 fr = normalize(t-o);
    vec3 ri = normalize(cross(vec3(0.0, 1.0, 0.0), fr));
    vec3 up = normalize(cross(fr, ri));
    vec3 dir = normalize(fr + uv.x * ri + uv.y * up);
    vec3 p = o + dir * 0.5;

   	matter mat;
    vec3 col = vec3(0.0);
    for(int i = 0; i < 150; ++i)
    {
        //p.xy *= rot(0.001);

     	map(mat, p);

        if(mat.m < 0.01)
        {
            if(mat.type == 1)
            {
                vec3 n = normals(p);
                dir = reflect(dir, -n);

                mat.m = 0.15;
                mat.type = 0;
                mat.reflected = true;
            }
         	mat.m = 0.1;
        }

        if(mod(mod02, 4.0) <= 1.0)
        	col += gg * 0.00030 * vec3(0.0, 0.5, 1.0);
        if(mod(mod02, 4.0) > 1.0)
        	col += gg * 0.00030 * vec3(1.0, 0.5, 0.0);

        p += dir * mat.m * 0.5;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

/*

