#define PI 3.141592654

vec2 rot(vec2 p,float a)
{
    float c=cos(a*15.83);
    float s=sin(a*15.83);
    return p*mat2(s,c,c,-s);
}

void mainImage(out vec4 o, in vec2 uv)
{
    uv/=iResolution.xx;
    uv=vec2(.125,.75)+(uv-vec2(.125,.75))*.03;
    float T=iTime*.25;

    vec3 c = clamp(1.-.7*vec3(
        length(uv-vec2(.1,0)),
        length(uv-vec2(.9,0)),
        length(uv-vec2(.5,1))
        ),0.,1.)*2.-1.;

    vec3 c0=vec3(0);
    float w0=0.;
    const float N=16.;
    for(float i=0.;i<N;i++)
    {
        float wt=(i*i/N/N-.2)*.3;
        float wp=0.5+(i+1.)*(i+1.5)*0.001;
        float wb=.05+i/N*0.1;
    	c.zx=rot(c.zx,1.6+T*0.65*wt+(uv.x+.7)*23.*wp);
    	c.xy=rot(c.xy,c.z*c.x*wb+1.7+T*wt+(uv.y+1.1)*15.*wp);
    	c.yz=rot(c.yz,c.x*c.y*wb+2.4-T*0.79*wt+(uv.x+uv.y*(fract(i/2.)-0.25)*4.)*17.*wp);
    	c.zx=rot(c.zx,c.y*c.z*wb+1.6-T*0.65*wt+(uv.x+.7)*23.*wp);
    	c.xy=rot(c.xy,c.z*c.x*wb+1.7-T*wt+(uv.y+1.1)*15.*wp);
        float w=(1.5-i/N);
        c0+=c*w;
        w0+=w;
    }
    c0=c0/w0*2.+.5;//*(1.-pow(uv.y-.5,2.)*2.)*2.+.5;
    c0*=.5+dot(c0,vec3(1,1,1))/sqrt(3.)*.5;
    c0+=pow(length(sin(c0*PI*4.))/sqrt(3.)*1.0,20.)*(.3+.7*c0);
	o=vec4(c0,1.0);
}

#define MAX_STEPS 100
#define MAX_DIST 100.f
#define SURF_DIST .01f

float sdCapsule(vec3 p, vec3 a, vec3 b, float r) {
	vec3 ab = b - a;
	vec3 ap = p - a;

	float t = dot(ab, ap) / dot(ab, ab);
	t = clamp(t, 0., 1.);

	vec3 c = a + t*ab;
	return length(p-c) - r;
}

float sdTorus(vec3 p, vec2 r) {
	float x = length(p.xz) - r.x;
	return length(vec2(x, p.y)) - r.y;
}

float dBox(vec3 p, vec3 hsize) {
	return length(max(abs(p) - hsize, 0.f));
}

float smin( float a, float b, float k )
{
	float h = max(k-abs(a-b),0.0);
	return min(a, b) - h*h*0.25/k;
}

float smax( float a, float b, float k )
{
	float h = max(k-abs(a-b),0.0);
	return max(a, b) + h*h*0.25/k;
}

float sdEllipsoid( vec3 p, vec3 r )
{
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdCappedCylinder( vec3 p, float r, float h )
{
  vec2 d = abs(vec2(abs(length(p.xz)),p.y)) - vec2(h,r);
  d.x = abs(d.x) - 0.01;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdCappedTorus(vec3 p, vec2 sc, float ra, float rb)
{
//  p.x = abs(p.x);
  p.z = -p.z;
  float k = (sc.y*p.z>sc.x*p.y || p.z < 0.) ? dot(p.zy,sc) : length(p.zy);
  return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

float GetDist(vec3 p) {
    //p = p//* vec3(0.8, 1.2, 0.8);
    vec3 c = p - vec3(0., 1.0, 0.);

	//float d = sdCappedCylinder(c, 0.3, 0.3);

    float bl1 = sdEllipsoid(c-vec3(0.2, 0., 0.), vec3(0.25, 0.35, 0.25));
    float bl2 = sdEllipsoid(c-vec3(-0.2, 0., 0.), vec3(0.25, 0.35, 0.25));
    float bls = smin(bl1, bl2, 0.2);

    float r = 1.3 + sin(iTime)/4.;

    float pn = sdCappedTorus(c-vec3(0., 0.15 - r, 0.), vec2(.8, 0.6), r, 0.2);

    float d = smin(bls, pn, 0.1);

	return d;
}

vec2 RayMarch(vec3 ro, vec3 rd) {
	vec2 res = vec2(0.0,-1.0);

    float tmin = 0.5;
    float tmax = 20.0;

#if 1
	// raytrace bounding plane
	float tp = (3.5-ro.y)/rd.y;
	if( tp>0.0 ) tmax = min( tmax, tp );
#endif

	// raymarch scene
	float t = tmin;
	for( int i=0; i<256 && t<tmax; i++ )
	{
		vec2 h = vec2(GetDist(ro+rd*t), 0.);
		if(abs(h.x)<(0.0005*t))
		{
			res = vec2(t,h.y);
			break;
		}
		t += h.x;
	}

	return res;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
	vec2 e = vec2(0.01, 0);

	vec3 n = d - vec3(
		GetDist(p - e.xyy),
		GetDist(p - e.yxy),
		GetDist(p - e.yyx));

	return normalize(n);
}

