float rand2(vec2 p) {
	return fract(sin(dot(p, vec2(12.5322, 57.2115)))*65231.1264);
}

float distBox(vec3 p, float size) {
	return length(max(abs(p)-size, 0.))-size*0.5;
}

const float num = 5.;
float interval_xy;
float interval_z = 15.;
float dist(vec3 p) {
	float d = 1e5;
	vec3 z;
	p.xy = mod(p.xy, interval_xy) - 0.5*interval_xy;
	for(float i=0.; i<num; i++) {
		z = p;
		z.z -= interval_z/num*i;
		z.z = mod(z.z, interval_z) - 0.5*interval_z;

		float size = 0.1 + abs(sin(iTime*4.+pi/num*i))*0.5;
		d = min(d, distBox(z, size));
	}
	return d;
}

vec3 getNormal(vec3 p) {
	float d = 0.0001;
	return normalize(vec3(dist(p+vec3(d,0,0))-dist(p+vec3(-d,0,0)),
			      dist(p+vec3(0,d,0))-dist(p+vec3(0,-d,0)),
			      dist(p+vec3(0,0,d))-dist(p+vec3(0,0,-d))
			      ));
}

float exp2Fog(float distance, float density) {
	float s = distance * density;
	return exp(-s*s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = (fragCoord * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
	vec3 color=vec3(1);

	vec3 cPos = vec3(0, 0, 5);
	vec3 cDir = vec3(0, 0, -1);
	vec3 cUp = vec3(0, 1, 0);
	cUp.xy *= rotate(iTime*0.1);
	vec3 cSide = cross(cDir, cUp);

	vec3 lightDir = normalize(vec3(-1, 1, 2));
	lightDir.xy *= rotate(iTime*0.1);

	float targetDepth = 2.5;
	vec3 ray = normalize(p.x*cSide + p.y*cUp + cDir*targetDepth);

    interval_xy = 5.+sin(iTime)*2.5;

	float distance = 0.;
	vec3 rPos = cPos;
	for(int i=0; i<100; i++) {
		distance = dist(rPos);
		if(abs(distance) < 0.001) {
			break;
		}
		rPos += ray * distance;
	}

    if(distance < 0.001) {
        vec3 normal = getNormal(rPos);
        vec3 Pos = rPos;

        Pos.xy = mod(Pos.xy, interval_xy)-0.5*interval_xy;
        vec2 id = rPos.xy - Pos.xy;
        id /= interval_xy;
        float r= rand2(id*0.005+floor(iTime*2.));
        vec3 base = hsv2rgb(r, 1., 1.);

        Pos *= num;
        Pos.z = mod(Pos.z, interval_z)-0.5*interval_z;

        vec3 temp = abs(Pos);
        vec2 fPos;
        if(temp.x > temp.y && temp.x > temp.z) {
            fPos = Pos.yz;
        } else if(temp.y > temp.z && temp.y > temp.x) {
            fPos = Pos.zx;
        } else {
            fPos = Pos.xy;
        }
        fPos /= num;

        fPos *= rotate(iTime*2.);
        float s = 0.06;
        vec2 z;
        z = fPos;
        z = pmod(z, 3.);
        base *= s/abs(z.x-0.3);
        z = fPos*rotate(pi/3.);
        z = pmod(z, 3.);
        base *= s/abs(z.x-0.3);
        z = fPos;
        base *= s/length(z-normalize(z)*0.6);
        float spec = pow(max(dot(lightDir, normal), 0.), 30.);	

        color = base + spec;
    }

    float fog = exp2Fog(length(rPos-cPos), 0.01);
    color = mix(vec3(1), color, fog);

	fragColor = vec4(color, 1.0);
}

