#define ZOOM 0.25f  // less is more zoom
#define SPEED 0.125f
#define MAX_STEPS 64
#define EPSILON 0.000000001f

// for noise
#define sample_nearest iChannel0
#define sample_nearest_dimensions iChannelResolution[0].xy
// for normals from noise
#define sample_linear iChannel2
#define sample_linear_dimensions iChannelResolution[2].xy

const float SQRT_MAX_STEPS = -2.0f * sqrt(float(MAX_STEPS));
const vec2 light_start = vec2(0, -1); 
const float noise_start = -0.75f;
const float opacity_threshold = 0.5f;

float zoom = ZOOM;

float fetch_opacity(in sampler2D source, in vec2 dimensions, in vec2 uv)
{
    return(textureLod(source, (uv * 0.5f + 0.5f) * iResolution.xy / dimensions * zoom, 0.0f).r);
}

float fetch_opacity(in vec2 uv)
{
    return(fetch_opacity(sample_nearest, sample_nearest_dimensions, uv));
}

vec2 computeNormal(in sampler2D source, in vec2 dimensions, in vec2 uv)
{
    vec3 half_texel_offset = vec3(0.5f, 0.5f, 0.0f) / vec3(dimensions, 1.0f);

	vec2 gradient;	

	gradient.x = fetch_opacity(source, dimensions, uv - half_texel_offset.xz) - fetch_opacity(source, dimensions, uv + half_texel_offset.xz);
	gradient.y = fetch_opacity(source, dimensions, uv - half_texel_offset.zy) - fetch_opacity(source, dimensions, uv + half_texel_offset.zy);

	return( normalize(gradient) );
}

vec2 computeNormal(in vec2 uv)
{
    return(computeNormal(sample_linear, sample_linear_dimensions, uv));
}



const vec3 royal_blue_purple = vec3(0.222f,0.222f,1.0f) * vec3(0.80f,0.65f,1.0f);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float tT = iTime * SPEED;
    zoom = exp2(-(tT + (44.0f * SPEED)) * ZOOM * 2.0f);

    vec2 uv = (fragCoord/iResolution.xy) * 2.0f - 1.0f;

    vec2 ro = vec2(uv.x, uv.y + tT);
    vec2 rd = normalize(vec2(0, 1));

    float dt = 1.0f / length(iResolution.xy * rd);

    vec2 p = ro + rd * dt * fetch_opacity(uv); // jittered offset

    vec2 light_pos = light_start + vec2(sin(iTime), tT);

    float intensity = 1.0f;
    float light = 0.0f;
    float tran = 1.0f;
    for(int i = MAX_STEPS; i >= 0; --i) {

        vec2 ld = p - light_pos;
        float dist = length(ld);    
        float att = 1.0f / (1.0f + dist*dist);

        float lightAmount = att * intensity * 2.0f;

        float opacity = fetch_opacity(p);
        float sigmaS = lightAmount * (1.0f-opacity);
        float inv_sigmaE = 1.0f / max(EPSILON, sigmaS); // to avoid division by zero extinction

        float sigma_dt = exp2(sigmaS * SQRT_MAX_STEPS * dt);
        float Sint = (sigmaS - sigmaS * sigma_dt) * inv_sigmaE;

        light += tran * Sint;

	    tran *= sigma_dt;

        if (tran < 0.015f)
            break;

        if (opacity - opacity_threshold >= 0.0f) {

            vec2 n = computeNormal(p);
            rd = reflect(rd, n);

            intensity = max(0.0f, dot(normalize(ld), n)) * tran;
          //  light_pos = p;  // optional adds flicker
        }

        p += rd * dt;
    }

    vec3 color = vec3(light * (1.0f - tran));

    color = mix(color, mix(royal_blue_purple, color, tran), light);
    //color = vec3(rd, 0);
    //color = vec3(normal * 0.5f + 0.5f, opacity) * opacity;

    fragColor = vec4(color,1.0);
}

#define PI 3.14159265359

float plot(vec2 st, float pct){
    return smoothstep(pct - 0.02, pct, st.y) -
           smoothstep(pct, pct + 0.02, st.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;

    float y = sin(iTime + st.x * PI) + sin(iTime + st.y * PI);

    // Time varying pixel color
    vec3 color = vec3(y, sin(iTime), 1);
    float pct = plot(st, y);

    // Output to screen
    color = (1.0 - pct) * color + pct * vec3(0.44, cos(iTime), sin(iTime));
    fragColor = vec4(color, 1.0);
}

vec2 rotatexy(vec2 uv, float r) 
{
    float x1 = sin(r);
    float x2 = cos(r);
    mat2 m = mat2(x1,x2,-x2,x1);
    return uv * m;
}

float bump(float x)
{
    return smoothstep(-1., 0., x) - smoothstep(0., 1., x);
}

float smoothbump(float r, float dr, float d)
{
    return smoothstep(r-dr, r, d) - smoothstep(r, r+dr, d);
}

float hardbump(float r, float dr, float d) 
{
    return step(r, d) - step(r+dr, d);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // normalize aspect
    vec2 R = iResolution.xy;
    vec2 uv = ( R - 2.*fragCoord ) / R.y;

    vec2 uv0 = uv;
    float phi = atan(uv0.y, uv0.x) + iTime*0.1;
    float d0 = length(uv0);
    float d = -0.3 + d0 + 0.3*sin(5.*phi);
    //d  = d * smoothstep(0., 0.2, d0);
    //d  = d * step(0.1, d0);

    float r = .5;
    float dr = 200./R.x;

    float c1 = smoothbump(r, dr, d);
    float c2 = smoothbump(r*1.5, dr, d);
    float c3 = smoothbump(r/1.5, dr, d);

//    float c4 = .8 * bump((d-0.3)*1.5);
    float c4 = smoothbump(0.1, dr, d0);
//    c4 = c4 - c4*smoothstep(0.2, .3, d);
    //c4 = c4 * step(0.01, d);

    // Time varying pixel color
    vec3 col1 = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col2 = col1.zyx;
    vec3 col3 = col1.yzx;
    vec3 col4 = col1.zxy;

    vec3 col = col1 * c1 + col2 * c2 + col3 * c3 + col4 * c4;
    // Output to screen
    fragColor = vec4(col,1.0);
}

vec3 blackbody(float temp)
{
    float red   = 1.5 * temp;
    float green = 1.2 * pow(temp, 2.0);
    float blue  = pow(temp, 5.0);

    return vec3(red, green, blue);
}

#define NUM_WAVES_FLAMES 6
vec3 flames(in vec2 offset, in vec2 uv)
{
    float vWavesPeriod[NUM_WAVES_FLAMES] = float[NUM_WAVES_FLAMES] ( 7.0, 10.0, 15.0, 20.0, 25.0, 33.0 );
    float vWavesScroll[NUM_WAVES_FLAMES] = float[NUM_WAVES_FLAMES] ( 2.3, -1.3, 4.5, -5.9, 1.1, -9.7 );
    float vWavesWeight[NUM_WAVES_FLAMES] = float[NUM_WAVES_FLAMES] ( 2.2, 1.8, 1.4, 1.4, 1.2, 1.0 );

    float sum = 0.0;
    float totalWeight = 0.0;
    for (int i = 0; i < NUM_WAVES_FLAMES; ++i)
    {
        sum += (0.5 + 0.5 * sin(offset.x + uv.x * vWavesPeriod[i] + iTime * vWavesScroll[i])) * vWavesWeight[i];
        totalWeight += vWavesWeight[i];
    }

    sum /= totalWeight;

    float flameIntensity;
    if (uv.y <= offset.y)
    {
        flameIntensity = sum;
    }
    else
    {
        flameIntensity = sum - uv.y + offset.y;
    }

    flameIntensity = pow(clamp(flameIntensity, 0.0, 1.0), 1.0);
    return blackbody(flameIntensity);
}

#define NUM_FLAME_LAYERS 3
vec3 flameLayers(in vec2 uv)
{
    vec2  flameOffsets[NUM_FLAME_LAYERS] = vec2[NUM_FLAME_LAYERS] (vec2(-2.0, 0.0), vec2(0.0, 0.2), vec2(4.0, 0.4));
    float flameWeights[NUM_FLAME_LAYERS] = float[NUM_FLAME_LAYERS] (0.7, 0.33, 0.33);

    vec3 totalFlames = vec3(0.0);
    for (int i = 0; i < NUM_FLAME_LAYERS; ++i)
    {
        vec3  fSrc  = flames(flameOffsets[i], uv);
        float fSrcA = flameWeights[i];
        vec3  fDst  = totalFlames;
        totalFlames = fSrc * fSrcA + fDst;
    }

    return totalFlames;
}

float backgroundIntensity(in vec2 uv)
{
    float baseY = 1.0 - uv.y;
    baseY = pow(baseY, 0.1);

    float disturbance = 0.5 + 0.5 * sin(uv.x * 3.1415);
    baseY *= pow(disturbance, 3.2);
    return baseY;
}


#define BRICKS_DIST_TO_CENTER 0.45
#define NUM_BRICKS_W 10.0
#define NUM_BRICKS_H 10.0
#define GROOT_W 0.025
#define GROOT_H 0.05
#define BRICK_COLOR vec3(166.0/255.0,  83.0/255.0,  38.0/255.0)
#define GROOT_COLOR vec3(150.0/255.0, 129.0/255.0, 117.0/255.0)
vec4 bricks( in vec2 uv )
{
    vec4 bricks = vec4(0.0);

    vec2 distToCenter = vec2(0.5) - uv;
    if (length(distToCenter) < BRICKS_DIST_TO_CENTER)
        return bricks;

    if (uv.y <= 0.5 && abs(distToCenter.x) < BRICKS_DIST_TO_CENTER)
        return bricks;

    bricks.w = 1.0;

    float brickYRow;
    float brickY = modf(uv.y * NUM_BRICKS_H, brickYRow);
    float evenOffset = ((int(brickYRow) & 1) == 0) ? 0.5 : 0.0;

    float brickX = fract(uv.x * NUM_BRICKS_W + evenOffset);
    if ( (brickX < GROOT_W || 1.0 - brickX < GROOT_W) ||
         (brickY < GROOT_H || 1.0 - brickY < GROOT_H))
    {
        bricks.xyz = GROOT_COLOR;
    }
    else
    {
        bricks.xyz = BRICK_COLOR;
    }

    return bricks;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    // Flames
    float bgIntensity     = backgroundIntensity(uv);
    vec3  flamesIntensity = flameLayers(uv);
    vec3  col             = bgIntensity * flamesIntensity;

    // Bricks
    vec4 bricks = bricks(uv);
    col = bricks.w * bricks.xyz + (1.0 - bricks.w) * col;

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define rr(s) (s*0.5+0.5)
#define dst(v) (length(uv-v))

vec2 bnc(float t,vec2 p){
    return abs(fract(p*t*0.05)-0.5)*4.-1.;
}

float ss(float t){
    return (3.*sin(t)*sin(t)+2.*cos(t)) *sin(t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;

    float mm = 7.;
    float mag = 1.0;

    mm += 10.*iMouse.y/iResolution.y;

    vec2 p = bnc(iTime,vec2(3.22,4.64));
    vec2 p2 = bnc(iTime,vec2(2.72,3.74));
    vec2 p3 = bnc(iTime,vec2(3.88,1.87));

    float d = log(mag+(sin(dst(p)*mm)));

    d *= log(mag+(sin(dst(p2)*mm)));
    d *= log(mag+(sin(dst(p3)*mm)));

    d += dst(vec2(0.))*33.;

    fragColor =  vec4( vec3( rr(ss(d)),0., rr(ss(d)) ), 1.0 );
}

