vec3 magma(float t) {

    const vec3 c0 = vec3(-0.002136485053939582, -0.000749655052795221, -0.005386127855323933);
    const vec3 c1 = vec3(0.2516605407371642, 0.6775232436837668, 2.494026599312351);
    const vec3 c2 = vec3(8.353717279216625, -3.577719514958484, 0.3144679030132573);
    const vec3 c3 = vec3(-27.66873308576866, 14.26473078096533, -13.64921318813922);
    const vec3 c4 = vec3(52.17613981234068, -27.94360607168351, 12.94416944238394);
    const vec3 c5 = vec3(-50.76852536473588, 29.04658282127291, 4.23415299384598);
    const vec3 c6 = vec3(18.65570506591883, -11.48977351997711, -5.601961508734096);

    t *= 2.;
    if(t >= 1.)
    {
        t = 2. - t;
    }

    return c0+t*(c1+t*(c2+t*(c3+t*(c4+t*(c5+t*c6)))));
}

vec3 c0 = vec3(0,2,5)/255.;
vec3 c1 = vec3(8,45,58)/255.;
vec3 c2 = vec3(38,116,145)/255.;
vec3 c3 = vec3(167,184,181)/260.;
vec3 c4 = vec3(38,116,145)/255.;

vec3 cmap(float t) {
    vec3 col = vec3(0);
    col = mix( c0,  c1, smoothstep(0. , .2, t));
    col = mix( col, c2, smoothstep(.2, .4 , t));
    col = mix( col, c3, smoothstep(.4 , .6, t));
    col = mix( col, c4, smoothstep(.6,  .8, t));
    col = mix( col, c0, smoothstep(.8, 1.,  t));
    return col;
}

vec2 cadd( vec2 a, float s ) { return vec2( a.x+s, a.y ); }
vec2 cmul( vec2 a, vec2 b )  { return vec2( a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x ); }
vec2 cdiv( vec2 a, vec2 b )  { float d = dot(b,b); return vec2( dot(a,b), a.y*b.x - a.x*b.y ) / d; }
vec2 csqrt( vec2 z ) { float m = length(z); return sqrt( 0.5*vec2(m+z.x, m-z.x) ) * vec2( 1.0, sign(z.y) ); }
vec2 conj( vec2 z ) { return vec2(z.x,-z.y); }
vec2 cpow( vec2 z, float n ) { float r = length( z ); float a = atan( z.y, z.x ); 
                               return pow( r, n )*vec2( cos(a*n), sin(a*n) ); }
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

vec3 map(vec2 z, vec2 c, float n) {
    float b = 4., i=0.;
    float ii = n;

    // x = x - f(x)/f'(x)


    //c = (2.*iMouse.xy-R) / R.y;
    //z = vec2(1,0);

    vec2 z_prev = z;
    for(i=0.; i < n; i++) {


        z_prev = z;

        float p = 3.;
        float t = iTime / 40.;
        vec2 a = vec2(cos(13.*t), sin(15.*t));
        float r = 2.*(cos(iTime)*.5 + .5) + .3;

        // z = z - cdiv(cpow(z, 2.0) + vec2(1.0, 0), cmul(z, vec2(2.0,0)));
        // z = z - cdiv(sin(z) - vec2(1.0,0), cos(z));

        z = z - cmul(a, cdiv(cpow(z, 3.0) - vec2(1,0), cmul(vec2(3.,0), cpow(z, 2.0))));


        // z = z - r * cdiv(cpow(z, p) - vec2(1.0, 0), cmul(cpow(z, p-1.0), vec2(p, 0))) + c;
        // z = z - cdiv(cpow(z - vec2(1.0, 0), 3.0) , cmul(cpow(z, 2.0), vec2(3.,0))) + c;

        // z = z - cdiv(cpow(sin(z), 2.0) - vec2(1.0,0), cmul(cmul(sin(z), cos(z)), vec2(2.0,0)));
        // z = z - cdiv(sin(z) - vec2(1.0, 0), cos(z));
        // z = z - cdiv(cpow(z, 3.) - vec2(1.0, 0), cmul(cpow(z,2.), vec2(3.0,0))) + c;
        // z = z - cmul(a, cdiv(cpow(z, p) - vec2(-1,0), cmul(vec2(p,0), cpow(z, p-1.))));

        if( abs(length(z-z_prev)) < 0.0001){
            ii = min(ii, i);
        }

        if(dot(z,z) > 8.) {
            //ii = min(i, ii);
            //break;
        }

	}   
    return vec3(z, ii / n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*C-R)/R.y;
    uv *= 1.2;
    float t = iTime / 4.;
    // uv *= mat2(cos(t), sin(t), -sin(t), cos(t));
    pixelSize = 1./R.y;
    vec3 m = map(uv, uv, 60.);
    vec2 z = m.xy;

    //z = uv;

    // Calculate polar coordinates
    float r = sqrt(dot(z, z));
    float theta = atan(z.y, z.x+0.000000001);

    // Normalize theta
    float thetaNorm = (pi + theta) / (2.*pi);

    // Color the angle based on rainbow colors, and show the distance
    float absW = log2(length(m.z));
    float a = 0.0;
    vec3 col = hsb2rgb(vec3(thetaNorm, 1., 1.) +.1) * (1.0 - a + a*absW);

    //col = hsb2rgb(vec3(fract(length(z)),1.,1.));
    //col = magma(fract(length(z)));
    //col = magma(m.z);


    // Output to screen
    fragColor = vec4(col,1.0);
}

// Shmup Shader
// by Katsuomi Kobayashi (@korinVR)
// http://framesynthesis.com/


#define SCREEN_W 0.7
#define SCROLL_SPEED 0.1

#define BLINK_INTERVAL 0.06
float blink = 0.;

vec2 p = vec2(0.);

vec2 myfighter_pos = vec2(0.);

highp float rand(vec2 co){
    highp float a = 12.9898;
    highp float b = 78.233;
    highp float c = 43758.5453;
    highp float dt= dot(co.xy ,vec2(a,b));
    highp float sn= mod(dt,3.14);
    return fract(sin(sn) * c);
}

float linear_map(float value, float s0, float s1, float d0, float d1)
{
	return d0 + (value - s0) * (d1 - d0) / (s1 - s0);
}

void set_myfighter_pos()
{
	float n = floor(iTime * 2.0);
	float t = fract(iTime * 2.0);

	float x0 = linear_map(rand(vec2(n, 0)), 0.0, 1.0, -0.3, 0.3);
	float x1 = linear_map(rand(vec2(n + 1.0, 0)), 0.0, 1.0, -0.3, 0.3);
	float y0 = linear_map(rand(vec2(0, n)), 0.0, 1.0, -0.4, -0.1);
	float y1 = linear_map(rand(vec2(0, n + 1.0)), 0.0, 1.0, -0.4, -0.1);

	float x = linear_map(t, 0.0, 1.0, x0, x1);
	float y = linear_map(t, 0.0, 1.0, y0, y1);

	myfighter_pos.x = x;
	myfighter_pos.y = y;
}

vec3 background()
{
	float interval = 0.1;
	float thick = 0.005;
	vec3 color = vec3(0.0, 0.2, 0.4);

	if (mod(p.y + iTime * SCROLL_SPEED, interval) < thick || mod(p.x, interval) < thick) {
		return color;
	}
	return vec3(0);
}

vec2 get_boss_pos(float offset)
{
	float x = sin(iTime - offset) * 0.2;

	return vec2(x, 0.35);
}

vec3 bullet(vec2 v, float t)
{
	vec2 pos = get_boss_pos(t) + v * t;

	if (length(p - pos) < 0.006) {
		return vec3(1);
	}
	if (length(p - pos) < 0.01) {
		return vec3(1, 0.4, 0.3);
	}
	return vec3(0);
}

vec3 myfighter(vec2 pos)
{
	float radius = 0.03;

	float dx = pos.x - p.x;
	float dy = pos.y - p.y;

	int px = int(floor(abs(dx) / 0.02));
	int py = int(floor(dy / 0.02));

	bool pixel = false;

	if (px == 0) {
		if (py >= -1 && py <= 1) {
			pixel = true;
		}
	}
	if (px == 1) {
		if (py >= 1 && py <= 2) {
			pixel = true;
		}
	}

	if (pixel) {
		return vec3(1, 1, 1);
	}
	return vec3(0);
}

float get_laser_hit_y()
{
	if (abs(myfighter_pos.x - get_boss_pos(0.0).x) < 0.09) {
		return 0.3;
	}
	return 2.0;
}

vec3 laser(vec2 pos)
{
	vec2 boss_pos = get_boss_pos(0.0);

	float a = 0.003 + blink * 0.004;

	float d;

	if (p.y > myfighter_pos.y && p.y < get_laser_hit_y()) {
		d = abs(p.x - pos.x);
	} else {
		d = length(p - pos);
	}
	float n = a / (d * 2.0);

	return vec3(2, 8, 5) * n;
}

vec3 laser_hit_effect()
{
	vec2 pos = vec2(myfighter_pos.x, get_laser_hit_y());

	float a = 0.02 + blink * 0.02;

	float d = length(p - pos);
	float n = a / (d * 5.0);

	return vec3(2, 2, 5) * n;
}

vec3 boss()
{
	vec2 pos = get_boss_pos(0.0);

	bool pixel = false;

	float dx = pos.x - p.x;
	float dy = pos.y - p.y;

	int px = int(floor(abs(dx) / 0.02));
	int py = int(floor(dy / 0.02));

	if (px >= -5 && px <= 5) {
		if (py >= -5 && py <= 3) {
			if (rand(vec2(px, py)) > 0.5) {
				pixel = true;
			}
		}
	}

	if (pixel) {
		if (abs(myfighter_pos.x - pos.x) < 0.08 && blink > 0.5) {
			return vec3(1);
		}
		return vec3(1, 0.5, 0);
	}
	return vec3(0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	blink = mod(iTime, BLINK_INTERVAL) / BLINK_INTERVAL;

	p = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.y;

	if (abs(p.x) > SCREEN_W / 2.0) {
		discard;
	}

	set_myfighter_pos();

	vec3 c = background();

	c += boss();

	for (int i = -4; i <= 2; i++) {
		float t = fract(iTime * 0.5 + float(i) * 0.03);
		vec2 v = normalize(vec2(float(i) * 0.1, -1.0));
		c += bullet(v, t);
	}

	for (int i = -2; i <= 4; i++) {
		float t = fract(iTime * 0.5 + 0.5 + float(i) * -0.03);
		vec2 v = normalize(vec2(float(i) * 0.1, -1.0));
		c += bullet(v, t);
	}

	c += myfighter(myfighter_pos);
	c += laser(myfighter_pos);
	c += laser_hit_effect();

	fragColor = vec4(c, 1);
}

