float dot2( in vec2 v ) { return dot(v,v); }
float cross2( in vec2 a, in vec2 b ) { return a.x*b.y - a.y*b.x; }

// unsigned distance to a quadratic bezier
float udBezier( in vec2 pos, in vec2 A, in vec2 B, in vec2 C )
{    
    vec2 a = B - A;
    vec2 b = A - 2.0*B + C;
    vec2 c = a * 2.0;
    vec2 d = A - pos;

    float kk = 1.0/dot(b,b);
    float kx = kk * dot(a,b);
    float ky = kk * (2.0*dot(a,a)+dot(d,b))/3.0;
    float kz = kk * dot(d,a);      

    float res = 0.0;

    float p = ky - kx*kx;
    float p3 = p*p*p;
    float q = kx*(2.0*kx*kx - 3.0*ky) + kz;
    float h = q*q + 4.0*p3;

    if( h>=0.0 ) 
    {   // 1 root
        h = sqrt(h);
        vec2 x = (vec2(h,-h)-q)/2.0;
        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
        float t = clamp( uv.x+uv.y-kx, 0.0, 1.0 );
        res = dot2(d+(c+b*t)*t);
    }
    else 
    {   // 3 roots
        float z = sqrt(-p);
        float v = acos(q/(p*z*2.0))/3.0;
        float m = cos(v);
        float n = sin(v)*1.732050808;
        vec3  t = clamp( vec3(m+m,-n-m,n-m)*z-kx, 0.0, 1.0 );
        res = min( dot2(d+(c+b*t.x)*t.x),
                   dot2(d+(c+b*t.y)*t.y) );
        // the third root cannot be the closest. See https://www.shadertoy.com/view/4dsfRS
        // res = min(res,dot2(d+(c+b*t.z)*t.z));
    }

    return sqrt( res );
}

// signed distance to a quadratic bezier
float sdBezier( in vec2 pos, in vec2 A, in vec2 B, in vec2 C )
{    
    vec2 a = B - A;
    vec2 b = A - 2.0*B + C;
    vec2 c = a * 2.0;
    vec2 d = A - pos;

    float kk = 1.0/dot(b,b);
    float kx = kk * dot(a,b);
    float ky = kk * (2.0*dot(a,a)+dot(d,b))/3.0;
    float kz = kk * dot(d,a);      

    float res = 0.0;
    float sgn = 0.0;

    float p = ky - kx*kx;
    float p3 = p*p*p;
    float q = kx*(2.0*kx*kx - 3.0*ky) + kz;
    float h = q*q + 4.0*p3;

    if( h>=0.0 ) 
    {   // 1 root
        h = sqrt(h);
        vec2 x = (vec2(h,-h)-q)/2.0;
        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
        float t = clamp( uv.x+uv.y-kx, 0.0, 1.0 );
        vec2  q = d+(c+b*t)*t;
        res = dot2(q);
    	sgn = cross2(c+2.0*b*t,q);
    }
    else 
    {   // 3 roots
        float z = sqrt(-p);
        float v = acos(q/(p*z*2.0))/3.0;
        float m = cos(v);
        float n = sin(v)*1.732050808;
        vec3  t = clamp( vec3(m+m,-n-m,n-m)*z-kx, 0.0, 1.0 );
        vec2  qx=d+(c+b*t.x)*t.x; float dx=dot2(qx), sx = cross2(c+2.0*b*t.x,qx);
        vec2  qy=d+(c+b*t.y)*t.y; float dy=dot2(qy), sy = cross2(c+2.0*b*t.y,qy);
        if( dx<dy ) { res=dx; sgn=sx; } else {res=dy; sgn=sy; }
    }

    return sqrt( res )*sign(sgn);
}

float udSegment( in vec2 p, in vec2 a, in vec2 b )
{
	vec2 pa = p - a;
	vec2 ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

	vec2 v0 = vec2(1.3,0.9)*cos(iTime*0.5 + vec2(0.0,5.0) );
    vec2 v1 = vec2(1.3,0.9)*cos(iTime*0.6 + vec2(3.0,4.0) );
    vec2 v2 = vec2(1.3,0.9)*cos(iTime*0.7 + vec2(2.0,1.0) );
    //vec2 v3 = vec2(1.3,0.9)*cos(iTime*0.8 + vec2(1.0,0.5) );
    vec2 v3 = v2 + (v2 - v1);
    vec2 v4 = vec2(1.3,0.9)*cos(iTime*0.8 + vec2(0.5,2.) );
    vec2 v5 = v4 + (v4 - v3);
    // vec2 v6 = v2 * vec2(-1.);
    vec2 v7 = v0 + (v0 - v1);
    vec2 v6 = v7 + (v7 - v5) * -0.5;

    float d = udBezier( p, v0,v1,v2 );
    float e = udBezier( p, v2,v3,v4 );
    float f = udBezier( p, v4,v5,v6 );
    float g = udBezier( p, v0,v7,v6 );
    d = min(min(min(d,e),f),g);
//    float f = smoothstep(-0.2,0.2,cos(2.0*iTime));
    vec3 col = vec3(1.0) - vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-4.0*abs(d));
	col *= 0.8 + 0.2*cos(140.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );


        d = min(min(min(min(min(min(min( udSegment(p,v0,v1),
                 udSegment(p,v1,v2)),
                 udSegment(p,v2,v3)),
                 udSegment(p,v3,v4)),
                 udSegment(p,v4,v5)),
                 udSegment(p,v5,v6)),
                 udSegment(p,v0,v7)),
                 udSegment(p,v7,v6));
        d = min( d, length(p-v0)-0.02 );
        d = min( d, length(p-v1)-0.02 );
        d = min( d, length(p-v2)-0.02 );
        d = min( d, length(p-v3)-0.02 );
        d = min( d, length(p-v4)-0.02 );
        d = min( d, length(p-v5)-0.02 );
        d = min( d, length(p-v6)-0.02 );
        d = min( d, length(p-v7)-0.02 );
        col = mix( col, vec3(1,0,0), 1.0-smoothstep(0.0,0.007,d) );

	fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 col;
    float t;

    for(int c=0;c<3;c++){
	    vec2 uv = (fragCoord*50.0-iResolution.xy)/iResolution.y/10.0;
        uv += iTime/10.0;
        t = iTime+float(c)/10.;
        float scale = 5.0;
        float scale1 = 1.4;
        for(int i=0;i<20;i++)
        {

            uv=sin(uv-(uv.yx/(2.0+sin((uv.x+uv.y)*(2.0)))/2.0)/scale)*scale/2.0+scale1*scale;
            uv /= scale1;
        	//uv += cos(uv/100.0);
        	uv=uv.yx+col.xy;
        }
     col[c] = fract((uv.x)/(uv.y));
	}

    fragColor = vec4(vec3(col),1.0);

}

//Adapted from Galaxy by Fabrice NEYRET:
//https://www.shadertoy.com/view/Xsl3zX

const float RETICULATION = 2.;  // strenght of dust texture
const float NB_ARMS = 3.;       // number of arms
//const float ARM = 3.;         // contrast in/out arms
const float COMPR = .21;         // compression in arms
const float SPEED = .1;
const float GALAXY_R = 1./2.;
const float BULB_R = 1./4.;
const vec3 GALAXY_COL = vec3(.9,.9,1.); //(1.,.8,.5);
const vec3 BULB_COL   = vec3(1.,.8,.8);
//const vec3 SKY_COL    = .5*vec3(.1,.3,.5);
const vec3 SKY_COL    = .5*vec3(0.,0.,0.);

#define t iTime

// --- base noise
float tex(vec2 uv) 
{
	float n = texture(iChannel0,uv, 0.).r;

#define MODE 3  // kind of noise texture
#if MODE==0         // unsigned
	#define A 2.
	return n;
#elif MODE==1       // signed
	#define A 3.
	return 2.*n-1.;
#elif MODE==2       // bulbs
	#define A 3.
	return abs(2.*n-1.);
#elif MODE==3       // wires
	#define A 1.5
	return 1.-abs(2.*n-1.);
#endif
}


// --- perlin turbulent noise + rotation
float noise(vec2 uv)
{
	float v=0.;
	float a=-SPEED*t,	co=cos(a),si=sin(a); 
	mat2 M = mat2(co,-si,si,co);
	const int L = 5;
	float s=1.;
	for (int i=0; i<L; i++)
	{
		uv = M*uv;
		float b = tex(uv*s);
		v += 1./s* pow(b,RETICULATION); 
		s *= 2.;
	}

    return v/2.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoomTime = pow(iTime,6.);
    if (zoomTime >= 19.23) {
        zoomTime = 19.23;
    }
    vec2 uv2 = ((fragCoord/iResolution.xy)-0.5)*vec2(iResolution.x/iResolution.y,1.);
	vec2 uv = mod((10.-.5*zoomTime)*fragCoord.xy/iResolution.y,1.);
    //uv -= vec2(.35+.1*sin(iTime*.001*uv.x),.1+.1*cos(iTime*.001*uv.x));
    uv -= vec2(.35,.2);

    uv *= vec2(-.7 + exp(.03*iTime), -.6 + exp(.02*iTime));
	vec3 col;

	// spiral stretching with distance
	float rho = length(uv)*5.; // polar coords
	float ang = atan(uv.y,uv.x);
	float shear = 4.*log(rho); // logarythmic spiral
	float c = cos(shear), s=sin(shear);
	mat2 R = mat2(c,-s,s,c);

	// galaxy profile
	float r; // disk
	r = rho/GALAXY_R; float dens = exp(-r*r);
	r = rho/BULB_R;	  float bulb = exp(-r*r);
	float phase = NB_ARMS*(ang-shear);
	// arms = spirals compression
	ang = ang-COMPR*cos(phase)+SPEED*t;
	uv = rho*vec2(cos(ang),sin(ang));
	// stretched texture must be darken by d(new_ang)/d(ang)
	float spires = 1.+NB_ARMS*COMPR*sin(phase);
	// pires = mix(1.,sin(phase),ARM);
	dens *= spires + .5*spires*cos(iTime);	

	// gaz texture
	float gaz = noise(.09*1.2*R*uv);
	float gaz_trsp = pow((1.-gaz*dens),2.);

	// stars
	//float a=SPEED*t, co=cos(a),si=sin(a); 
	//mat2 M = mat2(co,-si,si,co);
	// adapt stars size to display resolution
	float ratio = .8*iResolution.y/iChannelResolution[0].y;
	float stars1 = texture(iChannel1,ratio*uv+.5, 0.).r, // M*uv
	      stars2 = texture(iChannel0,ratio*uv+.5, 0.).r,
		  stars = pow(1.-(1.-stars1)*(1.-stars2),5.);

	stars = pow(stars,1.+.5*pow(cos(iTime),2.));

	// mix all	
	col = mix(SKY_COL,
			  gaz_trsp*(1.7*GALAXY_COL) + 1.2*stars, 
			  dens);
	col = mix(col, 1.2*BULB_COL, bulb);

    vec3 viewDir = normalize(vec3(uv2+vec2(0.0,-.4),.5));

    vec2 planarUV = viewDir.xz/abs(viewDir.y);

    float depth = planarUV.y;

    planarUV += vec2(iTime*0., iTime*.5);

    vec3 albedo = vec3(0.,0.,.2*texture( iChannel2, planarUV*.1 ).r);

    float light = (.5+0.1*iTime)/(depth*depth)*viewDir.z;

    //fragColor = vec4(light*albedo,1.);
    //fragColor = vec4(albedo,1.0);

	fragColor = vec4(col+light*albedo,1.);
}

