#define PI 3.14159265359
#define CT() abs(sin(iTime))

vec3 GlowingLine(vec2 fragCoord, vec3 color, float hoffset, float voffset, float hscale, float vscale, float timescale)
{
    float glowRadius = 0.04 * iResolution.y * (fragCoord.x / iResolution.x + 1.0) ;
    float glowIntensity = 1.0 * fragCoord.x / iResolution.x;
    float twopi = 2.0 * PI;
    float thickness = 1.5;
    float curve = thickness - abs(fragCoord.y - (sin(mod(fragCoord.x * hscale / 100.0 / iResolution.x * 1000.0 + iTime * timescale + hoffset, twopi)) * iResolution.y * 0.25 * vscale + voffset));
    float i = clamp(curve, 0.0, 1.0);

    i += clamp((glowRadius + curve) / glowRadius, 0.0, 1.0) * glowIntensity;
    return i * color;
}

vec3 rainbowGradient(float t) {
	vec3 c = 1.0 - pow(abs(vec3(t) - vec3(0.65, 0.5, 0.2)) * vec3(3.0, 3.0, 5.0), vec3(1.5, 1.3, 1.7));
	c.r = max(0.15 - (abs(t - 0.04) * 5.0) * (abs(t - 0.04) * 5.0), c.r);
	c.g = (t < 0.5) ? smoothstep(0.04, 0.45, t) : c.g;
	return clamp(c, 0.0, 1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = vec2(fragCoord.xy / iResolution.xy);

    vec3 color = vec3(0.0039, 0.0039, 0.1922);

    color += GlowingLine(fragCoord.xy, rainbowGradient(0.15  * CT()), 0.0 + 2.4, iResolution.y / 2.0, abs(st.x - 1.0), abs(sin(iTime)) * st.x, 5.0);
    color += GlowingLine(fragCoord.xy, rainbowGradient(0.1  * CT()), PI + 2.4, iResolution.y / 2.0, abs(st.x - 1.0), abs(sin(iTime + PI)) * st.x, 5.0);
    color += GlowingLine(fragCoord.xy, rainbowGradient(0.3  * CT()), PI + 2.4, iResolution.y / 2.0, abs(st.x - 1.0), sin(iTime + PI * 0.5) * st.x, 5.0);
    color += GlowingLine(fragCoord.xy, rainbowGradient(0.25 * CT()), PI + 2.4, iResolution.y / 2.0, abs(st.x - 1.0), sin(iTime - PI * 0.5) * st.x, 5.0);

    fragColor = vec4(color, 1.0);
}

const float TAU = 3.14159265358979*2.;
const float TAW = TAU * 2. / 3.;

uint roll(uvec2 xy, uint t) {
    const uint a = 1664525u;
    const uint m = 1013904223u;

    uint seed = 65538u * xy.x + 782651u * xy.y + 1979u * t;

    uint x0 = (seed * a) % m;
    uint x = (x0 * a) % m;

    return x & 65535u;
}

vec3 huey(float t) {
    return smoothstep(0.0, 1.0, vec3(sin(t * TAU), sin(t * TAU + TAW), sin(t * TAU - TAW)) + 1. / 2.);
}    

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float Time = fract(iTime / 4.);
    uint time = uint(iTime * 20.);

    // Normalized pixel coordinates (from 0 to 1)
    float scale = min(iResolution.x, iResolution.y);
    vec2 uv = fragCoord / scale;
    uv -= vec2(iResolution.x / scale, iResolution.y / scale) / 2.;
    uv *= 2.0;

    float linDis = distance(uv, vec2(0));
    float lolDis = 14. * log(linDis) - 6. * linDis;

    vec2 rt = vec2(
        256. + lolDis +
        0.5 * sin(0.4 * lolDis + Time * 2. * TAU)
    ,
        256. + mod(atan(uv.x, uv.y) / TAU * 64. +
        0.3 * sin((0.2 * lolDis + 2.0 * Time) * TAU), 32.)
    );

    float x = smoothstep(0.4,0.6,float(roll(uvec2(rt), time)) / 65536.);

    // Output to screen
    fragColor = vec4(
      (x)      * huey(4.0 * Time + 0.4 * linDis) +
      (1. - x) * huey(4.0 * Time + 0.6 * linDis)
    , 1);
}

