#define PI 3.14159265359

float HLine(vec2 uv, float ypos, float s)
{
    float res = 1.0;
    res *= smoothstep(ypos - s, ypos - s + 0.01, uv.y);
    res *= smoothstep(ypos + s + 0.01, ypos + s, uv.y);

    return res;
}

vec3 GlowingLine(vec2 fragCoord, vec3 color, float hoffset, float voffset, float hscale, float vscale, float timescale)
{
    float glowRadius = 0.03 * iResolution.x * (fragCoord.y / iResolution.y + 1.0) ;
    float glowIntensity = 1.0 * fragCoord.y / iResolution.y;
    float twopi = 2.0 * PI;
    float thickness = 1.5;
    float curve = thickness - abs(fragCoord.x - (sin(mod(fragCoord.y * hscale / 100.0 / iResolution.y * 1000.0  + hoffset, twopi)) * iResolution.x * 0.5 * vscale + voffset));
    float i = clamp(curve, 0.0, 1.0);

    i += clamp((glowRadius + curve) / glowRadius, 0.0, 1.0) * glowIntensity;
    return i * color;
}

vec3 rainbowGradient(float t) {
	vec3 c = 1.0 - pow(abs(vec3(t) - vec3(0.65, 0.5, 0.2)) * vec3(3.0, 3.0, 5.0), vec3(1.5, 1.3, 1.7));
	c.r = max(0.15 - (abs(t - 0.04) * 5.0) * (abs(t - 0.04) * 5.0), c.r);
	c.g = (t < 0.5) ? smoothstep(0.04, 0.45, t) : c.g;
	return clamp(c, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x/iResolution.y; 

    vec3 color = vec3(0.0039, 0.0039, 0.1922);

    color += GlowingLine(fragCoord.xy, rainbowGradient(0.22), 0.0 + 2.6, iResolution.x / 2.0, 1.0, 0.5 * abs(sin(iTime)) * uv.y, 5.0);
    color += GlowingLine(fragCoord.xy, rainbowGradient(0.22), PI + 2.6, iResolution.x / 2.0, 1.0, 0.5 * abs(sin(iTime + PI)) * uv.y, 5.0);
    color += GlowingLine(fragCoord.xy, rainbowGradient(0.22), PI + 2.66, iResolution.x / 2.0, 1.0, 0.5 * sin(iTime + PI * 0.5) * uv.y, 5.0);
    color += GlowingLine(fragCoord.xy, rainbowGradient(0.22), PI + 2.6, iResolution.x / 2.0, 1.0, 0.5 * sin(iTime - PI * 0.5) * uv.y, 5.0);

    fragColor = vec4(color, 1.0);
}

// Forked from https://www.shadertoy.com/view/wt2yW3

vec2 hash3( vec2 p )
{
    vec2 q = vec2(dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)));
    return fract(sin(q)*43758.5453);
}

float noise(in vec2 x)
{
    float intensity = 0.4;

    x *= intensity;

    vec2 p = floor(x);
    vec2 f = fract(x);


    float va = 0.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 g = vec2( float(i),float(j) );
        vec2 o = hash3(p + g);
        vec2 r = ((g - f) + o.xy) / intensity;
        float d = sqrt(dot(r,r));

        float a = max(cos(d - iTime * 2. + (o.x + o.y) * 5.0), 0.);
        a = smoothstep(0.99, 0.999, a);

        float ripple = mix(a, 0., d);
        va += max(ripple, 0.);
    }

    return va;
}


