#define gray(rgb) (rgb.r * 0.299 + rgb.g * 0.587 + rgb.b * 0.114)
#define cmpv(v, b) (v.x > b.x && v.y > b.y && v.x < (b.x + 1.0) && v.y < (b.y + 1.0))
#define m_p 3.14155
#define norm(a) (a + 1.0) * 0.5
//#define norm(a) abs(a)
//#define COLORED_OFF

vec2 calcAspect(vec2 iRes)
{
    vec2 aspect = vec2(float(iRes.x < iRes.y) * iRes.x / iRes.y,
					   float(iRes.y < iRes.x) * iRes.y / iRes.x);
    aspect.x += float(aspect.x == 0.0);
    aspect.y += float(aspect.y == 0.0);
    return aspect;
}

//------------------------------------------------------------
float waves(vec2 p, vec2 s, float t)
{
    p *= s * m_p;
    return norm(sin(sin(p.x - t) - p.y));
}

//------------------------------------------------------------
float radial(vec2 p, float s, float t)
{
	return norm(sin(length(p) / s - t));
}

//------------------------------------------------------------
float spiral(vec2 p, float s, float t)
{
    float e = exp(length(p)); /*can also try pow(l,l) or sqrt(l)*/
    return norm(sin((exp(length(p)) - s * atan(p.y, p.x) - t) / s));
}

//------------------------------------------------------------
float windmill(vec2 p, float c, float w, float t)
{
    float sl = sin(length(p)) * w;
    return norm(sin((atan(p.x, p.y) / (1.0 / c) - sl) + t));
}

//------------------------------------------------------------
float angles(vec2 p, float s, float t)
{
    return norm(sin(p.x * p.y / s - t));
}

//------------------------------------------------------------
float radial_waves(vec2 p, float s, vec2 w, float t)
{
    float a = atan(p.x, p.y) / (1.0 / w.x);
    return norm(sin(length(p) / s / norm(sin(a) + w.y) - t));
}

//------------------------------------------------------------
float windmill_waves(vec2 p, float c, float w, float t)
{
    float sl = sin(length(p) * w - t);
    return norm(sin((atan(p.x, p.y) / (1.0 / c) - sl) + t));
}

//------------------------------------------------------------
float spiral_waves(vec2 p, float s, float w, float t)
{
    float l = exp(sin(length(p)));
    float a = atan(p.y, p.x);

    return norm(sin((l - s * (a - sin(a * w - t) + t)) / s));
}

//------------------------------------------------------------
float double_waves(vec2 p, vec2 s, float w, float t)
{
    p *= s * m_p;
    p += norm(sin(p.y * w - t));
    return norm(sin(sin(p.x - t)  - p.y));
}
//------------------------------------------------------------

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 aspect = calcAspect(iResolution.xy);
    vec2 auv = uv * aspect;
    const vec2 cells = vec2(3, 3);
    vec2 cell = cells * uv;
    vec2 cc = (floor(cell) + 0.5) / cells * aspect;
    vec2 v = cc - auv;

    float g = 0.0;

    if(cmpv(cell, vec2(0.0, 0.0)))
    {
        g = waves(auv, vec2(16.0, 32.0), iTime);
    }
    else if(cmpv(cell, vec2(1.0, 0.0)))
    {
        g = radial(v, 0.008, iTime);
    }
    else if(cmpv(cell, vec2(2.0, 0.0)))
    {
        g = windmill(v, 8.0, 40.0, iTime);
    }
    else if(cmpv(cell, vec2(0.0, 1.0)))
    {
        g = spiral(v, 0.008, iTime * 0.02);
    }
    else if(cmpv(cell, vec2(1.0, 1.0))) 
    {
        g = angles(v, 0.0008, iTime);
    }
    else if(cmpv(cell, vec2(2.0, 1.0)))
    {   
        g = radial_waves(v, 0.0005, vec2(8.0, 10.0), iTime);
    }
    else if(cmpv(cell, vec2(0.0, 2.0)))
    {
        g = double_waves(auv, vec2(16.0, 32.0), 1.2, iTime);
    }
    else if(cmpv(cell, vec2(1.0, 2.0)))
    {
        g = spiral_waves(v, 0.008, 8.0, iTime);
    }
    else if(cmpv(cell, vec2(2.0, 2.0)))
    {
        g = windmill_waves(v, 8.0, 120.0, iTime);
    }

    vec2 fc = floor(cell);

    vec3 col = norm(vec3(sin(iTime + fc.x), sin(iTime + 1.0 + fc.y), sin(iTime + 2.0 + fc.y))) * 2.0;
    #ifdef COLORED_OFF
    	col = vec3(1.0);
    #endif
    // Output to screen
    fragColor = vec4(col * g, 1.0);
}

#define S(a,b,t) smoothstep(a,b,t)

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

vec4 TaperBox(vec2 p, float wb, float wt, float yb, float yt, vec4 col, float blur){
	float m = S(-blur,blur,p.y-yb);
    m *= S(blur,-blur,p.y-yt);

    p.x = abs(p.x);

    //0 p=yb; 1 p=yt
    float w = mix(wb,wt,(p.y-yb)/(yt-yb));

    m *= S(blur, -blur, p.x-w);
    return m*vec4(col);

}   

vec4 TaperBoxI(vec2 p, float hl, float hr, float xl, float xr, vec4 col, float blur){
	float m = S(-blur,blur,p.x-xl);
    m *= S(blur,-blur,p.x-xr);

    p.y = abs(p.y);

    float w = mix(hl,hr,(p.x-xl)/(xr-xl));

    m *= S(blur, -blur, p.y-w);
    return m*vec4(col);
}

vec4 LightHouse(vec2 uv, float x, float y, float blur){

    //Color Definition
    vec4 col1 = vec4(74.,2.,2.,255.)/255.;
    vec4 col2 = vec4(204.,204.,204.,255.)/255.;
    vec2 offs = vec2(x,y);
    uv *= 2.;
    vec4 m= TaperBox(uv-offs,0.2,.18,0.,.25,col1, blur);//Brick 1
    m += TaperBox(uv-offs,.18,.16,0.25,.5,col2,blur);//Brick 2
    m += TaperBox(uv-offs,.16,.14,0.5,.75,col1,blur);//Brick 3
    m += TaperBox(uv-offs,.035,.035,0.55,.68,vec4(1.),blur+.015);//Brick 3 Window
    m += TaperBox(uv-offs,.14,.12,0.75,1.,col2,blur);//Brick 4
    m += TaperBox(uv-offs,.12,.10,1.,1.25,col1,blur);//Brick 5
    m += TaperBox(uv-offs,.035,.035,1.05,1.18,vec4(1.),blur+.015);//Brick 5 Window
    m += TaperBox(uv-offs,.10,.09,1.25,1.35,col2,blur);//Bricke 6
    m += TaperBox(uv-offs,.09,.11,1.35,1.40,col1,blur);//Brick7 inverted
    m += TaperBox(uv-offs,.2,.2,1.40,1.50,col2,blur);//Top Deck 1
    m += TaperBox(uv-offs,.2,.2,1.50,1.60,col1,blur);//Top Deck 2
    m += TaperBox(uv-offs,.03,.03,1.60,1.70,col2,blur);//Light Holder
    m += TaperBox(uv-offs-vec2(-.1,0.),.01,.01,1.60,1.81,col1,blur);//Column Left
    m += TaperBox(uv-offs-vec2(.1,0.),.01,.01,1.60,1.81,col1,blur);//Column Right
    m += TaperBox(uv-offs,.01,.01,1.60,1.81,col1,blur);//Column Middle
    m += TaperBox(uv-offs,.16,.0,1.81,2.,col2,blur);//Roof

    //Light
    vec4 light = TaperBoxI(uv-offs-vec2(0.,1.65),0.02,0.4,0.,6.,vec4(0.96,.84,.109,1.),blur);

    light *= uv.x*0.2+0.5*sin(-iTime*0.5);



    m += light;




    return m;
}

vec4 Sun(vec2 uv, float rad, float x, float y, vec4 col, float blur){
    uv *= 5.;
    uv.x -= x;
    uv.y -= y;
	float d = length(uv);
    //float s = .3/d;

    float m = S(rad, rad-blur-.05,d);

    return m*col;
}

float GetHeight(vec2 uv){
    uv *= 0.2;
    float height = .562*sin(uv.x*.551)+.325*sin(uv.x*8.356)+sin(uv.x*.2350)+sin(uv.x*.356)*.175;
    return -height - .425*sin(uv.x)*.565;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = ((2.*fragCoord-iResolution.xy)/iResolution.y);


    //BG Gradient
    float wPercent = fragCoord.y / iResolution.y;
    vec4 color1 = vec4(208.,162.,21.,255.)/255.;
    vec4 color2 = vec4(192.,101.,11.,255.)/255.;
    float r = color1.x + wPercent * (color2.x - color1.x);
    float g = color1.y + wPercent * (color2.y - color1.y);
    float b = color1.z + wPercent * (color2.z - color1.z);
    vec4 col = vec4(r,g,b,1.);

    float blur = 0.01;
    vec4 lighthouse = LightHouse(uv, -0.5,-0.3, blur);

    col = mix(col,lighthouse,lighthouse.a);
    vec4 suncol = vec4(226.,194.,61.,255.)/255.;
    vec4 sun = Sun(uv, 0.8, 4., 1.9,suncol, .1)*1.2;
    //sun += Sun(uv,0.8, 4., 1.9,suncol, .1);
    col = mix(col,sun,sun.a);

    uv *= 2.;
    vec4 bg = TaperBox(uv,12.,12.,-0.96,-.2+GetHeight(uv*sin(uv.x*.256)),vec4(0.01,0.01,0.,1.),blur+.02);
    bg += TaperBox(uv*-1.,12.,12.,1.0,1.6+GetHeight(uv*sin(uv.x*.256)),vec4(0.01,0.01,0.,1.),blur+0.1);
    bg += TaperBox(uv,12.,12.,-2.,-1.,vec4(208.,162.,21.,50.)/255.,.2)*0.2;
    col =  mix(col,bg,bg.a);
    uv /= 2.;
    vec4 LHref = LightHouse(uv*vec2(1,-1.), -0.5,1.5, blur+.1);

    col = mix(col,LHref,LHref.a);
    float T = sin(iTime*0.01);
    vec4 boat = TaperBox(uv-vec2(-1.+T,0.2),.25,.3,-1.,-.93,vec4(0.01,0.01,0.01,1.),blur);
    boat += TaperBox(uv-vec2(-1.+T,0.137),.3,.25,-1.,-.93,vec4(0.01,0.01,0.01,1.),blur*4.5);

    col = mix(col,boat,boat.a);

    float d = length(uv+vec2(0.95-T,0.65));
    vec4 man = S(0.04,0.005,d)*suncol*vec4(0.,0.,0.,1.);
	man += TaperBox(uv-vec2(-0.95+T,0.2),.02,.01,-.93,-.87,vec4(0.01,0.01,0.01,1.),blur+.025)*2.;
    man += TaperBoxI(uv-vec2(-.95+T,-0.7),0.005,.005,0.,0.35,vec4(0.01,0.01,0.01,1.),blur);
  	man += TaperBoxI(uv-vec2(-.95+T,-0.78),0.005,.005,0.,0.35,vec4(0.01,0.01,0.01,1.),blur+.05);
	man += TaperBox(uv-vec2(-.6+T,-0.8),0.005,.005,0.,0.1,vec4(0.01,0.01,0.01,1.),blur);
    col = mix(col,man,man.a);
    // Output to screen
    fragColor = col;
}

void lookAt(inout vec3 rd,vec3 ro,vec3 ta,vec3 up){
    vec3 w=normalize(ta-ro),u=normalize(cross(w,up));
    rd=rd.x*u+rd.y*cross(u,w)+rd.z*w;
}

void rot(inout vec3 p,vec3 a,float t){
	a=normalize(a);
	vec3 u=cross(a,p),v=cross(a,u);
	p=u*sin(t)+v*cos(t)+a*dot(a,p);   
}

void sFold90(inout vec2 p)
{
    float e=2e-2;
    vec2 v=normalize(vec2(1,-1));
    float g=dot(p,v);
    p-=(g-sqrt(g*g+e))*v;
}

float frame(vec3 p)
{
	rot(p,vec3(cos(iTime*.3),sin(iTime*.3),1),iTime*.3);
	p=abs(p)-1.;
#if 1
	sFold90(p.xz);
	sFold90(p.yz);
	sFold90(p.xy);
#else
    if(p.x<p.z)p.xz=p.zx;
    if(p.y<p.z)p.yz=p.zy;
    if(p.x<p.y)p.xy=p.yx;
#endif
	p.x=abs(p.x)-.2;
	p.y=abs(p.y)-.2;
	p.y=abs(p.y)-.1;
	p.y=abs(p.y)-.1;
 	return length(vec2(length(p.xz)-1.,p.y))-.05;
}

float map(vec3 p)
{
    return min(p.y+6.,frame(p));
}

vec3 calcNormal(vec3 p)
{
  vec3 n=vec3(0);
  for(int i=0; i<4; i++){
    vec3 e=.001*(vec3(9>>i&1, i>>1&1, i&1)*2.-1.);
    n+=e*map(p+e);
  }
  return normalize(n);
}

float march(vec3 ro, vec3 rd, float near, float far)
{
    float t=near,d;
    for(int i=0;i<100;i++)
    {
        t+=d=map(ro+rd*t);
        if (d<.001) return t;
        if (t>=far) return far;
    }
    return far;
}

float calcShadow(vec3 light, vec3 ld, float len)
{
	float depth=march(light,ld,0.,len);	
	return step(len-depth,.01);
}

vec3 doColor(vec3 p)
{
    return vec3(.3,.5,.8)+cos(p*.2)*.5+.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy*2.-iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0,1,7);
    vec3 rd = normalize(vec3(uv,2));
    vec3 ta =vec3(0);
    lookAt(rd,ro,ta,vec3(0,1,0));    
    vec3 col= vec3(0);
	const float maxd=50.;
    float t=march(ro,rd,0.,maxd);
    if(t<maxd)
    {
        vec3 p=ro+rd*t;
        col=doColor(p); 
        vec3 n=calcNormal(p);      
		vec3 lightPos=ro+vec3(0,3,0);
    	vec3 li=lightPos-p;
		float len=length(li);
		li/=len;
		float dif=clamp(dot(n,li),0.,1.);
        float sha=calcShadow(lightPos,-li,len);
        col*=max(sha*dif,.2);
        float rimd=pow(clamp(1.-dot(reflect(-li,n),-rd),0.,1.),2.5);
		float frn=rimd+2.2*(1.-rimd);
    	col*=frn*.6;
        col*=max(.5+.5*n.y,.3);
        col*=exp2(-2.*pow(max(0.,1.-map(p+n*.8)/.8),2.));
        col+=vec3(.8,.6,.2)*pow(clamp(dot(reflect(rd,n),li),0.,1.),10.);
        col-=dot(.0005*p,p);
    }
    fragColor.xyz=col;
}

