// Fade function
vec2 fade(vec2 t) {return t*t*t*(t*(t*6.0-15.0)+10.0);}


float getCornerValue(vec2 gridXY, vec2 squareXY)
{
    int value = permutation[permutation[int(gridXY.x) & 255] + int(gridXY.y) & 255];
    vec2 gradVector;
    switch(value & 0x3){
     	case 0:
        	gradVector = vec2(1., 1.);
        break;
        case 1:
        	gradVector =  vec2(-1., 1.);
        break;
     	case 2:
        	gradVector =  vec2(1., -1.);
        break;
     	case 3:
        	gradVector =  vec2(-1., -1.);
        break;
     	default:
        break;

    }

    return dot(gradVector, squareXY);
}

float noise2d(vec2 fragCoord, vec2 squareSize)
{



    vec2 gridXY = floor(fragCoord / squareSize);
    vec2 squareXY = fract(fragCoord / squareSize);

    vec2 posBottomLeft = gridXY;
    vec2 posTopLeft = gridXY + vec2(0., 1.);
    vec2 posTopRight = gridXY + vec2(1., 1.);
    vec2 posBottomRight = gridXY + vec2(1., 0.);

    vec2 squareBottomLeft = squareXY;
    vec2 squareTopLeft = vec2(squareXY.x, squareXY.y - 1.);
    vec2 squareTopRight = vec2(squareXY.x - 1., squareXY.y - 1.);
    vec2 squareBottomRight = vec2(squareXY.x - 1., squareXY.y);

    float bottomLeft = getCornerValue(posBottomLeft, squareBottomLeft);
    float topLeft = getCornerValue(posTopLeft, squareTopLeft);
    float topRight = getCornerValue(posTopRight, squareTopRight);
    float bottomRight = getCornerValue(posBottomRight, squareBottomRight);

    vec2 uv = fade(squareXY);


	return mix(
		mix(bottomLeft, bottomRight, uv.x),
		mix(topLeft, topRight, uv.x),
        uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 squareSize = vec2(32., 32.);

    // Move (0,0) to center of screen
    fragCoord -= vec2(iResolution.x, iResolution.y) / 2.;

    // Output to screen
    float noise = (noise2d(fragCoord - (iTime * 64.), squareSize) + 1.) / 2.;
    fragColor = vec4(noise, noise, noise, 1.0);
}

