//---------------------------------------------------------------

vec4 sdBox( vec3 p, vec3 b ) // distance and normal
{
    vec3 d = abs(p) - b;
    float x = min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
    vec3  n = step(d.yzx,d.xyz)*step(d.zxy,d.xyz)*sign(p);
    return vec4( x, n );
}

mat2 rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, s, -s, c);

}

vec4 fbmd( in vec3 x )
{
    const float scale  = 1.5;

    float a = .5;
    float b = .5;
	float f = 2.0;
    vec3  d = vec3(0.0);
    for( int i=0; i<14; i++ )
    {
        vec3 r = f*x*scale;
        // i don't know how to get good sine fbms without rotations
        r.xy *= rot(float(f*f));
        r.yz *= rot(float(f*f*f));
        vec4 n = noised(r);
        // this undoes(?!) the rotation for the derivatives
        n.zw = rot(float(f*f*f)) * n.zw;
        n.yz = rot(float(f*f)) * n.yz;

        a += b*n.x;           // accumulate values		
        d += b*n.yzw*f*scale; // accumulate derivatives
        b *= .66;             // amplitude decrease
        f *= 1.4;             // frequency increase
    }

	return vec4( a, d );
}

vec4 map( in vec3 p )
{
	vec4 d1 = fbmd( p );
    d1.x -= 0.37;
	d1.x *= 0.7;
    d1.yzw = normalize(d1.yzw);

    // clip to box
    vec4 d2 = sdBox( p, vec3(1.5) );
    return (d1.x>d2.x) ? d1 : d2;
}

// ray-box intersection in box space
vec2 iBox( in vec3 ro, in vec3 rd, in vec3 rad ) 
{
    vec3 m = 1.0/rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*rad;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
	float tN = max( max( t1.x, t1.y ), t1.z );
	float tF = min( min( t2.x, t2.y ), t2.z );
	if( tN > tF || tF < 0.0) return vec2(-1.0);
	return vec2( tN, tF );
}

// raymarch
vec4 interesect( in vec3 ro, in vec3 rd )
{
	vec4 res = vec4(-1.0);

    // bounding volume    
    vec2 dis = iBox( ro, rd, vec3(1.5) ) ;
    if( dis.y<0.0 ) return res;

    // raymarch
    float tmax = dis.y;
    float t = dis.x;
	for( int i=0; i<128; i++ )
	{
        vec3 pos = ro + t*rd;
		vec4 hnor = map( pos );
        res = vec4(t,hnor.yzw);

		if( hnor.x<0.001 ) break;
		t += hnor.x * .6;
        if( t>tmax ) break;
	}

	if( t>tmax ) res = vec4(-1.0);
	return res;
}

// compute normal numerically
#ifdef SHOW_NUMERICAL_NORMALS
vec3 calcNormal( in vec3 pos )
{
	vec2 eps = vec2( 0.0001, 0.0 );
	vec3 nor = vec3( map(pos+eps.xyy).x - map(pos-eps.xyy).x,
	                 map(pos+eps.yxy).x - map(pos-eps.yxy).x,
	                 map(pos+eps.yyx).x - map(pos-eps.yyx).x );
	return normalize(nor);
}
#endif

// fibonazzi points in s aphsre, more info:
// http://lgdv.cs.fau.de/uploads/publications/spherical_fibonacci_mapping_opt.pdf
vec3 forwardSF( float i, float n) 
{
    const float PI  = 3.141592653589793238;
    const float PHI = 1.618033988749894848;
    float phi = 2.0*PI*fract(i/PHI);
    float zi = 1.0 - (2.0*i+1.0)/n;
    float sinTheta = sqrt( 1.0 - zi*zi);
    return vec3( cos(phi)*sinTheta, sin(phi)*sinTheta, zi);
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float ao = 0.0;
    for( int i=0; i<32; i++ )
    {
        vec3 ap = forwardSF( float(i), 32.0 );
        float h = hash(float(i));
		ap *= sign( dot(ap,nor) ) * h*0.25;
        ao += clamp( map( pos + nor*0.001 + ap ).x*3.0, 0.0, 1.0 );
    }
	ao /= 32.0;

    return clamp( ao*5.0, 0.0, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;

	// camera anim
    float an = 0.1*iTime;
	vec3 ro = 3.0*vec3( cos(an), 0.8, sin(an) );
	vec3 ta = vec3( 0.0 );

    // camera matrix	
	vec3  cw = normalize( ta-ro );
	vec3  cu = normalize( cross(cw,vec3(0.0,1.0,0.0)) );
	vec3  cv = normalize( cross(cu,cw) );
	vec3  rd = normalize( p.x*cu + p.y*cv + 1.7*cw );

	// render
	vec3 col = vec3(0.0);
    vec4 tnor = interesect( ro, rd );
	float t = tnor.x;

    if( t>0.0 )
	{
		vec3 pos = ro + t*rd;
        #ifndef SHOW_NUMERICAL_NORMALS
        vec3 nor = tnor.yzw; // no need to call calcNormal( pos );
        #else
        vec3 nor = calcNormal( pos );
        #endif
        float occ = calcAO( pos, nor );
        float fre = clamp( 1.0+dot(rd,nor), 0.0, 1.0 );
        float fro = clamp( dot(nor,-rd), 0.0, 1.0 );
        col = mix( vec3(0.05,0.2,0.3), vec3(1.0,0.95,0.85), 0.5+0.5*nor.y );
        //col = 0.5+0.5*nor;
        col += 10.0*pow(fro,12.0)*(0.04+0.96*pow(fre,5.0));
        col *= pow(vec3(occ),vec3(1.0,1.1,1.1) );
	}

    col = sqrt(col);

    fragColor=vec4(col,1.0);
}

#define time iTime

float esp(vec3 p, float s){
  return length(p)-s;
}

float box(vec3 p, vec3 s){
  vec3 q = abs(p)-s;
  return max(q.x, max(q.y, q.z));
}

vec2 rp(vec2 p, vec2 s){
  return (fract(p/s-.5)-.5)/s;
}

float rp(float p, float s){
  return (fract(p/s-.5)-.5)/s;
}

mat2 rot(float a){
  float aco = cos(a);
  float asi = sin(a);
  return mat2(aco, asi, -asi, aco);
}

float map2(vec3 p){
  return -box(p, vec3(40.));
}

float at =1.;
float map(vec3 p){
//  
  float t1 = time*.1;

  for(float i = 0.; i < 8.;++i){

    p.xz *= rot(t1*i*.2);
    p.yz *= rot(t1*.3*i);
    p -= vec3(0.1, .1, .1);

    p = abs(p)-.5-vec3(.2, 0.5, .2)*i*.5+sin(time)*.5-.5;

    p.xy *= rot(time*.13);

  }

  float d2 = box(p, vec3(1.));
  at += .1/(.1+d2*d2);

  return min(d2, map2(p));

}

vec3 nm(vec3 p){
  vec2 offs=vec2(0.01, 0.);
  return normalize(map(p)-vec3(map(p-offs.xyy),map(p-offs.yxy), map(p-offs.yyx)));
}

void cam(inout vec3 p){
  p.xz *= rot(time+cos(time*.6)*.25-.5);
  p.yz *=rot(time*.6+sin(time*.56)*.25-.5);
  p.xy *= rot(time*.25);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);  

  float timing = floor(mod(time, 3.));
  if(timing > 2.){
    float det = texture(iChannel0, vec2(0.001)).x * 2000.;
    uv = floor(uv/det)/det;
  }

  vec3 s = vec3(0., 0., -25.);
  float fov =.7-sin(time*.5)*.25-.25;
  vec3 r = normalize(vec3(-uv, fov));
  vec3 col = vec3(0.);

  cam(s);
  cam(r);


  vec3 p = s;
  float i = 0.;
  float pred = 1.;


  for(;i < 50.;i++){
    float d = map(p);
    if(d<0.0001){
      float fog = 1.-i/50.;
      vec3 n = nm(p);
      vec3 l = normalize(vec3(-1.));
      float toc = floor(mod(time, 10.));
      float pi = acos(-1.);
      if(toc < 3. && toc > 1.)
        col += .6-max(dot(l,n),0.)*fog*vec3(.45, 0.3456, 0.4)*pi*sin(time*5.)*.4;
      if(toc > 3.){
        col += .5-max(dot(l,n),0.)*vec3(.456, .456, .2)*pi*sin(time)*5.*.2;
      }

      col += .5-max(dot(l,n),0.)*vec3(.4, .3, .3)*pi*sin(time)*5.*.26;
      col += .1/(100.+i)*vec3(0.4, 0.3,0.65)*sin(time)*.94-.1;
      col *= .6;
      col += 1.-length(p)/65.;
      col += at*.005*vec3(1., .5,.2);
      pred *= 0.6;

      r = reflect(n, l);
      d = 0.1;
      if(pred < 0.001) break;
    }
    if(d>100.)break;
    p+=d*r;
  }
  col *= 0.3;
  fragColor = vec4(col, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Calculate the to center distance
    float d = length(uv - 0.5) * 2.0;

    // Calculate the ripple time
    float t = d * d * 25.0 - iTime * 3.0;

    // Calculate the ripple thickness
    d = (cos(t) * 0.5 + 0.5) * (1.0 - d);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5 * cos(t / 20.0 + uv.xyx + vec3(0.0,2.0,4.0));

    // Set the output color to rgb channels and the thickness to alpha channel
    // AO is automatically calculated
    fragColor = vec4(col, d);
}

/** SHADERDATA
{
	"title": "Pirellyon",
	"description": "Next world",
	"model": "person"
}
