vec2 GetPoint(in vec2 cell) {
    return 0.5 + 0.5 * vec2(sin(cell.x - cell.y + iTime), sin(cell.x) * cos(cell.x + cell.y * iTime));
}

vec3 GetColor(in vec2 cell) {
    return vec3(fract(sin(cell.x * 4647.543 + cell.y * 2435.32) * 9658.65),
                fract(cos(cell.x * 672.7963 + cell.y * 9873.35) * 124.355),
                fract(sin(cell.x * 9446.745 + cell.y * 772.345) * 8755.64));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    vec2 cellLocation = floor(uv);

    float minDist = 1000000.0;
    vec3 color = vec3(0.0, 0.0, 0.0);
    for (int i=-1; i < 2; i++) {
        for (int j=-1; j < 2; j++) {
            vec2 curCellLocation = cellLocation + vec2(i, j);
            vec2 curCellPoint = curCellLocation + GetPoint(curCellLocation);
            float curDist = length(uv - curCellPoint);
            if (curDist < minDist) {
                color = GetColor(curCellLocation);
                minDist = curDist;
            }
        }
    }

    fragColor = vec4(color, 1.0);
}

// #version 410 core

#define f float 
#define v2 vec2
#define v3 vec3

#define F gl_FragCoord
// #define R v2Resolution
// #define T fGlobalTime
#define R iResolution
#define T iTime
#define N normalize

// #define BI(v) (texture(texFFTIntegrated, v).x)
// #define BS(v) (texture(texFFTSmoothed, v).x)
// #define B(v)  (texture(texFFT, v).x)

// fake beat accumulation, next time I will save it in buffer
#define BI(v) (0.05*T+0.02*texture(iChannel0, v2(v, 0.5)).x)
#define BS(v) (0.02*texture(iChannel0, v2(v, 0.5)).x)
#define B(v)  (0.01*texture(iChannel0, v2(v, 0.5)).x)

f gB = 10e8;

v3 colA = v3(0.2, 0.5, 1.4);

mat2 rot(f a) { return mat2(cos(a), -sin(a), sin(a), cos(a)); }

f box(v3 p, v3 b) {
  v3 q = abs(p) -b;
  return length( max(q, v3(0.))) + min( max(max(q.x, q.y), q.z), 0.);
}

f plas( vec2 v, float time )
{
	float c = 0.5 + sin( v.x * 10.0 ) + cos( sin( time + v.y ) * 20.0 );
  vec4 p = vec4( sin(c * 0.2 + cos(time)), c * 0.15, cos( c * 0.1 + time / .4 ) * .25, 1.0 );
	return dot(p.xyz, p.xyz);
}

f map(v3 p) {
  f s = min( p.y +1.5 + plas(0.1*p.xz - 0.1*T, 0.1*BI(0.0)), -p.y + 6.);

  v3 p0 = p; 
  p0.z -= 10.0*T;  
  p0.x  = abs(p0.x);  
  p0.xy *= rot( -0.7);  
  p0.x -= 8.;  
  p0.z = mod(p0.z + 2.0, 4.0) - 2.0;
  s = min(s, box(p0, v3(1.0, 100.0, 1.0)));

  p -= v3(0, 2., 12. -100.0*BS(0.0));

  for (f i = 0.; i < 4.; ++i) {
    p -= v3(0.2, 0.6 + 0.5*abs(sin(BI(0.0))), 0.6);
    p.xy *= rot(  10.0*BI(0.0));
    p.yz *= rot( -20.0*BI(0.0));
    p = abs(p);

    f sl = box(p, v3(0.01, 100.0, 0.01));
    s = min(s, sl);
    gB = min(gB, sl);
  }

  s = min(s , 
    mix(
      length(p) -0.3 -200.0*B(0.0),
      box(p, v3(0.3)),
      abs(sin(T))
)  );

  return s;
}

void mainImage( out vec4 out_color, in vec2 fragCoord )
{    	
  v3 c = v3(0);

  vec2 q = (2.0*F.xy - R.xy) / R.y;
  out_color = vec4(0.);
  if (abs(q.y) > 0.75) return;

  v3 ro = v3( 2.0*sin(20.0*BI(0.0)), 2. + 20.0*B(0.0), 0);
  v3 rd = N(v3(q, 2.));
  rd.xy *= rot( 0.4*sin(20.0*BI(0.0)) );

  // c = v3(q, 0.);
  c = v3(0.);
  f t = 0.2;
  f tt = 10e8;
  for (f bi = 0.; bi < 4.; ++bi) {
    for (f i = 0.; i < 64.; ++i) {
      f d = map(ro + rd * t);    
      if (d < 0.0001 || t > 40.) break;
      t += d;
    }  
     tt = bi == 0. ? t : tt;
      if (t > 0.1 && t < 40.0) {
          v3 p = ro + rd * t;
          f str0 = smoothstep(.5, .51, fract(2.33*p.x));
          f str1 = smoothstep(.5, .51, fract(0.1*p.x));

          v2 e = 0.001 * v2(-1., 1);
          v3 n = N(
                e.xxx * map(p + e.xxx)
              + e.yxx * map(p + e.yxx)
              + e.xyx * map(p + e.xyx)
              + e.xxy * map(p + e.xxy)
          );
          // c = n;
          f fre = pow(dot(rd, n) + 1., 6.);
          c += colA.zyx * (0.5+0.5*fre) * (str0+str1);

          rd = N(reflect(rd, n) + 0.01 * fract(sin(100.0*p) * 43758.5453));
          ro = p;
      }  
  } 
  // c = v3(1.0 - (t / 32.));
  // c = mix(c, N(mix(colA, colA.zyx, sin(20.0*(t / 32.)))), sin(T));

  c = mix(c, 0.01*colA, 1.0 - exp(-0.01 *tt*tt));

  f sb = 1. + 50.0*BS(0.0) + 20.0*B(0.0);
  c += sb * colA * exp(gB * -20.0);
  c += sb * colA * exp(gB * -10.0);
  c += sb * colA * exp(gB * -2.0);
  c += sb * colA * exp(gB * -1.0);
  c += 20.0*B(0.0);

  c = c / (1. + c);
  c = pow(c, v3(0.4545));
  out_color = c.xyzz;
}

// Hacked by 404Glaciergargamel!

#define PALETTE 5.8

//3 to 5 works best
#define TERRAIN_COMPLEXITY 3.
#define ITR 90
#define FAR 600.
#define time mod(iTime,400.)

mat2 mm2(in float a){float c = cos(a), s = sin(a);return mat2(c,-s,s,c);}
float smoothfloor(const in float x, const in float w)
{
    return floor(x)+smoothstep(w, 0.-w,fract(x));
}

vec3 enpos()
{
    return vec3(sin(time)*90.+40.,sin(time)*20.+20.,200.+sin(time*.8+sin(time*0.78+0.1))*90.);
}

//--------------------------------------------------------
//---------------------------HUD--------------------------
//--------------------------------------------------------

float square(in vec2 p){ return max(abs(p.x),abs(p.y));}
float loz(in vec2 p){ return abs(p.x)+abs(p.y);}

//from Dave (https://www.shadertoy.com/view/4djSRW)
vec2 hash2(float p)
{
	vec2 p2  = fract(p * vec2(4.3983, 4.4427));
    p2 += dot(p2.yx, p2.xy +  vec2(11.5351, 4.3137));
	return fract(vec2(p2.x * p2.y * 85.4337, p2.x * p2.y * 87.597));
}

float line( in vec2 a, in vec2 b, in vec2 p )
{
	vec2 pa = p - a, ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), -0.1, 0.9 );
	return length( pa - ba*h );
}

float crosshair(in vec2 p , in float tk, in float rt)
{
    float d = abs(p.x)+abs(p.y);
    float a = atan(p.y,p.x);
    float rz = smoothstep(0.02*tk,.03*tk,abs(d-0.4));
    d = sin(a*2.+0.59-time*2.5-rt);
    rz += smoothstep(-0.1,.06*tk,d);
    return rz;
}

//inspired by otaviogood "runes" (https://www.shadertoy.com/view/MsXSRn)
float text2(in vec2 p)
{
    p = (p+vec2(0.75,-.7))*6.;
    p.x *= 0.5;
    float sd = floor(time*7.);
    vec2 p1 = vec2(0), p2 = hash2(sd);
    float d= 1.;
    vec2 fl = vec2(4.,4.);
    for(float i=0.;i<6.;i++)
    {
    	if(hash2(sd+i+9.).x<0.2)continue;
        p1 = hash2(i+sd);
    	p2 = hash2(i+sd+0.);
		p1 = (floor(p1*fl) + .4)/fl;
		p2 = (floor(p2*fl) + .4)/fl;
        if (p1 == p2) p2 = vec2(.4);
    	d = min(line(p1, p2, p), d);        
        p1 = p2;
    	p2 = hash2(i+sd+2.);
		p2 = (floor(p2*fl) + .4)/fl;
    	d = min(line(p1, p2, p), d);
        p1 = p2;
    	p2 = hash2(i+sd+4.);
		p2 = (floor(p2*fl) + .4)/fl;
        if (p1 == p2)
        {
            p2 = hash2(i+sd+6.);
			p2 = (floor(p2*fl) + .4)/fl;
        }
    	d = min(line(p1,p2,p),d);
        p.x -= .7;
    }

    d = smoothstep(0.02, .07,d);
    return d;
}

vec3 makeHud(in vec2 p, in float seek)
{
    float sk1 = smoothstep(0.89, 0., seek);
    float sk2 = step(1.-sk1, .4);
    //lens deformation
    float ll = abs(p.x)+abs(p.y)*0.15;
    p *= ll * -.2+1.29;
    p *= 2.;
    vec3 col = vec3(-1);
    float d= 0.;
    //crosshairs
    float rz = crosshair(p*1.1, .8,1.+sk1);
    rz = min(rz,crosshair(p*1.7,1., -time*5.5-0.1-sk1));
    //minimap (top right)
    float d2 = square(p+vec2(-0.45, -0.57))+0.01;
    d = smoothstep(0.2,0.21,d2);
    d = max(d,smoothstep(1.25,.45,min(sin(p.x*70.+0.9),sin(p.y*70.+time*5.))+0.4));
    d = min(d,smoothstep(0.001,0.008,abs(d2-0.2)));
    vec3 enp = enpos()/900.;
    enp.z = 0.-enp.z;
    float en = smoothstep(0.015, 0.023, loz(enp.xz+p-vec2(0.47, 0.4))) ;
    en += mod(floor(time*1.5), 1.);
    d = min(d,en);
    rz = min(d,rz);
    //text (top left)
    rz= min(rz,text2(p));
    //altitude bars
    d = min(rz,sin(p.y*90.+sin(time)*10.)*2.+2.);
    d2 = max(d,(p.x+0.49)*100.);
    d2 = max(d2,-(p.x+0.56)*100.);
    float d3 = max(d,(p.x-0.56)*100.);
    d3 = max(d3,-(p.x-.49)*100.);
    d2 = min(d2,d3);
    d2 += smoothstep(0.49, .5, -p.y);
    d2 += smoothstep(0.49, .5, p.y);
    rz = min(rz,d2);    
    //bottom left "status"
    float num = mod(floor(time*20.),20.);
    vec2 p2 = p+vec2(-0.32,.84);
    d = 1.;
    for(float i=0.;i<7.;i++)
    {
        d = min(d,length(p2)+float(num==i));
    	p2.x -= 0.075;
    }
    d = smoothstep(0.013,.02,d);
    rz = min(d,rz);

    vec3 hcol = (sin(vec3(0.25,0.3,0.38)*(2.35)*PALETTE)*0.4+.4);
    hcol.gb -= sk2;
    hcol.r += sk2;
    return hcol*(1.-rz);
}

