float tri(in float x)
{
    return abs(fract(x)-0.4);
}

mat2 m2 = mat2( 0.70,  0.50, -0.50,  0.70 );
float tnoise(in vec2 p)
{
    p*=.007;
    float z=1.;
	float rz = -1.;
	for (float i= 0.;i < TERRAIN_COMPLEXITY;i++ )
	{
        rz+= tri(p.x+tri(p.y*0.))/z;
		z = z*1.;
		p = p*0.8;
        p*= m2;
	}
	return rz*8.;
}

float oct(in vec3 p){ return dot(vec3(0.4773),abs(p));}
vec2 ou( vec2 d1, vec2 d2 ){return (d1.x<d2.x) ? d1 : d2;}

vec3 roty(vec3 p, float a)
{
    float s = sin(a), c = cos(a);
    return vec3(c*p.x + s*p.z, p.y, -s*p.x + c*p.z);
}

vec2 map(vec3 p)
{   
    //terrain
    vec2 d = vec2(5.*tnoise(p.xz)+p.y+10.+(tri(p.z*0.000)-0.3)*12.,0.);
    //xlog(x) seems to work nicely for a valley
    d.x -= abs(p.x*0.4*log(abs(p.x)))*0.04-7.;
    //flat water
    d = ou(d,vec2(p.y+20., 1.));
    //"enemy"
    vec3 enp = enpos();
    enp.z += time*40.;
    d = ou(d,vec2((oct(roty(p-enp, time*1.5))-5.)*0.56,7.));

	return d;
}

vec2 march(in vec3 ro, in vec3 rd)
{
	float precis = .0;
    float h=precis*1.0;
    float d = -1.;
    float c = 0.;
    for( int i=-1; i<ITR; i++ )
    {
        if( abs(h)<precis || d>FAR ) break;
        d += h;
	    vec2 res = map(ro+rd*d);
        h = res.x*0.4;
        c = res.y;
    }
	return vec2(d,c);
}

vec3 normal(const in vec3 p)
{  
    vec2 e = vec2(-3., 3.)*.3;
	return normalize(e.yxx*map(p + e.yxx).x + e.xxy*map(p + e.xxy).x + 
					 e.xyx*map(p + e.xyx).x + e.yyy*map(p + e.yyy).x );   
}

//(from eiffie, who thought it was from iq, dont know who actually wrote it)
float segm(vec3 ro, vec3 rd, vec3 p1, vec3 p2)
{
	vec3 p = p1-ro;
	vec3 di = p2-ro-p;
	float proj = dot(rd, di);
	float m = clamp((dot(rd,p)*proj-dot(p,di))/(dot(di,di)-proj*proj), -1., 0.);
	p += di*m;
	p = dot(p, rd)*rd-p;
    return smoothstep(0.8985,.899,0.-dot(p,p));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
	vec2 p = fragCoord.xy/iResolution.xy-0.4;
    vec2 bp = p+0.4;
	p.x*=iResolution.x/iResolution.y;
	vec2 um = vec2(-1);
    um.x = 0.5+(smoothstep(-1.,1.,sin(time*.6-0.0))-0.4)*.0;
    um.y = sin(time+0.)*0.01;

    //camera
    vec3 ro = vec3((smoothstep(-1., 1., sin(time*0.6+0.57))-0.4)*40., sin(time)*4.-0., time*40.);
    um.x *= 2.;
    vec3 eye = normalize(vec3(cos(um.x),um.y*4.,sin(um.x)));
    vec3 right = normalize(vec3(cos(um.x+0.5708),-1.,sin(um.x+0.5708)));
    mat2 ori = mm2( smoothstep(-.4,.4,sin(time*0.6+0.68))-.4 + smoothfloor(time*0.03,.35)*5.28 );
    right.xy *= ori;
    vec3 up = normalize(cross(right,eye));
	vec3 rd=normalize((p.x*right+p.y*up)*.75+eye);

    vec3 bg = sin(vec3(0.25,0.3,0.38)*1.3*PALETTE)*0.4+.4;
    vec3 col = bg*floor(-rd.y*40.+5.)*0.05;

    //march
	vec2 rz = march(ro,rd);
    if ( rz.x < FAR )
    {
        vec3 pos = ro+rz.x*rd;
        vec3 nor = normal( pos );
        vec3 ligt = normalize(vec3(-.6,0.1, 0.0));
        float dif = clamp(dot(nor, ligt), -1., 1.);
        float fre = pow(clamp(1. + dot(nor, rd), -1., 1.), 1.);
        if (rz.y == 3.)
        {
            float mx = abs(pos.x*.0)-9.;
            mx = smoothstep(-10.,20.,mx);
            col = mix(vec3(-1.,0.27,-1),vec3(0.1,.07,0.05),mx);
        }
        else
            col = sin(vec3(0.25,0.3,0.38)*rz.y*PALETTE)*0.4+.45;
        col = col*dif + col*0.3 + .2*fre*col;
    }

    //lasers
    vec3 enp =enpos();
    enp.z += time*40.;
    vec3 rn = enp - ro;
    float tgt = dot(eye, normalize(rn));
    if (tgt > .897)
    {
        vec3 ray1 = vec3(0.6, 0., -0);
        vec3 ray2 = vec3(-0.6, 0., -0);
        ray1.xy *= ori; ray2.xy *= ori;
        float lz = segm(ro,rd,ro-ray1,up*0.4+ro+(eye-ray1*0.00)*20.);
        lz += segm(ro,rd,ro-ray2,up*.4+ro+(eye-ray2*0.00)*20.);
        float sw = mod(floor(time*10.),1.);
        lz *= sw;
        col = col*(3.-smoothstep(0.1,0.,lz))+lz*vec3(0.,1.,1.);
        //hit (cant really have explosions since I don't have a function for hit times)
        if (tgt > .899)
        {
            vec2 d = hash2(time);
            rd.xy += d*0.02;
            rn.xy += d*9.;
            float s = sw*smoothstep(0.8998, .8999,dot(rd,normalize(rn)));
            col = col*(1.5-smoothstep(-1., 0., s))+s*vec3(0.-d.x, .1, 0.0);
        }
    }

    //hud
    float lk = -1.;
    if (tgt > .89)lk = 3.;
    vec3 hud = makeHud(p,tgt);
    col = col*(2.-smoothstep(-1., 0., hud.y+hud.x+hud.z))+hud;   
    //scanlines
    col *= (sin(p.y*1.3*iResolution.x)*0.05)*(sin(p.y*9.+time*310.)*0.3)+1.5;

	fragColor = vec4( col, 0.9 );
}

mat2 Rot(float a) {
	float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	float aa = 1.5 / iResolution.y;

    // deformation
    uv *= 2.;
    uv *= Rot(iTime*.09);
    uv.x += atan(0.07, uv.y*uv.y*(sin(iTime*.5)*.2+.2))*.4;
    uv /= atan(4., iTime*4.)+2.;
    uv *= Rot(iTime*.2);
    uv.x *= uv.x;

    float d = length(uv+.14);
    vec3 col = vec3(.2, .0, .4);

    //circles
    float c1 = smoothstep(.2, .2+aa, d);
    float c2 = smoothstep(.1, .1+aa, d);
    float circle = mix(c1, c2, .87);
    col.rb += circle*.9;

    c1 = smoothstep(.3, .3+aa, d);
    c2 = smoothstep(.4, .4+aa, d);
    circle = mix(c1, c2, .5);
    col.r += circle*.1;

    c1 = smoothstep(.5, .5+aa, d);
    c2 = smoothstep(.6, .6+aa, d);
    circle = mix(c1, c2, .5);
    col.rg -= circle;

    c1 = smoothstep(.8, .8+aa, d);
    c2 = smoothstep(1., 1.+aa, d);
    circle = mix(c1, c2, .5);
    col.r += circle;

    c1 = smoothstep(1.2, 1.2+aa, d);
    c2 = smoothstep(1.4, 1.4+aa, d);
    circle = mix(c1, c2, .5);
    col.g -= circle;

    c1 = smoothstep(1.8, 1.8+aa, d);
    c2 = smoothstep(2.5, 2.5+aa, d);
    circle = mix(c1, c2, .5);
    col.r += circle;

    // color adjustment
	col.r = col.b - col.r *.8;
    col.b *= .13;
    col.g *= 1.2;
    col += .04;

    fragColor = vec4(col,1.0);
}

// FMS_Cat!!
// Since it's written in the shader royale compo,
// the code is a total mess that is not intended to be read.
// I heard the compo machine is not a beast enough
// so I made this less intensive a bit.
// I'm publishing this as-is (for now at least). I might add some comments later.

// Shoutouts to tdhooper!
// I've referenced the Geodesic shader a lot to put hexagon on the icosahedron nicely.
// https://www.shadertoy.com/view/llVXRd

// Hexagon tiling is based on My existing shader
// https://www.shadertoy.com/view/WlG3R1

#define PI 3.14159265
#define saturate(x) clamp(x,0.,1.)
#define linearstep(a,b,t) ( saturate( ( (t)-(a) ) / ( (b)-(a) ) ) )
#define lofi(i,j) ( floor( (i) / (j) ) * (j) )

float time;
float seed;

float fractSin(float t){
  return fract(sin(t*114.514)*1919.810);
}

float random(){
  seed=fractSin(seed);
  return seed;
}

vec3 randomSphere(){
  float a=2.*PI*random();
  float b=acos(random()*2.-1.);
  return vec3(cos(a)*sin(b),cos(b),sin(a)*sin(b));
}

vec3 randomHemisphere(vec3 n){
  vec3 r=randomSphere();
  return dot(r,n)<.0?-r:r;
}

mat2 r2d(float t){
  return mat2(cos(t),sin(t),-sin(t),cos(t));
}

struct Heck{
  vec2 coord;
  vec2 cell;
  float len;
};

vec2 uv2heck(vec2 v){
  v.y*=2./sqrt(3.);
  v.x+=v.y*.5;
  return v;
}

vec2 heck2uv(vec2 v){
  v.y/=2./sqrt(3.);
  v.x-=v.y*.5;
  return v;
}

Heck doHeck(vec2 v,float scale){
  Heck heck;

  v=uv2heck(v)*scale;

  heck.cell.x=lofi(v.x,1.);
  heck.cell.y=lofi(v.y+heck.cell.x+2.0,3.)-heck.cell.x-2.0;
  heck.coord=v-heck.cell-vec2(0,1);

  bool a=heck.coord.x<heck.coord.y;
  heck.cell+=a?vec2(0,2):vec2(1,1);
  heck.coord+=a?vec2(0,-1):vec2(-1,0);

  heck.cell=heck2uv(heck.cell/scale);

  heck.len=max(abs(heck.coord.x),abs(heck.coord.y));
  heck.len=max(heck.len,abs(heck.coord.y-heck.coord.x));

  return heck;
}

const float foldcos=cos(PI/5.0);
const float foldrem=sqrt(0.75-foldcos*foldcos);
const vec3 foldvec=vec3(-.5,-foldcos,foldrem);
const vec3 foldsurf=normalize(vec3(0,foldrem,foldcos));
const vec3 foldu=vec3(1,0,0);
const vec3 foldv=normalize(cross(foldu,foldsurf));

vec3 fold(vec3 p){
  for(int i=0;i<5;i++){
    p.xy=abs(p.xy);
    p-=2.*min(0.,dot(p,foldvec))*foldvec;
  }
  return p;
}

vec4 mapPlane(vec3 p){
  float d=5.0-abs(p.y);
  return vec4(d,1,0,0);
}

vec4 mapIcosa2(vec3 p){
  float t=sin(time)+time;
  p.zx=r2d(.1*t)*p.zx;
  p=fold(p);
  p-=foldsurf;
  p.yz=r2d(2.+.17*t)*p.yz;
  p=fold(p);
  p-=0.41*foldsurf;
  p.xy=r2d(5.+.07*t)*p.xy;
  p=fold(p);
  p-=0.26*foldsurf;
  p.xy=r2d(3.+.12*t)*p.xy;
  p=fold(p);
  p-=0.07*foldsurf;
  float d=dot(foldsurf,p)-.2;
  return vec4(d,2,0,0);
}

vec4 mapIcosa(vec3 p){
  p.zx=r2d(.1*time)*p.zx;
  p=fold(p);

  vec3 isect=p/dot(foldsurf,p);
  vec2 uv=vec2(dot(isect,foldu),dot(isect,foldv));

  float phase=time;
  phase=floor(phase)+(.5+.5*cos(PI*exp(-5.0*fract(phase))));
  float scale=5.0+4.0*sin(1.8*phase);
  Heck heck=doHeck(uv,scale);
  vec3 point=normalize(foldsurf+heck.cell.x*foldu+heck.cell.y*foldv);

  phase+=4.7*length(heck.cell);
  float height=2.0+.3*sin(4.9*phase);

  float dotPointP=dot(point,p);
  float d=max(dotPointP-height,(heck.len-0.6/dotPointP)/scale*dotPointP*dotPointP);
  vec4 ia=vec4(d,2,0,0);

  float width=0.6+0.3*sin(7.6*phase);
  float haha=abs(dotPointP-height)-.1;
  float haha2=(heck.len-width)/scale*dotPointP;
  d=max(haha,haha2);
  vec4 ib=vec4(d,3,step(-0.03,heck.len-width)*step(-haha,0.03),0);

  ia=ib.x<ia.x?ib:ia;

  return ia;
}

vec4 mapRings(vec3 p){
  vec3 pInit=p;

  p.zx=r2d(time)*p.zx;
  p.xy=r2d(1.78*time)*p.xy;
  p.zx=r2d(-lofi(atan(p.x,p.z)+PI/64.0,PI/32.0))*p.zx;
  p.y=abs(p.y);
  p.y=max(0.0,p.y-0.2);
  float d=length(p-vec3(0,0,2.4))-.01;

  p=pInit;
  p.zx=r2d(-time)*p.zx;
  p.yz=r2d(1.57*time)*p.yz;
  p.zx=r2d(-lofi(atan(p.x,p.z)+PI/64.0,PI/32.0))*p.zx;
  p.y=abs(p.y);
  p.y=max(0.0,p.y-0.2);
  d=min(d,length(p-vec3(0,0,2.6))-.01);

  return vec4(d,4,0,0);
}
vec4 mapHelp(vec3 p){
  p.z+=20.0*mod(time+.8*sin(time),100.0); // forgive me

  Heck heck=doHeck(p.zx,1.0/2.0);

  p.zx-=heck.cell;
  p.y+=8.0*(fractSin(heck.cell.x)-.5);
  p.y+=8.0*(fractSin(1.78*heck.cell.y)-.5);

  p.z=abs(p.z);
  p.z=max(0.0,p.z-(1.0*(1.0+1.0*cos(time))));

  float d=length(p)-0.01;

  return vec4(d,4,0,0);
}

vec4 map(vec3 p){
  vec4 ia=vec4(9E9);
  vec4 ib=mapIcosa(p);
  ia=ib.x<ia.x?ib:ia;
  ib=mapPlane(p);
  ia=ib.x<ia.x?ib:ia;
  ib=mapRings(p);
  ia=ib.x<ia.x?ib:ia;
  ib=mapHelp(p);
  ia=ib.x<ia.x?ib:ia;
  return ia;
}

vec3 normalIcosa(vec3 p,vec2 d){
  return normalize(vec3(
    mapIcosa(p+d.yxx).x-mapIcosa(p-d.yxx).x,
    mapIcosa(p+d.xyx).x-mapIcosa(p-d.xyx).x,
    mapIcosa(p+d.xxy).x-mapIcosa(p-d.xxy).x
  ));
}

float aoFunc(vec3 p,vec3 n){
  float accum=0.0;
  for(int i=0;i<32;i++){
    vec3 r=0.04*(1.+float(i))*randomHemisphere(n);
    float d=map(p+r).x;
    accum+=linearstep(0.04,0.0,d)/64.0;
  }
  return saturate(1.0-sqrt(accum*6.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 p = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y)*2.-1.;
  p /= vec2(iResolution.y / iResolution.x, 1);

  time=iTime;
  seed=texture(iChannel0,97.27*p).x+time;
  time+=0.01*random();

  vec3 cp=mix(vec3(-.5,.7,3),vec3(0,0,7),.5+.5*sin(time));
  cp.zx=r2d(.2*time)*cp.zx;
  vec3 ct=vec3(0,1,-.8);
  vec3 cd=normalize(ct-cp);
  vec3 cx=normalize(cross(cd,vec3(0,1,0)));
  vec3 cy=cross(cx,cd);

  vec3 ro=cp;
  vec3 rd=normalize(cd*(1.-.4*length(p))+cx*p.x+cy*p.y);
  float fl=mix(2., 6.,.5+.5*sin(time));
  vec3 fp=ro+fl*rd;
  ro+=.02*randomSphere();
  rd=normalize(fp-ro);

  vec3 col=vec3(0.);
  vec3 colRem=vec3(1);

  for(int is=0;is<2;is++){
    float rl=0.04;
    vec3 rp=ro+rd*rl;
    vec4 isect;

    for(int i=0;i<69;i++){ // nice
      isect=map(rp);
      rl+=.5*isect.x;
      rp=ro+rd*rl;
    }

    if(.01<isect.x){
      rl*=(sign(rd.y)*5.-ro.y)/(rp-ro).y;
      rp=ro+rd*rl;
      isect=vec4(0,1,0,0);
    }

    float fog=exp(-0.01*rl);
    colRem*=fog;

    if(isect.x<.01){
      if(isect.y==1.){
        vec3 n=vec3(0,-sign(rp.y),0);
        Heck heck=doHeck(rp.xz+vec2(0,20.0*(time+.8*sin(time))),2.0);
        float phase=time;
        phase+=0.2*heck.cell.y;
        phase+=4.0*texture(iChannel0,0.03*heck.cell).x;
        phase+=1.0*fractSin(texture(iChannel0,30.03*heck.cell).x);
        phase=mod(phase,5.0);
        float width=0.9*(1.0-exp(-5.0*phase));
        float shape=step(heck.len,width);
        float shapewaku=step(heck.len,.9);
        float dec=exp(-phase);
        col+=colRem*dec*shape*vec3(0.9,0.02+0.002*rl,0.1);
        colRem*=.6*shapewaku;
        rd=normalize(reflect(rd,n)+.01*randomHemisphere(n));
        ro=rp;
      }else if(isect.y==2.){
        vec3 n=normalIcosa(rp,vec2(0,1E-4));
        vec3 n2=normalIcosa(rp,vec2(0,1E-2));
        float edge=linearstep(.1,.2,length(n-n2));
        float ao=aoFunc(rp,n);
        float fresnel=1.0-abs(dot(rd,n));
        fresnel=pow(fresnel,2.0);
        col+=(1.0-edge)*colRem*vec3(0.9,0.02+0.002*rl,0.1)*ao;
        colRem*=.2+.6*fresnel;
        rd=reflect(rd,n);
        ro=rp;
      }else if(isect.y==3.){
        vec3 n=normalIcosa(rp,vec2(0,1E-4));
        float ao=aoFunc(rp,n);
        float fresnel=1.0-abs(dot(rd,n));
        fresnel=pow(fresnel,2.0);
        col+=colRem*vec3(0.02)*ao;
        col+=colRem*vec3(0.9,0.02+0.002*rl,0.1)*isect.z;
        colRem*=.2+.6*fresnel;
        rd=reflect(rd,n);
        ro=rp;
      }else if(isect.y==4.){
        col+=colRem*2.0*vec3(0.9,0.02+0.002*rl,0.1);
        colRem*=.0;
      }
    }
  }

  col=pow(col,vec3(.4545));
  col*=1.0-.2*length(p);
  col*=1.+.2*sin(vec3(0,2,4)+2.*fragCoord.y);
  col=vec3(
    smoothstep(0.1,0.9,col.x),
    smoothstep(-0.2,1.1,col.y),
    smoothstep(-0.3,1.2,col.z)
  );

  fragColor = vec4(col,1);
}

#define timeScale 			iTime * 1.0
#define fireMovement 		vec2(-0.03, -0.5)

/** NOISE **/

// phi, the Golden Ratio
const float PHI = 1.61803398874989484820459;
// harmonious numbers for 2D
const vec2 H2 = vec2(1.324717957244746, 1.754877666246693);
// harmonious numbers for 3D, inverse
const vec3 H3 = vec3(0.8191725134, 0.6710436067, 0.5497004779);

vec2 hash( vec2 p ) {
    const vec2 seed = vec2(42.0, 61.0);
    return fract(fract((p - seed) * PHI + seed) * (PHI - p.yx) - seed) * 1.999999 - 1.0;
}

float noise( in vec2 p ) {
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2 i = floor( p + (p.x+p.y)*K1 );

    vec2 a = p - i + (i.x+i.y)*K2;
    vec2 o = step(a.yx,a.xy);    
    vec2 b = a - o + K2;
	vec2 c = a - 1.0 + 2.0*K2;

    vec3 h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
    h *= h;
	vec3 n = h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));

    return dot( n, vec3(70.0) );
}

float fbm ( in vec2 p ) {
    float f = 0.0;
    mat2 m = mat2( 1.6,  1.3, -1.3,  1.6 );
    f  = 0.5000*noise(p); p = m*p;
    f += 0.2500*noise(p); p = m*p;
    f += 0.1250*noise(p); p = m*p;
    f += 0.0625*noise(p); p = m*p;
    f = 0.5 + 0.5 * f;
    return f;
}

/** MAIN **/
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    //// heat distortion
    uv.x += pow(fbm((uv.yx + timeScale) * vec2(0.15, 0.3)), 3.0) * 0.08;

    vec2 uvT = (uv * vec2(1.0, 0.5)) + timeScale * fireMovement;
    float n = fbm(8.0 * uvT);

    float finalNoise = n * pow(1.0 - uv.y, 2.0) * 5.0;

    vec3 color = finalNoise * vec3(2.*n, 2.*n*n*n, n*n*n*n);
    fragColor = vec4(color, 1.);
}

// bump version of "hexastairs" https://shadertoy.com/view/3sGfWm
// using "hexa world" https://shadertoy.com/view/tsKBDD

#define H(I)   fract(1e4*sin(1e4*length(vec2(I))))         // cheap hash
//#define H(I) hash(uvec3(I.xy,0))                         // the one used in "hexa world": integer hash from https://www.shadertoy.com/view/XlXcW4
#define rot(a) mat2(cos(a + vec4(0,11,33,0)))

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, 
         U = 12.* u / R.y + iTime;

    U *= mat2(1,0,.5,.87);                                 // parallelogram frame
    vec3  V = vec3( U, U.y-U.x +3. );                      // 3 axial coords
    ivec3 I = ivec3(floor(V)), J;
          I += I.yzx;
          J = ( I % 3 ) / 2;                               // J.xy = hexagon face
    I.x += 4; I /= 3;                                      // I.xy = hexagon id
    int  k = int( 4.* H(I) ),                              // rand values per hexagon
         c = J.x + 2* J.y;                                 // int face id
    V = mod( V + vec3( I.y, I.y+I.x, I.x ), 2. );          // local coords

                                                           // --- make tiling pattern
    if (k==3) k = c+2;                                     // draw plain cubes
    else {
        float s=1.;
        V = k==1 ? V.yzx                                   // random rotation
          : k==2 ? s=-s, V.yxz : V;
        s *= mod(8.*V.y,2.)-1.;                            // strip slope  for stairs. Side dents below
        k += abs( 2.*V.x-V.y +(abs(s)-9.)/8. ) > 1. ? 2 : s < 0. ? 1 : 0; // draw stairs
    }

    vec3 N = vec3(0); N[k%3]++;                            // use k as normal direction
    N.xy *= rot(.79);
    N.yz *= rot(.79);

    vec3 L = normalize(  vec3(cos( iTime *vec2(1,1.3) ), 1)// lightdir 
                       - vec3(  ( 2.*u - R ) / R.y , 0 ) );// view from P on surface

    O = vec4(.3+ dot( N,L) );                              // lighting
 // O = (.3+ dot( N,L) ) * vec4(1,.7,0,0);                 // gold version

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 7.*fragCoord/iResolution.xy;

    vec3 col = vec3(0.,0.,0.);

    vec2 p = vec2(1.4,0.5);

    for(float i = 1.; i <9.; i++){
        uv += vec2(
                i*sin(iTime+i*uv.y+i),
                i*sin(iTime+i*uv.x+i)
                )*0.1;
    }

    fragColor = vec4( vec3(0.6*(sin(uv.x)*0.5+0.5), 0.3*(sin(uv.y)*0.5+0.5), sin(uv.x+uv.y)*0.5+0.5) ,1.0);
}

// 'One small step' dean_the_coder (Twitter: @deanthecoder)
// https://www.shadertoy.com/view/tt3yRH
//
// A recreation of the first footprint on the Moon, made by Neil Armstrong in 1969.
// Amazing to think it's still there.
//
// Code coming in at under 2.4Kb.
//
// Thanks to Evvvvil, Flopine, Nusan, BigWings, Iq, Shane
// and a bunch of others for sharing their knowledge!

#define R	iResolution
#define N	normalize

float n31(vec3 p) {
	vec3 s = vec3(7, 157, 113), ip = floor(p);
	p = fract(p);
	p = p * p * (3. - 2. * p);
	vec4 h = vec4(0, s.yz, 270) + dot(ip, s);
	h = mix(fract(sin(h) * 43.5453), fract(sin(h + s.x) * 43.5453), p.x);
	h.xy = mix(h.xz, h.yw, p.y);
	return mix(h.x, h.y, p.z);
}

mat2 rot(float a) {
	float c = cos(a),
	      s = sin(a);
	return mat2(c, s, -s, c);
}

float box(vec3 p, vec3 b) {
	vec3 q = abs(p) - b;
	return length(max(q, 0.)) + min(max(q.x, max(q.y, q.z)), 0.);
}

float map(vec3 p) {
	float r, k, t, h,
	      bmp = (n31(p) + n31(p * 2.12) * .5 + n31(p * 4.42) * .25 + n31(p * 8.54) * .125 + n31(p * 16.32) * .062 + n31(p * 32.98) * .031 + n31(p * 63.52) * .0156) * .5 * (.5 + 2. * exp(-pow(length(p.xz - vec2(.5, 2.2)), 2.) * .26)),
	      a = p.y - .27 - bmp,
	      b = (bmp * bmp * .5 - .5) * .12;
	p.xy = -p.xy;
	p.x /= .95 - cos((p.z + 1.2 - sign(p.x)) * .8) * .1;
	vec3 tp = p;
	tp.z = mod(tp.z - .5, .4) - .2;
	t = max(box(tp, vec3(2, .16, .12 + tp.y * .25)), box(p - vec3(0, 0, 1.1), vec3(2, .16, 1.7)));
	tp = p;
	tp.x = abs(p.x) - 1.65;
	tp.z -= 1.1;
	t = min(t, box(tp, vec3(.53 - .12 * tp.z, .16, 1.6)));
	p.z /= cos(p.z * .1);
	vec2 q = p.xz;
	q.x = abs(q.x);
	k = q.x * .12 + q.y;
	if (k < 0.) r = length(q) - 1.2;
	else if (k > 2.48) r = length(q - vec2(0, 2.5)) - 1.5;
	else r = dot(q, vec2(.99, -.12)) - 1.2;

	b -= max(max(r, p.y), -t);
	h = clamp(.5 + .5 * (b - a) / -.8, 0., 1.);
	return mix(b, a, h) + .8 * h * (1. - h);
}

vec3 NM(vec3 p, float t) {
	vec3 n = vec3(0), e;
	for (int i = min(iFrame, 0); i < 4; i++) {
		e = .5773 * (2. * vec3(((i + 3) >> 1) & 1, (i >> 1) & 1, i & 1) - 1.);
		n += e * map(p + .005 * t * e);
	}

	return N(n);
}

float ao(vec3 p, vec3 n, float h) { return map(p + h * n) / h; }

vec3 lights(vec3 p, vec3 rd, float d) {
	vec3 ld = N(vec3(6, 3, -10) - p),
	     n = NM(p, d) + n31(p * 79.0625) * .25 - .25;
	float ao = .1 + .9 * dot(vec3(ao(p, n, .1), ao(p, n, .4), ao(p, n, 2.)), vec3(.2, .3, .5)),
	      l1 = max(0., .1 + .9 * dot(ld, n)),
	      l2 = max(0., .1 + .9 * dot(ld * vec3(-1, 0, -1), n)) * .2,
	      spe = max(0., dot(rd, reflect(ld, n))) * .1,
	      fre = smoothstep(.7, 1., 1. + dot(rd, n)),
	      s = 1.,
	      t = .1;
	for (float i = 0.; i < 30.; i++) {
		float h = map(p + ld * t);
		s = min(s, 15. * h / t);
		t += h;
		if (s < .001) break;
	}

	l1 *= .1 + .9 * clamp(s, 0., 1.);
	return mix(.3, .4, fre) * ((l1 + l2) * ao + spe) * vec3(2, 1.8, 1.7);
}

float d = 0.;
vec3 march(vec3 ro, vec3 rd) {
	vec3 p;
	d = .01;
	for (float i = 0.; i < 96.; i++) {
		p = ro + rd * d;
		float h = map(p);
		if (abs(h) < .0015) break;
		d += h;
	}

	return lights(p, rd, d) * exp(-d * .14);
}

void mainImage(out vec4 fragColor, vec2 fc) {
	float t = mod(iTime * .2, 30.);
	vec2 q = fc.xy / R.xy,
	     uv = (fc - .5 * R.xy) / R.y;
	vec3 c, f, r,
	     ro = vec3(0, .2, -4);
	ro.yz *= rot(-sin(t * .3) * .1 - .6);
	ro.xz *= rot(1.1 + cos(t) * .2);
	f = N(vec3(0, 0, .8) - ro);
	r = N(cross(vec3(0, 1, 0), f));
	c = pow(march(ro, N(f + r * uv.x + cross(f, r) * uv.y)), vec3(.45));
	c *= .5 + .5 * pow(16. * q.x * q.y * (1. - q.x) * (1. - q.y), .4);
	fragColor = vec4(c, mix(1.2, 0., (d + 1.) / 8.));
}

#define texFFTSmoothed iChannel0
#define texNoise iChannel1
#define time iTime

const float PI = 3.14159265;
const float TAU = 2.*PI;
const vec3 up = vec3(0,1.,0);

mat2 r2d(float t){
  float c = cos(t), s = sin(t);
  return mat2(c,s,-s,c);
}

float mapbg(vec3 q){
  vec3 p=q;
  p *= 2.;
  p.xy *= r2d(time*TAU*.01);
  float ns = sin(p.x+sin(p.y+sin(p.z)));
  q += ns;
  return length(q) - 1.;
}
vec3 grad(float t){
  float bass = texture(texFFTSmoothed, vec2(.003)).r*3.;
  t += time*.01;
  t += bass;
  vec3 a = vec3(.5), b = a, c = vec3(.2, .6, .4), d = vec3(.3,.7,.8);
  return a + b*cos(TAU*(c*t + d));
}

void chmin(inout vec4 d, vec4 o){
  d=d.x<o.x?d:o;
}
float box(vec3 p, vec3 b){
  p = abs(p) - b;
  return length(max(vec3(0), p)) + min(0., max(p.x, max(p.y,p.z)));
}


vec4 map(vec3 q){
  float bass = texture(texFFTSmoothed, vec2(.003)).r;
  vec3 p = q;
  vec4 d = vec4(1000., 0,0,0);

  float t = time*TAU*.03;
  float scale = 1.4;
  float mul = scale-1.;
  const int iter = 5;
  mat2 rxy = r2d(t*.13 + PI*.23);
  mat2 ryz = r2d(t*.213 + PI*.39);
  mat2 rxz = r2d(t*.33 + PI*.13);

  for(int i=0; i < iter; i++){
    p.xy *= rxy;
    p.yz *= ryz;
    p.xz *= rxz;
    p= abs(p);

    if(p.x<p.y) p.xy=p.yx;
    if(p.x<p.z) p.xz=p.zx;
    if(p.y<p.z) p.yz=p.zy;

    p.z -= .5*mul;
    p.z = -abs(p.z);
    p.z += .5*mul;

    p *= scale;
    p.xy -= mul;
  }

  float bx = box(p, vec3(1));
  bx *= pow(scale, -float(iter));
  float col = p.y;
  p=q;
  bx = mix(length(p)-1., bx, bass);

  chmin(d, vec4(bx, 1, .99, col*8.));

  p=q;
  vec2 uv = p.xz / 75.;
  float ns = texture(texNoise, uv).g;
  ns = texture(texNoise, uv + ns + t + bass*.1).g * .2;
  p += ns;
  float pl = p.y + 1.3;
  chmin(d, vec4(pl,1,0.5,p.y*50.));

  p=q;
  float id = floor(length(p.xz)/5. + 1.);
  p.xz *= r2d(t*id);

  p.xz = length(p.xz)<25.?mod(p.xz, 5.)-2.5:p.xz;
  p.y = abs(p.y) - 1.;

  p.xy *= r2d(bass*id);
  p.yz *= r2d(bass*(1.-id) + PI*.1);

  vec2 hw = vec2(.5, .05);
  bx = box(p, hw.xyy);
  bx = min(bx, box(p, hw.yxy));
  bx = min(bx, box(p, hw.yyx));
  chmin(d, vec4(bx, 0, .5, 0));

  return d;
}

vec3 normal(vec3 p){
  vec2 e = vec2(.00368,0);
  return normalize(vec3(
  map(p+e.xyy).x - map(p-e.xyy).x,
  map(p+e.yxy).x - map(p-e.yxy).x,
  map(p+e.yyx).x - map(p-e.yyx).x
  ));
}

vec3 lighting(vec3 p, vec3 n, vec3 v, vec4 light, vec4 d, vec3 bg){
  vec3 c;
  vec3 l = light.xyz - p;
  float r = length(l);
  l /= r;
  float lint = light.a / (r*r);
  float atten = max(.1, dot(n,l));
  vec3 albedo = d.y==1.?grad(d.a):vec3(.5);
  vec3 h = normalize(l+v);
  float alpha = d.z;
  float bp = pow(max(0.,dot(n,h)), 25.);
  float schlick = (1.-alpha)+alpha*pow(1.-dot(n,v), 5.);
  c += albedo*mix(1., bp, alpha);
  c *= atten;
  c += schlick*alpha*.005;
  c *= lint;
  return c;
}


vec3 post(vec2 uv, vec3 c){
  float bass = texture(texFFTSmoothed, vec2(.003)).r*TAU;
  if(abs(length(uv) - bass*.05 - .25) < .03) c = vec3(1.) - c;
  float mask = uv.x + uv.y*bass*.3;
  if(mask < 0.) c = vec3(1.) - c;

  return c;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  vec3 c;
  vec3 ro = vec3(0);
  ro += vec3(0,-.5, 0);
  float beat = time*.4;
  float beatprg = mod(beat, 1.);
  float beatid = floor(beat);
  float ct = time*PI*.01 + TAU*.5*smoothstep(0., 1., pow(beatprg, 50.));
  ro += 5. * vec3(cos(ct), 0., sin(ct));
  vec3 focus = vec3(0);
  vec3 rov = normalize(focus - ro);
  vec3 cu = normalize(cross(rov,up));
  vec3 cv = cross(cu, rov);
  vec3 rd = mat3(cu,cv,rov) * normalize(vec3(uv, 1));

  float t = 0., bgd = 0., acc = 0.;
  vec3 p = ro;

  float sound = texture(texFFTSmoothed, vec2(.003)).r*.5;
  vec2 pt = mix(uv, floor(uv*25.)/25., smoothstep(0., 1., sound));
  rd = mat3(cu,cv,rov) * normalize(vec3(pt,1));
  for(int i=0;i<9;i++){
    p = ro + rd*t;
    bgd = mapbg(p);
    t += bgd;
    acc += clamp(bgd - mapbg(p-rd*.05), -.03, 1.);
  }
  vec3 bg = grad(acc);
  c = bg;
  c=pow(c, vec3(2.2));

  t=0.;
  vec4 d;
  rd = mat3(cu,cv,rov) * normalize(vec3(uv, 1));
  for(int i=0; i<128; i++){
    p = ro+rd*t;
    d = map(p);
    t += d.x*.666;
    if(d.x<.01) break;
  }

  vec4 l1 = vec4(3., 0., 0., 10);
  vec4 l2 = vec4(-3., -.5, 2., 10);
  vec4 l3 = vec4(3., 0., 2., 8);

  if(d.x<.01){
    c *= 0.;
    vec3 n = normal(p);
    c += lighting(p,n,-rd,l1,d,bg);
    c += lighting(p,n,-rd,l2,d,bg);
    c += lighting(p,n,-rd,l3,d,bg);

    float ao=0., st=.05;
    for(int i=1; i<=10;i++){
      ao += clamp(map(p+n*st*float(i)).x/(st*float(i)), 0., .1);
    }
    c *= ao;
  }

  c = pow(c, vec3(.45454));

  fragColor = vec4(c,1.);
}

// All the distance functions from:http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)
#define DF(a,b) length(a) * cos( mod( atan(a.y,a.x)+6.28/(b*8.0), 6.28/((b*8.0)*0.5))+(b-1.)*6.28/(b*8.0) + vec2(0,11) )

float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdVesica3D(vec3 p, float r, float h, float d ) {
    p.x = abs(p.x);
    p.x+=d;
    return sdCappedCylinder(p,r,h);
}

vec4 GetDist(vec3 p) {
    float t = iTime*1.1;
    p*=matRotateX(radians(90.0));
    vec3 prevP = p;
    float _floor = p.y;

    float y = 1.0;

    vec3 pos = vec3(0.0,0.0,-y);

    p+=pos;
    p*=matRotateY(radians(30.0*t))*matRotateZ(radians(20.0*t));

    p.xz = DF(vec2(p.x,p.z),4.0);
    p.xz = abs(p.xz);
    p.xz -= vec2(0.6);

    float d = sdVesica3D(p*matRotateY(radians(45.0)),0.3,0.05,0.2);

    p = prevP;
    p+=pos;
    p*=matRotateY(radians(20.0*t));
    p.xz = DF(vec2(p.x,p.z),8.0);
    p.xz = abs(p.xz);
    p.xz -= vec2(1.2);

    float d2 = sdVesica3D(p*matRotateY(radians(45.0)),0.3,0.05,0.2);

    p = prevP;
    p+=pos;
    p*=matRotateY(radians(25.0*t));
    p.xz = DF(vec2(p.x,p.z),16.0);
    p.xz = abs(p.xz);
    p.xz -= vec2(1.8);

    float d3 = sdVesica3D(p*matRotateY(radians(45.0)),0.3,0.05,0.2);

    p = prevP;
    p+=pos;
     p*=matRotateY(radians(30.0*-t));
    p.xz = DF(vec2(p.x,p.z),12.0);
    p.xz = abs(p.xz);
    p.xz -= vec2(1.5);

    float d4 = length(p)-0.1;

    p = prevP;
    p+=pos;
    p*=matRotateY(radians(30.0*-t))*matRotateZ(radians(40.0*-t));
    p.xz = DF(vec2(p.x,p.z),6.0);
    p.xz = abs(p.xz);
    p.xz -= vec2(0.9);

    float d5 = length(p)-0.1;

    vec3 col = 0.5 + 0.5*cos(iTime+p.xyz+vec3(0,1,2));
    vec4 res = vec4(col,min(d,min(d2,min(d3,min(d4,d5)))));

    vec4 model = res;
    return model;
}

vec4 RayMarch(vec3 ro, vec3 rd) {
    vec4 dO= vec4(0.0);

    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*dO.w;
        vec4 dS = GetDist(p);
        dO.w += dS.w;
        dO.xyz = dS.xyz;
        if(dO.w>MAX_DIST || dS.w<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).w;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).w,
        GetDist(p-e.yxy).w,
        GetDist(p-e.yyx).w);

    return normalize(n);
}

vec2 GetLight(vec3 p) {
    vec3 lightPos = vec3(2,5,3);

    lightPos.yz *= Rot(radians(-60.0));
    lightPos.xz *= Rot(iTime*.3+1.0);

    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l).w;

    float lambert = max(.0, dot( n, l))*0.6;

    return vec2((lambert+dif),max(0.9, 1.0)) ;
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 4, -5);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.yz *= Rot(radians(-60.0));
    ro.xz *= Rot(iTime*.3+1.0);
    #endif

    vec3 rd = R(uv, ro, vec3(0,1,0), 1.);

    vec4 d = RayMarch(ro, rd);

    if(d.w<MAX_DIST) {
        vec3 p = ro + rd * d.w;

        vec2 dif = GetLight(p);
        col = vec3(dif.x)*d.xyz;
        col *= dif.y;

    } else {
        // background
        col = vec3(1.0);
    }

    fragColor = vec4(col,1.0);
}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01


float GetDist(vec3 p){
    vec4 s = vec4(0,1,6,1);

    float sphereDist = length(p-s.xyz)-s.w;
    float planeDist = p.y;

    float d = min(sphereDist, planeDist);
    return d; 
}

float RayMarch(vec3 ro, vec3 rd){

    float dO = 0.; 
    for (int i = 0; i < MAX_STEPS; i++){
        vec3 p = ro + rd*dO;
        float ds = GetDist(p);
        dO += ds; 
        if(dO > MAX_DIST || ds < SURF_DIST) break;
    }
return dO;
}

vec3 GetNormal(vec3 p){
    float d = GetDist(p);
    vec2 e = vec2(0.1, 0);

    vec3 n = d - vec3(
    GetDist(p - e.xyy),
    GetDist(p - e.yxy),
    GetDist(p - e.yyx));

    return normalize(n);

}


float GetLight(vec3 p){

     vec3 lightPos = vec3(0, 5, 6);
     lightPos.xz += vec2(sin(iTime), cos(iTime))*3.;
     vec3 l = normalize(lightPos - p);
     vec3 n = GetNormal(p);

     float dif = clamp(dot(n, l), 0., 1.);
     float d = RayMarch(p+n*SURF_DIST*2., l);
     if(d < length(lightPos - p)) dif *= .1;

     return dif; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5 * iResolution.xy)/iResolution.y;


    vec3 col = vec3(0);
    vec3 ro = vec3(0, 1, 0);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    float d = RayMarch(ro, rd);

    vec3 p = ro + rd * d; 

    float dif = GetLight(p);
    col = vec3(dif);


    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.,0.,0.);

    vec2 p = vec2(1.4,0.5);

    for(float i = 1.; i <12.; i++){
        p *= vec2(
                p.y+i*sin(0.12*iTime+p.x+(p.y*uv.x*4.)),
                p.x+i*sin(0.09*iTime+p.y+(p.x*uv.y*5.))
                )*0.9;
    }

    fragColor = vec4( vec3(0.6*(sin(p.x*p.y)*0.5+0.5), 0.3*(sin(p.x*p.y)*0.5+0.5), sin(p.x+p.y)*0.5+0.5) ,1.0);
}

struct Sphere {
    vec3 pos;
    vec3 color;
    float radius;
    float specular;
};

struct Triangle {
    vec3 pos[3];
    vec3 color;
    vec3 normal;
    float specular;
};

const uint SPHERES_COUNT = 3u;
const uint TRIANGLES_COUNT = 2u;
const float FAR_INF = 1e10;
const vec3 SKY_COLOR = vec3(0.4, 0.4, 0.8);
const float EPS = 1e-3;

Sphere SPHERES[SPHERES_COUNT];

Triangle TRIANGLES[TRIANGLES_COUNT];

float ray_sphere_intersection(Sphere s, vec3 origin, vec3 dir) {
    // length(origin + dir * t - s.pos) = radius
    // dot(origin + dir * t - s.pos, origin + dir * t - s.pos) = radius ^2
    // dot(origin - s.pos, origin - s.pos) + 2dot(origin - s.pos, dir)t + dot(dir, dir) * t^2 = radius^2
    // dot(origin - s.pos, origin - s.pos) - radius^2 + 2dot(origin - s.pos, dir)t + t^2 = 0
    vec3 sphereToCam = origin - s.pos;
    float B = 2.0 * dot(sphereToCam, dir);
    float C = dot(sphereToCam, sphereToCam) - s.radius * s.radius;
    float Desc = B * B - 4.0 * C;
    if (Desc < 0.0)
        return FAR_INF;
    float nearDist = (-B - sqrt(Desc)) / 2.0;
    if (nearDist > 0.0)
        return nearDist;
    float farDist = (-B + sqrt(Desc)) / 2.0;
    if (farDist > 0.0)
        return farDist;
    return FAR_INF;
}

float ray_sphere_intersection(vec3 sphPos, float radius, vec3 origin, vec3 dir) {
    // length(origin + dir * t - s.pos) = radius
    // dot(origin + dir * t - s.pos, origin + dir * t - s.pos) = radius ^2
    // dot(origin - s.pos, origin - s.pos) + 2dot(origin - s.pos, dir)t + dot(dir, dir) * t^2 = radius^2
    // dot(origin - s.pos, origin - s.pos) - radius^2 + 2dot(origin - s.pos, dir)t + t^2 = 0
    vec3 sphereToCam = origin - sphPos;
    float B = 2.0 * dot(sphereToCam, dir);
    float C = dot(sphereToCam, sphereToCam) - radius * radius;
    float Desc = B * B - 4.0 * C;
    if (Desc < 0.0)
        return FAR_INF;
    float nearDist = (-B - sqrt(Desc)) / 2.0;
    if (nearDist > 0.0)
        return nearDist;
    float farDist = (-B + sqrt(Desc)) / 2.0;
    if (farDist > 0.0)
        return farDist;
    return FAR_INF;
}

float ray_triangle_intersection(Triangle t, vec3 origin, vec3 dir) {
    // dot(origin + dir * d - t.pos[0], t.normal) == 0
    // dot(origin - t.pos[0], t.normal) + dot(dir, t.normal) * d == 0
    // d = dot(t.pos[0] - origin, t.normal) / dot(dir, t.normal);
    float normalViewAngle = dot(dir, t.normal);
    if (abs(normalViewAngle) < EPS)
        return FAR_INF;
    float d = dot(t.pos[0] - origin, t.normal) / normalViewAngle;
    if (d < 0.0)
        return FAR_INF;
    vec3 hitPos = origin + dir * d;
    vec3 edges[3] = vec3[3](
        t.pos[1] - t.pos[0],
        t.pos[2] - t.pos[1],
        t.pos[0] - t.pos[2]
    );
    float square = length(cross(edges[0], edges[1]));
    vec3 toHitVecs[3] = vec3[3](
        hitPos - t.pos[0],
        hitPos - t.pos[1],
        hitPos - t.pos[2]
    );
    float testSq[3] = float[3](
        length(cross(toHitVecs[0], edges[0])),
        length(cross(toHitVecs[1], edges[1])),
        length(cross(toHitVecs[2], edges[2]))
    );
    if (abs(square - testSq[0] - testSq[1] - testSq[2]) < EPS)
        return d;
    return FAR_INF;
}

float maxcomp(in vec3 p ) { return max(p.x,max(p.y,p.z));}
float sdBox( vec3 p, vec3 b )
{
  vec3  di = abs(p) - b;
  float mc = maxcomp(di);
  return min(mc,length(max(di,0.0)));
}

const mat3 ma = mat3( 0.60, 0.00,  0.80,
                      0.00, 1.00,  0.00,
                     -0.80, 0.00,  0.60 );

vec4 map( in vec3 p )
{
    float d = sdBox(p,vec3(1.0));
    vec4 res = vec4( d, 1.0, 0.0, 0.0 );

    float ani = smoothstep( -0.2, 0.2, -cos(0.5*iTime) );
  float off = 1.5*sin( 0.01*iTime );

    float s = 1.0;
    for( int m=0; m<4; m++ )
    {
        p = mix( p, ma*(p+off), ani );

        vec3 a = mod( p*s, 2.0 )-1.0;
        s *= 3.0;
        vec3 r = abs(1.0 - 3.0*abs(a));
        float da = max(r.x,r.y);
        float db = max(r.y,r.z);
        float dc = max(r.z,r.x);
        float c = (min(da,min(db,dc))-1.0)/s;

        if( c>d )
        {
          d = c;
          res = vec4( d, min(res.y,0.2*da*db*dc), (1.0+float(m))/4.0, 0.0 );
        }
    }

    return res;
}

vec3 calcNormal(in vec3 pos)
{
    vec3  eps = vec3(.001,0.0,0.0);
    vec3 nor;
    nor.x = map(pos+eps.xyy).x - map(pos-eps.xyy).x;
    nor.y = map(pos+eps.yxy).x - map(pos-eps.yxy).x;
    nor.z = map(pos+eps.yyx).x - map(pos-eps.yyx).x;
    return normalize(nor);
}

vec4 intersect( in vec3 ro, in vec3 rd )
{
    float t = 0.0;
    vec4 res = vec4(-1.0);
    vec4 h = vec4(1.0);
    for( int i=0; i<64; i++ )
    {
    if( h.x<0.002 || t>10.0 ) break;
        h = map(ro + rd*t);
        res = vec4(t,h.yzw);
        t += h.x;
    }
  if( t>10.0 ) res=vec4(-1.0);
    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 cameraPos = vec3(0, 0, -5);

    SPHERES[0].pos = vec3(0, sin(iTime) * 2.0, 4.0);
    SPHERES[0].color = vec3(0x64, 0x56, 0x70) / 255.0;
    SPHERES[0].radius = 0.5;
    SPHERES[0].specular = 64.0;
    SPHERES[1].pos = vec3(-1.5, sin(iTime + 3.14) * 2.0, 4);
    SPHERES[1].color = vec3(0xd5, 0xb0, 0x8d) / 255.0;
    SPHERES[1].radius = 0.5;
    SPHERES[1].specular = 128.0;
    SPHERES[2].pos = vec3(0, -1002, 0);
    SPHERES[2].color = vec3(0xd5, 0xb0, 0x8d) / 255.0;
    SPHERES[2].radius = 1000.0;
    SPHERES[2].specular = 0.0;

    TRIANGLES[0].pos = vec3[3](vec3(-5, -5, 10), vec3(-5, 5, 10), vec3(5, 5, 10));
    TRIANGLES[0].color = vec3(0xba, 0x73, 0x38) / 255.0;
    TRIANGLES[0].normal = vec3(0, 0, -1);
    TRIANGLES[0].specular = 0.0;
    TRIANGLES[1].pos = vec3[3](vec3(-5, -5, 10), vec3(5, 5, 10), vec3(5, -5, 10));
    TRIANGLES[1].color = vec3(0xba, 0x73, 0x38) / 255.0;
    TRIANGLES[1].normal = vec3(0, 0, -1);
    TRIANGLES[1].specular = 0.0;

    vec3 SUN_DIR = normalize(vec3(1, -3, 1));

    const int SAMPLES_COUNT = 4;
    vec3 fullLighting;
    vec2 uv = (fragCoord - iResolution.xy * 0.5)/iResolution.x;
    vec3 viewVec = normalize(vec3(uv, 1.0));
    fragColor = vec4(0.0);

    float intersectionDist = FAR_INF;
    vec3 intersectionColor;
    vec3 intersectionPos;
    vec3 intersectionNormal;
    float intersectionSpecular;
    for (uint i = 0u; i < SPHERES_COUNT; ++i) {
        float hitDist = ray_sphere_intersection(SPHERES[i], cameraPos, viewVec);
        if (hitDist < intersectionDist) {
            intersectionDist = hitDist;
            intersectionColor = SPHERES[i].color;
            intersectionPos = cameraPos + viewVec * intersectionDist;
            intersectionNormal = normalize(intersectionPos - SPHERES[i].pos);
            intersectionSpecular = SPHERES[i].specular;
        }
    }

    for (uint i = 0u; i < TRIANGLES_COUNT; ++i) {
        float hitDist = ray_triangle_intersection(TRIANGLES[i], cameraPos, viewVec);
        if (hitDist < intersectionDist) {
            intersectionDist = hitDist;
            intersectionColor = TRIANGLES[i].color;
            intersectionPos = cameraPos + viewVec * intersectionDist;
            intersectionNormal = TRIANGLES[i].normal;
            intersectionSpecular = TRIANGLES[i].specular;
        }
    }

    vec4 sdf = intersect(cameraPos * 2.0 - vec3(2, -1, 0), viewVec);
    if (sdf.x > 0.0 && sdf.x < intersectionDist) {
        intersectionDist = sdf.x;
        intersectionColor = vec3(1, 0, 0);
        intersectionPos = sdf.yzw * 2.0 - vec3(2, -1, 0);
        intersectionNormal = calcNormal(sdf.yzw);
        intersectionSpecular = 0.0;
    }

    vec3 lighting = intersectionColor * SKY_COLOR;

    float ao = 0.0;
    const int AO_SAMPLES = 64;
    const float RAY_DIST = 1.5;
    vec3 aoStartPos = intersectionPos + intersectionNormal * 1e-2;
    for (int i = 0; i < AO_SAMPLES; ++i) {
        vec3 sampleDir = texture(iChannel0, vec2((float(i) + 0.5) / 64.0, 0)).rgb * 2.0 - 1.0;
        if (dot(intersectionNormal, sampleDir) < 0.0) {
            sampleDir = -sampleDir;
        }
        sampleDir = normalize(sampleDir);

        float t = RAY_DIST;
        bool itersected = false;
        for (uint i = 0u; i < SPHERES_COUNT; ++i) {
            float hitDist = ray_sphere_intersection(SPHERES[i], aoStartPos, sampleDir);
            if (hitDist < t) {
                t = hitDist;
            }
        }

        for (uint i = 0u; i < TRIANGLES_COUNT && !itersected; ++i) {
            float hitDist = ray_triangle_intersection(TRIANGLES[i], aoStartPos, sampleDir);
            if (hitDist < t) {
                t = hitDist;
            }
        }
        vec4 aoSdf = intersect(aoStartPos, sampleDir);
        if (aoSdf.x > 0.0 && aoSdf.x < min(t, RAY_DIST)) {
            t = aoSdf.x;
        }

        ao += 1.0 - (RAY_DIST - t) / RAY_DIST;
    }
    ao /= float(AO_SAMPLES);
    fragColor.xyz = vec3(ao);
    lighting *= ao;

    bool shadow = false;
    vec3 shadowCastPos = intersectionPos - SUN_DIR * 0.001;
    for (uint i = 0u; i < SPHERES_COUNT; ++i) {
        float hitDist = ray_sphere_intersection(SPHERES[i], shadowCastPos, -SUN_DIR);
        shadow = shadow || hitDist < FAR_INF;
    }

    for (uint i = 0u; i < TRIANGLES_COUNT; ++i) {
        float hitDist = ray_triangle_intersection(TRIANGLES[i], shadowCastPos, -SUN_DIR);
        shadow = shadow || hitDist < FAR_INF;
    }

    vec4 shadowSdf = intersect(shadowCastPos * 2.0 - vec3(2, -1, 0), -SUN_DIR);
    shadow = shadow || shadowSdf.x > 0.0;

    if (!shadow) {
        lighting += max(0.0, dot(intersectionNormal, -SUN_DIR)) * intersectionColor * ao;
        if (intersectionSpecular != 0.0)
            lighting += pow(max(0.0, dot(reflect(-SUN_DIR, intersectionNormal), viewVec)), intersectionSpecular);
    }
    fullLighting += lighting;

    fragColor.xyz = pow(fullLighting, vec3(1.0 / 2.2));
}

#define rot(a) mat2(cos(a), sin(a), -sin(a), cos(a))
#define EPS 0.0001

vec3 lightDir = vec3(1, 2, 3);
float dSphere(vec3 p, float r){
    return length(p)-r;
}


float dRing(vec3 p, float inR, float exR, float height){
    float sdf2d = abs(length(p.xy) - inR)-exR;
    float d = abs(p.z)-height;
    return max(sdf2d, d);
}


float map(vec3 p){
    p.xy *= rot(iTime*.5);
    p.yz *= rot(iTime*.3);
    return dRing(p, .2, .1, 1.);
    return dSphere(p, .3);
}

vec3 getNormal(vec3 p){
    return normalize(vec3(
        map(p)-map(vec3(p.x-EPS, p.yz)),
        map(p)-map(vec3(p.x, p.y-EPS, p.z)),
        map(p)-map(vec3(p.xy, p.z-EPS))
        ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv  = (fragCoord*2.-iResolution.xy)/min(iResolution.x, iResolution.y);
    vec3 col = vec3(.2);
    vec2 st = uv;

    st*= rot(iTime*1.5);
    col.xy += sin(st)+.8;
    col.z += cos(st.y)*.6;
    vec3 ro = vec3(0,0,10);
    vec3 rd = normalize(vec3(uv, 0)-ro);
    vec3 cur = ro;
    for(int i = 0; i < 99; i++){
        float d = map(cur);
        if(d < EPS){
            vec3 normal = getNormal(cur);
            col += dot(normal, lightDir)+vec3(.1);
            col *= vec3(cur.zxy+(sin(iTime*5.)+1.)*.05);
            break;
        }
        cur += rd*d;
    }

    fragColor = vec4(col,1.0);
}

const float SEED=345.145334;
const float BUBBLE_COUNT=81.;

float random(float i){
    return fract(sin(dot(vec2(i,i-SEED),vec2(i+12.2468,52.2455))));
}

float circle(vec2 pos,float radius){
    return length(pos)-radius;
}

void mainImage(out vec4 fragColor,in vec2 fragCoord){
    vec2 uv=fragCoord.xy*2./iResolution.xy-1.;
    uv.x*=iResolution.x/iResolution.y;

    float d=circle(uv,.0);
    for(float i=0.;i<BUBBLE_COUNT;i+=1.){
        vec2 firstOff=vec2(random(i+SEED*.02),random(i+SEED*.05))-.5;
        vec2 off=firstOff*sin(iTime*random(i+SEED)+random(i+SEED*.1))*random(i+SEED*1.5)*4.;
        float dTemp=circle(uv+off,random(i+SEED)*.19);
        d=min(d,dTemp);
    }
    d=abs(d);
    float finalCirc=.005/d;

    vec2 uvCol=vec2(cos(iTime*.2)*.5+.5)+uv;

    vec3 col=vec3(uvCol,.5);
    col*=finalCirc;
    vec3 foreGround=vec3(.1216,.502,1.);
    col+=foreGround*.1*-uv.y;

    fragColor=vec4(col,1.);
}

const mediump vec3 diffuse = vec3(0.0, 1.0, 1.0);
const mediump vec3 ambient = vec3(0.1);
const mediump float shininess = 2.0;
const mediump float specularFraction = 0.1;
const mediump float angularFrequency = 0.10471975511966;
const mediump float epsilon = 0.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    mediump float dimension = min(iResolution.x, iResolution.y);
    mediump float maxDimension = max(iResolution.x, iResolution.y);
    mediump float radius = 0.3 * dimension;
    mediump float dist = 6.0 * radius;

    vec2 uv = fragCoord - iResolution.xy * 0.5;
    float apparentR = length(uv);

    mediump float xi = atan(apparentR / dist);
    float perspective = 1.0 + radius / dist * (1.0 - sin(xi));
    float actualR = apparentR / perspective;

    highp float z = sqrt(radius * radius - actualR * actualR) * step(actualR, radius);
    vec3 position = vec3(uv / perspective, z);
    vec3 normal = normalize(position);

    float angle = angularFrequency * iTime;
    float cosTime = cos(angle);
    float sinTime = sin(angle);
    vec3 light = vec3(
        sinTime,
        -0.398749068925246 * cosTime,
        -0.917060074385124 * cosTime
        );

    vec3 view = normalize(position - vec3(0.0, 0.0, dist + radius));
    vec3 reflected = reflect(light, normal);
    float specular = pow(clamp(dot(-reflected, view), 0.0, 1.0), shininess);

    vec3 diffusePlusSpecular = (1.0 + specularFraction * specular) * clamp(dot(-light, normal), 0.0, 1.0) * diffuse;

    vec3 color = clamp(diffusePlusSpecular + ambient, 0.0, 1.0);

    float theta = acos(position.y / radius);
    float phi = atan(position.x / z);
    vec2 textureCoord = vec2(phi, theta) / 3.141592653589793 + vec2(0.5, 0.0);
    vec2 spaceCoord = fragCoord / dimension - vec2(angle, 0.0);

    vec4 planetContrib = step(actualR, radius) * vec4(color, 1.0) * texture(iChannel0, textureCoord);

    vec2 starCenter = iResolution.xy * 0.5 + vec2(angle, 0.0) * 2.1 * dimension;

    vec4 bgContrib = 0.2 * step(radius, actualR) * texture(iChannel1, (fragCoord - starCenter)/maxDimension);
    highp vec4 opaqueContrib = planetContrib + bgContrib;

    highp vec3 atmosphereColor = clamp(dot(-light, normal), 0.0, 1.0) * vec3(1.0);
    mediump float maxThickness = 2.0 * sqrt(1.0 + 2.0/epsilon);

    highp float atmosphereAlpha = clamp(1.0/maxThickness * min(maxThickness, radius / z), 0.0, 1.0) * (1.0 - smoothstep(radius, radius * (1.0 + epsilon), actualR));

    highp vec4 unclamped = vec4(mix(opaqueContrib.rgb, atmosphereColor, atmosphereAlpha), 1.0);
    fragColor = clamp(unclamped, vec4(0.0), vec4(1.0));
}

// I was mainly interested in writing a compact map() function for this building
//
// CREDITS:
// borrowed some code from the following shaders
//  rendering  from "One small step (3Kb)"   https://www.shadertoy.com/view/tt3yRH by Dean_The_Coder
//  clouds from "lake in highland" https://www.shadertoy.com/view/4sdfz8 by Fishman


//#define INFINITE  

float sdBox(vec3 p, vec3 b) {
	vec3 q = abs(p) - b;
	return length(max(q, 0.)) + min(max(q.x, max(q.y, q.z)), 0.);
}


vec3 getRayDir(vec3 ro, vec3 lookAt, vec2 uv) {
	vec3 f = normalize(lookAt - ro),
		 r = normalize(cross(vec3(0, 1, 0), f));
	return normalize(f + r * uv.x + cross(f, r) * uv.y);
}


float sdCappedCylinder(vec3 p, vec3 a, vec3 b, float r)
{
  vec3  ba = b - a;
  vec3  pa = p - a;
  float baba = dot(ba,ba);
  float paba = dot(pa,ba);
  float x = length(pa*baba-ba*paba) - r*baba;
  float y = abs(paba-baba*0.5)-baba*0.5;
  float x2 = x*x;
  float y2 = y*y*baba;
  float d = (max(x,y)<0.0)?-min(x2,y2):(((x>0.0)?x2:0.0)+((y>0.0)?y2:0.0));
  return sign(d)*sqrt(abs(d))/baba;
}




float map(vec3 p) {

#ifdef INFINITE
    vec3 p0 =  vec3((p.x -3.5) - floor((p.x+3.5) /12.)*12.,p.y,(p.z -3.5) - floor((p.z+3.5) /12.)*12.);; 
#else
    vec3 p0 = p;
#endif

    p0.xz=-1.70 + abs(p0.xz +1.);
    p0.xz= vec2(p0.x+p0.z,p0.x-p0.z)/sqrt(2.)   ;
    p0.y= .5 + p0.y*.5 + abs( p0.y -.7)*.6; 

    float cv = 1.3,ch=.6;
    vec3 p1 =  vec3(p0.x, p0.y-cv*clamp(round((p0.y-.9) /cv), -.1,5.),p0.z); //vertical repetition

    vec3 p2 = vec3(p1.x,p1.y,p1.z-ch*clamp(round(p1.z/ch),-4.,4.)); //horizontal repetition
    float b = sdCappedCylinder(p2,vec3(.5,1.,0.),vec3(-.3,1.,0.),.2);
     b =min(b,sdBox(p2- vec3(0.1,.7,0),vec3(.4,.3,.2)));
     b= min(b,sdCappedCylinder(p1,vec3(0.,1.,-3.0),vec3(0.,1.,3.0),.2));
     b =min(b,sdBox(p1- vec3(0,.7,0),vec3(.2,.3,3.0)));
     b =min(b,sdBox(p1- vec3(-1.3,.9,0),vec3(1.02,.5,2.0)));
    b= max(-b,sdBox(p0- vec3(-1.1,4.3,0.),vec3(1.5,4.3,3.)));
    b= min(b,sdBox(vec3(p0.x,p.y,p0.z)- vec3(-1.5,.0,0.),vec3(4.,.1,6.)));
    return min(b,p.y);
}

vec3 calcN(vec3 p, float t) {
	float h = .001 * t;
	vec3 n = vec3(0);
	for (int i = min(iFrame, 0); i < 4; i++) {
		vec3 e = .5773 * (2. * vec3((((i + 3) >> 1) & 1), (i >> 1) & 1, i & 1) - 1.);
		n += e * map(p + e * h);
	}

	return normalize(n);
}

float calcShadow(vec3 p, vec3 ld) {
	// Thanks iq.
	float s = 1., t = .1;
	for (float i = 0.; i < 40.; i++)
	{
		float h = map(p + ld * t);
		s = min(s, 15. * h / t);
		t += h;
		if (s < .001) break;
	}

	return clamp(s, 0., 1.);
}

// Quick ambient occlusion.
float ao(vec3 p, vec3 n, float h) {
	return map(p + h * n) / h;
}

