vec3 vignette(vec3 c, vec2 fc) {
	vec2 q = fc.xy / iResolution.xy;
	c *=  1.;//.5 + .5 * pow(16. * q.x * q.y * (1. - q.x) * (1. - q.y), .4);
	return c;
}

// from iq
float Noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
  	f = f*f*(3.0-2.0*f);
  	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
  	vec2 rg = textureLod( iChannel0, (uv+0.5)/256.0, 0.0).yx;
  	return mix( rg.x, rg.y, f.z );
}

// ref https://www.shadertoy.com/view/Xs33Df
float Noise3D(in vec3 p){
    const vec3 s = vec3(7, 157, 113);
	vec3 ip = floor(p); // Unique unit cell ID.
    vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);
	p -= ip; // Cell's fractional component.
    p = p*p*(3. - 2.*p);
    h = mix(fract(sin(h)*43758.5453), fract(sin(h + s.x)*43758.5453), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z); // Range: [0, 1].

}
vec3 lightDir = normalize( vec3(6,19,-6.) );
const mat2 m2 = mat2( 0.60, -0.80, 0.80, 0.60 );
float FBM( in vec3 p )
{
    float n = 0.0;
    n += 0.50000*Noise( p*1.0 );
    n += 0.25000*Noise( p*2.0 );
    n += 0.12500*Noise( p*4.0 );
    n += 0.06250*Noise( p*8.0 );
    n += 0.03125*Noise( p*16.0 );
    return n/0.984375;
}

//ref: https://www.shadertoy.com/view/Msdfz8
vec3 Cloud(vec3 bgCol,vec3 ro,vec3 rd,vec3 cloudCol,float spd)
{
    vec3 col = bgCol;
    float t = iTime * 0.15* spd;
    vec2 sc = ro.xz + rd.xz*((3.)*40000.0-ro.y)/rd.y;
    vec2 p = 0.00002*sc;
    float f = 0.0;
  	float s = 0.5;
  	float sum =0.;
  	for(int i=0;i<5;i++){
    	p += t;t *=1.5;
    	f += s*textureLod( iChannel0, p/256.0, 0.0).x; p = m2*p*2.02;
    	sum+= s;s*=0.6;
  	}
    float val = f/sum; 
    col = mix( col, cloudCol, 0.5*smoothstep(0.5,0.8,val) );
    return col;
}
vec3 RayMarchCloud(vec3 ro,vec3 rd){
    vec3 col = vec3(0.0,0.0,0.0);  
    float sundot = clamp(dot(rd,lightDir),0.0,1.0);

     // sky      
    col = vec3(0.2,0.5,0.85)*1.1 - rd.y*rd.y*0.5;
    col = mix( col, 0.85*vec3(0.7,0.75,0.85), pow( 1.0-max(rd.y,0.0), 4.0 ) );
    // sun
    col += 0.25*vec3(1.0,0.7,0.4)*pow( sundot,5.0 );
    col += 0.25*vec3(1.0,0.8,0.6)*pow( sundot,64.0 );
    col += 0.4*vec3(1.0,0.8,0.6)*pow( sundot,512.0 );
    // clouds
    col = Cloud(col,ro,rd,vec3(1.0,0.95,1.0),1.);
            // .
    col = mix( col, 0.68*vec3(0.4,0.65,1.0), pow( 1.0-max(rd.y,0.0), 16.0 ) );
    return col;
}

vec3 lights(vec3 p, vec3 rd, float d) {
	vec3 ld = normalize(lightDir*6.5 - p), n = calcN(p, d) ;

	float ao = .1 + .9 * dot(vec3(ao(p, n, .1), ao(p, n, .4), ao(p, n, 2.)), vec3(.2, .3, .5)),

	// Primary light.
	l1 = max(0., .2 + .8 * dot(ld, n)),

	// Secondary(/bounce) light.
    #ifdef INFINITE
	l2 = 1.,
    #else
    l2 =  max(0., .2 + .8 * dot(ld * vec3(-18, .5, -12), n)) * .4,
    #endif

	// Specular.
	spe = max(0., dot(rd, reflect(ld, n))) * .1,

	// Fresnel
	fre = smoothstep(.7, 1., 1. + dot(rd, n));

	// Combine.
	l1 *= .1 + .9 * calcShadow(p, ld);
	vec3 lig = ((l1 + l2) * ao + spe) * vec3(1.) *2.5;
	return mix(.3, .4, fre) * lig;
}

vec3 march(vec3 ro, vec3 rd) {
	// Raymarch.
	vec3 p;

	float d = .01;
	for (float i = 0.; i < 120.; i++) {
		p = ro + rd * d;
		float h = map(p);

		if (abs(h) < .0015)
			break;

		d += h; // No hit, so keep marching.
	}
    if(d>50.) return  RayMarchCloud( ro, rd)*.6;

	return lights(p, rd, d) * exp(-d * .085)*(p.y<0.01? vec3(0,1,0):vec3(1))
    #ifndef INFINITE
     * (sdBox(vec3(p.x+p.z+2.,p.y,p.x-p.z),vec3(3,8.,3.))<0. ?vec3(0.165,0.165,0.016):vec3(1.))
     #endif
     ;
}

void mainImage(out vec4 fragColor, vec2 fc)
{
	float t =iTime * .2;
	vec3 ro = vec3(-3.-cos(t)*5., 4., 4.5+cos(t)*5.);


	vec2 uv = (fc - .5 * iResolution.xy) / iResolution.y;
	fragColor = vec4(vignette(pow(march(ro, getRayDir(ro, vec3(-1.5,ro.y,-1.5), uv)), vec3(.45)), fc), 0);
}

