const float pi = 3.1415927;

#define VAR_ZERO min (iFrame, 0)

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

vec2 SphGrid (vec3 p)
{
  vec3 q;
  vec2 a, sc, nSeg;
  float dMin, d1, d2, r;
  nSeg = vec2 (16., 19.);
  sc = sin (0.07 * 2. * pi / nSeg + vec2 (0., 0.5 * pi));
  q = p.yxz;
  r = length (q.yz);
  a = 2. * pi * (floor (nSeg * atan (q.zx, - vec2 (q.y, r)) / (2. * pi)) + 0.5) / nSeg;
  q.yz = Rot2D (q.yz, a.x);
  d1 = dot (vec2 (q.y, abs (q.z)), sc);
  q.yz = Rot2D (vec2 (r, q.x), a.y);
  d2 = dot (vec2 (q.y, abs (q.z)), sc);
  return vec2 (d1, d2);
}

float ObjDf (vec3 p)
{
  vec3 q, qq;
  vec2 d2;
  float dMin, d, dh, s, zp, rh, cvOrd, a, flshLen;
  dMin = dstFar;
  q = p;
  rh = length (q.xz);
  q.y -= 2. * psgHt + psgWd + 2. * ringWd;
  d2 = SphGrid (q);
  s = abs (domeRd - length (q));
  d = max (SmoothMax (s - wThk, min (max (d2.x, oculRd - rh), d2.y), 0.02), - q.y);
  d = min (d, max (max (s - 0.3 * wThk, oculRd - rh), - q.y));
  DMINQ (idDom);
  q.xz = mix (q.xz, q.zx, step (abs (q.z), abs (q.x)));
  q.z = abs (q.z);
  q.y -= - psgWd - 2. * ringWd;
  zp = abs (mod (q.z + pcGap, 2. * pcGap) - pcGap);
  d = max (max (abs (abs (q.x) - psgWd + 3. * wThk) - 4.5 * wThk,
     abs (abs (q.y + psgHt) - psgHt) - 0.5 * wThk), q.z - psgLen);
  DMINQ (idPilB);
  qq = q;
  q = vec3 (abs (q.x) - psgWd + 5. * wThk, q.y + psgHt, abs (zp) - pcGap);
  d = max (PrCylDf (q.xzy, 1.5 * wThk, psgHt), qq.z - psgLen);
  DMINQ (idPil);
  q = qq;
  dh = length (vec2 (q.x, max (q.y, 0.))) - psgWd;
  d = max (SmoothMin (abs (dh) - wThk, max (abs (dh) - 1.7 * wThk, pcGap - zp - 0.02), 0.02),
     domeRd - rh);
  d = max (max (d, min (min (0.1 - length (vec2 (max (q.y + 0.4 * psgHt, 0.), zp)), q.y + 1.6 * psgHt),
     0.3 * psgLen - abs (q.z - 0.6 * psgLen))), max (q.z - psgLen, - 2. * psgHt - q.y));
  DMINQ (idPsg);
  d = max (dh - 4. * wThk, abs (q.z - psgLen - 2. * wThk) - 2. * wThk);
  d = max (max (d, - dh - 7.5 * wThk), - 2. * psgHt - 0.5 * wThk  - q.y);
  DMINQ (idEnt);
  d = max (abs (q.x) - psgWd + 7.5 * wThk, 0.);
  d = max (d, max (q.z - psgLen - 4. * wThk, 0.));
  d = max (d, max (abs (q.y + 2. * psgHt + 0.25 * wThk) - 0.25 * wThk, 0.));
  DMINQ (idFlor);
  d = max (max (abs (domeRd - rh) - wThk, abs (q.y - 4. * ringWd) - 2. * ringWd - wThk), - wThk - dh);
  d = max (d, 0.7 * ringWd - length (vec2 (q.x, q.y - 5. * ringWd - wThk)));
  DMINQ (idDomB);
  d = PrSphDf (q - vec3 (0., 4. * ringWd + 2. * wThk, domeRd - wThk), 0.5 * wThk);
  DMINQ (idSpkEx);
  q = p;
  q.y -= 6. * torScl + 2.5 * wThk;
  d = PrCylDf (q.xzy, rTor + 6. * torScl, 9. * torScl);
  if (d < 0.05) { // from "Plasma Coil"
    d = length (abs (abs (vec2 (rh - rTor, q.y)) - 4.5 * torScl)) - 0.5 * torScl;
    DMINQ (idTorL);
    qq = q;
    qq.xz = Rot2D (q.xz, 0.25 * pi);
    d = PrSphDf (vec3 (abs (qq.xz) - rTor + 1.41 * rTorEx, q.y - 4.5 * torScl), 0.8 * torScl);
    DMINQ (idSpkEx);
    d = length (vec2 (rh - rTor, q.y)) - 1.5 * torScl;
    DMINQ (idTorR);
    d = PrCylDf (vec3 (abs (qq.xz) - rTor + 0.11 * rTorEx, q.y + 8. * torScl),
       (0.6 - 1.6 * (q.y + 8. * torScl)) * torScl, 3.5 * torScl);
    DMINQ (idTorL);
    q.xz = mix (qq.xz, qq.zx, step (abs (qq.z), abs (qq.x)));
    q.z = abs (q.z) - rTor;
    d = (length (vec2 (length (q.yz) - rTorEx, q.x)) - torScl * (1. + crExt));
    DMINQ (idTorS);
    q = qq;
    q.xy = vec2 (q.y, - q.x);
    q.yz = vec2 (atan (q.y, q.z) * rTor, length (q.yz) - rTor);
    cvOrd = 7.;
    a = atan (q.z, q.x) / (2. * pi);
    q.xz = Rot2D (vec2 (length (q.xz) - 2. * torScl, mod (q.y + (2. * a + 1.) * torScl,
       2. * torScl) - torScl), 2. * pi * (cvOrd - 1.) * a);
    d = 0.8 * (length (Rot2D (q.xz, - (floor ((0.5 * pi - atan (q.x, q.z)) + pi / cvOrd))) -
       vec2 (0.6 * torScl, 0.)) - 0.2 * torScl);
    DMINQ (idTorC);

  } else dMin = min (dMin, d);
  dh = dstFar; // from "Tesla's Laboratory"
  d = dh;
  flshLen = 1.1;
  for (int k = VAR_ZERO; k < 4; k ++) {
    if (illum[k] > 0.) {
      q = p;
      q.xz = Rot2D (q.xz, float (k) * 0.5 * pi);
      q.y -= flshLen + 0.39 + 2. * wThk;
      q.z -= -0.71;
      q.yz = Rot2D (q.yz, -0.38 * pi);
      d = PrCylDf (q, 0.1, flshLen);
      if (d < dMin) {
        q.xy -= 0.15 * smoothstep (0., 0.1, flshLen - abs (q.z)) * (2. *
           vec2 (Fbm2 (8. * (vec2 (0.4 * q.z, 0.) + flPth[k].xy)),
           Fbm2 (8. * (vec2 (0.3 * q.z, 0.) + flPth[k].zw))) - 1.);
        dh = min (dh, PrCylDf (q, 0.001, flshLen));
      }
    }
    d = 0.7 * dh;
  }
  DMINQ (idSpk);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float TrObjDf (vec3 p)
{
  vec3 q;
  q = p;
  q.y -= 6. * torScl + 2.5 * wThk;
  q.xz = Rot2D (q.xz, 2. * pi * aRotTor / nSegTor);
  q.xz = Rot2D (q.xz, 2. * pi * ((floor (nSegTor * atan (q.z, - q.x) / (2. * pi)) +
     0.5) / nSegTor));
  return (length (vec2 (length (vec2 (abs (q.x) - rTor, q.y)) - 2.5 * torScl,
     q.z * torzFac)) - 2. * torScl);
}

float TrObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 80; j ++) {
    d = TrObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 TrObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = TrObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec2 CapsHit (vec3 ro, vec3 rd, float cRad, float cLen)
{
  float dIn, dOut, d, a, b, bs, w, s, ws, roz;
  dIn = dstFar;
  dOut = dstFar;
  a = dot (rd.xy, rd.xy);
  b = dot (rd.xy, ro.xy);
  w = b * b - a * (dot (ro.xy, ro.xy) - cRad * cRad);
  if (w > 0. && a > 0.) {
    ws = sqrt (w);
    d = - (b + ws) / a;
    s = ro.z + d * rd.z;
    if (d > 0. && abs (s) < cLen) dIn = d;
    else {
      roz = ro.z;
      ro.z -= cLen * sign (s);
      bs = dot (rd, ro);
      w = bs * bs - (dot (ro, ro) - cRad * cRad);
      if (w > 0.) {
        d = - bs - sqrt (w);
        if (d > 0.) dIn = d;
      }
      ro.z = roz;
    }
  }
  if (dIn < dstFar) {
    d = (- b + ws) / a;
    s = ro.z + d * rd.z;
    if (abs (s) < cLen) dOut = d;
    else {
      ro.z -= cLen * sign (s);
      bs = dot (rd, ro);
      w = bs * bs - (dot (ro, ro) - cRad * cRad);
      if (w > 0.) dOut = - bs + sqrt (w);
    }
  }
  return vec2 (dIn, dOut);
}

float FlmDens (vec3 p)
{
  float c;
  c = max (0., 1.1 * BumpFbm3 (0.2 * vec3 (p.xy / flRad, p.z + 4. * tCur)) - 0.1 +
     0.4 * smoothstep (-0.5 * flRad, 0.1 * flRad, p.z));
  return c * (1. - smoothstep (-0.3 * flRad, 0., length (vec3 (p.xy,
     p.z - clamp (p.z, - 2. * flLen, 0.))) - flRad * (1. + 0.8 * p.z / (2. * flLen)))) *
     smoothstep (0., flRad, p.z + 2. * flLen);
}

vec3 FlmCol (vec3 ro, vec3 rd, vec2 dCyl2, float dstObj)
{   // from "Cloud Computing", with mods and color
  vec4 col4;
  vec3 q, c;
  float s, ds, f, fn, r;
  const float sMax = 20.;
  ds = min (2. * flRad, dCyl2.y - dCyl2.x) / sMax;
  s = dCyl2.x;
  ro.z -= flLen;
  q = ro + s * rd;
  r = length (q.xy) / flRad;
  q.xy = Rot2D (q.xy, 0.5 * r * (2. * smoothstep (0.4, 0.8, r) - 1.) * q.z);
  f = FlmDens (q);
  col4 = vec4 (0.);
  for (float j = float (VAR_ZERO); j < sMax; j ++) {
    s += ds;
    q = ro + (s + ds * Hashfv3 (16. * rd)) * rd;
    r = length (q.xy) / flRad;
    q.xy = Rot2D (q.xy, 0.5 * r * (2. * smoothstep (0.4, 0.8, r) - 1.) * q.z);
    fn = FlmDens (q);
    c = mix (mix (vec3 (0.1, 0.3, 1.), vec3 (0., 0.8, 1.), clamp ((0.3 / ds) * (f - fn), 0., 1.)),
       vec3 (1.), smoothstep (-0.5 * flRad, 0.5 * flRad, q.z)) *
       (1. - smoothstep (-0.1, 0., s - dstObj));
    col4 += (1. - col4.a) * vec4 (c, 1.) * f / sMax;
    f = fn;
    if (s > dstObj || col4.a > 1.) break;
  }
  return clamp (col4.rgb, 0., 1.);
}

vec3 StarPat (vec3 rd, float scl)
{   // from "Star Globe"
  vec3 tm, qn, u;
  vec2 q;
  float f;
  tm = -1. / max (abs (rd), 0.0001);
  qn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  u = Maxv3 (tm) * rd;
  q = atan (vec2 (dot (u.zxy, qn), dot (u.yzx, qn)), vec2 (1.)) / pi;
  f = 0.57 * (Fbm2 (11. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 131.13 * scl * q) +
      Fbm2 (13. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 171.13 * scl * q.yx));
  return 5. * vec3 (1., 1., 0.8) * pow (f, 16.);
}

vec3 SkyCol (vec3 rd)
{
  vec3 col, mDir, vn;
  float mRad, bs, ts;
  mDir = normalize (vec3 (0.03, 0.08, 1.));
  mRad = 0.015;
  col = vec3 (0.06, 0.06, 0.03) * pow (clamp (dot (rd, mDir), 0., 1.), 16.);
  bs = dot (rd, mDir);
  ts = bs * bs - 1. + mRad * mRad;
  if (ts > 0.) {
    ts = bs - sqrt (ts);
    if (ts > 0.) {
      vn = normalize ((ts * rd - mDir) / mRad);
      col += vec3 (1., 0.9, 0.5) * clamp (dot (normalize (vec3 (-0.9, 0.4, 0.5)), vn) *
         (1. - 0.3 * Noisefv3 (8. * vn)), 0., 1.);
    }
  } else col += StarPat (rd, 16.);
  return col;
}

vec3 ShStagGrid (vec2 p)
{
  vec2 sp, ss;
  if (2. * floor (0.5 * floor (p.y)) != floor (p.y)) p.x += 0.5;
  sp = smoothstep (0.03, 0.07, abs (fract (p + 0.5) - 0.5));
  p = fract (p) - 0.5;
  ss = 0.5 * smoothstep (0.4, 0.5, abs (p.xy)) * sign (p.xy);
  if (abs (p.x) < abs (p.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.x, sp.x * sp.y, ss.y);
}

vec4 ShStagGrid3d (vec3 p, vec3 vn)
{
  vec3 rg;
  rg = ShStagGrid ((abs (vn.x) > 0.99) ? p.zy : ((abs (vn.y) > 0.99) ? p.zx : p.xy));
  if (abs (vn.x) > 0.99) {
    rg.xz *= sign (vn.x);
    if (rg.x == 0.) vn.xy = Rot2D (vn.xy, rg.z);
    else vn.xz = Rot2D (vn.xz, rg.x);
  } else if (abs (vn.y) > 0.99) {
    rg.xz *= sign (vn.y);
    if (rg.x == 0.) vn.yx = Rot2D (vn.yx, rg.z);
    else vn.yz = Rot2D (vn.yz, rg.x);
  } else if (abs (vn.z) > 0.99) {
    rg.xz *= sign (vn.z);
    if (rg.x == 0.) vn.zy = Rot2D (vn.zy, rg.z);
    else vn.zx = Rot2D (vn.zx, rg.x);
  }
  return vec4 (vn, rg.y);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, rg4, a1, a2, b1, b2;
  vec3 col, vn, roo, glCol, ltPos, ltDirL, stg, q;
  vec2 vf, dCyl2, w;
  float dstObj, dstTrObj, dstGrnd, s, att, nDotL, dSum, sSum, tLitCyc, t, tm, st, sp, f,
     illTot, fBot, a;
  bool inBldg, psgDir, isMet;
  psgWd = 1.;
  wThk = 0.05;
  domeRd = psgWd + 0.18;
  ringWd = 0.25;
  psgLen = 4.;
  psgHt = 0.75;
  pcGap = 0.25;
  oculRd = 0.28 * psgWd;
  torScl = 0.04;
  rTor = (80. / (2. * pi)) * torScl;
  rTorEx = 5. * torScl;
  nSegTor = 12.;
  flRad = oculRd;
  flLen = 30. * flRad;
  t = 0.5 * tCur;
  a1 = vec4 (0.1, 0.17, 0.15, 0.12);
  a2 = vec4 (0.1, 0.5, -0.2, 0.2);
  b1 = vec4 (0.8, 0.3, 0.5, 0.4);
  b2 = vec4 (-1., 0.2, 0.3, -0.2);
  for (int k = 0; k < 4; k ++) {
    f = 0.1 * float (k);
    flPth[k] = vec4 (4. + f + (1.5 + f) * t, sin ((0.11 + 0.1 * f) * t),
       5. + 2. * f + (1.3 - f) * t, sin ((0.12 - 0.1 * f) * t));
    for (int j = 0; j < 4; j ++) flPth[k][j] += (b1[j] + b2[j] * f) * sin (a1[j] + a2[j] * f * t);
  }
  tLitCyc = 50.;
  t = 2. * pi * tCur / tLitCyc;
  tm = mod (tCur / tLitCyc, 1.);
  aRotTor = -1. * (0.01 * tLitCyc * t + 30. * (sin (t) - t));
  torzFac = 0.8 + 0.2 * cos (t);
  crExt = 0.05 * (-1. + sin (512. * tCur)) * SmoothBump (0.2, 0.8, 0.1, tm);
  illTot = 0.;
  for (int k = 0; k < 4; k ++) {
    f = float (k);
    illum[k] = step (torzFac, 0.95) * step (0.05, Noiseff (16. * tCur + f)) *
       (0.3 + 0.7 * smoothstep (0.3, 0.9, Fbm1 (4. * tCur + f)));
    illTot += illum[k];
  }
  st = mod (nSegTor * atan (ro.z, - ro.x) / (2. * pi) - aRotTor, 1.);
  roo = ro;
  glCol = mix (vec3 (0.9, 0.9, 0.3) * (0.97 + 0.03 * sin (64. * tCur)), vec3 (1., 1., 0.9),
     SmoothBump (0.3, 0.7, 0.1, tm)) * (0.2 + 0.8 * SmoothBump (0.2, 0.8, 0.15, tm));
  dstTrObj = TrObjRay (ro, rd);
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    stg = vec3 (0.);
    sp = length (vec2 (qHit.x, max (qHit.y, 0.))) - psgWd;
    psgDir = (abs (ro.x) < abs (ro.z));
    if (idObj == idDom) inBldg = (length (qHit) < domeRd);
    else if (idObj == idDomB) inBldg = (sp < wThk || length (qHit.xz) < domeRd);
    else if (idObj == idPsg) inBldg = (sp < 0.5 * wThk);
    else if (idObj == idPilB) inBldg = (abs (qHit.x) < psgWd);
    else if (idObj == idEnt) inBldg = (abs (qHit.z) < psgLen + 3.5 * wThk && sp < 0.);
    else inBldg = true;
    vf = vec2 (0.);
    if (idObj == idDom) {
      col4 = vec4 (0.7, 0.7, 0., 0.2) * (0.7 + 0.3 * step (0.5 * wThk,
         abs (length (qHit) - domeRd)));
    } else if (idObj == idDomB) {
      if (qHit.y < 4. * ringWd + wThk) col4 = 0.7 * vec4 (0.8, 0.5, 0., 0.2);
      else col4 = vec4 (0.8, 0.7, 0., 0.2) * (0.8 + 0.2 * smoothstep (0., 0.05,
         abs (mod (16. * qHit.y, 1.) - 0.5)));
      vf = vec2 (64., 0.5);
    } else if (idObj == idPsg) {
      col4 = vec4 (0.8, 0.5, 0., 0.05);
      if (abs (sp) < wThk + 0.005) {
        col4 *= 0.7;
        if (abs (qHit.y + psgHt) < psgHt) {
          if (abs (sp) > wThk - 0.005) stg = ro;
          else col4 *= 0.7;
        } else if (inBldg) col4 *= 0.9 + 0.1 * step (0.1, mod (32. * atan (ro.y - 2. * psgHt,
           (psgDir ? ro.x : ro.z)) / pi, 1.));
      } else col4.a = 0.2;
      vf = vec2 (64., 0.5);
    } else if (idObj == idPilB) {
      col4 = vec4 (0.55, 0.55, 0.6, 0.2);
      vf = vec2 (64., 1.);
    } else if (idObj == idPil) {
      col4 = vec4 (0.55, 0.55, 0.6, 0.2);
      a = mod (32. * (atan (qHit.z, - qHit.x) / (2. * pi)), 1.);
      vn.xz = Rot2D (vn.xz, -0.15 * pi * sin (pi * a * a));
      vf = vec2 (128., 0.25);
    } else if (idObj == idEnt) {
      if (abs (abs (qHit.y + psgHt) - psgHt) < 0.5 * wThk) col4 = vec4 (0.5, 0.5, 0.6, 0.2);
      else {
        col4 = 0.7 * vec4 (0.8, 0.5, 0., 0.2);
        if (abs (qHit.y + psgHt) < psgHt - 0.02) stg = ro;
      }
      vf = vec2 (64., 0.5);
    } else if (idObj == idFlor) {
      col4 = vec4 (0.2, 0.2, 0.22, 0.1);
      if (qHit.z > psgWd - 0.305) stg = vec3 ((psgDir ? ro.zx : ro.xz), ro.y).xzy;
      vf = vec2 (64., 0.5);
    } else if (idObj == idSpkEx) {
      col4 = mix (vec4 (0.5, 0.5, 0.6, 0.2), vec4 (0.8, 0.8, 0.2, -1.), step (0.05, illTot));
    } else if (idObj == idTorL) {
      col4 = vec4 (0.45, 0.5, 0.45, 0.2);
      if (ro.y > 0.17) col4 *= (0.9 + 0.1 * sin (256. * atan (ro.z, - ro.x)));
      else col4 *= (0.8 + 0.2 * sin (512. * ro.y));
    } else if (idObj == idTorR) {
      col4 = mix (vec4 (0.7, 0.7, 0., 0.), vec4 (1., 1., 1., 0.), 0.5 + 0.5 * sin (t)) *
         (0.4 + 0.6 * SmoothBump (0.35, 0.65, 0.05, st * torzFac));
    } else if (idObj == idTorC) {
      col4 = mix (vec4 (0.6, 0.5, 0.5, 0.), mix (vec4 (1., 0., 0., 0.), vec4 (1., 1., 1., 0.),
         0.5 + 0.5 * cos (t)), SmoothBump (0.25, 0.75, 0.1, st));
    } else if (idObj == idTorS) {
      col4 = vec4 (0.7, 0.6, 0.6, 0.) * (0.9 + 0.1 * sin (128. * atan (qHit.z, - qHit.y)));
    } else if (idObj == idSpk) {
      col4 = vec4 (vec3 (1., 1., 0.9) * clamp (2. * (0.1 + 0.9 * illTot) *
         (1. - 0.3 * Fbm1 (5. * qHit.z)) - 0.2, 0., 1.), -1.);
    } else {
      col4 = vec4 (1., 1., 0., 0.2);
    }
    if (stg != vec3 (0.)) {
      rg4 = ShStagGrid3d (10. * stg, vn);
      vn = rg4.xyz;
      col4.rgb *= 0.8 + 0.2 * rg4.w;
    }
    if (vf.x > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
    if (col4.a >= 0.) {
      if (inBldg) {
        dSum = 0.;
        sSum = 0.;
        for (int k = 0; k < 7; k ++) {
          if (k < 6) {
            ltPos = vec3 (0., 0.5 * psgHt, 0.8 * rTor);
            ltPos.xz = Rot2D (ltPos.xz, 2. * pi * (- aRotTor + float (k) / 6.));
            ltDirL = normalize (ltPos);
            s = length (ltPos - ro);
            att = 0.6 * max (dot (ltDirL, ltPos - ro) / s, 0.) / (1. + 0.1 * s * s);
          } else {
            ltDirL = - ltDir;
            att = 0.5;
          }
          isMet = (idObj == idTorL || idObj == idTorR || idObj == idSpkEx);
          nDotL = max (dot (vn, ltDirL), 0.);
          if (isMet) nDotL *= nDotL;
          dSum += att * nDotL;
          sSum += att * pow (max (dot (normalize (ltDirL - rd), vn), 0.), 16.);
        }
        col = col4.rgb * (0.1 + 0.5 * dSum) + 0.5 * col4.a * sSum;
        col = mix (col, vec3 (0.3, 0.3, 1.), 0.1 * (1. - smoothstep (0.7, 0.95, torzFac)));
        if (idObj == idTorC || idObj == idTorS) col.r += ((idObj == idTorC) ? 0.5 : 0.2) *
           max (- dot (rd, vn), 0.) * SmoothBump (0.3, 0.7, 0.1, tm);
        if (idObj == idFlor || idObj == idDom) {
          w = Rot2D (ro.xz, 2. * pi * aRotTor / nSegTor);
          w = vec2 (abs (dot (w, sin (2. * pi * (floor (nSegTor * atan (w.y, - w.x) / (2. * pi)) +
             0.5) / nSegTor + vec2 (0., 0.5 * pi)))), 0.5 * abs (length (ro.xz) - rTor - 0.1));
          col += 0.2 * glCol * (1. - smoothstep (0.03, 0.12, length (w)));
        }
      } else {
        col = col4.rgb * (0.2 + 0.4 * max (dot (vn, ltDir), 0.)) +
           col4.a * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
      }
    } else col = col4.rgb * (0.6 + 0.4 * max (- dot (rd, vn), 0.));
  } else {
    dstGrnd = - ro.y / rd.y;
    if (rd.y < 0. && dstGrnd < dstFar) {
      ro += dstGrnd * rd;
      psgDir = (abs (ro.x) < abs (ro.z));
      f = smoothstep (0.1, 1., dstGrnd / dstFar);
      col = vec3 (0.05, 0.07, 0.05) * (0.7 + 0.3 * Fbm2 (8. * ro.xz)) * (1. - f);
      s = length (vec2 (abs (psgDir ? ro.xz : ro.zx) - vec2 (0., psgLen + 4. * wThk)));
      col += (1. - smoothstep (-0.3, 0., s - 0.8 * psgWd)) * vec3 (0.2, 0.2, 0.3);
      dstObj = dstGrnd;
    } else {
      f = 1.;
      col = vec3 (0.);
    }
    col = mix (col, SkyCol (rd), f);
  }
  dstObj = min (dstObj, dstFar);
  if (dstTrObj < dstObj) col += glCol * smoothstep (0., 0.1, dstObj - dstTrObj) *
     (0.05 + 0.4 * clamp (- dot (TrObjNf (roo + dstTrObj * rd), rd), 0., 1.));
  f = 1. - smoothstep (0.7, 0.95, torzFac);
  if (f > 0.) {
    ro = roo;
    ro.yz = Rot2D (ro.yz, -0.5 * pi);
    rd.yz = Rot2D (rd.yz, -0.5 * pi);
    fBot = flLen - flRad + 0.4;
    ro.z += fBot;
    dCyl2 = CapsHit (ro, rd, flRad, flLen);
    if (inBldg && col4.a >= 0.) col = clamp (col * vec3 (1. - 0.2 * f, 1. + 0.2 * f,
       1. + 0.3 * f), 0., 1.);
    if (dCyl2.x < dstObj && ro.z + dCyl2.x * rd.z < fBot - 0.1)
       col += 3. * f * FlmCol (ro, rd, dCyl2, dstObj);
  }
  return clamp (col, 0., 1.);
}

#define AA  0   // optional antialiasing

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, vd, col;
  vec2 canvas, uv, uvv, mMid[2], ut[2], mSize, msw;
  float el, az, zmFac, asp, vuId, regId, s, t, sr;
  int vuMode;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  mSize = (1./7.) * vec2 (asp, 1.);
  mMid[0] = vec2 (asp, 1.) * (1. - mSize.y) * vec2 (1., -1.);
  mMid[1] = vec2 (asp, 1.) * (1. - mSize.y) * vec2 (-1., -1.);
  for (int k = 0; k < 2; k ++) ut[k] = abs (uv - mMid[k]) - mSize;
  regId = -1.;
  if (mPtr.z > 0.) {
    for (int k = 0; k < 2; k ++) {
      msw = 2. * mPtr.xy - mMid[k] / vec2 (asp, 1.);
      if (Maxv2 (abs (msw)) < mSize.y) {
        regId = 1. + float (k);
        msw /= 2. * mSize.y;
        break;
      }
    }
    if (regId == -1.) msw = mPtr.xy;
  }
  vuId = 0.;
  for (int k = 0; k < 2; k ++) {
    if (Maxv2 (ut[k]) < 0.) {
      uv = (uv - mMid[k]) / mSize.y;
      vuId = float (k + 1);
      break;
    }
  }
  if (regId > 0. && (vuId == 0. || vuId == regId)) vuId = regId - vuId;
  vuMode = int (vuId) + 1;
  if (vuMode == 1) {
    t = mod (tCur / 30., 1.);
    s = SmoothBump (0.25, 0.75, 0.2, t) - 0.5;
    ro = vec3 (1.6 * sign (t - 0.5) * (0.5 - abs (s)), 1., 8. * s);
    vd = normalize (vec3 (0., 0.5, 0.) - ro);
    az = atan (vd.x, vd.z);
    el = asin (vd.y);
    if (t > 0.5) el += 0.35 * pi * (1. - smoothstep (0.05, 0.12, abs (abs (t - 0.5) - 0.25)));
    zmFac = 3.;
  } else if (vuMode == 2) {
    az = 0.;
    el = -0.05 * pi;
    ro = vec3 (0., 1., -1.5);
    zmFac = 1.5;
  } else if (vuMode == 3) {
    az = 0.02 * pi;
    el = -0.05 * pi;
    ro = vec3 (0., 1.5, -20.);
    zmFac = 6.;
  }
  if (mPtr.z > 0.) {
    az += 2. * pi * msw.x;
    el += pi * msw.y;
  }
  el = clamp (el, -0.35 * pi, 0.45 * pi);
  vuMat = StdVuMat (el, az);
  if (vuMode == 3) {
    ro = vuMat * ro;
    ro.y = max (0.5, ro.y);
  }
  dstFar = 60.;
  ltDir = normalize (vec3 (0., 1., 0.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi)) / zmFac;
    rd = vuMat * normalize (vec3 ((2. * tan (0.5 * atan (uvv.x / asp))) * asp, uvv.y, 1.));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  for (int k = 0; k < 2; k ++) {
    if (Maxv2 (ut[k]) < 0. && Minv2 (abs (ut[k])) * canvas.y < 2.) col = vec3 (0.5);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float PerBumpf (float p)
{
  return 0.5 * smoothstep (0., 0.5, abs (fract (p) - 0.5));
}

vec3 PerBumpv3 (vec3 p)
{
  return 0.5 * smoothstep (0., 0.5, abs (fract (p) - 0.5));
}

float BumpFbm3 (vec3 p)
{  // variation of triangular noise in "Misty Flight", adapted from nimitz's "Oblivion"
  vec3 q;
  float a, f;
  a = 1.;
  f = 0.;
  q = p;
  for (int j = 0; j < 4; j ++) {
    p += PerBumpv3 (q + PerBumpv3 (q).yzx);
    p *= 1.5;
    f += a * (PerBumpf (p.z + PerBumpf (p.x + PerBumpf (p.y))));
    q = 2. * q + 0.5;
    a *= 0.75;
  }
  return f;
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

float Hashfv3 (vec3 p)
{
  return fract (sin (dot (p, vec3 (37., 39., 41.))) * cHashM);
}

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  return fract (sin (dot (p, cHashVA3) + vec4 (0., cHashVA3.xyz)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

float Cloud(vec2 uv, float size, float aa, float time, float flip, float random) {
    uv *= 1.2;
    uv.y -= .34;
    uv.x += 1.18;
    if (flip > 0.) {
        uv.x -= fract(iTime*.05*time)*2.9;
    } else {
        uv.x += fract(iTime*.05*time)*2.9;
    }

    uv.y += cos(iTime*time)*.06+.1;
    float cloud;
    for(float i = .1; i<.9; i+=.1) {
        float randomX = fract(548.546*i*random)*.48+sin(iTime)*.027;
        float randomY = fract(124.786*i*.355)*.25+sin(iTime*.5)*.077;
        float d = length(vec2(uv.x+randomX, uv.y+randomY));
        cloud += smoothstep(size/4.+aa+randomX*.2, size/4.+randomX*.2, d)*2.;
    }
    return cloud;
}

vec3 Rainbow(vec2 uv, vec3 bg, float aa) {
    float d = length(vec2(uv.x, uv.y+.5));

    float red = smoothstep(.9+aa, .9, d);
    red -= smoothstep(.85+aa, .85, d);

    float orange = smoothstep(.85+aa, .85, d);
    orange -= smoothstep(.8+aa, .8, d);

    float yellow = smoothstep(.8+aa, .8, d);
    yellow -= smoothstep(.75+aa, .75, d);

    float green = smoothstep(.75+aa, .75, d);
    green -= smoothstep(.7+aa, .7, d);

    float turquoise = smoothstep(.7+aa, .7, d);
    turquoise -= smoothstep(.65+aa, .65, d);

    float blue = smoothstep(.65+aa, .65, d);
    blue -= smoothstep(.6+aa, .6, d);

    float purple = smoothstep(.6+aa, .6, d);
    purple -= smoothstep(.55+aa, .55, d);

    vec3 rainbow;
    rainbow = red * vec3(1., 0., 0.) - bg * red;
    rainbow += orange * vec3(7., .6, 0.) - bg * orange;
    rainbow += yellow * vec3(1., 1., 0.) - bg * yellow;
    rainbow += green * vec3(0., 1., 0.) - bg * green;
    rainbow += turquoise * vec3(0., .8, 1.) - bg * turquoise;
    rainbow += blue * vec3(0., .27, 1.) - bg * blue;
    //rainbow += purple * vec3(.6, .0, 1.) - bg * purple;

    return rainbow;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float aa = 1.5 / iResolution.y;

    uv *= 1. - uv * sin(iTime*.3) *.05;

    float bmod = sin(iTime)*.3+.3;
    vec3 bg = mix(vec3(0.5, 0.5, 1.), vec3(0.0, 0.0, .9-bmod), uv.y+.15);
    vec3 col = bg;

    col += Cloud(uv, .3, aa, .7, 1., .687);

    /*uv.y *= 1.5;
    uv.x = -uv.x;
    uv.x -= 2.4;
    float cloudBack = Cloud(vec2(uv.x, uv.y-.1), .31, aa, 0.23, -1., .431);
    uv.x = -uv.x;
    uv.x -= 2.4;
    uv.y /= 1.5;*/

    uv.x -= .6;
    uv.y -= .07;
    uv.y += sin(iTime*.5)*.02;
    float ground1 = smoothstep(2.+aa, 2., length(vec2(uv.x+.3, uv.y+2.35)));

    uv.x += .6;
    uv.y += .07;

    uv.y -= sin(iTime*.5)*.02;
    uv.y -= cos(iTime*.3)*.02;
    vec3 rainbow = Rainbow(uv, bg, aa);
    col += rainbow;

    //float rainb = clamp(rainbow.r, .0, 1.) + clamp(rainbow.g, .0, 1.) + clamp(rainbow.b, .0, 1.);
    //rainb *= 1000.;
    //cloudBack = cloudBack - rainb;
    //col += clamp(cloudBack, .0, .5);

    uv.y +=.0;
    uv.y += cos(iTime*.3)*.02;
    uv.y += cos(iTime*.3)*.02;
    float ground2 = smoothstep(4.+aa, 4., length(vec2(uv.x, uv.y+4.35)));

    ground1 *= ground1 - ground2;
    col = clamp(col, 0., 1.);
    ground1 += rainbow.r + rainbow.g + rainbow.b * 20.;
    ground1 *= 1.-step(-.2, uv.y);

    float g1 = clamp(ground1, 0., 1.);
    col -= g1 * vec3(.35, .02, .85);
    col *= clamp(vec3(1., 1., 1.) * (1.-ground2), 0., 1.);
    col += clamp(vec3(1., 1., 1.) * ground2, 0., 1.)*vec3(.1, .55, .1);

    col.r += sin(iTime*.76)*.2;
    col.b += sin(iTime*.14)*.1;
    col.rg += .1;

    fragColor = vec4(col,1.0);
}

#define PI 3.1415
#define TWO_PI 6.2831

float sdCircle( vec2 p, float r ){
    return length(p) - r;
}
float sdLine (in vec2 p, in vec2 a, in vec2 b){
	vec2 pa = p-a, ba = b-a;
	float h = clamp(dot(pa,ba)/dot(ba,ba),0.,1.);
	return length (pa-ba*h);
}
vec2 ref (vec2 p, vec2 v){   //reflection
    return p - v * (1.*dot(p,v));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
	uv *= 2.;      //zoom
    float di = 3.; //reflections number
    vec2 v;
    vec2 rf;

    //circle center
    vec2 p = vec2(sin(iTime*5.),cos(iTime*4.));

    //draw circle
    vec3 col = 1.- vec3(vec2(smoothstep(sdCircle(uv-p ,0.1),0.0,0.01)),1.); 

    for(float i; i<di; i++){
    	v = 1.5*vec2(sin(PI/di*i),cos(PI/di*i));//vector
        rf  = ref(p,v );						//reflection
        //draw
        col +=1.- vec3(smoothstep(sdCircle(uv-rf,0.1),0.0,0.01));
    	col += vec3(step(sdLine(uv,v ,-v ),.01));

    }
    fragColor = vec4(col,1.0);
}

const float FAR = 50.;
vec3 CAMERA_POS = vec3(-0.4, 0.87, .3);
vec3 CAMERA_LOOK = vec3(0.,-0.3,0.2);
vec2 WIND = vec2(0.2, -0.1);
const float EPS = 0.002;
const int OCEAN_OCT = 12;
const int CLOUD_OCT = 6;
const vec3 SUN = normalize(vec3(0.3, -0.4, .01));
const vec3 SUN_C = vec3(0.8, 0.6, 0.4)*3.7;

vec3 ray_direction(float fov, vec2 size, vec2 coord) {
    // from https://www.shadertoy.com/view/Xtd3z7
    vec2 xy = coord - size / 2.0;
    float z = size.y / tan(radians(fov) / 2.0);
    return normalize(vec3(xy, -z));
}

mat4 view_matrix(vec3 eye, vec3 center, vec3 up) {
    // from https://www.shadertoy.com/view/Xtd3z7
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}

vec2 hash( vec2 p ) // uses a texture, causes some artifacting but it is not to noticable in this shader
{
    return texture(iChannel0, p*0.01).xy;

}

float noise( in vec2 p )
{
    //from https://www.shadertoy.com/view/Msf3WH

    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    //return hash(i).x;
    return dot( n, vec3(70.0) );
}
float ocean_height(vec2 p) // ridged fbm(using abs noise)
{
    float sc = .15;
    float am = .4;
    float n = 0.;
    for(int i = 0; i < OCEAN_OCT; i++)
    {
		n += -abs(noise(p*sc+float(i)+WIND*iTime))*am;
        float cs = cos(float(i)*0.3);
		float sn = sin(float(i)*0.3);
        p = vec2(p.x * cs - p.y * sn, p.x * sn + p.y * cs);
        sc *= 1.45;
        am *= 0.6;
    }
	return n;
}
vec3 ocean_nrm(vec2 p)
{
    //from https://www.iquilezles.org/www/articles/terrainmarching/terrainmarching.htm
    return normalize( vec3( ocean_height(vec2(p.x-EPS,p.y)) - ocean_height(vec2(p.x+EPS,p.y)),
                           ocean_height(vec2(p.x,p.y-EPS)) - ocean_height(vec2(p.x,p.y+EPS)),
                            2.0f*EPS) );
}
vec3 fresnel(float cosTheta, vec3 F0)
{
    //from https://learnopengl.com/PBR/Theory
    return F0 + (1.0 - F0) * pow(1.0 - cosTheta, 5.0);
}
float cloud_d(vec3 p)// fbm for  clouds
{
	vec2 cloud_p = p.xy/p.z*.4;
    float sc = .3;
    float am = .8;
    float n = 0.;
    for(int i = 0; i < OCEAN_OCT; i++)
    {
		n += noise(cloud_p*sc+float(i)+WIND*0.5*iTime)*am;
        float cs = cos(float(i)*0.3);
		float sn = sin(float(i)*0.3);
        cloud_p = vec2(cloud_p.x * cs - cloud_p.y * sn, cloud_p.x * sn + cloud_p.y * cs);
        sc *= 1.3;
        am *= 0.73;
    }
    return n+.3;
}
vec3 shade_sky(vec3 p, bool c)
{
    p = normalize(p);
	vec3 col = mix(vec3(0.6, 0.6, 0.85), vec3(0.55, 0.55, 0.85), p.z)*1.4;//sky
    col = mix(vec3(0.8, 0.5, 0.54), col, clamp(p.z+0.4, 0., 1.))*0.8;//pink band
    float sun = dot(p, SUN);//sun dot
    col = mix(col, SUN_C, min(max(sun-.996, 0.)*500., 1.));//sun colour
    if(c && p.z>0.) // use clouds, lighted with effectivly single step ray march
    {
        float cl = cloud_d(p);//clouds
        float cl2 = cloud_d(p-SUN*0.3);//clouds offset
        float l = cl-cl2;//clouds lighting estimate

        vec3 ccol = mix(vec3(0.65, 0.7, 0.7)*SUN_C, vec3(0.4), l-min(max(sun-.8, 0.)*11., 1.))*0.6;//cloud col

        col = mix(col, ccol, clamp(cl, 0., 1.)*0.6);
    }
    //col = vec3(l);
	return col*vec3(0.9, 1.,1.);
}


vec3 shade_ocean(vec3 p, vec3 dir, float h)
{


    vec3 col = vec3(0.2, 0.2, 0.7)*0.4;//base_col
    vec3 nrm = ocean_nrm(p.xy);
    vec3 refr = refract(dir, nrm, 1.03);//refraction ray
    float fr = fresnel(dot(dir, nrm), vec3(1.055)).x;//fresnel
    vec3 ref = reflect(nrm, dir);//reflection ray
    vec3 rrc = mix(col, shade_sky(p+refr, false)*0.9, clamp(refr.z+0.8, 0., 1.));//refraction ray, uses sky or if low the base col
    vec3 rc = shade_sky(p+ref, true)*0.96; // reflection col, using sky
    col = mix(rrc, rc, fr);//mix reflection and refraction by fresnel
    float f = min(max(h+.16, 0.)*20., 1.)*nrm.z*0.6;//foam on highest points
    vec3 fc = vec3(.75)+SUN_C*clamp(dot(SUN, nrm), 0., 1.); //foam col, simple diffuse shading
    col = mix(col, fc, f);
	return col*vec3(1., 0.9, 0.9);
}


vec2 march_ocean(vec3 p, vec3 d)
{
    float dep = 0.;
    float h = 0.;
    float eps = EPS;
    while(dep < FAR)
    {
        vec3 pos = p + d * dep;
        h = ocean_height(pos.xy);
		float dist = pos.z-h;
        if(dist < eps)
        {
			return vec2(dep, h);
        }
        eps *= 1.15;//relax eps after each step
		dep += dist;

    }
    return vec2(FAR, 0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    CAMERA_POS.z += sin(iTime*0.1)*0.4;
    CAMERA_LOOK.z += sin(iTime*0.1)*0.1;//camera anim
    vec3 local_dir = ray_direction(85., iResolution.xy, fragCoord);

    mat4 view = view_matrix(CAMERA_POS, CAMERA_LOOK, vec3(0.,0.,1.));
    vec3 dir = (view * vec4(local_dir, 0.)).xyz;
    vec2 o = march_ocean(CAMERA_POS, dir);
    float dep = o.x;
    vec3 pos = CAMERA_POS + dir * dep;

    vec3 col = shade_sky(dir, false); //sky w/out clouds, for mist 
    if(dep < FAR)
    {
		col = mix(shade_ocean(pos, dir, o.y), col, dep/FAR);
    }
    else
    {
		col = shade_sky(dir, true);//sky w, clouds for view
    }
    // Output to screen
 	//col = vec3(noise(fragCoord*0.06));
    col *= vec3(1.19, .9, 1.14)*0.7;//colour correct
    fragColor = vec4(pow(col, vec3(1./2.1)),1.0);//gamma and out
}

