void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy / 2.) / iResolution.xy;
    float time = iTime + iMouse.x / 125.;
    vec2 NfragCoord = floor(uv * 450.);

    float terrainHeight = (sin((uv.x + time / 4.) * 4.) / 4. + 0.5) - uv.y;
    float terrainCut = clamp(round(terrainHeight), 0., 1.);

    vec2 posForRand = vec2(floor((NfragCoord.y + time * 300.) / 10.), floor((NfragCoord.x - time * 200. + floor(NfragCoord.y / 1.25)) / 2.));
    float Frandom = rand(posForRand + 1234.5);
    float random = rand(posForRand);
    float Frain = Frandom * Frandom * Frandom * Frandom * Frandom;
    float rain = random * random * random * random * random;
    float Frain_cut = 1. - clamp(round(Frain * .55), 0., 2.);
    float rain_cut = 1. - clamp(round(rain * .7), 0., 2.);

    float lighting = length(uv.xy - 0.9) * 1.45;
    lighting = max(lighting, 0.9);
    lighting -= (uv.y  * terrainCut) / 1.5;

    float lightingColMask = length(uv.xy - 0.5) - 3.;
    lightingColMask = clamp(round(lightingColMask), 0., 1.);
    lightingColMask += (uv.y * terrainCut) * 1.25;

    vec3 otherSnowColor = vec3(0.85) * terrainCut + .9 * (1. - terrainCut);
    vec3 snowColor = otherSnowColor * (1. - Frain_cut);

    vec3 col = vec3(0.8);
    col = col * rain_cut + 0.9 * (1. - rain_cut);
    col = col * (1. - terrainCut) + (0.95 * (uv.y + 0.92)) * terrainCut;
    col = col * Frain_cut + snowColor;
    col /= lighting;
    col *= lightingColMask + vec3(0.673, 0.673, 0.96) * (1. - lightingColMask);

    fragColor = vec4(col, 1.0);
}

