struct PointLight
{
    vec3 pos;
    vec3 col;
};

struct Ob
{
    float t;
    vec3  c;
    PointLight  ptL; 
    int prop;
};

float mapPlane(vec3 p)
{
    float r = 0.5;
    float l = p.y + (r);
    return l;
}
float quadEq(float t)
{
    return 1.2*t*t+1.0*t+0.01;
}
float mapCube(vec3 p, float r)
{
    //p=p-vec3(0.5,0.47,2.0);
    p = p - vec3(0.5,0.47,0.5);
    vec3 t = vec3(quadEq(p.x),quadEq(p.y),quadEq(p.z));
    return length(t)-r;
}
Ob map(vec3 p)
{
    Ob o; 
    float tme = iTime*0.5;
    vec3 pt2 = vec3(0.9*cos(tme),-0.3*sin(tme/2.0),0.8*sin(tme));
    vec3 pt1 = vec3(0.9*sin(tme),-0.3*sin(tme/2.0),0.8*cos(tme));

    vec3 mov = p-pt1;
    vec3 mov1 =  p-pt2;
    float sph= length(p-vec3(0.0,0.0,0.1))-0.25;
    float p1 = length(mov)-0.02;
    float p2 =  length(mov1)-0.02;
    float pl = mapPlane(p);
    float cb = mapCube(p,0.25);
    o.t = min(min(sph,pl),cb);
    o.t = min(min(p1,p2),o.t);

    o.ptL.pos = min(mov,mov1);
    vec3 A= ptLight2Col*(1.0-p2)+ptLight1Col*p2;
    vec3 B= ptLight1Col*(1.0-p1)+ptLight2Col*p1;

    o.ptL.col = mix(A,B,0.5);

    /*vec3 mn = normalize(o.ptL.pos);
    vec3 dirPt = normalize(pt2 - pt1);
    float an = dot(mn, dirPt);
    vec3 A= ptLight1Col;
    vec3 B= ptLight2Col;
    o.ptL.col = mix(A,B,an);*/

    if (sph < pl && sph < cb)
    {
        o.c = vec3(0.1,0.1,0.2);
        o.prop = SPH;

    }
    else if (pl < sph && pl < cb)
    {
        o.c = vec3(0.1,0.2,0.1);
        o.prop = PLN;
    }
    else if (cb < sph && cb < pl)
    {
        o.c = vec3(0.8,0.2,0.5);
        o.prop = CUBE;
    }
    if (p1 < sph && p1 < pl && p1 < cb && p1 < p2)
    {
        o.c = ptLight1Col;
        o.prop = PT;

    }
    else if (p2 < sph && p2 < pl  && p2 < cb &&  p2 < p1)
    {
        o.c = ptLight2Col;
        o.prop = PT;
    }
    return o;
}

vec3 calcNormal(vec3 pos)
{
    vec2 e = vec2(0.0001,0.0);
    return normalize(vec3(map(pos+e.xyy).t-map(pos-e.xyy).t,
                          map(pos+e.yxy).t-map(pos-e.yxy).t,
                          map(pos+e.yyx).t-map(pos-e.yyx).t));
}


void castRay(in vec3 ro, vec3 rd,inout Ob o)
{
    float t =0.0;
    for(int i=0;i<100;i++)
    {
        vec3 pos = ro+t*rd;
        o = map(pos);
        float h = o.t;
        if (h<0.001)
            break;
        t+=h;
        if (t>20.0) break;
    }
    if (t>20.0) t=-0.1;
    o.t =t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    randState = (2.0*fragCoord-iResolution.xy)/iResolution.y ;
    /*float an = 10.0*iMouse.x/iResolution.x;
    vec3 ro = vec3(2.0*cos(an),-0.3,2.0*sin(an));*/

    float an = 10.0*fract(0.05*iTime)+iMouse.x/iResolution.x;
    float up = cos(iTime)-an;
    vec3 ro = vec3(2.0*cos(an),-0.3*up,2.0*sin(an));

    vec3 ta = vec3(0.0,0.0,0.0);

    vec3 ww = normalize(ta - ro);
    vec3 uu = normalize(cross(ww,vec3(0.0,1.0,0.0)));
    vec3 vv = normalize(cross(uu,ww));

    vec3 rd = normalize(p.x*uu+p.y*vv+1.5*ww);

    vec3 col = vec3(0.0)-0.7*rd.y;

    Ob o;
    castRay(ro, rd,o);
    if (o.t > 0.0) 
    {
        vec3 pos = ro + o.t*rd;

        vec3 nor = calcNormal(pos);

        vec3 mate = o.c;
        nor +=normalize(vec3(0.0,0.0,0.5));
        vec3 sun_light = (o.ptL.pos);
        float sun_shad = step(o.t,0.0);

        float kc=0.3, kl=0.3,kq = 0.03;

        float d = length(sun_light);
        vec3 oc = o.ptL.col;

        if (o.prop == SPH)
        {

            mate  += ((1.0/(kc+kl*d+kq*d*d))*oc) ;
            float sun_dif = clamp(dot(nor,sun_light),0.0,1.0);
            float sky_dif = clamp(0.5+0.5*dot(nor,vec3(0.0,1.0,0.0)),0.0,1.0);
            float boun_dif = clamp(0.9-0.5*dot(nor,vec3(0.0,-1.0,0.0)),0.0,1.0);

            col = mate*sun_dif*sun_shad;   
            col +=mate*sky_dif;    
            col +=mate*boun_dif; 
        }
        else if (o.prop == PLN)
        {
            mate = ((1.0/(kc+kl*d+kq*d*d))*oc) ;
            float sun_dif = clamp(dot(nor,sun_light),0.0,1.0);
            float sky_dif = clamp(0.5+0.5*dot(nor,vec3(0.0,1.0,0.0)),0.0,1.0);
            float boun_dif = clamp(0.5+0.5*dot(nor,vec3(0.0,-1.0,0.0)),0.0,1.0);

            col = mate*sun_dif*sun_shad;   
            col +=mate*sky_dif;    
            col +=mate*boun_dif;
        }
        else if (o.prop == CUBE)
        {
            mate = ((1.0/(kc+kl*d+kq*d*d))*oc) ;
            float sun_dif = clamp(dot(nor,sun_light),0.0,1.0);
            float sky_dif = clamp(0.5+0.5*dot(nor,vec3(0.0,1.0,0.0)),0.0,1.0);
            float boun_dif = clamp(0.5+0.5*dot(nor,vec3(0.0,-1.0,0.0)),0.0,1.0);

            col = mate*sun_dif*sun_shad;   
            col +=mate*sky_dif;    
            col +=mate*boun_dif;
        }
        else if (o.prop == PT)
        {

            vec3 sun_light = o.ptL.pos;

            float d = length(sun_light-pos);

            mate += ((1.0/(kc+kl*d+kq*d*d))*o.ptL.col);
            col = mate;
        }
    }

    fragColor = vec4(col,1.0);
}

vec2 rotateUV(vec2 uv, float angle) { 
    float u = cos(angle) * uv.x + -sin(angle) * uv.y; 
    float v = sin(angle) * uv.x + cos(angle) * uv.y; 

    return vec2(u,v); 
} 



vec3 hsv2rgb(vec3 c) { 
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0); 
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www); 

  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y); 

} 



void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{ 
    vec2 uv = (fragCoord/iResolution.xy * 2.0 - 1.0) * vec2(iResolution.x/iResolution.y, 1.0); 
    uv = rotateUV(uv, iTime); 

    float squaritude = sin(iTime) * 0.5 + 0.5; 
    float cd = 1.0/length(uv); 
    float sqd = abs(uv.x) + abs(uv.y); 


    if (cd+(sqd-cd)*squaritude < 1.0) 
        fragColor = vec4(hsv2rgb(vec3(squaritude,1.0,1.0)), 1.0); 
    else 
        fragColor = vec4(0.0); 

}

// About the shader:
// There are 4 parts: the sun, sun glow, horizontal lines and vertical lines
// 
// 2 effects added: pixelation and chromatic aberration
// 
// I just started learning GLSL so this is a messy experiment
//
// Feel free to leave any comment with critiques, ways to improve, etc, 
// I'll really apreciate it! :)
// 
// Twitter @lopoisaac

float vertical_mask(vec2 uv) 
{    
    return clamp(-pow(uv.y, 2.) + .8, 0., 1.);
}

vec4 horizontal_lines(vec2 uv)
{
    vec4 h_lines;
  	float scale_y = pow(uv.y, 2.) * 100.;

    h_lines.a = (sin(iTime * 6. + scale_y)) * vertical_mask(uv); 
    h_lines.a = smoothstep(0.4, .8, h_lines.a); 
    h_lines.xyz = vec3(.2, 0., 1.);

    return h_lines;
}

vec4 vertical_lines(vec2 uv, vec2 chroma)
{
 	vec4 v_lines;
    float scale_x = -40. / abs(uv.y -1.);

    v_lines.r = sin(-1. + abs(uv.x -.5) * scale_x + chroma.r);
    v_lines.g = sin(-1. + abs(uv.x -.5) * scale_x - chroma.g);
    v_lines.a = sin(-1. + abs(uv.x -.5) * scale_x) * vertical_mask(uv);
    v_lines.a = smoothstep(0.4, 1., v_lines.a);	

	v_lines.xyz =  vec3(v_lines.r, v_lines.g, 0.8) * 3.;

    return v_lines;
}

vec4 draw_sun(vec2 uv)
{     
	vec2 sun_position = vec2((uv.x -.5) * 1.5, uv.y -.8);
    vec4 sun_color = vec4(9., .9, 0.7, 1.);

    sun_color.a = length(sun_position) * 0.8;
    sun_color.a = smoothstep(.11, 0.09, sun_color.a);

    return sun_color;
}

float rectangle(vec2 position) 
{
    float area;
    area = min(position.y +.3, -position.y +.1) * 0.4;
    //area = position.y + .5 ;
	return area;
}

vec4 draw_glow(vec2 uv) {

    vec2 sun_position = vec2((uv.x -.5) * 1.4, uv.y -.8);
    vec4 sun_glow = vec4(3., .8, 0.3, 1.);

    sun_glow.a = length(sun_position) + 0.01 * fract(sin(iTime * 8.));

    sun_glow.a = smoothstep(.4, .0, sun_glow.a) * 0.3;
    sun_glow.a += rectangle(sun_position);

    return sun_glow;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    // effects
    vec2 chroma = vec2(1.5, 1.8); // chromatic aberration
    uv = floor(uv * 320.) / 320.; // pixelated effect

    // draw objects
    vec4 h_lines = horizontal_lines(uv);
   	vec4 v_lines = vertical_lines(uv, chroma);
   	vec4 sun = draw_sun(uv);
    vec4 sun_glow = draw_glow(uv);

    // output
    vec4 color = vec4(.1, .1, .2, 1.); // set background color

    color = mix(color, sun_glow, sun_glow.a);
    color = mix(color, h_lines, h_lines.a);
    color = mix(color, v_lines, v_lines.a);
    color = mix(color, sun, sun.a);

    fragColor = color;
}

