    ivec2 I = ivec2(U+3.*iTime);
    int x = I.x, y = I.y,
        i = x - y ^ y + x;

    float v = iTime * 0.75 + float(i);  // try also: i*i , i*i*i

    return (fract( v * 0.051618 ));
}

