// A modified Branchless Voxel Raycasting
// that uses the generated sdf to jump more than
// one voxel per iteration.
// https://www.shadertoy.com/view/4dX3zl

// A similar idea to https://www.shadertoy.com/view/MllcD7
// but far less complex.


const int MAX_RAY_STEPS = 40;

float sdSphere(vec3 p, float d) { return length(p) - d; } 

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) +
         length(max(d,0.0));
}

float getVoxel(ivec3 c) {
	vec3 p = vec3(c) + vec3(0.5);
	float d = min(max(-sdSphere(p, 75.), sdBox(p, vec3(60.))), -sdSphere(p, 2500.0));
	return d;
}

vec2 rotate2d(vec2 v, float a) {
	float sinA = sin(a);
	float cosA = cos(a);
	return vec2(v.x * cosA - v.y * sinA, v.y * cosA + v.x * sinA);	
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 screenPos = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
	vec3 cameraDir = vec3(0.0, 0.0, 0.8);
	vec3 cameraPlaneU = vec3(1.0, 0.0, 0.0);
	vec3 cameraPlaneV = vec3(0.0, 1.0, 0.0) * iResolution.y / iResolution.x;
	vec3 rayDir = cameraDir + screenPos.x * cameraPlaneU + screenPos.y * cameraPlaneV;
	vec3 rayPos = vec3(0.0, 2.0 * sin(iTime * 2.7), -12.0);

	rayPos.xz = rotate2d(rayPos.xz, iTime);
	rayDir.xz = rotate2d(rayDir.xz, iTime);

	ivec3 mapPos = ivec3(floor(rayPos + 0.));

	vec3 deltaDist = abs(vec3(length(rayDir)) / rayDir);

	ivec3 rayStep = ivec3(sign(rayDir));

	vec3 sideDist = (sign(rayDir) * (vec3(mapPos) - rayPos) + (sign(rayDir) * 0.5) + 0.5) * deltaDist; 

	vec3 mask;

	for (int i = 0; i < MAX_RAY_STEPS; i++) {
        float sdf = floor(getVoxel(mapPos));
		if (sdf < 0.) break;

        vec3 mi = min(min(sideDist.x, sideDist.y), sideDist.z) - sideDist;
        mask = ceil((max(sdf - 2.0, 0.0001) + mi) / deltaDist);
        sideDist += vec3(mask) * deltaDist;
        mapPos += ivec3(vec3(mask)) * rayStep;
	}

	vec3 color = vec3(1);
	if (mask.x != 0.) {
		color *= vec3(0.5);
	}
	if (mask.y != 0.) {
		color *= vec3(1.0);
	}
	if (mask.z != 0.) {
		color *= vec3(0.75);
	}
	fragColor.rgb = color;
}

#define PI 3.141592
#define TAU PI * 2.

vec4 mainTexture( in vec2 uv ) {
  vec4 limit = texture(iChannel0, uv);
  return vec4(mod(uv.y + limit.y + sin(iTime + uv.x * TAU) * .03, .5) < mod(limit.x, .25))
    * vec4(mod(uv.y + limit.y + sin(iTime * 1.5 + uv.y * TAU) * .03, .125) < mod(limit.x, .06125)) + 0.5;
}

float dither8x8(vec2 position, float brightness) {
  float limit = texture(iChannel0, position / 1024.).x;
  return brightness < limit ? 0.0 : 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 cpos = ( 2.* fragCoord -  iResolution.xy ) / iResolution.y;

  vec3 sun = vec3(cos(iTime * 0.5), 0., sin(iTime * 0.5));
  vec3 norm = normalize(vec3(cpos, sqrt(1. - dot(cpos, cpos))));

  vec2 tex = vec2(
    0.5 + atan(norm.z, norm.x) / TAU,
    0.5 + acos(norm.y) / PI
  );

  float d = length(cpos);
  float a = dot(sun, norm);

  if (d < 0.99) {
  	fragColor = vec4(dither8x8(fragCoord, a * mainTexture(tex).x));
  } else {
    fragColor = vec4(d < 1.);
  }
}

