#define xor(a,b,c) min(max(a,-(b)), max(-(a) + c,b))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
    vec2 uv = p;

    float env = pow(abs(sin(iTime*0.5)),0.3)*sign(sin(iTime*0.5 ))*0.5 + 0.5 ;


    p += vec2(sin(iTime + sin(iTime*0.4))*0.04,sin(iTime*0.6 - sin(iTime*0.4))*0.04);

    p *= 1. + env;
    df = dFdx(p.x);

    vec3 col = vec3(0.01,0.4,0.91);

    col += sin(p.xyy + sin(iTime + length(p))*0.4)*0.5;


    float d = 10e4;

    vec3 colo = vec3(0);


    #define TP(P) (floor(T) + pow(fract(T),P))
    float T = iTime - 14.;
    for(float i = 0.; i < 142.; i++){
        float env = sin(TP(5. + sin(i)*2.));
        env = pow(abs(env),4.)*sign(env);
        T += .44;
        p.x += 0. + env*0.004;
        p *= rot(sin(TP(2.)*0.001)*0.41);
        float ld = length(p) - .2 - sin(i*1.4 + sin(T)*0.2)*0.4;
        vec3 c = pal(0.5,.5,vec3(3,2.1,1.5),1.,i + p.x);

        if(sin(i *0.4) < -0.4){
            ld = xor(ld,-(p.y) - .5*sin(i + T*0.13 ),.4);

        }
        if(sin(i *0.4) > 0.1){
            ld = abs(ld - 0.1*sin(i + TP(5.)));
        }


        colo = add(d,ld,colo,c,0.);


        d = xor(d,ld,-0. - sin(i)*0.1);

        //d = min(d,ld);
    }



    col = add(10.,d + 0.01,col,colo,0.);



    col = mix(1. - col,col, env);    
    col *= smoothstep(1.,0.,dot(uv,uv)*0.7);

    col = mix(col,smoothstep(0.,1.,col),0.6);

    col = pow(col,vec3(0.454545));

    fragColor = vec4(col,1.0);
}

const float leftborder = -0.89;
const float rightborder = 0.89;

float drawBand(float axis, float start, float end, float blur) 
{
    float step1 = smoothstep(start-blur, start+blur, axis);
    float step2 = smoothstep(end+blur, end-blur, axis);
    return step1*step2;
}

float drawRect(vec2 uv, float left, float right, float top, float bottom, float blur)
{
    float band1 = drawBand(uv.x, left, right, blur);
    float band2 = drawBand(uv.y, top, bottom, blur);
    return band1 * band2;
}

float remap(float value, float low1, float high1, float low2, float high2)
{
    return ((value - low1) / (high1 - low1)) * (high2 - low2) + low2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // hogy kozepen legyen
    // Normalized pixel coordinates (from -0.5 to 0.5)
    uv -= .5;
    // hogy ne legyen ellipszis
    // Normalized pixel coordinates (from -0.5*1.78 to 0.5*1.78)
    uv.x *= iResolution.x/iResolution.y;

    float x = uv.x;
    // domain distortion, makes it wavey
    float t = iTime;
    float m = sin(t+ x*2.)/3.;
    float y = uv.y - m;
    // increasing blur 
    float blur = .004;
    blur = remap(x, leftborder, .89, .005, .25);
    //blur *= blur;
    blur = pow(blur*3., 4.);

    float mask1 = drawRect(vec2(x,y), leftborder, .89, -.08, .1, blur);
    // base color
   	vec3 color1 = vec3(0, .9, 0);
    // masking the base color with the rectangles
    color1 *= mask1;

    m = sin(t/2.+ x*3.)/3.;
    y = uv.y - m;
    float mask2 = drawRect(vec2(x,y), leftborder, .89, -.3, -.2, blur);
    // base color
   	vec3 color2 = vec3(.9, 0, 0);
    // masking the base color with the rectangles
    color2 *= mask2;

    m = sin(t*1.1+ x*2.)/3.;
    y = uv.y - m;
    float mask3 = drawRect(vec2(x,y), leftborder, .89, .2, .3, blur);
    // base color
   	vec3 color3 = vec3(0, 0, .9);
    // masking the base color with the rectangles
    color3 *= mask3;

    // Output to screen
    fragColor = vec4(color1+color2+color3,1.0);
}

float noise(in vec2 p) {
    return fract(sin(p.x * 17.6452 + p.y * 565.337) * 5425.235);
}

float SmoothNoise(in vec2 p) {
    vec2 luv = smoothstep(0.0, 1.0, fract(p)); // :P
    vec2 cid = floor(p);

    float bl = noise(cid);
    float br = noise(cid + vec2(1.0, 0.0));
    float tl = noise(cid + vec2(0.0, 1.0));
    float tr = noise(cid + vec2(1.0, 1.0));

    return mix(mix(bl, br, luv.x), mix(tl, tr, luv.x), luv.y);
}

float CoherentNoise(in vec2 p, in float scale, in float octaves) {
    float value = 0.0;
    float nscale = 1.0;
    float tscale = 0.0;

    for (float octave=0.0; octave < octaves; octave++) {
        value += SmoothNoise(p * pow(2.0, octave) * scale) * nscale;
        tscale += nscale;
        nscale *= 0.5;
    }

    return value / tscale;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.y;
    fragColor = vec4(CoherentNoise(uv + 0.1 * iTime, 6.0, 4.0));
}

