const int MAX_STEPS = 1000;
const float EPSILON = 0.01;
const float MIN_DIST = 0.25;
const float MAX_DIST = 100.;

//Surface height
float height(vec2 pos){
    float h = 0.7*voronoi(vec3(pos/2.,0.4*iTime)).x;

    return h+0.7*sin((pos.x-pos.y+2.*iTime)/2.);
}

float terrainSDF(vec3 pos){
    vec2 p = vec2(pos.x,pos.z);

	return pos.y - height(p);
}

/*
Raymarching code and Phong Illumination taken and adapted from user jlfwong's excellent tutorials, check them out
*/

float distToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_STEPS; i++) {
        float dist = terrainSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist*0.1;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}

/*
This is the same ray marching algorithm, but the signed distance function has opposite sign
So we raymarch from below the surface and stops when it hits the surface above.
*/

float distFromSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_STEPS; i++) {
        float dist = -terrainSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        //Our SDF function is an approximation, one can't really get the closest distance from a point p
        //to a heightmap just by looking at the difference p.y - height(p)
        //But this can be partly by taking a smaller step
        depth += dist*0.5;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}

vec3 rayDirection(float fov, vec2 size, vec2 fragCoord){
	vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fov) / 2.0);
    return normalize(vec3(xy, -z));

}

vec3 estimateNormal(vec3 p) {
    return -normalize(vec3(
        				  (height(vec2(p.x+EPSILON,p.z))-height(vec2(p.x-EPSILON,p.z)))/(2.*EPSILON),
                          -1,
                          (height(vec2(p.x,p.z+EPSILON))-height(vec2(p.x,p.z-EPSILON)))/(2.*EPSILON)));
}

mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}

/*
Once again:
Raymarching code and Phong Illumination taken and adapted from user jlfwong's excellent tutorials, check them out
*/

vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = eye+vec3(-2.*MAX_DIST,2.*MAX_DIST,2.*MAX_DIST);
    vec3 light1Intensity = vec3(1.0, 1.0, 1.0);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);

    return color;
}

