const vec4 cHashA4 = vec4 (0., 1., 57., 58.);
const vec3 cHashA3 = vec3 (1., 57., 113.);
const float cHashM = 43758.54;

vec4 Hashv4f (float p)
{
  return fract (sin (p + cHashA4) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 i = floor (p);
  vec2 f = fract (p);
  f = f * f * (3. - 2. * f);
  vec4 t = Hashv4f (dot (i, cHashA3.xy));
  return mix (mix (t.x, t.y, f.x), mix (t.z, t.w, f.x), f.y);
}

float Noisefv3 (vec3 p)
{
  vec3 i = floor (p);
  vec3 f = fract (p);
  f = f * f * (3. - 2. * f);
  float q = dot (i, cHashA3);
  vec4 t1 = Hashv4f (q);
  vec4 t2 = Hashv4f (q + cHashA3.z);
  return mix (mix (mix (t1.x, t1.y, f.x), mix (t1.z, t1.w, f.x), f.y),
     mix (mix (t2.x, t2.y, f.x), mix (t2.z, t2.w, f.x), f.y), f.z);
}

vec3 Noisev3v2 (vec2 p)
{
  vec2 i = floor (p);
  vec2 f = fract (p);
  vec2 ff = f * f;
  vec2 u = ff * (3. - 2. * f);
  vec2 uu = 30. * ff * (ff - 2. * f + 1.);
  vec4 h = Hashv4f (dot (i, cHashA3.xy));
  return vec3 (h.x + (h.y - h.x) * u.x + (h.z - h.x) * u.y +
     (h.x - h.y - h.z + h.w) * u.x * u.y, uu * (vec2 (h.y - h.x, h.z - h.x) +
     (h.x - h.y - h.z + h.w) * u.yx));
}

float SmoothMin (float a, float b, float r)
{
  float h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

float PrCapsDf (vec3 p, vec2 b)
{
  return length (p - vec3 (0., 0., b.x * clamp (p.z / b.x, -1., 1.))) - b.y;
}

float PrCylDf (vec3 p, vec2 b)
{
  return max (length (p.xy) - b.x, abs (p.z) - b.y);
}

float PrConeDf (vec3 p, vec3 b)
{
  return max (dot (vec2 (length (p.xy), p.z), b.xy), abs (p.z) - b.z);
}

int idObj;
mat3 flyerMat;
vec3 flyerPos, engPos, qHit, qHitTransObj, sunDir, sunCol;
vec2 trkOffset;
float szFac, wSpan, tCur;
const float dstFar = 400.;
const float pi = 3.14159;

vec3 TrackPath (float t)
{
  return vec3 (24. * sin (0.035 * t) * sin (0.012 * t) * cos (0.01 * t) +
     19. * sin (0.0032 * t) + 100. * trkOffset.x, 0., t);
}

float GrndHt (vec2 p, int hiRes)
{
  const vec2 vRot = vec2 (1.4624, 1.6721);
  vec2 q = p * 0.06;
  float w = 0.75 * Noisefv2 (0.25 * q) + 0.15;
  w *= 36. * w;
  vec2 vyz = vec2 (0.);
  float ht = 0.;
  for (int j = 0; j < 10; j ++) {
    vec3 v = Noisev3v2 (q);
    vyz += v.yz;
    ht += w * v.x / (1. + dot (vyz, vyz));
    if (j == 4) {
      if (hiRes == 0) break;
    }
    w *= -0.37;      
    q *= mat2 (vRot.x, vRot.y, - vRot.y, vRot.x);
  }
  vec3 pt = TrackPath (p.y);
  pt.y -= 2.;
  float g = smoothstep (1.5, 4.5, sqrt (abs (p.x - pt.x)));
  return min (ht, pt.y * (1. - g) + ht * g);
}

vec3 GrndNf (vec3 p, float d)
{
  float ht = GrndHt (p.xz, 1);
  vec2 e = vec2 (max (0.01, 0.00001 * d * d), 0.);
  return normalize (vec3 (ht - GrndHt (p.xz + e.xy, 1), e.x,
     ht - GrndHt (p.xz + e.yx, 1)));
}

vec4 GrndCol (vec3 p, vec3 n)
{
  const vec3 gCol1 = vec3 (0.6, 0.7, 0.7), gCol2 = vec3 (0.2, 0.1, 0.1),
     gCol3 = vec3 (0.4, 0.3, 0.3), gCol4 = vec3 (0.1, 0.2, 0.1),
     gCol5 = vec3 (0.7, 0.7, 0.8), gCol6 = vec3 (0.05, 0.3, 0.03),
     gCol7 = vec3 (0.1, 0.08, 0.);
  vec2 q = p.xz;
  float f, d;
  float cSpec = 0.;
  f = 0.5 * (clamp (Noisefv2 (0.1 * q), 0., 1.) +
      0.8 * Noisefv2 (0.2 * q + 2.1 * n.xy + 2.2 * n.yz));
  vec3 col = f * mix (f * gCol1 + gCol2, f * gCol3 + gCol4, 0.65 * f);
  if (n.y < 0.5) {
    f = 0.4 * (Noisefv2 (0.4 * q + vec2 (0., 0.57 * p.y)) +
       0.5 * Noisefv2 (6. * q));
    d = 4. * (0.5 - n.y);
    col = mix (col, vec3 (f), clamp (d * d, 0.1, 1.));
    cSpec += 0.1;
  }
  if (p.y > 22.) {
    if (n.y > 0.25) {
      f = clamp (0.07 * (p.y - 22. - Noisefv2 (0.2 * q) * 15.), 0., 1.);
      col = mix (col, gCol5, f);
      cSpec += f;
    }
  } else {
    if (n.y > 0.45) {
      vec3 c = (n.y - 0.3) * (gCol6 * vec3 (Noisefv2 (0.4 * q),
         Noisefv2 (0.34 * q), Noisefv2 (0.38 * q)) + vec3 (0.02, 0.1, 0.02));
      col = mix (col, c, smoothstep (0.45, 0.65, n.y) *
         (1. - smoothstep (15., 22., p.y - 1.5 + 1.5 * Noisefv2 (0.2 * q))));
    }
    if (p.y < 0.65 && n.y > 0.4) {
      d = n.y - 0.4;
      col = mix (col, d * d + gCol7, 2. * clamp ((0.65 - p.y -
         0.35 * (Noisefv2 (0.4 * q) + 0.5 * Noisefv2 (0.8 * q) +
         0.25 * Noisefv2 (1.6 * q))), 0., 0.3));
      cSpec += 0.1;
    }
  }
  return vec4 (col, cSpec);
}

float GrndRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float h, s, sLo, sHi;
  s = 0.;
  sLo = 0.;
  float dHit = dstFar;
  for (int j = 0; j < 150; j ++) {
    p = ro + s * rd;
    h = p.y - GrndHt (p.xz, 0);
    if (h < 0.) break;
    sLo = s;
    s += max (0.15, 0.4 * h) + 0.008 * s;
    if (s > dstFar) break;
  }
  if (h < 0.) {
    sHi = s;
    for (int j = 0; j < 10; j ++) {
      s = 0.5 * (sLo + sHi);
      p = ro + s * rd;
      h = step (0., p.y - GrndHt (p.xz, 0));
      sLo += h * (s - sLo);
      sHi += (1. - h) * (s - sHi);
    }
    dHit = sHi;
  }
  return dHit;
}

float WaterHt (vec3 p)
{
  p *= 0.1;
  float ht = 0.;
  const float wb = 1.414;
  float w = 0.2 * wb;
  for (int j = 0; j < 7; j ++) {
    w *= 0.5;
    p = wb * vec3 (p.y + p.z, p.z - p.y, 2. * p.x);
    ht += w * abs (Noisefv3 (p) - 0.5);
  }
  return ht;
}

vec3 WaterNf (vec3 p, float d)
{
  float ht = WaterHt (p);
  vec2 e = vec2 (max (0.01, 0.001 * d * d), 0.);
  return normalize (vec3 (ht - WaterHt (p + e.xyy), e.x, ht - WaterHt (p + e.yyx)));
}

vec3 SkyBg (vec3 rd)
{
  const vec3 sbCol = vec3 (0.15, 0.2, 0.65);
  vec3 col;
  col = sbCol + 0.2 * sunCol * pow (1. - max (rd.y, 0.), 5.);
  return col;
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  const float skyHt = 200.;
  vec3 col;
  float cloudFac;
  if (rd.y > 0.) {
    ro.x += 0.5 * tCur;
    vec2 p = 0.01 * (rd.xz * (skyHt - ro.y) / rd.y + ro.xz);
    float w = 0.65;
    float f = 0.;
    for (int j = 0; j < 4; j ++) {
      f += w * Noisefv2 (p);
      w *= 0.5;
      p *= 2.3;
    }
    cloudFac = clamp (5. * (f - 0.5) * rd.y - 0.1, 0., 1.);
  } else cloudFac = 0.;
  float s = max (dot (rd, sunDir), 0.);
  col = SkyBg (rd) + sunCol * (0.35 * pow (s, 6.) +
     0.65 * min (pow (s, 256.), 0.3));
  col = mix (col, vec3 (0.85), cloudFac);
  return col;
}

float GrndSShadow (vec3 ro, vec3 rd)
{
  float sh = 1.;
  float d = 0.01;
  float eps = 0.001;
  for (int i = 0; i < 80; i++) {
    vec3 p = ro + rd * d;
    float h = p.y - GrndHt (p.xz, 0);
    sh = min (sh, 20. * h / d);
    d += 0.5;
    if (h < eps*(1.0+d)) break;
    eps *= 1.02;
  }
  return clamp (sh, 0., 1.);
}

struct WingParm
{
  float span, sRad, trans, thck, leCut, leRad;
};

float WingDf (vec3 p, WingParm wg)
{
  vec2 q = p.yz;
  float w = max (length (q - vec2 (wg.sRad, 0.)),
     length (q + vec2 (wg.sRad, 0.)));
  w = max (max (w - wg.thck, abs (p.x - wg.trans) - wg.span),
     p.z - wg.leCut);
  return min (w, max (length (q - vec2 (0., wg.leCut)) - wg.leRad,
     abs (p.x - wg.trans) - wg.span));
}

float PropelDf (vec3 p, float dHit)
{
  vec3 q;
  float d;
  dHit /= szFac;
  p /= szFac;
  q = p;
  q.x = abs (q.x);
  q -= engPos;
  d = PrCylDf (q - vec3 (0., 0., 3.65), vec2 (1.9, 0.05));
  if (d < dHit) {
    dHit = d;
    qHitTransObj = q;
  }
  return dHit * szFac;
}

float TransObjDf (vec3 p)
{
  float dHit = dstFar;
  dHit = PropelDf (flyerMat * (p - flyerPos), dHit);
  return dHit;
}

float TransObjRay (vec3 ro, vec3 rd)
{
  float dTol = 0.001;
  float d;
  float dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    d = TransObjDf (ro + dHit * rd);
    dHit += d;
    if (d < dTol*(1.0+dHit) || dHit > dstFar) break;
    dTol *= 1.01;
  }
  return dHit;
}

float FlyerDf (vec3 p, float dHit)
{
  vec3 q;
  WingParm wg;
  float d, wr, ws, cLen;
  const float wSweep = 0.2;
  const float fusLen = 11.;
  const float taPos = 12.5;
  dHit /= szFac;
  p /= szFac;
  q = p;
  wr = q.z / fusLen;
  d = PrCapsDf (q - fusLen * vec3 (0., 0.045 + 0.08 * wr, 0.),
     fusLen * vec2 (0.46, 0.11));
  if (d < dHit) {
    dHit = d;  idObj = 1;  qHit = q;
  }
  d = PrCapsDf (q - fusLen * vec3 (0., 0., -0.32),
     fusLen * vec2 (1., 0.15 - 0.051 * wr * wr));
  if (d < dHit + 0.1) {
    dHit = SmoothMin (dHit, d, 0.1);  idObj = 2;  qHit = q;
  }
  ws = wSweep * abs (p.x) / wSpan;
  q = p + vec3 (0., 0.054 * fusLen - 6. * ws, 12. * ws);
  wg = WingParm (wSpan, 13.7, 0., 14., 1.72, 0.195);
  d = WingDf (q, wg);
  if (d < dHit + 0.2) {
    dHit = SmoothMin (dHit, d, 0.2);  idObj = 3;  qHit = q;
   }
  q = p + vec3 (0., -0.1 - 6. * ws, taPos + 12. * ws);
  wg = WingParm (0.4 * wSpan, 6.8, 0., 7., 1.2, 0.095);
  d = WingDf (q, wg);
  if (d < dHit + 0.1) {
    dHit = SmoothMin (dHit, d, 0.1);  idObj = 4;  qHit = q;
  }
  ws = wSweep * abs (p.y) / wSpan;
  q = p.yxz + vec3 (-0.2, 0., taPos + 12. * ws);
  wg = WingParm (0.15 * wSpan, 6.8, 2.2, 7., 1.2, 0.095);
  d = WingDf (q, wg);
  if (d < dHit + 0.1) {
    dHit = SmoothMin (dHit, d, 0.1);  idObj = 5;  qHit = q;
  }
  q = p;
  q.x = abs (q.x);
  cLen = 3.5;
  wr = q.z / cLen;
  d = PrCylDf (q - engPos, cLen * vec2 (0.2  - 0.07 * wr * wr, 1.));
  float d2 = PrCylDf (q - engPos, cLen * vec2 (0.04, 1.02));
  d = max (d, - d2);
  if (d < dHit) {
    dHit = d;  idObj = 6;  qHit = q;
  }
  q = p;
  q.x = abs (q.x);
  d = PrConeDf (q - engPos - vec3 (0., 0., 4.2), vec3 (0.8, 0.6, 0.7));
  if (d < dHit) {
    dHit = d;  idObj = 7;  qHit = q;
  }
  q = p;
  cLen = 2.8;
  q.z += wSweep * wSpan - 0.025 * cLen;
  q.x = abs (q.x);
  wr = q.z / cLen;
  d = PrCapsDf (q - vec3 (wSpan + 0.1, 0.5 * wSweep * wSpan - 0.6, -0.5),
     cLen * vec2 (1., 0.15 - 0.055 * wr * wr));
  if (d < dHit) {
    dHit = d;  idObj = 8;  qHit = q;
  }
  return 0.8 * dHit * szFac;
}

float ObjDf (vec3 p)
{
  float dHit = dstFar;
  dHit = FlyerDf (flyerMat * (p - flyerPos), dHit);
  return dHit;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dTol = 0.001;
  float d;
  float dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < dTol*(1.0+dHit) || dHit > dstFar) break;
    dTol *= 1.01;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  const vec3 e = vec3 (0.001, -0.001, 0.);
  float v0 = ObjDf (p + e.xxx);
  float v1 = ObjDf (p + e.xyy);
  float v2 = ObjDf (p + e.yxy);
  float v3 = ObjDf (p + e.yyx);
  return normalize (vec3 (v0 - v1 - v2 - v3) + 2. * vec3 (v1, v2, v3));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float dTol = 0.01;
  float sh = 1.;
  float d = 0.07 * szFac;
  for (int i = 0; i < 50; i++) {
    float h = ObjDf (ro + rd * d);
    sh = min (sh, 20. * h / d);
    d += 0.07 * szFac;
    if (h < dTol*(1.0+d)) break;
    dTol *= 1.01;
  }
  return clamp (sh, 0., 1.);
}

vec3 ObjCol (vec3 p, vec3 n)
{
  vec3 bCol = vec3 (0.8, 0.8, 0.85), wCol = vec3 (0.3, 0.3, 0.7),
     tCol = vec3 (0.9, 0.7, 0.), uCol = vec3 (0.9, 0.1, 0.);
  float cFac = 1.;
  if (idObj >= 3 && idObj <= 5) {
    float s1, s2;
    if (idObj == 3) {
      s1 = 2.2;  s2 = 6.;
    } else if (idObj == 4) {
      s1 = 1.2;  s2 = 1.7;
    } else if (idObj == 5) {
      s1 = 1.;  s2 = 1.;
    }
    if (abs (qHit.x) > s2 - 0.03)
       cFac = 1. - 0.9 * SmoothBump (- 0.08, 0.08, 0.02, qHit.z + s1);
    if (qHit.z < - s1)
       cFac = 1. - 0.9 * SmoothBump (- 0.05, 0.05, 0.02, abs (qHit.x) - s2);
  }
  vec3 col;
  vec3 nn;
  if (idObj >= 1 && idObj <= 5) nn = flyerMat * n;
  if (idObj == 1 || idObj == 2) {
    col = mix (uCol, bCol, 1. - smoothstep (-0.6, 0., nn.y));
    if (idObj == 2 && nn.y < 0.)
       col = mix (bCol, wCol, SmoothBump (-0.8, 0.8, 0.3, qHit.z + 0.28));         
  } else if (idObj == 3 || idObj == 4) {
    col = mix (bCol, wCol, SmoothBump (-0.8, 0.8, 0.3, qHit.z));
  } else if (idObj == 5) {
    col = mix (bCol, tCol, SmoothBump (-0.6, 0.8, 0.3, qHit.z));
  } else if (idObj == 6) {
    col = bCol;
  } else if (idObj == 7 || idObj == 8) {
    col = tCol;
  }
  if (idObj == 1) {
    if (qHit.z > 4.5 && abs (qHit.x) > 0.07) idObj = 10;
  } else if (idObj == 2) {
    float s = - qHit.z;
    if (s > 0. && s < 9.) {
      vec2 ws = vec2 (qHit.y - 0.5, mod (s + 1.5, 1.5) - 0.75);
      ws *= ws;
      if (dot (ws, ws) < 0.02) idObj = 10;
    }
  }
  return col * cFac;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  float eps = 0.01;
  vec4 col4;
  vec3 objCol, col, vn;
  float dstHit, dstGrnd, dstObj, dstPropel, f;
  int idObjT;
  vec3 roo = ro;
  dstHit = dstFar;
  dstGrnd = GrndRay (ro, rd);
  wSpan = 12.;
  engPos = vec3 (0.35 * wSpan, -0.2, -1.5);
  idObj = 0;
  dstObj = ObjRay (ro, rd);
  idObjT = idObj;
  dstPropel = TransObjRay (ro, rd);
  if (dstObj < dstPropel) dstPropel = dstFar;
  float refFac = 1.;
  if (dstGrnd < dstObj && ro.y + dstGrnd * rd.y < 0.) {
    float dw = - ro.y / rd.y;
    ro += dw * rd;
    rd = reflect (rd, WaterNf (ro, dw));
    ro += eps * rd;
    eps *= 1.01;
    dstGrnd = GrndRay (ro, rd);
    idObj = 0;
    dstObj = ObjRay (ro, rd);
    idObjT = idObj;
    refFac *= 0.6;
  }
  bool isGrnd = false;
  if (dstObj < dstGrnd) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    idObj = idObjT;
    objCol = ObjCol (ro, vn);
    if (idObj == 10) objCol = vec3 (0.2) + 0.5 * SkyCol (ro, reflect (rd, vn));
    float dif = max (dot (vn, sunDir), 0.);
    col = sunCol * objCol * (0.2 * (1. +
       max (dot (vn, - normalize (vec3 (sunDir.x, 0., sunDir.z))), 0.)) +
       max (0., dif) * ObjSShadow (ro, sunDir) *
       (dif + 0.5 * pow (max (0., dot (sunDir, reflect (rd, vn))), 100.)));
    dstHit = dstObj;
  } else {
    vec3 rp = ro + dstGrnd * rd;
    if (refFac < 1.) dstHit = length (rp - roo);
    else dstHit = dstGrnd;
    if (dstHit < dstFar) {
      ro = rp;
      isGrnd = true;
    } else {
      col = refFac * SkyCol (ro, rd);
    }
  }
  if (isGrnd) {
    vn = GrndNf (ro, dstHit);
    col4 = GrndCol (ro, vn);
    col = col4.xyz * refFac;
    float dif = max (dot (vn, sunDir), 0.);
    col *= sunCol * (0.2 * (1. +
       max (dot (vn, - normalize (vec3 (sunDir.x, 0., sunDir.z))), 0.)) +
       max (0., dif) * GrndSShadow (ro, sunDir) *
       (dif + col4.w * pow (max (0., dot (sunDir, reflect (rd, vn))), 100.)));
  }
  if (dstPropel < dstFar) col = 0.7 * col + 0.1 -
     0.04 * SmoothBump (1.5, 1.7, 0.02, length (qHitTransObj.xy));
  if (dstHit < dstFar) {
    f = dstHit / dstFar;
    col = mix (col, refFac * SkyBg (rd), clamp (1.03 * f * f, 0., 1.));
  }
  col = sqrt (clamp (col, 0., 1.));
  return clamp (col, 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = 2. * fragCoord.xy / iResolution.xy - 1.;
  vec2 uvs = uv;
  uv.x *= iResolution.x / iResolution.y;
  trkOffset = vec2 (0.);
  float zmFac = 2.7;
  tCur = 15. * iTime + 100. * trkOffset.y;
  sunDir = normalize (vec3 (0.9, 1., 0.4));
  sunCol = vec3 (1., 0.9, 0.8);
  vec3 ro, rd, vd, fpF, fpB, vDir;
  szFac = 0.25;
  float dt = 1.;
  fpF = TrackPath (tCur + dt);
  flyerPos = TrackPath (tCur);
  fpB = TrackPath (tCur - dt);
  vec3 vel = (fpF - fpB) / (2. * dt);
  vel.y = 0.;
  vec3 acc = (fpF - 2. * flyerPos + fpB) / (dt * dt);
  acc.y = 0.;
  vec3 va = cross (acc, vel) / length (vel);
  float roll = 12. * length (va);
  if (va.y < 0.) roll *= -1.;
  vDir = normalize (vel);
  float cRl = cos (roll);
  float sRl = sin (roll);
  flyerMat = mat3 (cRl, - sRl, 0., sRl, cRl, 0., 0., 0., 1.) *
     mat3 (vDir.z, 0., vDir.x, 0., 1., 0., - vDir.x, 0., vDir.z);
  float vuPeriod = 500.;
  flyerPos.y = 3. + 1.5 * sin (2.5 * tCur / vuPeriod);
  float lookDir = 2. * mod (floor (tCur / vuPeriod), 2.) - 1.;
  ro = TrackPath (tCur - 40. * lookDir * (1. -
     0.8 * abs (sin (pi * mod (tCur, vuPeriod) / vuPeriod))));
  ro.y = 3. + 0.6 * sin (0.16 * tCur / vuPeriod);
  vd = flyerPos - ro;
  vd.y = 0.;
  vd = normalize (lookDir * vDir + 0.3 * normalize (vd));
  mat3 scMat = mat3 (vd.z, 0., - vd.x, 0., 1., 0., vd);
  rd = scMat * normalize (vec3 (uv, zmFac));
  vec3 col = ShowScene (ro, rd);
  uvs *= uvs * uvs;
  col = mix (vec3 (0.7), col, pow (max (0., 0.95 - length (uvs * uvs * uvs)), 0.3));
  fragColor = vec4 (col, 1.);
}

float rand(float n){return fract(sin(n) * 43758.5453123);}

float noise(float p){
	float fl = floor(p);
  float fc = fract(p);
	return mix(rand(fl), rand(fl + 1.0), fc);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 offset = vec2(sin(iTime / 5.0), cos(iTime / 5.0)) / 10.0 + iMouse.xy/1000.0;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    vec3 col = vec3(0);
    for (float i = 0.; i < 500.; i++) {
        vec2 star = mod(vec2(noise(i), noise(i * 23.)) + offset, vec2(1.0));

        float intensity = 0.5 + noise(i * 17.) * 0.5;

        col += clamp(intensity * pow(1. - distance(star, uv)/2., 1200.0), 0., 1.);

        // First 100 stars are yellow
        if (i < 100.) {
            col.b = 0.;
        }
    }

    // Add some contrast
    col = (col - 0.2) * 1.5;

    fragColor = vec4(col, 1.0);
}

// Hue to RGB function from Fabrice's shadertoyunofficial blog:
#define hue2rgb(hue) 0.6 + 0.6 * cos(6.3 * hue + vec3(0.0, 23.0, 21.0))

#define SIN_15 0.2588190451
#define COS_15 0.96592582628

// Hash from "Hash without Sine" by Dave_Hoskins (https://www.shadertoy.com/view/4djSRW):
float Noise3D(in vec3 p3) {
	p3  = fract(p3 * 0.1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float SmoothNoise3D(in vec3 p) {
    vec3 cell = floor(p);
    vec3 local = fract(p);
    local *= local * (3.0 - 2.0 * local);

    float ldb = Noise3D(cell);                       // Left, Down, Back
    float rdb = Noise3D(cell + vec3(1.0, 0.0, 0.0)); // Right, Down, Back
    float ldf = Noise3D(cell + vec3(0.0, 0.0, 1.0)); // Left, Down, Front
    float rdf = Noise3D(cell + vec3(1.0, 0.0, 1.0)); // Right, Down, Front
    float lub = Noise3D(cell + vec3(0.0, 1.0, 0.0)); // Left, Up, Back
    float rub = Noise3D(cell + vec3(1.0, 1.0, 0.0)); // Right, Up, Back
    float luf = Noise3D(cell + vec3(0.0, 1.0, 1.0)); // Left, Up, Front
    float ruf = Noise3D(cell + vec3(1.0, 1.0, 1.0)); // Right, Up, Front

    return mix(mix(mix(ldb, rdb, local.x),
                   mix(ldf, rdf, local.x),
                   local.z),

               mix(mix(lub, rub, local.x),
                   mix(luf, ruf, local.x),
                   local.z),

               local.y);
}

float FractalNoise3D(in vec3 p, in float scale, in float octaves) {
    float value = 0.0;
    float nscale = 1.0;
    float tscale = 0.0;

    for (float octave=0.0; octave < octaves; octave++) {
        value += SmoothNoise3D(p * pow(2.0, octave) * scale) * nscale;
        tscale += nscale;
        nscale *= 0.5;
    }

    return value / tscale;
}

float mapScene(in vec3 p) {
    float octaves = 4.0;
    float threshold = 0.4;

    if (iMouse.z > 0.0) {
        octaves = iMouse.y / iResolution.y * 4.0;
        threshold = iMouse.x / iResolution.x * 0.9;
    }

    vec3 q = abs(p) -1.5;
    float bbox = max(q.x, max(q.y, q.z));

    return max(FractalNoise3D(p, 2.0, octaves) - threshold, bbox);
}

vec3 getNormal(in vec3 p) {
    return normalize(vec3(mapScene(p + vec3(0.001, 0.0, 0.0)) - mapScene(p - vec3(0.001, 0.0, 0.0)),
                          mapScene(p + vec3(0.0, 0.001, 0.0)) - mapScene(p - vec3(0.0, 0.001, 0.0)),
                          mapScene(p + vec3(0.0, 0.0, 0.001)) - mapScene(p - vec3(0.0, 0.0, 0.001))));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec3 ro = vec3(0.0, 0.0, 5.0), rd = normalize(vec3(uv, -1.0)), p;
    float dt = 0.0, d;
    bool hit = false;

    for (int iter=0; iter < 100; iter++) {
        p = ro + rd * dt;

        p.y -= 0.3;
        p.yz *= mat2(COS_15, SIN_15, -SIN_15, COS_15);
        float c = cos(iTime), s = sin(iTime);
        p.xz *= mat2(c, s, -s, c);

        vec3 q = abs(p) - 1.5;
        d = max(q.x, max(q.y, q.z));
        if (d < 0.001) {
            d = mapScene(p);
            if (d < 0.001) {
                hit = true;
                break;
            }
        }

        if (dt > 10.0) {
            break;
        }

        dt += d;
    }

    if (hit) {
        vec3 n = getNormal(p);
        fragColor.rgb += 0.5 + 0.5 * n;
    }
}

// Kate's Kaleidoscope.


// A thing inspired by a thing, inspired by another thing, inspired by a thing by Frank Herbert.


// An attempt to code a visual effect on my lunch break,
// to match a piece of music that K8-Bit made on her lunch break.   (https://soundcloud.com/k8-bit)


// by Nickenstein79, Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License. (02/12/2020)


#define NUM_LAYERS 					(11)
#define LAYER_SEPERATION_FACTOR		(0.03621)
#define ZOOM_FACTOR_PERIOD 			(90.0)  	
#define ZOOM_FACTOR_MIN 			(0.5)
#define ZOOM_FACTOR_MAX 			(2.8)
#define SCROLL_SPEED_AT_MIN_ZOOM	(1.72)
#define SCROLL_SPEED_AT_MAX_ZOOM	(3.343138)
#define ROTATION_MATRIX_MAX_SKEW	(0.277)		// The maximum skewing/warping of the rotation matrix
#define ROTATION_MATRIX_SKEW_PERIOD (8.2)		// The time taken to fully skew and un-skew the rotation matrix

#define CRT_FILTER_ON							// Toggle this line to dissable/enable the CRT Filter
#define CRT_VIGNETTE_ON						// Toggle this line to dissable/enable the CRT-edge vignette
#define CRT_EXTRA_NOISE_ON					// Toggle this line to dissable/enable the CRT-Extra Noise

#define TWO_PI						(6.283185307179586476925286766559)
#define LAYER_STEP_SIZE 			(1.0/float(NUM_LAYERS))

// Returns a psuedo-random float value (in the range 0.0 to 1.0) generated from a 2D vector
// works by combining the input seed value with prime numbers.
// Using primes causes sequential inputs (such as pixel coordinates) to still generate non-sequential outputs.
float Hash_From2D(vec2 Vec)
{
    float f = Vec.x + Vec.y * 37.0;
    return fract(sin(f)*104003.9);
}

// Returns a sin wave driven value that oscilates between Min and Max over the given time period
float OscilateSinScalar(float Min, float Max, float Period)
{
	return (Max-Min) * (sin(iTime*TWO_PI/Period)*0.5+0.5) + Min;
}

// Returns the interpolant (fraction) of CurrentValue between Min and Max in the range (0.0 to 1.0)
float GetInterpolant(float Min, float Max, float CurrentValue)
{
    return (CurrentValue-Min) / (Max-Min);
}

// returns a skewed Z rotation matrix (The skewing changes over time)
mat2 ZRotate_Skewed(float Angle)
{
    // Get a value to skew the rotion matrix with, which bounces between 0.0 and ROTATION_MATRIX_MAX_SKEW, over a given time period.
    float Skew = 1.0 - OscilateSinScalar(0.0, ROTATION_MATRIX_MAX_SKEW, ROTATION_MATRIX_SKEW_PERIOD);

    // As the input angle is based on an incrimental timer multiplied by magic numbers and getting the cosine of these values,
    // this generates an animated angle which drifts chaotically and 
    // unpredictably over time (Like a double-pendulum's motion. Actually, a triple-pendulum as we combine three cos() values).
    // twiddle with the multiply values being passed to cos() until you like the motion of the simulation.
	Angle = cos(Angle*0.1)*cos(Angle*0.7)*cos(Angle*0.73)*2.0;    

    // build and return the skewed Z-rotation matrix
    return mat2(sin(Angle * Skew),cos(Angle),-cos(Angle * Skew),sin(Angle));
}

// sample the input textures which creates our desired material surface effect
vec4 SampleMaterial(vec2 uv)
{
   float t = iTime * 0.5;

   // sample texture 0, just the red channel to get an initial grey-scale value in range 0.0 to 1.0
   float Sample0 = texture(iChannel0, uv * 0.1 ).b;

   // Modify Sample0 by subtracting circlular patterns that grow across the texture over time
   // fiddle with the numbers to get differening circular patterns
   Sample0 -= 0.5 + sin(t + sin(uv.x) + sin(uv.y)) * 0.7; 

   // adjust brightness
   Sample0 *= 1.6;

   // ensure no negative values with abs(), this also causes a double
   // concentric-circle pattern as sin() returns -1 to +1 values.
   Sample0 = abs(Sample0);

   // invert these fat bright negative-circles to get thin bright circles and curves
   Sample0 = 1.0/(Sample0*10.0+1.0);

   // make a greyscale colour from Sample0, then multiply it by a full-colour sample from eleswhere on texture 0
   vec4 Colour = vec4(Sample0) * texture(iChannel0, uv * 0.05);

   // multiple this colour by a scrolling sample from texture 1 (random coloured noise texture)
   // Adjust the multiplyer to expand or shrink the range of the coloured noise texture in use
   return Colour * texture(iChannel1,(uv + (iTime*1.3)) * 0.001735);
}

// Generate fast downward cycling fat scanlines
float scanline(vec2 uv, float TexHeight) 
{
	return sin(iResolution.y * uv.y * 0.7 - iTime * 10.0);
}

// Generate slow upward cycling thin scanlines
float slowscan(vec2 uv, float TexHeight) 
{
	return sin(iResolution.y * uv.y * 0.02 + iTime * 6.0);
}

// warp uv coordinates to emulate a curved crt screen
vec2 crt_bend_uv_coords(vec2 coord, float bend)
{
	// put in symmetrical coords
	//coord = (coord - 0.5) * 2.0;
	//coord *= 0.5;	

	// deform coords
	coord.x *= 1.0 + pow((abs(coord.y) / bend), 2.0);
	coord.y *= 1.0 + pow((abs(coord.x) / bend), 2.0);

	// transform back to 0.0 - 1.0 space
	coord  = (coord / 1.0) + 0.5;

	return coord;
}

// wibble the screen, crt magnetic interferance distortion effect
vec2 scandistort(vec2 uv) 
{
	float scan1 = clamp(cos(uv.y * 2.0 + iTime), 0.0, 1.0);
	float scan2 = clamp(cos(uv.y * 2.0 + iTime + 4.0) * 10.0, 0.0, 1.0);
	float amount = scan1 * scan2 * uv.x; 
	uv.x -= 0.05 * mix( texture(iChannel1, vec2(uv.x, amount)).x * amount, amount, 0.9 );

	return uv;
}

// CRT edge vignette
vec3 CRT_Vignette(vec3 Colour, vec2 uv)
{
    uv.x /= iResolution.x / iResolution.y;
	float Vignette = clamp(pow(cos(uv.x * 3.1415), 1.3) * pow(cos(uv.y * 3.1415), 1.3) * 50.0, 0.0, 1.0);
    Colour *= Vignette;

    return Colour;    
}

// Cathode ray tube filter, to look like an old cathode ray-tube monitor with scanlines, and V-Hold, etc...
vec3 CRT_Filter(vec3 Colour, vec2 uv)
{
#ifdef CRT_EXTRA_NOISE_ON    
    Colour.r += Hash_From2D(uv * iTime * 911.911 * 4.0) * 0.19;
    Colour.g += Hash_From2D(uv * iTime * 563.577 * 4.0) * 0.19;
    Colour.b += Hash_From2D(uv * iTime * 487.859 * 4.0) * 0.19;
#endif    

    vec2 sd_uv = uv;
	vec2 crt_uv = crt_bend_uv_coords(sd_uv, 2.0);    
    vec3 scanline_Colour;
	vec3 slowscan_Colour;
	scanline_Colour.x = scanline(crt_uv, iResolution.y);
    slowscan_Colour.x = slowscan(crt_uv, iResolution.y);
    scanline_Colour.y = scanline_Colour.z = scanline_Colour.x;
	slowscan_Colour.y = slowscan_Colour.z = slowscan_Colour.x;
	Colour = mix(Colour, mix(scanline_Colour, slowscan_Colour, 0.5), 0.04);

    // apply the CRT-vignette filter
#ifdef CRT_VIGNETTE_ON
    Colour = CRT_Vignette(Colour, uv);
#endif     

    return Colour;
}

// Perform some post processing effects on the output colour
vec3 PostProcessColour(vec3 Colour, vec2 uv)
{
    // Vignette (Darken the pixels nearer the corners of the screen)
    Colour -= vec3(length(uv*0.1));

    // Add some random noise to the colour with the Hashing function
	Colour += Hash_From2D(uv*iTime*0.01)*0.02;

    // apply the CRT-screen filter
#ifdef CRT_FILTER_ON
    Colour = CRT_Filter(Colour, uv);
#endif 

    // Approximate the brightness of the colour by using it as a 3d spacial vector and getting its length in colour space
    float Brightness = length(Colour);

    // inrease the colour contrast, by dimming the darker colours and brightening the lighter ones, 
    // via linear interpolation of the colour and its approximated brightness value
	Colour = mix(Colour, vec3(Brightness), Brightness - 0.5);

    return Colour;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // get a UV chord for each texel in the range -1.0 to 1.0 (so we can scale/rotate everything from the center of the screen)
	vec2 uv = fragCoord.xy / iResolution.xy - 0.5;

    // modify the X component of the uv, by the screen's aspect ratio to avoid stretching of the input textures in width
	uv.x *= iResolution.x / iResolution.y;

    // start with black
	vec3 Colour = vec3(0.0, 0.0, 0.0);

    // Determine a scale value that oscilates between MIN_ZOOM and MAX_ZOOM over the desired time period
    float ScaleValue = OscilateSinScalar(ZOOM_FACTOR_MIN, ZOOM_FACTOR_MAX, ZOOM_FACTOR_PERIOD);

    // Determine a speed to scroll through the texture space.
    // This works by generating a speed value between MIN_SCROLL and MAX_SCROLL
    // which is guided by the current zoom value, such that when the texture
    // is fully zoomed out, the scroll speed is at the desired maximum, and when
    // the texture is fully zoomed in, the scroll speed is at the desired minimum.
    float ScrollInterpolant = GetInterpolant(ZOOM_FACTOR_MIN, ZOOM_FACTOR_MAX, ScaleValue);
    // mix performs a linear interpolation between two inputs
    float ScrollValue = mix(SCROLL_SPEED_AT_MIN_ZOOM, SCROLL_SPEED_AT_MAX_ZOOM, ScrollInterpolant); 

	// Sum the colour contribution of each layer   
	for (float i = 0.0; i < 1.0; i += LAYER_STEP_SIZE)
	{
        // clone the uv, so the original is preserved for each itteration of the loop
		vec2 uv2 = uv;

        // Rotate with a Z-Rotation matrix that skewes over time 
        // (Giving a slighlty different input angle for each layer, as i-squared is combined in)
        uv2 *= ZRotate_Skewed(iTime * i*i * 12.0 * LAYER_SEPERATION_FACTOR);

        // Scale (Again, giving a slighlty different scale for each layer, as i-squared is combined in)
		uv2 *= ScaleValue * (i*i+1.0); 

        // Scroll the sampling position over time
		uv2.xy += ScrollValue + iTime*0.125;        

        // sample the material, building up each layer's colour contribution
		Colour += SampleMaterial(uv2).xyz*LAYER_STEP_SIZE*3.5;
	}

    // Perform some post processing on the accumulated colour layers
    Colour = PostProcessColour(Colour, uv);

    // set the generated colour as the final output pixel colour
	fragColor = vec4(Colour, 1.0);
}

#define PI 3.14159265358979323846

vec2 rotate2D(vec2 st, float angle){
    st -= 0.5;
    st =  mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle)) * st;
    st += 0.5;
    return st;
}

vec2 tile(vec2 st, float zoom,float rotD){
    st *= zoom;
     if(rotD==1.) {
        st.x+=.5;
        st.y+=.5;
     }
    return fract(st);
}

float square(vec2 st, vec2 side){
    vec2 border = vec2(0.5)-side*0.5;
    vec2 pq = smoothstep(border,border+.01,st);
    pq *= smoothstep(border,border+.01,vec2(1.0)-st);
    return pq.x*pq.y;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord.xy/iResolution.y;
    float color;
    float Nsquares=4.;
    float rotDirection=0.;

	uv = rotate2D(uv,-sin(iTime)*PI*.5);
    rotDirection=step(0.,sin(iTime));
    uv = tile(uv,Nsquares,rotDirection);
    uv = rotate2D(uv,PI/4.-sin(iTime)*PI*0.25);
    if (rotDirection==1.)
        color = 1.0-square(uv,vec2(0.7));
    else 
    color=square(uv,vec2(0.7));
    fragColor = vec4(vec3(color),1.0);
    //fragColor=vec4(color,fract(123.56*sin(.0001*iTime)),fract(241.56*sin(.0001*iTime)),1.);
}

// Hash from "Hash without Sine" by Dave_Hoskins (https://www.shadertoy.com/view/4djSRW):
float Noise3D(in vec3 p3) {
	p3  = fract(p3 * 0.1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float SmoothNoise3D(in vec3 p) {
    vec3 cell = floor(p);
    vec3 local = fract(p);
    local *= local * (3.0 - 2.0 * local);

    float ldb = Noise3D(cell);                       // Left, Down, Back
    float rdb = Noise3D(cell + vec3(1.0, 0.0, 0.0)); // Right, Down, Back
    float ldf = Noise3D(cell + vec3(0.0, 0.0, 1.0)); // Left, Down, Front
    float rdf = Noise3D(cell + vec3(1.0, 0.0, 1.0)); // Right, Down, Front
    float lub = Noise3D(cell + vec3(0.0, 1.0, 0.0)); // Left, Up, Back
    float rub = Noise3D(cell + vec3(1.0, 1.0, 0.0)); // Right, Up, Back
    float luf = Noise3D(cell + vec3(0.0, 1.0, 1.0)); // Left, Up, Front
    float ruf = Noise3D(cell + vec3(1.0, 1.0, 1.0)); // Right, Up, Front

    return mix(mix(mix(ldb, rdb, local.x),
                   mix(ldf, rdf, local.x),
                   local.z),

               mix(mix(lub, rub, local.x),
                   mix(luf, ruf, local.x),
                   local.z),

               local.y);
}

float FractalNoise3D(in vec3 p, in float scale, in float octaves) {
    float value = 0.0;
    float nscale = 1.0;
    float tscale = 0.0;

    for (float octave=0.0; octave < octaves; octave++) {
        value += SmoothNoise3D(p * pow(2.0, octave) * scale) * nscale;
        tscale += nscale;
        nscale *= 0.5;
    }

    return value / tscale;
}

