#define MAX_RAY_DIS 4.1
#define EPSILON 0.001

float sdSphere(vec3 p, float r) {
    return length(p) - r;
}
float sdPlane(vec3 p, float y) {
    return p.y - y;
}
float sdVerticalCapsule(vec3 p, float h, float r) {
    vec3 closestP = vec3(0.0, clamp(p.y, 0.0, h), 0.0);
    return length(p - closestP) - r;
}
float sdCapsules(vec3 p, float cx, float r) {
    return 0.0;
}
float getSde(vec3 p) {
    //return min(length(p) - 1.0, p.y + 0.4);
    return min(min(sdSphere(p, 1.0), sdPlane(p, -0.4)), sdVerticalCapsule(p - vec3(-1.2, -0.2, -0.2), 1.0, 0.1));
}
float marchShadowRay(vec3 ro, vec3 l) {
    vec3 rd = normalize(l - ro);
    float ld = length(l - ro);
    float t = EPSILON * 15.0;
    while(t < MAX_RAY_DIS * 3.0) {
        float sde = getSde(ro + rd * t);
        if(sde < EPSILON) {
            break;
        }
        t += sde;
        if(t >= ld) {
            return 1.0;
        }
    }
    return 0.0;
}
// Thanks to Baron for this
vec3 getSkyCol(vec3 rd) {
    float t = (rd.y + 1.0) / 2.0;
    return vec3((1.0 - t) + t * 0.3, (1.0 - t) + t * 0.5, (1.0 - t) + t);
}
// Thanks to IQ for this
vec3 estimateNormal(vec3 p) {
    vec2 h = vec2(EPSILON, 0.0);
    return normalize(vec3(getSde(p + h.xyy) - getSde(p - h.xyy),
                         getSde(p + h.yxy) - getSde(p - h.yxy),
                         getSde(p + h.yyx) - getSde(p - h.yyx)));
}
vec3 getPixel(vec2 coords) {
    //vec3 ro = vec3(sin(iTime) * 3.0, 0.0, cos(iTime) * -3.0);
    //vec3 ro = vec3(0.0, 0.0, -3.0);
    vec3 ro = vec3(0.0, 0.0, -3.0 + sin(iTime / 2.0));
    float ry = iTime;
    //vec3 rd = normalize(vec3(cos(ry + 90.0) + cos(ry) * coords.x, coords.y, sin(ry + 90.0) + sin(ry) * coords.x));
    vec3 rd = normalize(vec3(coords, 1.0));
    float t = 0.0;
    bool hit = false;
    while(t < MAX_RAY_DIS) {
        float sde = getSde(ro + rd * t);
        if(sde < EPSILON) {
            hit = true;
            break;
        }
        t += sde;
    }
    if(hit) {
        vec3 col = vec3(0.2, 0.8, 0.6);
        vec3 p = ro + rd * t;
        //vec3 lightPos = vec3(0.0, 2.0, -3.0);
        vec3 lightPos = vec3(sin(iTime) * 3.0, 2.0, cos(iTime) * -3.0);
        float lightOnPoint = marchShadowRay(p, lightPos);
        if(lightOnPoint == 0.0) {
            return col * 0.1;
        }
        vec3 norm = estimateNormal(p);
        vec3 lightDir = normalize(lightPos - p);
        float ambient = 0.1;
        float diffuse = clamp(dot(norm, lightDir), 0.0, 1.0);
        float shininess = 32.0;
        float specular = clamp(pow(dot(reflect(lightDir, norm), rd), shininess) * (1.0 - dot(norm, lightDir)), 0.0, 1.0);
        return (col * (ambient + diffuse) + vec3(1.0, 1.0, 1.0) * specular) * lightOnPoint;
    }
    return getSkyCol(rd);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float mind = min(iResolution.x, iResolution.y);
    vec2 coords = fragCoord;
    if(mind == iResolution.y) {
        coords.x -= (iResolution.x - mind) / 2.0;
    } else {
        coords.y -= (iResolution.y - mind) / 2.0;
    }
    coords = coords / mind - 0.5;
    vec3 col = getPixel(coords);
    fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 O, in vec2 f )
{
    vec2 uv = f / iResolution.x;
    float t = iTime * 0.1;
    uv.x -= t;
    float yy = exp(uv.y * 4.+.7),
        k = floor(yy)*3.0,
        x = fract(uv.x*k*2.0);
    vec2 u = vec2(x, fract(yy));    
    u.x = abs(u.x - .5) * 2.;
    vec2 m = u - vec2(.0,1.);
    m.y *= 1.5;
    vec3 g = vec3(fract(u.x)*(1.0-u.y), 0.0, 0.0);
    float c = step(length(m),0.7);
    float b = u.x + u.y,
        a = uv.y * .1,
        d = smoothstep(b + a , b - a, 1.);
    float tip = clamp(c - d, .0, 1.);
    vec3 gradient = vec3(fract(u.x)*(1. - u.y));
    vec3 white = vec3(1., 1., .93) - gradient * .4;
    vec3 red = vec3(.8,0.,0.) - g * .5;
    float e = fract(u.x - .5) * (u.y) * .3;
    vec3 black = vec3(e);
    vec3 col = mix(white, red, d);
    float n =  (u.y);
    col.r += n * .2;
    col = mix(col, black,tip);
    col -= uv.y * .37;
    O = vec4(col,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.yy;

    vec3 start = vec3(0.9f, 0.5f, 0);

    vec3 ray = normalize(vec3(uv.xy, 5) - start);

    vec3 point = vec3(0.5, 0.5, 200);    

    float dist = 10.f;

    float t = ray.x * (point.x - start.x) + ray.y * (point.y - start.y) + ray.z * (point.z - start.z);
    t /= ray.x * ray.x + ray.y * ray.y + ray.z * ray.z;

    vec3 hitPoint = start + ray * t;

    if(distance(hitPoint, point) > dist)
    {
        fragColor = vec4(0.f);
        return;
    }

    vec3 l = normalize(vec3(0, 1, 0));
    l.x = l.x * cos(iTime) - l.y * sin(iTime);
    l.y = l.x * sin(iTime) + l.y * cos(iTime);
    vec3 n = normalize(point - hitPoint);

    vec4 baseColor = vec4(1.f, 0.f, 0.f, 1.f);

    vec4 ambient = vec4(0.5, 0.5, 0.5, 1.0);

    vec4 color = vec4(0.f);

    color = baseColor * ambient;

    color += baseColor * min(max(dot(l, n), 0.f), 1.f);

    fragColor = color;

}

